--
-- Cấu trúc bảng cho bảng `uml_product`
--

CREATE TABLE `uml_product` (
  `product_id` int(11) UNSIGNED NOT NULL,
  `product_catid` int(11) NOT NULL,
  `product_name` varchar(255) NOT NULL,
  `product_slug` varchar(255) NOT NULL,
  `product_img` varchar(100) NOT NULL,
  `product_detail` text NOT NULL,
  `product_number` int(11) UNSIGNED NOT NULL,
  `product_price` float(12,0) NOT NULL,
  `product_pricesale` float(12,0) NOT NULL,
  `product_metakey` varchar(255) NOT NULL,
  `product_metadesc` varchar(255) NOT NULL,
  `product_createdat` timestamp NOT NULL DEFAULT '0000-00-00 00:00:00',
  `product_createdby` int(11) UNSIGNED NOT NULL DEFAULT '1',
  `product_updatedat` timestamp NOT NULL DEFAULT '0000-00-00 00:00:00',
  `product_updatedby` int(11) UNSIGNED NOT NULL DEFAULT '1',
  `product_status` int(11) NOT NULL DEFAULT '1'
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

--
-- Đang đổ dữ liệu cho bảng `uml_product`
--

INSERT INTO `uml_product` (`product_id`, `product_catid`, `product_name`, `product_slug`, `product_img`, `product_detail`, `product_number`, `product_price`, `product_pricesale`, `product_metakey`, `product_metadesc`, `product_createdat`, `product_createdby`, `product_updatedat`, `product_updatedby`, `product_status`) VALUES
(1, 7, 'Samsung Galaxy A70', 'dien-thoai-samsung-galaxy-a70', 'dien-thoai-samsung-galaxy-a70.png', '<h2>Samsung Galaxy A70 là một phiên bản phóng to của chiếc&nbsp;Samsung Galaxy A50 đã ra mắt trước đó với nhiều cải tiến tới từ bên trong.</h2><h3>Màn hình kích thước lớn, trải nghiệm \"đã hơn\"</h3><p>Màn hình của chiếc Galaxy A70 có kích thước khá lớn lên đến 6.7 inch độ phân giải Full HD+ trên tấm nền Super AMOLED.</p><figure class=\"image\"><img src=\"https://cdn.tgdd.vn/Products/Images/42/195012/samsung-galaxy-a70-tgdd-6.jpg\" alt=\"Samsung Galaxy A70 | Thiết kế màn hình\"></figure><p>Các cạnh viền bezel được tinh chỉnh mỏng hơn, điều này giúp máy trông gọn gàng và cân xứng hơn rất nhiều.</p><figure class=\"image\"><img src=\"https://cdn.tgdd.vn/Products/Images/42/195012/samsung-galaxy-a70-tgdd-10.jpg\" alt=\"Samsung Galaxy A70 | Viền màn hình\"></figure><p>Samsung vẫn trang bị cho Galaxy A70 một mặt lưng vẫn bằng nhựa, nhưng sử dụng chất liệu cao cấp hơn mà Samsung gọi là “3D Graffitistic”.</p><figure class=\"image\"><img src=\"https://cdn.tgdd.vn/Products/Images/42/195012/samsung-galaxy-a70-tgdd-7.jpg\" alt=\"Samsung Galaxy A70 | Thiết kế mặt lưng\"></figure><p>Ngôn ngữ nhựa giả thủy tinh này cho trải nghiệm tốt hơn, cứng cáp và chắc chắn, khi nhấn vào không bị ọp ẹp như chất liệu nhựa.</p><figure class=\"image\"><img src=\"https://cdn.tgdd.vn/Products/Images/42/195012/samsung-galaxy-a70-mau-xanh.jpg\" alt=\"Samsung Galaxy A70 | Màu xanh\"></figure><p>Galaxy A70 có 3 màu sắc bán ra tương tự các dòng sản phẩm thấp hơn trước đó là \"Đen, Trắng và Xanh\". Cả 3 phiên bản này đều được trang bị thêm hiệu ứng lấp lánh nhiều màu khi nghiêng theo góc nhìn rất đẹp mắt.</p><figure class=\"image\"><img src=\"https://cdn.tgdd.vn/Products/Images/42/195012/samsung-galaxy-a70-mau-den.jpg\" alt=\"Samsung Galaxy A70 | Màu đen\"></figure><h3>Cảm biến vân tay dưới màn hình tiên tiến</h3><p>Tương tự như người anh em Galaxy A50 thì&nbsp;Galaxy A70 vẫn sở hữu cho mình công nghệ cảm biến vân tay dưới màn hình đang rất hot hiện nay.</p><p>Tốc độ nhận diện và mở khóa máy khá nhanh giúp bạn tiết kiệm được thời gian so với việc nhập mật khẩu thông thường.</p><figure class=\"image\"><img src=\"https://cdn.tgdd.vn/Products/Images/42/195012/samsung-galaxy-a70-vantay.jpg\" alt=\"Samsung Galaxy A70 | Vân tay dưới màn hình\"></figure><p>Việc được trang bị cảm biến vân tay bên trong màn hình giúp máy trông liền mạch và thanh thoát hơn vì không phải hi sinh bất cứ bộ phận nào để đặt cảm biến này.</p><figure class=\"image\"><img src=\"https://cdn.tgdd.vn/Products/Images/42/195012/samsung-galaxy-a70-tgdd-11.jpg\" alt=\"Samsung Galaxy A70 | Vân tay dưới màn hình\"></figure><p>Song song với cảm biến vân tay thì chiếc <a href=\"https://www.thegioididong.com/dtdd\">smartphone</a> này cũng hỗ trợ mở khóa bằng khuôn mặt với độ chính xác khá cao.</p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p><h3>Camera chất lượng cao, nhiều nâng cấp</h3><p>Cụm 3 camera trên Galaxy A70 đã được nâng cấp rõ rệt với camera chính độ phân giải 32 MP cùng&nbsp;ống kính thứ 2 vẫn là 8 MP chụp ảnh góc siêu rộng và ống kính 5 MP dùng để chụp ảnh chân dung xóa phông.</p><figure class=\"image\"><img src=\"https://cdn.tgdd.vn/Products/Images/42/195012/samsung-galaxy-a70-tgdd-8.jpg\" alt=\"Samsung Galaxy A70 | Camera sau\"></figure><p>Với bộ 3 camera thì Samsung Galaxy A50 đáp ứng cho bạn hầu hết các nhu cầu trong sử dụng hằng ngày từ chụp ảnh xóa phông hay chụp phong cảnh trong những chuyến đi chơi xa.</p><figure class=\"image\"><img src=\"https://cdn.tgdd.vn/Products/Images/42/195012/samsung-galaxy-a70-tgdd-12.jpg\" alt=\"Samsung Galaxy A70 | Giao diện camera\"></figure><p>Bên cạnh đó với công nghệ AI Camera đi kèm giúp máy có thể nhận diện được 20 vật thể khác nhau để có thể điều chỉnh màu sắc, độ tương phản và độ sáng sao cho phù hợp.</p><figure class=\"image\"><img src=\"https://cdn.tgdd.vn/Products/Images/42/195012/samsung-galaxy-a70-tgdd-1.jpg\" alt=\"Samsung Galaxy A70 | Camera AI\"></figure><p>Camera selfie trên máy cũng sở hữu độ phân giải khủng 32 MP giúp bạn luôn có được những bức ảnh selfie ấn tượng nhất với làn da được làm đẹp một cách tự nhiên.</p><h3>Hiệu năng tốt, pin dùng cả ngày</h3><p>Máy được trang bị vi xử lý <a href=\"https://www.thegioididong.com/tin-tuc/qualcomm-ra-mat-chip-smartphone-tam-trung-moi-snapdragon-675-1126381\">Snapdragon 675</a> mạnh mẽ, đi kèm đó là dung lượng RAM 6 GB, bộ nhớ trong 128 GB.</p><figure class=\"image\"><img src=\"https://cdn.tgdd.vn/Products/Images/42/195012/samsung-galaxy-a70-tgdd-3.jpg\" alt=\"Samsung Galaxy A70 | Điểm Antutu Benchmark\"></figure><p>Đặc biệt dung lượng pin lên tới 4500 mAh giúp bạn sử dụng cả ngày mà không phải lo lắng về việc sạc pin cho thiết bị.</p><figure class=\"image\"><img src=\"https://cdn.tgdd.vn/Products/Images/42/195012/samsung-galaxy-a70-tgdd-2.jpg\" alt=\"Samsung Galaxy A70 | Trải nghiệm hiệu năng\"></figure><p>Bên cạnh đó, Galaxy A70 còn hỗ trợ công nghệ sạc nhanh 25W, giúp bạn hồi lại năng lượng để tiếp tục mọi trải nghiệm một cách rất nhanh chóng.</p><figure class=\"image\"><img src=\"https://cdn.tgdd.vn/Products/Images/42/195012/samsung-galaxy-a70-tgdd-9.jpg\" alt=\"Samsung Galaxy A70 | Cổng sạc Type-C\"></figure>', 20, 9290000, 12290000, '', '', '2019-06-11 18:38:06', 1, '2019-06-23 21:12:30', 1, 0),
(2, 7, 'Samsung Galaxy S10+ 128GB Bạc Đa Sắc', 'dien-thoai-samsung-galaxy-s10-128gb-bac-da-sac', 'dien-thoai-samsung-galaxy-s10-128gb-bac-da-sac.png', 'Samsung Galaxy S10+ đã ra mắt và thực sự người dùng khó có thể cường lại sức hút kỳ lạ tới từ siêu phẩm này của Samsung và giờ đây hãng sản xuất smartphone tới từ Hàn Quốc tiếp tục tung ra phiên bản Samsung Galaxy S10+ 128GB Bạc Đa Sắc thậm chí còn thu hút hơn các phiên bản cũ.\r\nPhiên bản giới hạn với bộ phụ kiện cao cấp\r\nKhác biệt hoàn toàn với phiên bản S10+ thông thường, chiếc điện thoại S10+ Park Hang Seo (Bạc Đa Sắc) này sở hữu bộ phụ kiện độc đáo không máy nào có được.\r\n\r\nSamsung Galaxy S10+ 128GB Bạc Đa Sắc | Phụ kiện theo máy\r\n\r\nĐiểm nhấn của phiên bản này là chiếc ốp lưng màu đỏ, in hình người thầy đáng kính của đội tuyển Việt Nam.\r\n\r\nSamsung Galaxy S10+ 128GB Bạc Đa Sắc | Phụ kiện theo máy\r\n\r\nHơn nữa, máy còn độc đáo ở chỗ \"màu bạc đa sắc\" sẽ cho từng màu khác nhau khi chuyển đổi góc máy.\r\n\r\nSamsung Galaxy S10+ 128GB Bạc Đa Sắc | Thiết kế mặt lưng\r\n\r\nTừng màu vàng, bạc, hay đỏ,... sẽ xuất hiện giúp người dùng thêm độc đáo và không bị \"đụng hàng\".\r\n\r\nSamsung Galaxy S10+ 128GB Bạc Đa Sắc | Thiết kế mặt lưng\r\n\r\nThiết kế gây ấn tượng từ cái nhìn đầu tiên\r\nSamsung Galaxy S10+ sở hữu màn hình Infinity-O tràn viền với màu sắc vô cùng sống động chắc chắn sẽ làm tan chảy bất kì con tim nào.\r\n\r\nSamsung Galaxy S10+ 128GB Bạc Đa Sắc | Thiết kế màn hình\r\n\r\nNhờ thiết kế đục lỗ, Galaxy S10+ có màn hình tràn viền thực sự, nhìn đã mắt và tỷ lệ màn hình/mặt trước của máy là 89%, cao hơn nhiều so với thế hệ Galaxy S9 cũ.\r\n\r\nSamsung Galaxy S10+ 128GB Bạc Đa Sắc | Thiết kế màn hình\r\n\r\nCông nghệ màn hình Dynamic AMOLED, tỷ lệ màn hình 19.5:9, viền bezel cực mỏng, có thể nói là mỏng nhất từ trước đến nay từng thấy, trải nghiệm hình ảnh trên S10+ là cực kỳ đẳng cấp và đáng giá.\r\n\r\nMột nâng cấp tuy nhỏ nhưng khá quan trọng khác là màn hình trên Galaxy S10+ hỗ trợ chuẩn hình ảnh tiên tiến nhất hiện nay công nghệ HDR10+.\r\n\r\nSamsung Galaxy S10+ 128GB Bạc Đa Sắc | Thiết kế màn hình\r\n\r\nĐiều này sẽ giúp màu sắc tái hiện sống động hơn và đặc biệt độ sâu của màu đen nhìn đã hơn, sướng mắt hơn.\r\n\r\nCamera được nâng cấp mạnh mẽ\r\nSamsung đã \"bắt trend\" rất nhanh ở trào lưu 3 camera khi Galaxy S10+ được trang bị thêm một camera góc rộng siêu rộng nữa bên cạnh camera tele và camera tiêu chuẩn.\r\n\r\nSamsung Galaxy S10+ 128GB Bạc Đa Sắc | Giao diện camera\r\n\r\nCamera góc siêu rộng 16 MP trên S10+ có góc chụp tới 123 độ, tiêu cự 12mm, khẩu độ f/2.2, là một trong những camera góc rộng nhất hiện nay trên smartphone.\r\n\r\nSamsung Galaxy S10+ 128GB Bạc Đa Sắc | Ảnh chụp góc rộng\r\n\r\nVới cụm ba máy ảnh, Nếu bạn muốn lấy nhiều cảnh vật hơn, bạn có thể chuyển qua ống kính siêu rộng, nếu bạn muốn chụp chân dung, bạn chuyển qua ống kính tele.\r\n\r\nẢnh chụp từ camera của điện thoại Samsung Galaxy S10+ 128GB Bạc Đa Sắc chính hãng\r\n\r\nThậm chí nếu bạn muốn điều khiển toàn bộ thông số máy ảnh, bạn có thể chọn chế độ chuyên nghiệp trên phần mềm chụp ảnh. S10+ có thể phơi sáng tối đa 10 giây, đủ để bạn có được những cảnh chụp đêm hay phong cảnh huyền bí.\r\n\r\nẢnh chụp từ camera của điện thoại Samsung Galaxy S10+ 128GB Bạc Đa Sắc chính hãng\r\n\r\nVới camera selfie, S10+ có đến 2 camera gồm 1 camera chính 10 MP, khẩu độ f/1.9, tiêu cự 26mm, điểm ảnh 1.22µm, hỗ trợ lấy nét Dual Pixel PDAF.\r\n\r\nBên cạnh đó là 1 camera 8 MP, khẩu độ f/2.2, tiêu cự 22mm (wide), điểm ảnh 1.12µm, chuyên để thu độ sâu phục vụ việc xóa phông.\r\n\r\nẢnh chụp từ camera của điện thoại Samsung Galaxy S10+ 128GB Bạc Đa Sắc chính hãng\r\n\r\nCác chế độ làm đẹp hay xóa phông đều làm việc hiệu quả, tạo ra những bức ảnh selfie \"lung linh\" hơn nhưng không cà mặt quá đà như trước nữa.\r\n\r\nHiệu năng hàng đầu thế giới smartphone\r\nCung cấp sức mạnh cho Samsung Galaxy S10+ 128GB Bạc Đa Sắc là con chip hàng đầu tới từ Samsung Exynos 9820 8 nhân 64-bit cùng 8 GB RAM và 128 GB bộ nhớ trong.\r\n\r\nTrong sử dụng thực tế, không bất ngờ khi hiệu năng của S10+ đáp ứng rất nhanh và mượt mà, các ứng dụng mở gần như ngay tức thì, cuộn trang và các hiệu ứng chuyển động trên giao diện phần mềm diễn ra trơn tru.\r\n\r\nSamsung Galaxy S10+ 128GB Bạc Đa Sắc | Trải nghiệm chơi game\r\n\r\nNếu kết hợp với đế Galaxy DeX, thậm chí bạn hoàn toàn có thể để laptop ở nhà mà vẫn có thể đảm bảo công việc văn phòng chạy trơn tru như bình thường.\r\n\r\nNgoài ra máy còn sở hữu chuẩn kết nối Wi-Fi 6 hoàn toàn mới đảm bảo chiếc smartphone của bạn có thể kết nối internet nhanh nhất có thể.\r\n\r\nSamsung Galaxy S10+ 128GB Bạc Đa Sắc | Trên tay và trải nghiệm\r\n\r\nThời lượng pin 4100mAh sẽ đủ để bạn dùng từ sáng đến tối mà không cần phải sạc giữa chừng, thậm chí còn dư để có thể sạc không dây cho các thiết bị khác khi cần.\r\n\r\nNhiều trang bị cao cấp khác nữa\r\nSamsung là một hãng smartphone hiếm hoi vẫn giữ được những chất riêng cho chiếc smartphone của mình.\r\n\r\nBạn sẽ vẫn có jack cắm tai nghe 3.5 mm để có thể sử dụng với các tai nghe phổ biến hiện nay mà không cần thông qua jack chuyển.\r\n\r\nSamsung Galaxy S10+ 128GB Bạc Đa Sắc | Jack 3.5 mm\r\n\r\nBạn vẫn sẽ có tiêu chuẩn chống nước và bụi bẩn cao cấp nhất hiện nay IP68 giúp máy có thể chịu đựng được trong các môi trường khắc nghiệt.\r\n\r\nVà kèm theo đó là một bộ giải mã âm thanh cực kỳ đáng giá hỗ trợ chuẩn Dolby Atmos/AKG để phục vụ các tín đồ yêu âm nhạc.\r\n\r\nSamsung Galaxy S10+ 128GB Bạc Đa Sắc | Vân tay siêu âm\r\n\r\nSamsung Galaxy S10+ 128GB Bạc Đa Sắc cũng là một trong những smartphone đầu tiên sở hữu công nghệ cảm biến vân tay siêu âm bên trong màn hình với độ chính xác rất cao.', 10, 23990000, 0, '', '', '2019-06-23 05:44:09', 1, '2019-06-23 21:12:31', 1, 0),
(3, 9, 'Huawei Y7 Pro (2019)', 'dien-thoai-huawei-y7-pro-2019', 'dien-thoai-huawei-y7-pro-2019.png', '<h2>Hoàn toàn lột xác so với phiên bản tiền nhiệm,&nbsp;<a href=\"https://www.thegioididong.com/dtdd/huawei-y7-pro-2019\">Y7 Pro (2019)</a>&nbsp;đã giúp&nbsp;<a href=\"https://www.thegioididong.com/dtdd-huawei\">Huawei</a>&nbsp;có&nbsp;thêm điểm cộng&nbsp;trong mắt người dùng nhờ việc đem thiết kế mặt lưng gradient, màn hình giọt nước và pin khủng lên chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd\">smartphone</a>&nbsp;giá rẻ của mình.</h2><h3>Màn hình hiển thị giọt nước cực lớn</h3><p>Y7 Pro (2019) được trang bị màn hình rộng lên đến 6.26 inch, với độ phân giải HD+.</p><figure class=\"image\"><img src=\"https://cdn.tgdd.vn/Products/Images/42/197205/huawei-y7-pro-2019-1-1.jpg\" alt=\"Màn hình Huawei Y7 Pro 2019 chính hãng\"></figure><p>Cùng với&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-ips-lcd-la-gi-905752\">tấm nền IPS</a>, hình ảnh hiển thị trên máy rõ nét và đầy đủ, màu sắc tự nhiên trên mọi góc nhìn. Người dùng hoàn toàn có thể trải nghiệm thế giới đầy màu sắc trong tầm tay.</p><figure class=\"image\"><img src=\"https://cdn.tgdd.vn/Products/Images/42/197205/huawei-y7-pro-2019-10.jpg\" alt=\"Màn hình Huawei Y7 Pro 2019 chính hãng\"></figure><h3>Mặt lưng chuyển màu gradient cao cấp</h3><p>Tưởng rằng mặt lưng gradient chỉ áp dụng cho các dòng sản phẩm tầm trung hoặc cao cấp, thì nay với Y7 Pro (2019) bạn đã có thể sở hữu được một thiết bị có thiết kế đầy nổi bật với cá tính.</p><figure class=\"image\"><img src=\"https://cdn.tgdd.vn/Products/Images/42/197205/huawei-y7-pro-2019-6.jpg\" alt=\"Mặt lưng Huawei Y7 Pro 2019 chính hãng\"></figure><p>Phần khung viền của máy được bo cong mềm mại, giúp khả năng cầm nắm trơn tru, gọn trong lòng bàn tay, mọi thao tác đều dễ dàng và nhanh chóng.</p><figure class=\"image\"><img src=\"https://cdn.tgdd.vn/Products/Images/42/197205/huawei-y7-pro-2019-2.jpg\" alt=\"Thiết kế Huawei Y7 Pro 2019 chính hãng\"></figure><h3>Khả năng nhiếp ảnh thông minh</h3><p>Với&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/cong-nghe-camera-kep-2-camera-la-gi-giong-va-khac-nhau-nhu-the-nao--962027\">camera kép</a>&nbsp;mặt sau tích hợp&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/ai-la-gi-sao-tri-tue-nhan-tao-lam-smartphone-thong-minh-hon-955420\">trí tuệ nhân tạo AI</a>&nbsp;bao gồm&nbsp;thấu kính chính 13 MP + thấu kính phụ 2 MP, Y7 Pro (2019) có thể dễ dàng nhận ra các vật thể, lấy nét tốt với ánh sáng hợp lý hơn nhờ khẩu độ f/1.8.</p><figure class=\"image\"><img src=\"https://cdn.tgdd.vn/Products/Images/42/197205/huawei-y7-pro-2019-4.jpg\" alt=\"Cụm camera kép Huawei Y7 Pro 2019 chính hãng\"></figure><p>Camera AI trên máy nhận biết được hơn 500 bối cảnh thuộc 22 danh mục ngay lập tức, do đó mọi ảnh chụp của bạn đều trở nên chân thực và đẹp nhất.</p><figure class=\"image\"><img src=\"https://cdn.tgdd.vn/Products/Images/42/197205/huawei-y7-pro-2019-5.jpg\" alt=\"Camera trước Huawei Y7 Pro 2019 chính hãng\"></figure><p>Ngoài ra, camera selfie 16 MP có khả năng tự động tinh chỉnh, bạn không cần tốn công chỉnh sửa hình ảnh và tự tin up ngay tấm hình vừa chụp lên mạng xã hội chia sẻ với bạn bè.</p><h3>Dung lượng pin lớn 4000 mAh</h3><p>So với Y7 Pro (2018) có viên pin chỉ 3000 mAh thì phiên bản 2019 này đã có sự cải thiện tới 4000 mAh.</p><figure class=\"image\"><img src=\"https://cdn.tgdd.vn/Products/Images/42/197205/huawei-y7-pro-2019-9.jpg\" alt=\"Đánh giá Huawei Y7 Pro 2019 chính hãng\"></figure><p>Cùng với công nghệ tiết kiệm năng lượng bằng AI, Huawei Y7 Pro (2019) giúp bạn thoải mái sử dụng, nhất là trong các chuyến đi dài, tha hồ trải nghiệm cuộc sống mà không lo sạc pin.</p><h3>Hiệu năng mượt mà và thông minh hơn</h3><p>Huawei Y7 Pro (2019) mang trong mình con chip 8 nhân&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/qualcomm-snapdragon-450-ra-mat-sieu-tiet-kiem-pin-ho-tro-camera-kep-quick-charge-3-0-997437\">Snapdragon 450</a>&nbsp;với RAM 3 GB.&nbsp;</p><figure class=\"image\"><img src=\"https://cdn.tgdd.vn/Products/Images/42/197205/huawei-y7-pro-2019-13.jpg\" alt=\"Chơi game trên Huawei Y7 Pro 2019 chính hãng\"></figure><p>Cấu hình này đủ để đáp ứng các giải trí cơ bản thường ngày, cũng như xử lý ổn định các dòng game phổ biến hiện nay như Liên Quân ở mức cấu hình trung hay DDTank, Plants And Zombies,...</p>', 20, 3490000, 4680000, '', '', '2019-06-23 07:58:32', 1, '2019-06-23 21:12:33', 1, 0),
(4, 12, 'Laptop Dell Inspiron 3576 i5 8250U/4GB/1TB/Win10', 'laptop-dell-inspiron-3576-i5-8250u-4gb-1tb-win10', 'laptop-dell-inspiron-3576-i5-8250u-4gb-1tb-win10-p63f002n76f.png', '<h2>Thiết kế thanh lịch, trọng lượng khá nhẹ phù hợp cho việc di chuyển hằng ngày đến lớp học, công sở -&nbsp;<a href=\"https://www.thegioididong.com/laptop/dell-inspiron-3576-p63f002n76f\">Laptop Dell Inspiron 3576 i5 8250U&nbsp;</a>được trang bị cấu hình đủ mạnh để chạy tốt các ứng dụng văn phòng, cho phản hồi các thao tác kéo thả trong photoshop, AI khá nhanh rất phù hợp cho&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=hoc-tap-van-phong\">sinh viên, nhân viên văn phòng.</a></h2><h3>Thiết kế đơn giản, thuận tiện di chuyển</h3><p>Vỏ được làm bằng nhựa nhám bao bọc giúp cho máy hạn chế được bụi bẩn, trầy xước cũng như chịu va chạm tốt trong quá trình sử dụng. Chiếc <a href=\"https://www.thegioididong.com/laptop-dell\">máy tính xách tay Dell</a>&nbsp;có trọng lượng vừa phải chỉ 2.3 kg, vì vậy chúng ta có thể mang theo bên mình khi làm việc ngoài các không gian khác.</p><figure class=\"image\"><img src=\"https://cdn.tgdd.vn/Products/Images/44/166521/laptop-dell-inspiron-3576-p63f002n76f-9.jpg\" alt=\"Thiết kế truyền thống trên máy tính xách tay Dell Inspiron 3576\"></figure><h3>Xử lý tốt đồ hoạ cơ bản</h3><p><a href=\"https://www.thegioididong.com/laptop\">Laptop</a> Dell Inspiron 3576 <a href=\"https://www.thegioididong.com/laptop?g=core-i5\">core i5&nbsp;thế hệ 8</a>, xung nhịp&nbsp;1.6 Ghz kết hợp cùng <a href=\"https://www.thegioididong.com/laptop?g=4-gb\">4GB RAM</a>&nbsp;đáp ứng đầy đủ nhu cầu xử lý đồ hoạ cơ bản như photoshop, Ai. Chiến các game như liên minh huyền thoại, fifa online 3 khá mượt mà.</p><figure class=\"image\"><img src=\"https://cdn.tgdd.vn/Products/Images/44/166521/dell-inspiron-3576-p63f002n76f-33397-cauhinh.jpg\" alt=\"Cấu hình mạnh mẽ trên máy tính xách tay Dell Inspiron 3576 i5 8250U\"></figure><h3>Màn hình rộng trải nghiệm giải trí hoàn hảo</h3><p>Sở hữu&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=tren-15\">màn hình rộng&nbsp;15.6 inch</a>&nbsp;cùng với đó là độ phân giải HD, với chất lượng ảnh tươi sáng, không gian làm việc rộng rãi, dễ nhìn.</p><h3>Bàn phím to, rõ độ nảy tốt thuận tiện đánh máy</h3><p>Bàn phím fullsize trên máy tính xách tay Dell Inspiron 3576 phù hợp cho kế toán, người thường sử dụng bàn phím số để nhập liệu. Độ nảy phím tốt sẽ giúp gõ phím chính xác, hạn chế sai lỗi chính tả không cần thiết.</p><figure class=\"image\"><img src=\"https://cdn.tgdd.vn/Products/Images/44/166521/dell-inspiron-3576-p63f002n76f-33397-banphim.jpg\" alt=\"Bàn phím của máy tính xách tay Dell Inspiron 3576 i5 8250U\"></figure><h3>Ổ đĩa quang thuận tiện học tập, <a href=\"https://www.thegioididong.com/hoi-dap/usb-30-la-gi-926737\">USB 3.0 </a>truyền dữ liệu nhanh chóng</h3><p>Máy có ổ đĩa quang giúp bạn xem phim hay học tiếng Anh với đĩa DVD dễ dàng hơn. Một điểm cộng tiếp theo máy vẫn trang bị cổng mạng LAN giúp bạn kết nối tiện lợi với internet khi Wi-Fi yếu.&nbsp;Kèm theo cổng USB 2.0 là&nbsp;2 x USB 3.0&nbsp;giúp truyền tải dữ liệu nhanh hơn. Thêm nữa, cổng HDMI cho phép kết nối với tivi hoặc máy chiếu tiện lợi.</p>', 50, 14390000, 0, '', '', '2019-06-23 08:01:43', 1, '2019-06-23 21:12:34', 1, 0),
(5, 8, 'Xiaomi Redmi Note 7 64GB', 'xiaomi-redmi-note-7-64gb', 'xiaomi-redmi-note-7-64gb.png', '<h3>Thiết kế hiện đại, theo xu thế</h3><p>Chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd-xiaomi\">điện thoại Xiaomi</a> mới là một sự lột xác hoàn toàn về thiết kế so với các dòng Redmi trước đây.</p><figure class=\"image\"><img src=\"https://cdn.tgdd.vn/Products/Images/42/167535/xiaomi-redmi-note-7-tgdd-4.jpg\" alt=\"Xiaomi Redmi Note 7 4GB 64GB | Thiết kế\"></figure><p>Máy sở hữu cho mình&nbsp;mặt lưng bằng kính phối màu gradient, mang lại cho máy cảm giác cao cấp và sang trọng hơn rất nhiều.</p><p>4 góc máy được tăng cường độ bền đáng kể giúp chống vỡ kính gấp 4 lần so với các smartphone khác trên thị trường hiện nay.</p><h3>Màn hình giọt nước thích mắt</h3><p>Xiaomi Redmi Note 7 sở hữu màn hình 6.3 inch của có tỷ lệ khung hình 19.5:9, <a href=\"https://www.thegioididong.com/tin-tuc/do-phan-giai-man-hinh-qhd-hd-fullhd-2k-4k-la-gi--592178#fullhd\">độ phân giải Full HD+</a>(2.340 x 1.080 pixel).</p><figure class=\"image\"><img src=\"https://cdn.tgdd.vn/Products/Images/42/167535/xiaomi-redmi-note-7-tgdd-1.jpg\" alt=\"Xiaomi Redmi Note 7 4GB 64GB | Màn hình\"></figure><p>Thiết kế màn hình theo kiểu giọt nước giúp máy gây ấn tượng cho người dùng ngay từ cái nhìn đầu tiên.</p><p>Độ sáng của máy cũng khá cao lên tới&nbsp;450 nits&nbsp;giúp bạn dễ dàng xem nội dung dưới ánh sáng mặt trời và bảo vệ mắt.</p><h3>Camera chất lượng tốt trong tầm giá</h3><p>Xiaomi Redmi Note 7 trở thành chiếc điện thoại có camera \"khủng\" giá rẻ nhất trên thị trường hiện nay với \"số chấm\" lên tới&nbsp;&nbsp;48 MP.</p><figure class=\"image\"><img src=\"https://cdn.tgdd.vn/Products/Images/42/167535/xiaomi-redmi-note-7-camera-tgdd-4.jpg\" alt=\"Xiaomi Redmi Note 7 4GB 64GB | Ảnh chụp từ camera\"></figure><p>Đi kèm cảm biến phụ 5 MP dùng để lấy dữ liệu chiều sâu trong chế độ chụp ảnh chân dung.</p><p>Camera của máy còn được hỗ trợ bởi AI giúp bạn có được những bức ảnh đẹp mà không cần phải qua hậu kỳ.</p><figure class=\"image\"><img src=\"https://cdn.tgdd.vn/Products/Images/42/167535/xiaomi-redmi-note-7-camera-tgdd-2.jpg\" alt=\"Xiaomi Redmi Note 7 4GB 64GB | Ảnh chụp chân dung\"></figure><p>Camera trước cũng được đầu tư mạnh mẽ với độ phân giải 13 MP cũng được tích hợp sẵn chế độ làm đẹp hứa hẹn sẽ không làm các chị em phải thất vọng.</p><h3>Dung lượng pin khủng</h3><p>Xiaomi Redmi Note 7 được trang bị viên pin có dung lượng lên tới 4000 mAh cho bạn sử dụng thoải mái cả ngày dài.</p><figure class=\"image\"><img src=\"https://cdn.tgdd.vn/Products/Images/42/167535/xiaomi-redmi-note-7-10.jpg\" alt=\"Xiaomi Redmi Note 7 4GB 64GB | Trải nghiệm và đánh giá\"></figure><p>Một điểm cộng nữa chính là máy&nbsp;hỗ trợ sạc nhanh giúp bạn rút ngắn thời gian nạp đầy năng lượng cho máy đáng kể.</p><figure class=\"image\"><img src=\"https://cdn.tgdd.vn/Products/Images/42/167535/xiaomi-redmi-note-7-tgdd-2.jpg\" alt=\"Xiaomi Redmi Note 7 4GB 64GB | Cổng sạc Type-C\"></figure><p>Và máy cũng có một thay đổi đáng kể là đã chuyển sang cổng kết nối USB Type-C thay vì Micro USB như trước.</p>', 50, 4900000, 0, '', '', '2019-06-23 08:07:22', 1, '2019-06-23 21:12:35', 1, 0),
(6, 7, 'Samsung Galaxy Note 8', 'samsung-galaxy-note-8', 'samsung-galaxy-note-8.jpg', '<h2><a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-note8\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung Galaxy Note 8 tại Thegioididong.com\" type=\"Tham khảo điện thoại Samsung Galaxy Note 8 tại Thegioididong.com\">Galaxy Note 8</a> là niềm hy vọng vực lại dòng Note danh tiếng của <a href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại Samsung đang kinh doanh tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại Samsung đang kinh doanh tại Thegioididong.com\">điện thoại Samsung</a> với diện mạo nam tính, sang trọng. Máy trang bị camera kép xóa phông thời thượng, <a href=\"https://www.thegioididong.com/dtdd?f=man-hinh-tran-vien\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại có thiết kế màn hình tràn viền tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại có thiết kế màn hình tràn viền tại Thegioididong.com\">màn hình vô cực</a> như trên <a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-s8-plus\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung Galaxy S8 Plus tại Thegioididong.com\" type=\"Tham khảo điện thoại Samsung Galaxy S8 Plus tại Thegioididong.com\">S8 Plus</a>, bút S Pen cùng nhiều tính năng mới và nhiều công nghệ được ưu ái.</h2><h3>Màn hình vô cực hiện đại tiên tiến</h3><p>Dù được giới thiệu là màn hình lên tới 6.3 inch nhưng thực sự khi cầm <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại đang kinh doanh tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại đang kinh doanh tại Thegioididong.com\">điện thoại</a> Note 8 trên tay rất nhỏ gọn nhờ vào công nghệ \"màn hình vô cực\" tiên tiến nhất hiện nay của Samsung.</p><p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Files/2017/09/09/1020228/z764741903200_347a0ee08cf7acf973047629cf2c29ac-_2560x1920-800-resize.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại Samsung Galaxy Note 8\" data-original=\"https://cdn.tgdd.vn/Files/2017/09/09/1020228/z764741903200_347a0ee08cf7acf973047629cf2c29ac-_2560x1920-800-resize.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Files/2017/09/09/1020228/z764741903200_347a0ee08cf7acf973047629cf2c29ac-_2560x1920-800-resize.jpg\" title=\"Màn hình điện thoại Samsung Galaxy Note 8\"></a></p><p>Các cạnh bên máy được làm cong đều khiến mình khi cầm có cảm giác ôm tay rất thoải mái và không hề bị cấn.</p><p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Files/2017/09/09/1020228/z764741534567_c7686e1d5bdbcd1a1a365182240bd1fd-_2560x1920-800-resize.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại Samsung Galaxy Note 8\" data-original=\"https://cdn.tgdd.vn/Files/2017/09/09/1020228/z764741534567_c7686e1d5bdbcd1a1a365182240bd1fd-_2560x1920-800-resize.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Files/2017/09/09/1020228/z764741534567_c7686e1d5bdbcd1a1a365182240bd1fd-_2560x1920-800-resize.jpg\" title=\"Thiết kế điện thoại Samsung Galaxy Note 8\"></a></p><p>Cả mặt trước và mặt sau máy đều được phủ kín bằng lớp kính phản chiếu rất đẹp như đã từng xuất hiện ở bộ đôi S8/S8+.</p><p>Và màn hình 2K+ trên Note 8 hoạt động tốt, góc nhìn sắc nét.</p><p><div class=\'ratingbox\'><div class=\'item\'><p><b>❝</b>Điện thoại kiểu dáng nam tính, thiết kế gọn và mỏng hơn những dòng note trước... cảm thấy rất thích thú khi sở hữu chiếc galaxy note 8 này lắm... cám ơn SAMSUNG rất nhiều.<b>❞</b></p><label class=\'line\'></label><span class=\'info\'><b>La Tuấn Huỳnh</b> - Q.5, TP.HCM<label class=\'sttB\'><i class=\'iconcom-checkbuy\'></i>Đã mua tại Thế giới di động</label></span></div></div></p><h3>Cấu hình mạnh mẽ bậc nhất</h3><p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Files/2017/09/09/1020228/z764740566215_8c972c312d8bddcc08180c8590a87824-_2560x1920-800-resize.jpg\" onclick=\"return false;\"><img alt=\"Giao diện Android điện thoại Samsung Galaxy Note 8\" data-original=\"https://cdn.tgdd.vn/Files/2017/09/09/1020228/z764740566215_8c972c312d8bddcc08180c8590a87824-_2560x1920-800-resize.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Files/2017/09/09/1020228/z764740566215_8c972c312d8bddcc08180c8590a87824-_2560x1920-800-resize.jpg\" title=\"Giao diện Android điện thoại Samsung Galaxy Note 8\"></a></p><p>Điện thoại Samsung Galaxy Note 8 được cài sẵn hệ điều hành Android Nougat đi kèm với giao diện Samsung Experience mới nhất. Theo nhiều người dùng đánh giá thì đây có thể nói là bộ giao diện đẹp và hiệu quả nhất của Samsung tới tận bây giờ.</p><p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Files/2017/09/09/1020228/z764696952365_76c2667ff0451a11b42b27a3f0531a11-_2560x1920-800-resize.jpg\" onclick=\"return false;\"><img alt=\"Giao diện Android điện thoại Samsung Galaxy Note 8\" data-original=\"https://cdn.tgdd.vn/Files/2017/09/09/1020228/z764696952365_76c2667ff0451a11b42b27a3f0531a11-_2560x1920-800-resize.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Files/2017/09/09/1020228/z764696952365_76c2667ff0451a11b42b27a3f0531a11-_2560x1920-800-resize.jpg\" title=\"Giao diện Android điện thoại Samsung Galaxy Note 8\"></a></p><p>Với cấu hình khủng long Exynos 8895 8 nhân và RAM 6 GB, Note 8 hỗ trợ chạy đa nhiệm rất tốt, đáp ứng hầu hết các nhu cầu chơi game 3D nặng.</p><h3>Camera kép xoá phông chuẩn xác</h3><p>Camera kép xóa phông trên Note 8 có cùng độ phân giải 12 MP với ống kính chính góc rộng 12 MP và một ống kính 12 MP còn lại dùng để zoom quang, có thể nói là ... cảm giác như mình đang sử dụng một chiếc máy ảnh thực thụ vậy.</p><p> </p><p>Xem thêm bài đánh giá chi tiết Camera Note 8 <a href=\"https://www.thegioididong.com/tin-tuc/danh-gia-nhanh-camera-galaxy-note-8-1022313\" target=\"_blank\" title=\"Đánh giá camera Note 8\">tại đây</a>.</p><p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Files/2017/09/09/1020228/z764695551993_4e53d714ec90efa122e24b691fecde8d_986x620-800-resize.jpg\" onclick=\"return false;\"><img alt=\"Giao diện camera trên điện thoại Samsung Galaxy Note 8\" data-original=\"https://cdn.tgdd.vn/Files/2017/09/09/1020228/z764695551993_4e53d714ec90efa122e24b691fecde8d_986x620-800-resize.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Files/2017/09/09/1020228/z764695551993_4e53d714ec90efa122e24b691fecde8d_986x620-800-resize.jpg\" title=\"Giao diện camera trên điện thoại Samsung Galaxy Note 8\"></a></p><p>Camera trước 8 MP cũng có khả năng chụp hình góc rộng có hỗ trợ chế độ làm đẹp giúp bạn tự sướng trông xinh đẹp hơn, cùng với đó còn có tính năng chụp bằng giọng nói phá bỏ giới hạn chụp hình của bạn.</p><h3>Sự đặc biệt ở SPen thần thánh</h3><p>Suýt nữa thì quên không nhắc tới S-Pen thần thánh, chiếc bút đã làm nên thương hiệu của dòng Note.</p><p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Files/2017/09/09/1020228/z764742026769_afed1116f4c414a94018ff3fdd8dd0be-_2560x1920-800-resize.jpg\" onclick=\"return false;\"><img alt=\"Cây bút SPen trên điện thoại Samsung Galaxy Note 8\" data-original=\"https://cdn.tgdd.vn/Files/2017/09/09/1020228/z764742026769_afed1116f4c414a94018ff3fdd8dd0be-_2560x1920-800-resize.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Files/2017/09/09/1020228/z764742026769_afed1116f4c414a94018ff3fdd8dd0be-_2560x1920-800-resize.jpg\" title=\"Cây bút SPen trên điện thoại Samsung Galaxy Note 8\"></a></p><p>Như thường lệ thì S-Pen sẽ càng ngày càng \"khôn\" với những tính năng như viết ngay cả khi màn hình đã tắt, live message,... và điều thú vị nữa là S-Pen sẽ có cùng với màu máy luôn. Khỏi sợ đụng hàng nha.</p><p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Files/2017/09/09/1020228/z764742155244_49913d91e2b539f95b6ecdb7b67be33b-_2560x1920-800-resize.jpg\" onclick=\"return false;\"><img alt=\"Thao tác bút SPen trên điện thoại Samsung Galaxy Note 8\" data-original=\"https://cdn.tgdd.vn/Files/2017/09/09/1020228/z764742155244_49913d91e2b539f95b6ecdb7b67be33b-_2560x1920-800-resize.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Files/2017/09/09/1020228/z764742155244_49913d91e2b539f95b6ecdb7b67be33b-_2560x1920-800-resize.jpg\" title=\"Thao tác bút SPen trên điện thoại Samsung Galaxy Note 8\"></a></p><p>Cuối cùng là pin 3.300 mAh trên Note 8 được cho là đuối hơn so với S8+ nhưng qua một số bài thử nghiệm cho thấy, khả năng trụ vững của Note 8 cũng khá đáng nể, khi vượt qua cả iPhone 7 Plus và Galaxy S8.</p><p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Files/2017/09/09/1020228/z764695361368_e4c34c2815f6935f37d6b728c6033647-_2560x1920-800-resize.jpg\" onclick=\"return false;\"><img alt=\"Thao tác bút SPen trên điện thoại Samsung Galaxy Note 8\" data-original=\"https://cdn.tgdd.vn/Files/2017/09/09/1020228/z764695361368_e4c34c2815f6935f37d6b728c6033647-_2560x1920-800-resize.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Files/2017/09/09/1020228/z764695361368_e4c34c2815f6935f37d6b728c6033647-_2560x1920-800-resize.jpg\" title=\"Thao tác bút SPen trên điện thoại Samsung Galaxy Note 8\"></a></p><p><div class=\'ratingbox\'><div class=\'item\'><p><b>❝</b>Sản phẩm tuyệt vời, bút S pen quá đỉnh, chức năng nhắn tin bằng bút là ưng ý nhất. Màn hình infinity đẹp long lanh, là chiếc điện thoại ưng ý nhất tôi từng mua.<b>❞</b></p><label class=\'line\'></label><span class=\'info\'><b>TRAN QUANG MINH NHAT</b> - Gò Vấp, TP.HCM<label class=\'sttB\'><i class=\'iconcom-checkbuy\'></i>Đã mua tại Thế giới di động</label></span></div></div></p></article>', 77, 4600000, 0, '', '', '2019-06-23 20:37:19', 1, '2019-06-23 20:37:19', 1, 1),
(7, 6, 'iPhone 7 Plus 32GB', 'iphone-7-plus-32gb', 'iphone-7-plus-32gb.jpg', '<h2>Mặc dù giữ nguyên vẻ bề ngoài so với dòng <a href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại iPhone tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại iPhone tại Thegioididong.com\">điện thoại iPhone</a> đời trước, bù lại <a href=\"https://www.thegioididong.com/dtdd/iphone-7-plus\" target=\"_blank\" title=\"Chi tiết điện thoại iPhone 7 Plus 32GB\" type=\"Chi tiết điện thoại iPhone 7 Plus 32GB\">iPhone 7 Plus 32GB</a> lại được trang bị nhiều nâng cấp đáng giá như camera kép đầu tiên cũng như cấu hình mạnh mẽ.</h2><h3>Chiếc <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại tại Thegioididong.com\">điện thoại</a> sở hữu camera kép đầu tiên của Apple</h3><p>iPhone 7 Plus là chiếc iPhone đầu tiên được trang bị camera kép có cùng độ phân giải 12 MP, đem lại khả năng chụp ảnh ở hai tiêu cự khác nhau.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/78124/iphone-7-plus-32gb-tgdd-7.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại smartphone iPhone 7 Plus 32GB | Camera kép\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/78124/iphone-7-plus-32gb-tgdd-7.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/78124/iphone-7-plus-32gb-tgdd-7.jpg\" title=\"Điện thoại smartphone iPhone 7 Plus 32GB | Camera kép\"></a></p><p>Camera chính chụp hình góc rộng, còn camera phụ có tiêu cự phù hợp để chụp chân dung, có tính năng chụp chân dung xóa phông (làm mờ hậu cảnh). </p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/78124/iphone-7-plus-32gb-tgdd-3.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại smartphone iPhone 7 Plus 32GB | Ảnh xoá phông\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/78124/iphone-7-plus-32gb-tgdd-3.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/78124/iphone-7-plus-32gb-tgdd-3.jpg\" title=\"Điện thoại smartphone iPhone 7 Plus 32GB | Ảnh xoá phông\"></a></p><p>Với 1 chạm nhanh chóng bạn có thể chuyển đổi giữa chế độ 1x và zoom 2x, hoặc bạn có thể kéo thanh trượt hay dùng 2 ngón tay đến zoom. Apple đã thêm tính năng zoom kỹ thuật số lên đến 10x.</p><h3><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/78124/iphone-7-plus-32gb-tgdd-1.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại smartphone iPhone 7 Plus 32GB | Ảnh chụp cảnh\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/78124/iphone-7-plus-32gb-tgdd-1.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/78124/iphone-7-plus-32gb-tgdd-1.jpg\" title=\"Điện thoại smartphone iPhone 7 Plus 32GB | Ảnh chụp cảnh\"></a></h3><p>Camera trước nâng cấp độ phân giải 7MP với khẩu độ mở lớn f/2.2 hỗ trợ chụp trong điều kiện thiếu sáng tuyệt vời với công nghệ Retina Flash, Auto HDR.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/78124/iphone-7-plus-32gb-tgdd-2.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại smartphone iPhone 7 Plus 32GB | Ảnh selfie\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/78124/iphone-7-plus-32gb-tgdd-2.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/78124/iphone-7-plus-32gb-tgdd-2.jpg\" title=\"Điện thoại smartphone iPhone 7 Plus 32GB | Ảnh selfie\"></a></p><p><div class=video width=\"560\" height=\"315\" src=\"https://www.youtube.com/embed/IohJQwa-Q0Y\" frameborder=\"0\" allow=\"accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen=\"\"></div></p><h3>Cấu hình mạnh mẽ với chip Apple A10</h3><p>Ngoài trái tim <a href=\"https://www.thegioididong.com/hoi-dap/tong-quan-ve-chip-a10-fusion-cua-apple-885052\" target=\"_blank\" title=\"Tìm hiểu chip Apple A10 Fusion 4 nhân 64-bit\" type=\"Tìm hiểu chip Apple A10 Fusion 4 nhân 64-bit\">Apple A10 Fusion 4 nhân</a> với hiệu năng cực kì mạnh mẽ và ấn tượng thì iPhone 7 Plus còn được cập nhật hệ điều hành mới nhất với nhiều tính năng bất ngờ, và thú vị.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/78124/iphone-7-plus-32gb-tgdd-4.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại smartphone iPhone 7 Plus 32GB | Giao diện iOS\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/78124/iphone-7-plus-32gb-tgdd-4.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/78124/iphone-7-plus-32gb-tgdd-4.jpg\" title=\"Điện thoại smartphone iPhone 7 Plus 32GB | Giao diện iOS\"></a></p><p>“Táo khuyết” cũng không quên nhấn mạnh iPhone 7 Plus sẽ nhanh hơn iPhone đời đầu tới… 140 lần. Những ai mê chơi game trên di động cũng sẽ “phải lòng” iPhone mới khi có chip xử lý đồ họa được nâng tầm đúng chất “máy chơi game”.</p><h3>Màn hình Retina sắc nét</h3><p>Màn hình Retina trên 7 Plus hỗ trợ DCI-P3 gam màu rộng, nghĩa là chúng có khả năng tái tạo màu sắc trong phạm vi của sRGB.</p><p>Nói đơn giản, chúng có thể hiển thị sống động hơn, sắc thái hình ảnh tốt hơn trước đó. Độ phân giải, mật độ điểm ảnh và kích thước màn hình vẫn giữ nguyên so với iPhone 6s Plus.</p><h3>Dàn loa stereo sống động</h3><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/78124/iphone-7-plus-32gb-tgdd-9.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại smartphone iPhone 7 Plus 32GB | Cụm loa bên dưới\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/78124/iphone-7-plus-32gb-tgdd-9.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/78124/iphone-7-plus-32gb-tgdd-9.jpg\" title=\"Điện thoại smartphone iPhone 7 Plus 32GB | Cụm loa bên dưới\"></a></p><p>Lần đầu tiên iPhone xuất hiện tính năng âm thanh Stereo. Phim ảnh, podcast và các cuộc gọi loa ngoài bây giờ âm thanh rõ ràng hơn rất nhiều. Đó là do Apple đã dùng công nghệ thiết lập loa chứ không phải thiết kế 2 loa ngay trên mặt.</p><h3>Thiết kế có sự thay đổi nhẹ</h3><p>Điểm nhấn ấn tượng nhất nằm ở mặt lưng của sản phẩm với hiệu ứng phản chiếu ánh sáng khi thay đổi góc nhìn rất đặc biệt. Hiệu ứng này cũng được áp dụng cho toàn bộ khung máy, từ cạnh trên, cạnh dưới cho đến các nút bấm nên cho cảm giác một thiết bị cực kỳ hoàn thiện, gần như không có bất kỳ một chi tiết thừa nào cả. </p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/78124/iphone-7-plus-32gb-tgdd-8.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại smartphone iPhone 7 Plus 32GB | Tổng thiết kế\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/78124/iphone-7-plus-32gb-tgdd-8.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/78124/iphone-7-plus-32gb-tgdd-8.jpg\" title=\"Điện thoại smartphone iPhone 7 Plus 32GB | Tổng thiết kế\"></a></p><p>Thay đổi dãy nhựa an-ten bắt sóng được đưa vòng lên trên thay vì cắt ngang ở mặt lưng như trước.</p><p>Nút Home quen thuộc không còn là phím vật lý nữa mà được thay thế bằng cảm ứng, nó sẽ rung lên khi bạn ấn. Vì đã dùng iPhone một thời gian rất dài, nên tôi công nhận rằng hơi khó để làm quen với nó, nhưng có lẽ chỉ mất vài ngày thôi.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/78124/iphone-7-plus-32gb-tgdd-6.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại smartphone iPhone 7 Plus 32GB | Home lực\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/78124/iphone-7-plus-32gb-tgdd-6.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/78124/iphone-7-plus-32gb-tgdd-6.jpg\" title=\"Điện thoại smartphone iPhone 7 Plus 32GB | Home lực\"></a></p><p>iPhone 7 Plus thật sự là một lựa chọn hợp lý trong tầm giá.</p><p><div class=video width=\"560\" height=\"315\" src=\"https://www.youtube.com/embed/EbY_yzC58cc\" frameborder=\"0\" allow=\"accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen=\"\"></div></p><ul><li><strong>Ưu điểm:</strong><ul><li>Hiệu năng rất cao</li><li>Camera trước sau rất tuyệt vời</li><li>Thời lượng pin tốt</li></ul></li><li><strong>Nhược điểm:</strong><ul><li>Loại bỏ jack 3.5mm</li><li>Không thay đổi thiết kế quá nhiều mặc dù đã 3 năm</li><li>Phím Home cần thời gian để làm quen</li></ul></li></ul></article>', 102, 24500000, 0, '', '', '2019-06-23 20:38:56', 1, '2019-06-23 20:38:56', 1, 1);
INSERT INTO `uml_product` (`product_id`, `product_catid`, `product_name`, `product_slug`, `product_img`, `product_detail`, `product_number`, `product_price`, `product_pricesale`, `product_metakey`, `product_metadesc`, `product_createdat`, `product_createdby`, `product_updatedat`, `product_updatedby`, `product_status`) VALUES
(8, 6, 'iPhone XS Max 64GB', 'iphone-xs-max-64gb', 'iphone-xs-max-64gb.jpg', '<h2>Hoàn toàn xứng đáng với những gì được mong chờ, phiên bản cao cấp nhất <a href=\"https://www.thegioididong.com/dtdd/iphone-xs-max\" target=\"_blank\" title=\"Chi tiết điện thoại iPhone XS Max\" type=\"Chi tiết điện thoại iPhone XS Max\">iPhone Xs Max</a> của Apple năm nay nổi bật với chip A12 Bionic mạnh mẽ, màn hình rộng đến 6.5 inch, cùng camera kép trí tuệ nhân tạo và Face ID được nâng cấp.</h2><h3>Thiết kế cao cấp với viền thép không gỉ và mặt kính cường lực</h3><p><a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại tại Thegioididong.com\">Điện thoại</a> iPhone Xs Max sở hữu lối thiết kế vô cùng đẹp mắt với những đường cong mềm mại được thừa hưởng từ chiếc <a href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại iPhone\" type=\"Tham khảo các dòng điện thoại iPhone\">iPhone</a> đời trước đó.</p><h3><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/190321/iphone-xs-max-gold-5.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại iPhone Xs Max chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-5.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-5.jpg\" title=\"Thiết kế điện thoại iPhone Xs Max chính hãng\"></a></h3><p>Tuy nhiên, iPhone Xs Max lại có một thân hình to bản ngang bằng với kích thước dòng Plus nhưng chứa đựng một màn hình rộng lớn lên đến 6.5 inch.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/190321/iphone-xs-max-gold-10.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại iPhone Xs Max chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-10.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-10.jpg\" title=\"Màn hình điện thoại iPhone Xs Max chính hãng\"></a></p><p>Nhờ thế mà bạn sẽ có một không gian trải nghiệm vô cùng rộng rãi để thưởng thức những bộ phim chất lượng cao được trở nên trọn vẹn.</p><h3><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/190321/iphone-xs-max-gold-1.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại iPhone Xs Max chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-1.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-1.jpg\" title=\"Thiết kế điện thoại iPhone Xs Max chính hãng\"></a></h3><h3><a href=\"https://www.thegioididong.com/tin-tuc/man-hinh-oled-la-gi-smartphone-nao-co-man-hinh-oled-1048951\" target=\"_blank\" title=\"Tìm hiểu về màn hình OLED\" type=\"Tìm hiểu về màn hình OLED\">Màn hình OLED</a> chất lượng cao rộng 6.5 inch đầu tiên của Apple</h3><p>Với công nghệ Super Retina kết hợp tấm nền OLED trên iPhone Xs Max đem lại dải màu sắc cực kì sống động và sắc nét đến từng chi tiết.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/190321/iphone-xs-max-gold-8.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại iPhone Xs Max chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-8.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-8.jpg\" title=\"Màn hình điện thoại iPhone Xs Max chính hãng\"></a></p><p>Bên cạnh đó, Apple còn tích hợp thêm công nghệ HDR10 giúp chất lượng hình ảnh được nâng cao và mượt mà hơn đáng kể.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/190321/iphone-xs-max-gold-4.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại iPhone Xs Max chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-4.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-4.jpg\" title=\"Màn hình điện thoại iPhone Xs Max chính hãng\"></a></p><p>Việc sở hữu màn hình lớn đem đến cho bạn khá nhiều tiện ích như dễ dàng chỉnh sửa ảnh, xem phim, lướt web nhưng sẽ khó khăn hơn trong việc di chuyển.</p><h3>Hiệu năng đỉnh của đỉnh với <a href=\"https://www.thegioididong.com/tin-tuc/chi-tiet-chip-apple-a12-bionic-ben-trong-iphone-xs-xs-max-1116982\" target=\"_blank\" title=\"Tìm hiểu chip Apple A12\" type=\"Tìm hiểu chip Apple A12\">chip Apple A12</a></h3><p>Là một flagship cao cấp, iPhone Xs Max được Apple trang bị cho con chip mới toanh hàng đầu của hãng mang tên Apple A12 bionic.</p><p>Chip A12 bionic được xây dựng trên tiến trình 7nm đầu tiên mà hãng sản xuất với 6 nhân đáp ứng vượt trội trong việc xử lý các tác vụ và khả năng tiết kiệm năng lượng tối ưu.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/190321/iphone-xs-max-gold-12.jpg\" onclick=\"return false;\"><img alt=\"Chơi game trên điện thoại iPhone Xs Max chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-12.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-12.jpg\" title=\"Chơi game trên điện thoại iPhone Xs Max chính hãng\"></a></p><p>Hơn nữa, iPhone Xs Max còn có bộ xử lý đồ họa mạnh mẽ được Apple thiết kế riêng giúp hiệu năng được cải thiện rất lớn về mặt đồ họa của máy.</p><h2><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/190321/iphone-xs-max-gold-6.jpg\" onclick=\"return false;\"><img alt=\"Trải nghiệm điện thoại iPhone Xs Max chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-6.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-6.jpg\" title=\"Trải nghiệm điện thoại iPhone Xs Max chính hãng\"></a></h2><p>Chưa dừng lại ở đó, máy còn được tích hợp trí thông minh nhân tạo giúp phần cứng tối ưu hiệu suất, nhờ đó mà các thao tác của bạn được xử lý một cách nhanh chóng hơn.</p><h3>Ấn tượng về <a href=\"https://www.thegioididong.com/tin-tuc/camera-kep-la-gi-co-phai-la-xu-huong-tren-smartphone-2017-953888\" target=\"_blank\" title=\"Tìm hiểu về cẩm kép trên điện thoại\" type=\"Tìm hiểu về cẩm kép trên điện thoại\">camera kép</a> tích hợp trí tuệ nhân tạo</h3><p>Dù không sở hữu thông số camera khủng nhưng iPhone XS Max luôn cho thấy sự đẳng cấp của mình về khả năng nhiếp ảnh với cụm camera kép có cùng độ phân giải 12 MP.</p><p>Máy được trang bị hệ thống xử lý hình ảnh chất lượng cân bằng sáng, giảm nhiễu, tăng cường độ phơi sáng, màu da sao cho phù hợp và tự nhiên nhất.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/190321/iphone-xs-max-gold-3.jpg\" onclick=\"return false;\"><img alt=\"Camera sau điện thoại iPhone Xs Max chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-3.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-3.jpg\" title=\"Camera sau điện thoại iPhone Xs Max chính hãng\"></a></p><p>Cùng với đó là khả năng điều chỉnh khẩu độ ấn tượng từ f/1.4 đến f/16 ngay trên bức ảnh sau khi chụp ảnh với chế độ chân dung.</p><p>Chưa dừng lại ở đó, máy còn được tích hợp thêm công nghệ Smart HDR giúp tái tạo hình ảnh và cho ra một bức hình với độ sáng tốt nhất.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/190321/iphone-xs-max-gold-2.jpg\" onclick=\"return false;\"><img alt=\"Camera sau điện thoại iPhone Xs Max chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-2.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-2.jpg\" title=\"Camera sau điện thoại iPhone Xs Max chính hãng\"></a></p><p>Ngoài ra, iPhone XS Max còn được hỗ trợ bởi trí thông minh nhân tạo đem đến khả năng tự động điều chỉnh màu sắc, độ sáng và độ tương phản sao cho phù hợp với từng vật thể khác nhau.</p><h3>Một số tính năng cao cấp được cập nhật và bổ sung</h3><p><a href=\"https://www.thegioididong.com/hoi-dap/face-id-la-gi-va-cach-hoat-dong-cua-face-id-1021871\" target=\"_blank\" title=\"Tìm hiểu về tính năng Face ID trên điện thoại iPhone\" type=\"Tìm hiểu về tính năng Face ID trên điện thoại iPhone\">Face ID</a> đã được Apple cải tiến về khả năng bảo mật cũng như cho tốc độ mở khóa được nhanh hơn nhờ các thuật toán mới..</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/190321/iphone-xs-max-gold-7.jpg\" onclick=\"return false;\"><img alt=\"Mở khoá điện thoại iPhone Xs Max chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-7.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-7.jpg\" title=\"Mở khoá điện thoại iPhone Xs Max chính hãng\"></a></p><p>Bên cạnh đó, tính năng <a href=\"https://www.thegioididong.com/tin-tuc/giai-thich-su-khac-nhau-giua-ar-emoji-cua-samsung-va-animoji-cua-apple-1070052\" target=\"_blank\" title=\"Tìm hiểu về Animoji trên điện thoại Apple\" type=\"Tìm hiểu về Animoji trên điện thoại Apple\">Animoji</a> cũng được cập nhật thêm một số biểu tượng mới trông khá ngộ nghĩnh và đáng yêu.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/190321/iphone-xs-max-gold-11.jpg\" onclick=\"return false;\"><img alt=\"Animojis trên điện thoại iPhone Xs Max chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-11.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-11.jpg\" title=\"Animojis trên điện thoại iPhone Xs Max chính hãng\"></a></p><p>Với hệ thống camera <a href=\"https://www.thegioididong.com/tin-tuc/mot-su-that-bat-ngo-ve-camera-truedepth-tren-iphone-x-1021510\" target=\"_blank\" title=\"Tìm hiểu về công nghệ TrueDepth\" type=\"Tìm hiểu về công nghệ TrueDepth\">TrueDepth</a> nay bạn có thể tự tạo cho bản thân những bức ảnh ấn tượng với công nghệ thực tế ảo tăng cường AR.</p><p>Ngoài ra, hệ thống âm thanh 2 chiều trên <a href=\"https://www.thegioididong.com/sieu-pham-moi\" target=\"_blank\" title=\"Đặt gạch siêu phẩm mới iPhone 2018 \" type=\"Đặt gạch siêu phẩm mới iPhone 2018 \">siêu phẩm mới</a> được Apple tinh chỉnh lại cho dải âm rộng, âm thanh sống động hơn hay khả năng kháng nước và bụi cũng được nâng cấp lên thành IP 68 đảm bảo an toàn hơn cho máy.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/190321/iphone-xs-max-gold-9.jpg\" onclick=\"return false;\"><img alt=\"Đánh giá điện thoại iPhone Xs Max chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-9.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-9.jpg\" title=\"Đánh giá điện thoại iPhone Xs Max chính hãng\"></a></p><p><div class=video allow=\"accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"https://www.youtube.com/embed/9UTC7J9LdHY\" width=\"560\"></div></p></article>', 196, 14100000, 14880000, '', '', '2019-06-23 20:43:55', 1, '2019-06-23 20:43:55', 1, 1),
(9, 6, 'iPhone X 256GB', 'iphone-x-256gb', 'iphone-x-256gb.jpg', '<h2><a href=\"https://www.thegioididong.com/dtdd/iphone-x-256gb\" target=\"_blank\" title=\"Chi tiết điện thoại iPhone X 256GB\" type=\"Chi tiết điện thoại iPhone X 256GB\">iPhone X</a> được đã được <a href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại Apple iPhone tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại Apple iPhone tại Thegioididong.com\">Apple</a> cho ra mắt ngày 12/9 vừa rồi đánh dấu chặng đường 10 năm lần đầu tiên iPhone ra đời. iPhone X mang trên mình thiết kế hoàn toàn mới với màn hình Super Retina viền cực mỏng và trang bị nhiều công nghệ hiện đại như nhận diện khuôn mặt Face ID, <a href=\"https://www.thegioididong.com/dtdd?f=sac-pin-nhanh\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại sạc pin nhanh\" type=\"Tham khảo các dòng điện thoại sạc pin nhanh\">sạc pin nhanh</a> và sạc không dây cùng khả năng <a href=\"https://www.thegioididong.com/dtdd?f=chong-nuoc-bui\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại chống nước chống bụi\" type=\"Tham khảo các dòng điện thoại chống nước chống bụi\">chống nước bụi</a> cao cấp.</h2><h3>Thiết kế luôn mang tính đi đầu và cao cấp</h3><p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/apple-iphone-2017-20170912-11675_800x450.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại iPhone X\" data-original=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/apple-iphone-2017-20170912-11675_800x450.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/apple-iphone-2017-20170912-11675_800x450.jpg\" title=\"Thiết kế điện thoại iPhone X\"></a></p><p>Màn hình <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại tại Thegioididong.com\">điện thoại</a> iPhone X được phủ kín gần như toàn bộ ở mặt trước và vẫn chừa lại một phần màn hình cho loa, camera và các cảm biến ở phía trên.</p><p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/apple-iphone-2017-20170912-11690_800x450.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại iPhone X\" data-original=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/apple-iphone-2017-20170912-11690_800x450.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/apple-iphone-2017-20170912-11690_800x450.jpg\" title=\"Thiết kế điện thoại iPhone X\"></a></p><p>Còn mặt sau vẫn là chất liệu kính, đồng thời iPhone X cũng trang bị chuẩn chống nước IP67.</p><p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/ava_800x450.jpg\" onclick=\"return false;\"><img alt=\"Mặt lưng điện thoại iPhone X\" data-original=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/ava_800x450.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/ava_800x450.jpg\" title=\"Mặt lưng điện thoại iPhone X\"></a></p><p>Thay vì sử dụng công nghệ TrueTone HD thì màn hình OLED 5.8 inch của iPhone X được trang bị công nghệ Super Retina cho mật độ điểm ảnh lên tới 458 dpi. </p><p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/apple-iphone-2017-20170912-11620_800x450.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại iPhone X\" data-original=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/apple-iphone-2017-20170912-11620_800x450.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/apple-iphone-2017-20170912-11620_800x450.jpg\" title=\"Màn hình điện thoại iPhone X\"></a></p><p>Với màn hình tràn viền như này mà không có nút Home thì người dùng sẽ phải vuốt từ dưới lên để mở trình quản lý đa nhiệm.</p><p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/apple-iphone-2017-20170912-11680_800x451.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại iPhone X\" data-original=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/apple-iphone-2017-20170912-11680_800x451.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/apple-iphone-2017-20170912-11680_800x451.jpg\" title=\"Màn hình điện thoại iPhone X\"></a></p><p>Tương tự như Galaxy Note 8, giờ đây iPhone X cũng có riêng một phím để gọi Siri ra hỗ trợ.</p><p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/apple-iphone-2017-20170912-11670_800x450.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại iPhone X\" data-original=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/apple-iphone-2017-20170912-11670_800x450.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/apple-iphone-2017-20170912-11670_800x450.jpg\" title=\"Thiết kế điện thoại iPhone X\"></a></p><h3>Cấu hình mạnh mẽ, ổn định vượt bậc với <a href=\"https://www.thegioididong.com/tin-tuc/chi-tiet-a11-bionic-chip-co-nhieu-thanh-phan-apple-tu-trong-nhat-tu-truoc-den-nay-1021653\" target=\"_blank\" title=\"Tìm hiểu chip Apple A11 trên iPhone X\" type=\"Tìm hiểu chip Apple A11 trên iPhone X\">chip Apple A11</a></h3><p>Đi kèm với màn hình chất lượng đó thì chúng ta sẽ có Chip A11 Bionic. Một cái tên gì đó nghe có vẻ rất là \"Chất\". Và chip này có 6 lõi, cho hiệu suất hoạt động tốt hơn 25% so với A10.</p><p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/apple-iphone-2017-20170912-11979-_2040x1360-800-resize.jpg\" onclick=\"return false;\"><img alt=\"Chip Apple A11 trên điện thoại iPhone X\" data-original=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/apple-iphone-2017-20170912-11979-_2040x1360-800-resize.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/apple-iphone-2017-20170912-11979-_2040x1360-800-resize.jpg\" title=\"Chip Apple A11 trên điện thoại iPhone X\"></a></p><h3>Nhận diện khuôn mặt <a href=\"https://www.thegioididong.com/tin-tuc/face-id-la-gi-bao-mat-3d-quet-khuon-mat-3d-tren-iphone-x-1018816\" target=\"_blank\" title=\"Tìm hiểu tính năng Face ID trên điện thoại iPhone X\" type=\"Tìm hiểu tính năng Face ID trên điện thoại iPhone X\">Face ID</a> trong mọi điều kiện</h3><p>Vẫn là vấn đề cũ, không có Touch ID thì mở khóa bằng... mặt. Giờ đây chúng ta đã có Face ID để làm điều đó. Với công nghệ vô cùng đặc biệt này, bạn có thể mở khóa bằng khuôn mặt của mình dù là ngoài trời nắng hay trong bóng đêm, kể cả chưa cạo râu hay mới cắt tóc,... Face ID chấp hết.</p><p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/apple-iphone-2017-20170912-11729-_2040x1360-800-resize.jpg\" onclick=\"return false;\"><img alt=\"Face ID trên điện thoại iPhone X\" data-original=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/apple-iphone-2017-20170912-11729-_2040x1360-800-resize.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/apple-iphone-2017-20170912-11729-_2040x1360-800-resize.jpg\" title=\"Face ID trên điện thoại iPhone X\"></a></p><p>Theo Apple, Face ID có độ bảo mật còn cao hơn cả Touch ID, đạt tỷ lệ nhận diện sai chỉ là 1 trên 1.000.000 (1 triệu), trong khi Touch ID là 1 trên 50.000.</p><p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/apple-iphone-2017-20170912-11773-_2040x1360-800-resize.jpg\" onclick=\"return false;\"><img alt=\"Face ID trên điện thoại iPhone X\" data-original=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/apple-iphone-2017-20170912-11773-_2040x1360-800-resize.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/apple-iphone-2017-20170912-11773-_2040x1360-800-resize.jpg\" title=\"Face ID trên điện thoại iPhone X\"></a></p><h3>Camera tích hợp Animoji</h3><p>Một tính năng mới đi kèm với Face ID là Animoji vô cùng mới mẻ khi bạn có thể tạo được những emotion chuyển động theo khuôn mặt của bạn một cách rất ngộ nghĩnh và đáng yêu.</p><p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/apple-iphone-2017-20170912-11716_800x450.jpg\" onclick=\"return false;\"><img alt=\"Tính năng Animoji tích hợp trên camera điện thoại iPhone X\" data-original=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/apple-iphone-2017-20170912-11716_800x450.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/apple-iphone-2017-20170912-11716_800x450.jpg\" title=\"Tính năng Animoji tích hợp trên camera điện thoại iPhone X\"></a></p><p>Không chỉ camera trước, mà camera sau của iPhone X cũng nhận được những nâng cấp mạnh mẽ với hệ thống camera kép có cùng độ phân giải 12MP theo đó ống kính chính góc rộng và ống kính phụ tele. Đặc biệt, cả hai camera của iPhone X đều được trang bị công nghệ chống rung quang học, đi cùng với đó là hệ thống đèn Flash LED mới.</p><p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/apple-iphone-2017-20170912-11638_800x450.jpg\" onclick=\"return false;\"><img alt=\"Cụm camera sau điện thoại iPhone X\" data-original=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/apple-iphone-2017-20170912-11638_800x450.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/apple-iphone-2017-20170912-11638_800x450.jpg\" title=\"Cụm camera sau điện thoại iPhone X\"></a></p><h3>Hỗ trợ sạc không dây và dung lượng pin bền bỉ hơn</h3><p>Tương tự như bộ đôi <a href=\"https://www.thegioididong.com/dtdd/iphone-8-64gb\" target=\"_blank\" title=\"Chi tiết điện thoại iPhone 8\" type=\"Chi tiết điện thoại iPhone 8\">iPhone 8</a> và <a href=\"https://www.thegioididong.com/dtdd/iphone-8-plus\" target=\"_blank\" title=\"Chi tiết điện thoại iPhone 8 Plus\" type=\"Chi tiết điện thoại iPhone 8 Plus\">iPhone 8 Plus</a> thì iPhone X cũng được trang bị tính năng sạc không dây. Chúng ta sẽ được thấy một chiếc sạc không dây tới từ Apple có tên là AirPower.</p><p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/apple-iphone-2017-20170912-11649_800x450.jpg\" onclick=\"return false;\"><img alt=\"iPhone X hỗ trợ sạc không dây\" data-original=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/apple-iphone-2017-20170912-11649_800x450.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/apple-iphone-2017-20170912-11649_800x450.jpg\" title=\"iPhone X hỗ trợ sạc không dây\"></a></p><p>Và thời lượng pin của iPhone X sẽ nhiều hơn 2 giờ so với iPhone 7.</p><p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/apple-iphone-2017-20170912-11983-_2040x1360-800-resize.jpg\" onclick=\"return false;\"><img alt=\"Dung lượng pin trên điện thoại iPhone X\" data-original=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/apple-iphone-2017-20170912-11983-_2040x1360-800-resize.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/apple-iphone-2017-20170912-11983-_2040x1360-800-resize.jpg\" title=\"Dung lượng pin trên điện thoại iPhone X\"></a></p><p>iPhone X sẽ có 2 tùy chọn bộ nhớ trong là 64 GB và 256 GB tương tự như iPhone 8/8 Plus.</p><p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/apple-iphone-2017-20170912-12115-_2040x1360-800-resize.jpg\" onclick=\"return false;\"><img alt=\"Các phiên bản của điện thoại iPhone X\" data-original=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/apple-iphone-2017-20170912-12115-_2040x1360-800-resize.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Files/2017/09/13/1021094/apple-iphone-2017-20170912-12115-_2040x1360-800-resize.jpg\" title=\"Các phiên bản của điện thoại iPhone X\"></a></p><p>Với thiết kế cực sang trọng cùng những tính năng hiện đại, iPhone X - phiên bản iPhone kỷ niệm 10 năm ngày ra mắt iPhone nhắm đến phân khúc người dùng cực kỳ cao cấp, việc được sỡ hữu siêu phẩm iPhone X sẽ là niềm tự hào với cộng đồng iFan.</p></article>', 27, 23700000, 0, '', '', '2019-06-23 20:44:52', 1, '2019-06-23 20:44:52', 1, 1),
(10, 6, 'iPhone 8 Plus 256GB', 'iphone-8-plus-256gb', 'iphone-8-plus-256gb.jpg', '<h2><a href=\"https://www.thegioididong.com/dtdd/iphone-8-plus-256gb\" target=\"_blank\" title=\"Chi tiết điện thoại iPhone 8 Plus 256GB\" type=\"Chi tiết điện thoại iPhone 8 Plus 256GB\">iPhone 8 Plus</a> là bản nâng cấp nhẹ của chiếc <a href=\"https://www.thegioididong.com/dtdd/iphone-7-plus-256gb\" target=\"_blank\" title=\"Chi tiết điện thoại iPhone 7 Plus\" type=\"Chi tiết điện thoại iPhone 7 Plus\">iPhone 7 Plus</a> đã ra mắt trước đó với cấu hình mạnh mẽ cùng camera có nhiều cải tiến.</h2><h3>Thiết kế quen thuộc vốn có của dòng <a href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại Apple iPhone tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại Apple iPhone tại Thegioididong.com\">iPhone Apple</a></h3><p>Về ngoại hình <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại tại Thegioididong.com\">điện thoại</a> iPhone 8 Plus không có quá nhiều khác biệt so với người đàn anh đi trước.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/114114/iphone-8-plus-256gb2-1.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại iPhone 8 Plus\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb2-1.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb2-1.jpg\" title=\"Thiết kế điện thoại iPhone 8 Plus\"></a></p><p>Thay đổi lớn nhất chính là Apple đã chuyển từ thiết kế kim loại nguyên khối sang mặt lưng kính nhằm mang tính năng sạc không dây lên 8 Plus.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/114114/iphone-8-plus-256gb-h1-1.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại iPhone 8 Plus\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-h1-1.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-h1-1.jpg\" title=\"Thiết kế điện thoại iPhone 8 Plus\"></a></p><h3>Màn hình rộng và sắc nét</h3><p>iPhone 8 Plus sở hữu màn hình kích thước 5.5 inch độ phân giải <a href=\"https://www.thegioididong.com/tin-tuc/do-phan-giai-man-hinh-qhd-hd-fullhd-2k-4k-la-gi--592178#hd\" target=\"_blank\" title=\"Tìm hiểu độ phân giải Full HD (1080 x 1920 pixels)\">Full HD (1080 x 1920 pixels)</a> đem lại khả năng hiển thị sắc nét.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/114114/iphone-8-plus-256gb-h3.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại iPhone 8 Plus\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-h3.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-h3.jpg\" title=\"Màn hình điện thoại iPhone 8 Plus\"></a></p><p>Máy vẫn sử dụng tấm nền LED-backlit IPS LCD kết hợp với công nghệ True Tone giúp bạn dễ dàng quan sát với nhiều điều kiện khác nhau.</p><h3>Hiệu năng mạnh mẽ hàng đầu</h3><p>Cung cấp sức mạnh cho iPhone 8 Plus chính là con chip <a href=\"https://www.thegioididong.com/tin-tuc/chi-tiet-a11-bionic-chip-co-nhieu-thanh-phan-apple-tu-trong-nhat-tu-truoc-den-nay-1021653\" target=\"_blank\" title=\"Apple A11 Bionic 6 nhân\">Apple A11 Bionic 6 nhân</a> cùng với 3 GB RAM và 32 GB bộ nhớ trong.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/114114/iphone-8-plus-256gb-vang-7-1.jpg\" onclick=\"return false;\"><img alt=\"Trình điều khiển trên điện thoại iPhone 8 Plus\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-vang-7-1.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-vang-7-1.jpg\" title=\"Trình điều khiển trên điện thoại iPhone 8 Plus\"></a></p><p>Máy sử dụng mượt mà với tất cả các game và ứng dụng hiện có trên App Store mà không gặp phải bất cứ độ trễ hay giật lag nào trong quá trình sử dụng.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/114114/iphone-8-plus-256gb3.jpg\" onclick=\"return false;\"><img alt=\"Hiệu năng trên điện thoại iPhone 8 Plus\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb3.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb3.jpg\" title=\"Hiệu năng trên điện thoại iPhone 8 Plus\"></a></p><p>Ngoài ra iPhone 8 còn được Apple nhấn mạnh về khả năng xử lý các tác vụ AR đem lại một trải nghiệm hoàn toàn mới mẻ trên di động.</p><h3>Camera chất lượng hàng đầu</h3><p>So với iPhone 7 Plus thì iPhone 8 Plus đã có những cải tiến rõ rệt về chất lượng ảnh chụp. Về phần cứng thì máy vẫn sử dụng bộ đôi camera chính và phụ đều có độ phân giải 12 MP và giờ đây Apple đã tích hợp thêm một con chip xử lý hình ảnh để nâng cao chất lượng ảnh chụp.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/114114/iphone-8-plus-256gb-vangdong-13-1.jpg\" onclick=\"return false;\"><img alt=\"Giao diện camera của điện thoại iPhone 8 Plus\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-vangdong-13-1.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-vangdong-13-1.jpg\" title=\"Giao diện camera của điện thoại iPhone 8 Plus\"></a></p><p>Khả năng zoom quang 2X không thay đổi chất lượng cũng như xóa phông chân dung vẫn được tích hợp trên iPhone 8 Plus cùng với <a href=\"https://www.thegioididong.com/tin-tuc/post-1029635\" target=\"_blank\" title=\"PORTRAIT LIGHTING\" type=\"PORTRAIT LIGHTING\">Portrait Lighting</a>, tính năng chụp ảnh sân khấu hoàn toàn mới.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/114114/iphone-8-plus-256gb4.jpg\" onclick=\"return false;\"><img alt=\"Giao diện camera của điện thoại iPhone 8 Plus\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb4.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb4.jpg\" title=\"Giao diện camera của điện thoại iPhone 8 Plus\"></a></p><p>Camera trước độ phân giải 7 MP hỗ trợ chụp ảnh HDR đủ để làm hài lòng những người dùng khó tính năng.</p><h3>Vẫn còn <a href=\"https://www.thegioididong.com/tin-tuc/touch-id-la-gi--590286\" target=\"_blank\" title=\"Tìm hiểu tính năng Touch ID trên điện thoại iPhone\" type=\"Tìm hiểu tính năng Touch ID trên điện thoại iPhone\">Touch ID</a></h3><p>Khi mà Face ID vẫn chưa thực sự khiến người dùng hoàn toàn yên tâm thì Touch ID trên iPhone 8 vẫn là một cái gì đó quen thuộc và được nhiều người tin dùng hơn.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/114114/iphone-8-plus-256gb-vangdong-14-2.jpg\" onclick=\"return false;\"><img alt=\"Bảo mật vân tay trên điện thoại iPhone 8 Plus\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-vangdong-14-2.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-vangdong-14-2.jpg\" title=\"Bảo mật vân tay trên điện thoại iPhone 8 Plus\"></a></p><p>Tốc độ nhận diện nhanh chóng giúp bạn dễ dàng mở khóa thiết bị.</p></article>', 137, 25200000, 25980000, '', '', '2019-06-23 20:44:54', 1, '2019-06-23 20:44:54', 1, 1),
(11, 6, 'iPhone Xr 256GB', 'iphone-xr-256gb', 'iphone-xr-256gb.jpg', '<h2>Ngoài các phiên bản như thường lệ mọi năm, vào đêm 12/9 theo giờ Việt Nam, Apple đã cho ra mắt thêm một phiên bản <a href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại iPhone\" type=\"Tham khảo các dòng điện thoại iPhone\">iPhone</a> khác với tên gọi <a href=\"https://www.thegioididong.com/dtdd/iphone-xr-256gb\" target=\"_blank\" title=\"Chi tiết điện thoại iPhone XR 256GB\" type=\"Chi tiết điện thoại iPhone XR 256GB\">iPhone Xr</a>, có mức giá dễ chịu hơn nhưng vẫn mang đầy đủ các tính năng cao cấp đầy hấp dẫn.</h2><h3>Mượt mà mọi trải nghiệm nhờ <a href=\"https://www.thegioididong.com/tin-tuc/chi-tiet-chip-apple-a12-bionic-ben-trong-iphone-xs-xs-max-1116982\" target=\"_blank\" title=\"Tìm hiểu chip Apple A12\" type=\"Tìm hiểu chip Apple A12\">chip Apple A12</a></h3><p>Với mỗi lần ra mắt, Apple lại giới thiệu một con chip mới và Apple A12 Bionic là con chip đầu tiên sản xuất với tiến trình 7nm được tích hợp trên iPhone Xr.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/190326/iphone-xr-256gb-a12.jpg\" onclick=\"return false;\"><img alt=\"Chip A12 trên điện thoại iPhone Xr chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/190326/iphone-xr-256gb-a12.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/190326/iphone-xr-256gb-a12.jpg\" title=\"Chip A12 trên điện thoại iPhone Xr chính hãng\"></a></p><p>Apple A12 được tích hợp trí tuệ thông minh nhân tạo, mọi phản hồi trên máy đều nhanh chóng và gần như là ngay lập tức, kể cả khi bạn chơi game hay thao tác bình thường.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/190326/iphone-xr-256gb-6.jpg\" onclick=\"return false;\"><img alt=\"iOS trên điện thoại iPhone Xr chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/190326/iphone-xr-256gb-6.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/190326/iphone-xr-256gb-6.jpg\" title=\"iOS trên điện thoại iPhone Xr chính hãng\"></a></p><p>Hơn nữa với AI trên Apple A12, <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại tại Thegioididong.com\">smartphone</a> iPhone Xr có thể ghi nhớ được thao tác hằng ngày của bạn để hoàn thiện và hỗ trợ người dùng tốt hơn.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/190326/iphone-xr-256gb-7.jpg\" onclick=\"return false;\"><img alt=\"Trải nghiệm điện thoại iPhone Xr chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/190326/iphone-xr-256gb-7.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/190326/iphone-xr-256gb-7.jpg\" title=\"Trải nghiệm điện thoại iPhone Xr chính hãng\"></a></p><p>Thực tế khi chơi game trên iPhone Xr, môi trường và hiệu ứng trong game rất thật. Hơn nữa trên chiếc iPhone còn hỗ trợ thực tế tăng cường: Chơi game thực tế ảo với nhiều người cùng chơi, trải nghiệm cùng 1 môi trường với nhau.</p><h3>Dàn loa cho âm thanh đa chiều</h3><p>Trước đây để trải nghiệm tối đa âm thanh thì tai nghe là thiết bị không thể thiếu. Nhưng với iPhone thế hệ mới, công nghệ âm thanh đa chiều từ loa sẽ giúp bạn hoà vào âm nhạc cũng như các bộ phim thú vị hay chơi game hào hứng nhất.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/190326/iphone-xr-256gb-3.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại iPhone Xr chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/190326/iphone-xr-256gb-3.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/190326/iphone-xr-256gb-3.jpg\" title=\"Thiết kế điện thoại iPhone Xr chính hãng\"></a></p><h3>Màn hình tràn viền công nghệ LCD - True Tone</h3><p>Khác với iPhone Xs hay Xs Max, chiếc smartphone này sở hữu màn hình LCD.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/190326/iphone-xr-256gb-1.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại iPhone Xr chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/190326/iphone-xr-256gb-1.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/190326/iphone-xr-256gb-1.jpg\" title=\"Màn hình điện thoại iPhone Xr chính hãng\"></a></p><p>Bù lại với công nghệ <a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-cong-nghe-man-hinh-true-tone-992705\" target=\"_blank\" title=\"Tìm hiểu công nghệ màn hình True Tone\" type=\"Tìm hiểu công nghệ màn hình True Tone\">True Tone</a> cùng màn hình tràn viền rộng tới 6.1 inch, mọi trải nghiệm trên máy vẫn đem lại sự thích thú và hoàn hảo, như dòng cao cấp khác của Apple.</p><p>Ngoài ra, Apple cũng giới thiệu rằng, iPhone Xr được trang bị một loại công nghệ mới có tên Liquid Retina. Máy có độ phân giải 1792 x 828 Pixels cùng 1.4 triệu điểm ảnh.</p><h3>Camera với hiệu ứng bokeh cực đẹp</h3><p>Nếu ai đó khẳng định chỉ <a href=\"https://www.thegioididong.com/tin-tuc/camera-kep-la-gi-co-phai-la-xu-huong-tren-smartphone-2017-953888\" target=\"_blank\" title=\"Tìm hiểu về cẩm kép trên điện thoại\" type=\"Tìm hiểu về cẩm kép trên điện thoại\">camera kép</a> mới xoá phông thì đó là một sai lầm. iPhone Xr có khả năng tạo hiệu ứng bokeh tuyệt đỉnh, làm nổi bật chân dung người chụp mà hình ảnh vẫn rất sắc nét, chi tiết.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/190326/iphone-xr-256gb-13.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ điện thoại iPhone Xr chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/190326/iphone-xr-256gb-13.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/190326/iphone-xr-256gb-13.jpg\" title=\"Ảnh chụp từ điện thoại iPhone Xr chính hãng\"></a></p><p>iPhone Xr hỗ trợ người chụp ảnh từ cơ bản đến cả nâng cao chuyên nghiệp, đem lại sự hài lòng tối đa.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/190326/iphone-xr-256gb-9.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ điện thoại iPhone Xr chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/190326/iphone-xr-256gb-9.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/190326/iphone-xr-256gb-9.jpg\" title=\"Ảnh chụp từ điện thoại iPhone Xr chính hãng\"></a></p><p>Máy còn có tính năng Smart HDR: Dù bạn chụp ảnh có độ sáng tối khác biệt nhiều nhưng vẫn giữ được chủ thể.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/190326/iphone-xr-256gb-12.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ điện thoại iPhone Xr chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/190326/iphone-xr-256gb-12.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/190326/iphone-xr-256gb-12.jpg\" title=\"Ảnh chụp từ điện thoại iPhone Xr chính hãng\"></a></p><p>Camera selfie của máy mang công nghệ <a href=\"https://www.thegioididong.com/tin-tuc/mot-su-that-bat-ngo-ve-camera-truedepth-tren-iphone-x-1021510\" target=\"_blank\" title=\"Tìm hiểu về công nghệ TrueDepth\" type=\"Tìm hiểu về công nghệ TrueDepth\">TrueDepth</a> mới nhất cùng <a href=\"https://www.thegioididong.com/tin-tuc/giai-thich-su-khac-nhau-giua-ar-emoji-cua-samsung-va-animoji-cua-apple-1070052\" target=\"_blank\" title=\"Tìm hiểu về Animoji trên điện thoại Apple\" type=\"Tìm hiểu về Animoji trên điện thoại Apple\">Animoji</a> thông minh hơn, mọi cảm xúc và biểu cảm của bạn trở nên hoàn hảo và thú vị.</p><h3><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/190326/iphone-xr-256gb-animoji.jpg\" onclick=\"return false;\"><img alt=\"Animoji trên điện thoại iPhone XR 256GB\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/190326/iphone-xr-256gb-animoji.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/190326/iphone-xr-256gb-animoji.jpg\" title=\"Animoji trên điện thoại iPhone XR 256GB\"></a></h3><h3>Một số tính năng cao cấp khác</h3><p>Như thường lệ, các dòng siêu phẩm mới sau này đều được hỗ trợ chống nước chống bụi và iPhone Xr cũng thế.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/190326/iphone-xr-256gb-5.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại iPhone Xr chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/190326/iphone-xr-256gb-5.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/190326/iphone-xr-256gb-5.jpg\" title=\"Thiết kế điện thoại iPhone Xr chính hãng\"></a></p><p>Ngoài ra bảo mật khuôn mặt Face ID càng ngày càng hoàn thiện giúp bạn mở máy nhanh chóng trong mọi điều kiện sử dụng.</p><p><div class=video width=\"560\" height=\"315\" src=\"https://www.youtube.com/embed/9LpyLziSSK0\" frameborder=\"0\" allow=\"accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen=\"\"></div></p></article>', 60, 16600000, 17380000, '', '', '2019-06-23 20:44:56', 1, '2019-06-23 20:44:56', 1, 1),
(12, 6, 'iPhone Xr 128GB', 'iphone-xr-128gb', 'iphone-xr-128gb.jpg', '<h2>Được xem là phiên bản <a href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại iPhone\" type=\"Tham khảo các dòng điện thoại iPhone\">iPhone</a> giá rẻ đầy hoàn hảo, <a href=\"https://www.thegioididong.com/dtdd/iphone-xr-128gb\" target=\"_blank\" title=\"Chi tiết điện thoại iPhone XR 128GB\" type=\"Chi tiết điện thoại iPhone XR 128GB\">iPhone Xr 128GB</a> khiến người dùng có nhiều sự lựa chọn hơn về màu sắc đa dạng nhưng vẫn sở hữu cấu hình mạnh mẽ và thiết kế sang trọng.</h2><h3>Màn hình tràn viền công nghệ LCD - True Tone</h3><p>Thay vì sở hữu màn hình OLED truyền thống, chiếc <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại tại Thegioididong.com\">smartphone</a> này sở hữu màn hình LCD.</p><p>Bù lại với công nghệ <a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-cong-nghe-man-hinh-true-tone-992705\" target=\"_blank\" title=\"Tìm hiểu công nghệ màn hình True Tone\" type=\"Tìm hiểu công nghệ màn hình True Tone\">True Tone</a> cùng màn hình tràn viền rộng tới 6.1 inch, mọi trải nghiệm trên máy vẫn đem lại sự thích thú và hoàn hảo, như dòng cao cấp khác của Apple.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/191483/iphone-xr-128gb-1-1.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại iPhone Xr chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/191483/iphone-xr-128gb-1-1.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/191483/iphone-xr-128gb-1-1.jpg\" title=\"Màn hình điện thoại iPhone Xr chính hãng\"></a></p><p>Ngoài ra, Apple cũng giới thiệu rằng, iPhone Xr được trang bị một loại công nghệ mới có tên Liquid Retina. Máy có độ phân giải 1792 x 828 Pixels cùng 1.4 triệu điểm ảnh.</p><h3>Mượt mà mọi trải nghiệm nhờ <a href=\"https://www.thegioididong.com/tin-tuc/chi-tiet-chip-apple-a12-bionic-ben-trong-iphone-xs-xs-max-1116982\" target=\"_blank\" title=\"Tìm hiểu chip Apple A12\" type=\"Tìm hiểu chip Apple A12\">chip Apple A12</a></h3><p>Với mỗi lần ra mắt, Apple lại giới thiệu một con chip mới và Apple A12 Bionic là con chip đầu tiên sản xuất với tiến trình 7nm được tích hợp trên iPhone Xr.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/191483/iphone-xr-128gb-a12.jpg\" onclick=\"return false;\"><img alt=\"Chip A12 trên điện thoại iPhone Xr chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/191483/iphone-xr-128gb-a12.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/191483/iphone-xr-128gb-a12.jpg\" title=\"Chip A12 trên điện thoại iPhone Xr chính hãng\"></a></p><p>Apple A12 được tích hợp trí tuệ thông minh nhân tạo, mọi phản hồi trên máy đều nhanh chóng và gần như là ngay lập tức, kể cả khi bạn chơi game hay thao tác bình thường.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/191483/iphone-xr-128gb-6-1.jpg\" onclick=\"return false;\"><img alt=\"iOS trên điện thoại iPhone Xr chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/191483/iphone-xr-128gb-6-1.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/191483/iphone-xr-128gb-6-1.jpg\" title=\"iOS trên điện thoại iPhone Xr chính hãng\"></a></p><p>Hơn nữa với AI trên Apple A12, iPhone Xr có thể ghi nhớ được thao tác hằng ngày của bạn để hoàn thiện và hỗ trợ người dùng tốt hơn.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/191483/iphone-xr-128gb-7-1.jpg\" onclick=\"return false;\"><img alt=\"Trải nghiệm điện thoại iPhone Xr chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/191483/iphone-xr-128gb-7-1.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/191483/iphone-xr-128gb-7-1.jpg\" title=\"Trải nghiệm điện thoại iPhone Xr chính hãng\"></a></p><p>Thực tế khi chơi game trên iPhone Xr, môi trường và hiệu ứng trong game rất thật. Hơn nữa trên chiếc iPhone còn hỗ trợ thực tế tăng cường: Chơi game thực tế ảo với nhiều người cùng chơi, trải nghiệm cùng 1 môi trường với nhau.</p><h3>Dàn loa cho âm thanh đa chiều</h3><p>Trước đây để trải nghiệm tối đa âm thanh thì tai nghe là thiết bị không thể thiếu. Nhưng với iPhone thế hệ mới, công nghệ âm thanh đa chiều từ loa sẽ giúp bạn hoà vào âm nhạc cũng như các bộ phim thú vị hay chơi game hào hứng nhất.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/191483/iphone-xr-128gb-3-1.jpg\" onclick=\"return false;\"><img alt=\"Cụm loa dưới điện thoại iPhone Xr chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/191483/iphone-xr-128gb-3-1.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/191483/iphone-xr-128gb-3-1.jpg\" title=\"Cụm loa dưới điện thoại iPhone Xr chính hãng\"></a></p><h3>Camera với hiệu ứng bokeh cực đẹp</h3><p>Nếu ai đó khẳng định chỉ <a href=\"https://www.thegioididong.com/tin-tuc/camera-kep-la-gi-co-phai-la-xu-huong-tren-smartphone-2017-953888\" target=\"_blank\" title=\"Tìm hiểu về cẩm kép trên điện thoại\" type=\"Tìm hiểu về cẩm kép trên điện thoại\">camera kép</a> mới xoá phông thì đó là một sai lầm. iPhone Xr có khả năng tạo hiệu ứng bokeh tuyệt đỉnh, làm nổi bật chân dung người chụp mà hình ảnh vẫn rất sắc nét, chi tiết.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/191483/iphone-xr-128gb-12-1.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera điện thoại iPhone Xr chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/191483/iphone-xr-128gb-12-1.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/191483/iphone-xr-128gb-12-1.jpg\" title=\"Ảnh chụp từ camera điện thoại iPhone Xr chính hãng\"></a></p><p>iPhone Xr hỗ trợ người chụp ảnh từ cơ bản đến cả nâng cao chuyên nghiệp, đem lại sự hài lòng tối đa.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/191483/iphone-xr-128gb-8-1.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera điện thoại iPhone Xr chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/191483/iphone-xr-128gb-8-1.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/191483/iphone-xr-128gb-8-1.jpg\" title=\"Ảnh chụp từ camera điện thoại iPhone Xr chính hãng\"></a></p><p>Máy còn có tính năng Smart HDR: Dù bạn chụp ảnh có độ sáng tối khác biệt nhiều nhưng vẫn giữ đc chủ thể.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/191483/iphone-xr-128gb-9-1.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera điện thoại iPhone Xr chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/191483/iphone-xr-128gb-9-1.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/191483/iphone-xr-128gb-9-1.jpg\" title=\"Ảnh chụp từ camera điện thoại iPhone Xr chính hãng\"></a></p><p>Camera selfie của máy mang công nghệ <a href=\"https://www.thegioididong.com/tin-tuc/mot-su-that-bat-ngo-ve-camera-truedepth-tren-iphone-x-1021510\" target=\"_blank\" title=\"Tìm hiểu về công nghệ TrueDepth\" type=\"Tìm hiểu về công nghệ TrueDepth\">TrueDepth</a> mới nhất cùng <a href=\"https://www.thegioididong.com/tin-tuc/giai-thich-su-khac-nhau-giua-ar-emoji-cua-samsung-va-animoji-cua-apple-1070052\" target=\"_blank\" title=\"Tìm hiểu về Animoji trên điện thoại Apple\" type=\"Tìm hiểu về Animoji trên điện thoại Apple\">Animoji</a> thông minh hơn, mọi cảm xúc và biểu cảm của bạn trở nên hoàn hảo và thú vị.</p><h3><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/191483/iphone-xr-128gb-12.jpg\" onclick=\"return false;\"><img alt=\"Tính năng Animoji trên điện thoại iPhone XR 128GB\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/191483/iphone-xr-128gb-12.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/191483/iphone-xr-128gb-12.jpg\" title=\"Tính năng Animoji trên điện thoại iPhone XR 128GB\"></a></h3><h3>Một số tính năng cao cấp khác</h3><p>Như thường lệ, các dòng siêu phẩm mới sau này đều được hỗ trợ chống nước chống bụi và iPhone Xr cũng thế.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/191483/iphone-xr-128gb-5-1.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại iPhone Xr chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/191483/iphone-xr-128gb-5-1.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/191483/iphone-xr-128gb-5-1.jpg\" title=\"Thiết kế điện thoại iPhone Xr chính hãng\"></a></p><p>Ngoài ra bảo mật khuôn mặt Face ID càng ngày càng hoàn thiện giúp bạn mở máy nhanh chóng trong mọi điều kiện sử dụng.</p><p><div class=video width=\"560\" height=\"315\" src=\"https://www.youtube.com/embed/9LpyLziSSK0\" frameborder=\"0\" allow=\"accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen=\"\"></div></p></article>', 156, 6000000, 6780000, '', '', '2019-06-23 20:44:59', 1, '2019-06-23 20:44:59', 1, 1);
INSERT INTO `uml_product` (`product_id`, `product_catid`, `product_name`, `product_slug`, `product_img`, `product_detail`, `product_number`, `product_price`, `product_pricesale`, `product_metakey`, `product_metadesc`, `product_createdat`, `product_createdby`, `product_updatedat`, `product_updatedby`, `product_status`) VALUES
(13, 6, 'iPhone X 64GB', 'iphone-x-64gb', 'iphone-x-64gb.jpg', '<h2><a href=\"https://www.thegioididong.com/dtdd/iphone-x-64gb\" target=\"_blank\" title=\"Chi tiết điện thoại iPhone X 64GB\" type=\"Chi tiết điện thoại iPhone X 64GB\">iPhone X</a> là cụm từ được rất nhiều người mong chờ muốn biết và tìm kiếm trên Google bởi đây là chiếc điện thoại mà Apple kỉ niệm 10 năm <a href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại Apple iPhone tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại Apple iPhone tại Thegioididong.com\">iPhone</a> đầu tiên được bán ra.</h2><h3>Thiết kế mang tính đột phá</h3><p>Như các bạn cũng đã biết thì đã 4 năm kể từ chiếc <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại tại Thegioididong.com\">điện thoại</a> iPhone 6 và iPhone 6 Plus thì Apple vẫn chưa có thay đổi nào đáng kể trong thiết kế của mình.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/114115/iphone-x-64gb1.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế đột phá của điện thoại iPhone X\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/114115/iphone-x-64gb1.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/114115/iphone-x-64gb1.jpg\" title=\"Thiết kế đột phá của điện thoại iPhone X\"></a></p><p>Nhưng với iPhone X thì đó lại là 1 câu chuyện hoàn toàn khác, máy chuyển qua sử dụng màn hình tỉ lệ 19.5:9 mới mẻ với phần diện tích hiển thị mặt trước cực lớn.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/114115/iphone-x-64gb2.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế đột phá của điện thoại iPhone X\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/114115/iphone-x-64gb2.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/114115/iphone-x-64gb2.jpg\" title=\"Thiết kế đột phá của điện thoại iPhone X\"></a></p><p>Mặt lưng kính hỗ trợ sạc nhanh không dây cũng như phần camera kép đặt dọc cũng là những thứ đầu tiên xuất hiện trên 1 chiếc iPhone.</p><h3>Màn hình với tai thỏ</h3><p>Điểm khiến iPhone X bị chê nhiều nhất đó chính là phần \"tai thỏ\" phía trên màn hình, Apple đã chấp nhận điều này để đặt cụm camera TrueDepth mới của hãng.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/114115/iphone-x-64gb15.jpg\" onclick=\"return false;\"><img alt=\"Màn hình tai thỏ của điện thoại iPhone X\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/114115/iphone-x-64gb15.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/114115/iphone-x-64gb15.jpg\" title=\"Màn hình tai thỏ của điện thoại iPhone X\"></a></p><p>Màn hình kích thước 5.8 inch độ phân giải 1125 x 2436 pixels đem đến khả năng hiển thị xuất sắc.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/114115/iphone-x-64gb20.jpg\" onclick=\"return false;\"><img alt=\"Màn hình tai thỏ của điện thoại iPhone X\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/114115/iphone-x-64gb20.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/114115/iphone-x-64gb20.jpg\" title=\"Màn hình tai thỏ của điện thoại iPhone X\"></a></p><p>iPhone X sử dụng tấm nền <a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-oled-la-gi-905762\" target=\"_blank\" title=\"Tìm hiểu màn hình OLED\" type=\"Tìm hiểu màn hình OLED\">OLED</a> (được tinh chỉnh bởi Apple) thay vì LCD như những chiếc iPhone trước đây và điều này đem lại cho máy 1 màu đen thể hiện rất sâu cùng khả năng tái tạo màu sắc không kém phần chính xác.</p><h3>Face ID tạo nên đột phá</h3><p>Touch ID trên iPhone X đã bị loại bỏ, thay vào đó là bạn sẽ chuyển qua sử dụng Face ID, một phương thức bảo mật sinh trắc học mới của Apple.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/114115/iphone-x-64gb7.jpg\" onclick=\"return false;\"><img alt=\"Face ID tạo nên đột phá trên điện thoại iPhone X\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/114115/iphone-x-64gb7.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/114115/iphone-x-64gb7.jpg\" title=\"Face ID tạo nên đột phá trên điện thoại iPhone X\"></a></p><p>Với sự trợ giúp của cụm camera TrueDept thì iPhone X có khả năng nhận diện khuôn mặt 3D của người dùng từ đó mở khóa chiếc iPhone một cách nhanh chóng.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/114115/iphone-x-64gb11.jpg\" onclick=\"return false;\"><img alt=\"Face ID tạo nên đột phá trên điện thoại iPhone X\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/114115/iphone-x-64gb11.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/114115/iphone-x-64gb11.jpg\" title=\"Face ID tạo nên đột phá trên điện thoại iPhone X\"></a></p><p>Tuy sẽ hơi hụt hẫng khi Touch ID đã quá quen thuộc trên những chiếc iPhone như tốc độ cũng như trải nghiệm \"khóa như không khóa\" của Face ID hoàn toàn có thể khiến bạn yên tâm sử dụng.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/114115/iphone-x-64gb10.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPhone X tích hợp Face ID\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/114115/iphone-x-64gb10.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/114115/iphone-x-64gb10.jpg\" title=\"Điện thoại iPhone X tích hợp Face ID\"></a></p><h3>Thao tác vuốt và vuốt</h3><p>Không còn phím Home cứng nên các thao tác trên iPhone X cũng hoàn toàn khác so với những đàn anh đi trước.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/114115/iphone-x-64gb3.jpg\" onclick=\"return false;\"><img alt=\"Thao tác vuốt và vuốt trên điện thoại iPhone X\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/114115/iphone-x-64gb3.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/114115/iphone-x-64gb3.jpg\" title=\"Thao tác vuốt và vuốt trên điện thoại iPhone X\"></a></p><p>Giờ đây chỉ với thao tác vuốt và vuốt từ cạnh dưới là bạn đã có thể truy cập vào đa nhiệm, trở về màn hình Home một cách nhanh chóng.</p><h3>Camera kép cải tiến tích hợp Animoji</h3><p>iPhone X vẫn sở hữu bộ đôi camera kép có cùng độ phân giải 12 MP nhưng camera tele thứ 2 với khẩu độ được nâng lên mức f/2.4 so với f/2.8 của iPhone 7 Plus.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/114115/iphone-x-64gb19.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPhone X tích hợp camera kép\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/114115/iphone-x-64gb19.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/114115/iphone-x-64gb19.jpg\" title=\"Điện thoại iPhone X tích hợp camera kép\"></a></p><p>Ngoài ra thì cả 2 camera trên iPhone X đều sở hữu cho mình khả năng chống rung quang học giúp bạn dễ dàng bắt trọn mọi khoảnh khắc trong cuộc sống.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/114115/iphone-x-64gb9.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPhone X tích hợp camera kép\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/114115/iphone-x-64gb9.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/114115/iphone-x-64gb9.jpg\" title=\"Điện thoại iPhone X tích hợp camera kép\"></a></p><p>Camera trước độ phân giải 7 MP với khả năng selfie xóa phông đáp ứng tốt mọi nhu cầu tự sướng của giới trẻ hiện nay.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/114115/iphone-x-64gb8.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPhone X tích hợp camera kép\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/114115/iphone-x-64gb8.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/114115/iphone-x-64gb8.jpg\" title=\"Điện thoại iPhone X tích hợp camera kép\"></a></p><p>Bộ đôi camera trên iPhone X được đánh giá rất cao về chất lượng ảnh chụp và là một trong những chiếc camera phone chụp ảnh đẹp nhất trong năm 2017.</p><h3>Hiệu năng mạnh mẽ với Apple A11</h3><p>Hiệu năng của những chiếc iPhone chưa bao giờ là vấn đề và với iPhone X thì mọi thứ vẫn rất ấn tượng.</p><p>Con chip <a href=\"https://www.thegioididong.com/tin-tuc/chi-tiet-a11-bionic-chip-co-nhieu-thanh-phan-apple-tu-trong-nhat-tu-truoc-den-nay-1021653\" target=\"_blank\" title=\"Apple A11 Bionic 6 nhân\">Apple A11 Bionic 6 nhân</a> kết hợp với 3 GB RAM thì iPhone X tự tin là chiếc smartphone mạnh mẽ nhất mà Apple từng sản xuất.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/114115/iphone-x-64gb13.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPhone X có hiệu năng mạnh mẽ\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/114115/iphone-x-64gb13.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/114115/iphone-x-64gb13.jpg\" title=\"Điện thoại iPhone X có hiệu năng mạnh mẽ\"></a></p><p>Ngoài ra với iPhone X thì Apple cũng nhấn mạnh với trải nghiệm thực tế ảo tăng cường giúp bạn có những phút giây thư giãn thú vị.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/114115/iphone-x-64gb17.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPhone X có hiệu năng mạnh mẽ\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/114115/iphone-x-64gb17.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/114115/iphone-x-64gb17.jpg\" title=\"Điện thoại iPhone X có hiệu năng mạnh mẽ\"></a></p><p>Viên pin trên iPhone X có dung lượng 2716 mAh (lớn hơn cả trên <a href=\"https://www.thegioididong.com/dtdd/iphone-8-plus\" target=\"_blank\" title=\"Chi tiết điện thoại iPhone 8 Plus\" type=\"Chi tiết điện thoại iPhone 8 Plus\">iPhone 8 Plus</a>) cho bạn sử dụng khá ổn trong khoảng một ngày liên tục.</p></article>', 142, 20800000, 0, '', '', '2019-06-23 20:45:01', 1, '2019-06-23 20:45:01', 1, 1),
(14, 6, 'iPhone Xr 128GB Trắng', 'iphone-xr-128gb-trang', 'iphone-xr-128gb-trang.jpg', '<h2><a href=\"https://www.thegioididong.com/dtdd/iphone-xr-128gb-trang\" target=\"_blank\" title=\"Tham khảo điện thoại iPhone Xr 128GB Trắng chính hãng\">iPhone Xr 128GB Trắng</a> là một lựa chọn sáng giá trong phân khúc smartphone cao cấp \"giá tốt\" với hiệu năng mạnh mẽ như <a href=\"https://www.thegioididong.com/dtdd/iphone-xs-max-256gb\" target=\"_blank\" title=\"Chi tiết điện thoại iPhone XS Max 256GB\" type=\"Chi tiết điện thoại iPhone XS Max 256GB\">iPhone Xs Max</a> nhưng có mức giá rẻ hơn khá nhiều.</h2><h3>Hiệu năng mạnh mẽ hàng đầu thế giới <a href=\"https://www.thegioididong.com/dtdd\" style=\"color: rgb(80, 168, 227); font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; line-height: 18px; margin: 0px; padding: 0px; text-decoration-line: none; outline: none; zoom: 1;\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại tại Thegioididong.com\">smartphone</a></h3><p>Dù có mức giá rẻ hơn nhưng không vì thế mà iPhone Xr 128GB Trắng bị cắt giảm đi về mặt cấu hình.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/204181/iphone-xr-128gb-trang-tgdd4.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình của điện thoại iPhone Xr 128GB Trắng chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/204181/iphone-xr-128gb-trang-tgdd4.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/204181/iphone-xr-128gb-trang-tgdd4.jpg\" title=\"Cấu hình của điện thoại iPhone Xr 128GB Trắng chính hãng\" /></a></p><p>Cung cấp sức mạnh cho máy chính là con chip <a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-chip-apple-a12-bionic-con-chip-7nm-dau-1171937\" target=\"_blank\" title=\"Tìm hiểu chip iPhone Xr 128GB Đen\">Apple A12 Bionic 6 nhân</a> nhân mạnh mẽ trên tiến trình 7nm đáp ứng vượt trội trong việc xử lý các tác vụ và khả năng tiết kiệm năng lượng tối ưu.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/204181/iphone-xr-128gb-trang-tgdd5.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình của điện thoại iPhone Xr 128GB Trắng chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/204181/iphone-xr-128gb-trang-tgdd5.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/204181/iphone-xr-128gb-trang-tgdd5.jpg\" title=\"Cấu hình của điện thoại iPhone Xr 128GB Trắng chính hãng\" /></a></p><p>Với những người là \"tín đồ\" của game mobile thì hứa hẹn cũng sẽ có được những trải nghiệm mượt mà nhất từ trước tới nay với chip đồ họa được Apple tự thiết kế riêng cho mình.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/204181/iphone-xr-128gb-trang-tgdd7.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình của điện thoại iPhone Xr 128GB Trắng chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/204181/iphone-xr-128gb-trang-tgdd7.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/204181/iphone-xr-128gb-trang-tgdd7.jpg\" title=\"Cấu hình của điện thoại iPhone Xr 128GB Trắng chính hãng\" /></a></p><p>Chưa dừng lại ở đó, máy còn được tích hợp trí thông minh nhân tạo giúp phần cứng tối ưu hiệu suất, nhờ đó mà các thao tác của bạn được xử lý một cách nhanh chóng hơn.</p><p>Cuối cùng cũng không thể quên hệ điều hành iOS mượt mà và ổn định giúp bạn có thể sử dụng máy trong khoảng 3-4 năm nữa mà vẫn hoạt động rất mượt mà.</p><h3>Thời lượng pin tốt nhất từ trước tới nay</h3><p>Nếu như bạn đã \"quá chán\" với việc ngày nào cũng phải sạc pin cho chiếc iPhone của mình thì iPhone Xr sẽ mang lại cho bạn một trải nghiệm hoàn toàn khác.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/204181/iphone-xr-128gb-trang-tgdd6.jpg\" onclick=\"return false;\"><img alt=\"Thời lượng pin của điện thoại iPhone Xr 128GB Trắng chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/204181/iphone-xr-128gb-trang-tgdd6.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/204181/iphone-xr-128gb-trang-tgdd6.jpg\" title=\"Thời lượng pin của điện thoại iPhone Xr 128GB Trắng chính hãng\" /></a></p><p>Được đánh giá là chiếc <a href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại Apple chính hãng\">iPhone</a> có thời lượng pin tốt nhất từ trước tới nay chiếc iPhone mới này cho bạn thời gian sử dụng khá thoải mái trong hơn một ngày.</p><p>Nếu bạn là người ít chơi game thì máy có thể trụ được tới ngày thứ 2 là điều hoàn toàn bình thường.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/204181/iphone-xr-128gb-trang-tgdd9.jpg\" onclick=\"return false;\"><img alt=\"Thời lượng pin của điện thoại iPhone Xr 128GB Trắng chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/204181/iphone-xr-128gb-trang-tgdd9.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/204181/iphone-xr-128gb-trang-tgdd9.jpg\" title=\"Thời lượng pin của điện thoại iPhone Xr 128GB Trắng chính hãng\" /></a></p><p>Tất nhiên cũng không thể thiếu khả năng sạc nhanh cũng như sạc nhanh không dây được tích hợp trên máy giúp bạn rút ngắn được đáng kể thời gian chờ sạc.</p><h3>Camera đơn nhưng \"ăn đứt\" camera kép</h3><p>Nhiều người sẽ nghĩ camera đơn trên iPhone Xr sẽ cho chất lượng không tốt bằng những chiếc máy có <a href=\"https://www.thegioididong.com/tin-tuc/camera-kep-la-gi-co-phai-la-xu-huong-tren-smartphone-2017-953888\" target=\"_blank\" title=\"Tìm hiểu về cẩm kép trên điện thoại\" type=\"Tìm hiểu về cẩm kép trên điện thoại\">camera kép</a> tuy nhiên sự thật lại hoàn toàn khác.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/204181/iphone-xr-128gb-trang-tgdd10.jpg\" onclick=\"return false;\"><img alt=\"Camera sau của điện thoại iPhone Xr 128GB Trắng chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/204181/iphone-xr-128gb-trang-tgdd10.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/204181/iphone-xr-128gb-trang-tgdd10.jpg\" title=\"Camera sau của điện thoại iPhone Xr 128GB Trắng chính hãng\" /></a></p><p>Vẫn là camera 12 MP nhưng với chip xử lý mới, có riêng một con chip xử lý hình ảnh thì iPhone Xr 128GB Trắng vẫn cho chất lượng ảnh hàng đầu hiện nay.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/204181/iphone-xr-128gb-trang-tgdd1.jpg\" onclick=\"return false;\"><img alt=\"Camera sau của điện thoại iPhone Xr 128GB Trắng chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/204181/iphone-xr-128gb-trang-tgdd1.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/204181/iphone-xr-128gb-trang-tgdd1.jpg\" title=\"Camera sau của điện thoại iPhone Xr 128GB Trắng chính hãng\" /></a></p><p>Đây cũng là chiếc iPhone đầu tiên có khả năng xóa phông với chỉ một camera và chất lượng ảnh xóa phông thì chắc chắn sẽ khiến nhiều máy camera kép phải \"ghen tỵ\".</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/204181/iphone-xr-128gb-trang-tgdd2.jpg\" onclick=\"return false;\"><img alt=\"Camera trước selfie của điện thoại iPhone Xr 128GB Trắng chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/204181/iphone-xr-128gb-trang-tgdd2.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/204181/iphone-xr-128gb-trang-tgdd2.jpg\" title=\"Camera trước selfie của điện thoại iPhone Xr 128GB Trắng chính hãng\" /></a></p><p>Khả năng quay video trên chiếc iPhone mới này tới từ Apple cũng được đáng giá hàng đầu hiện nay với hình ảnh ổn định, chống rung tốt và âm thanh thu lại \"tuyệt vời\".</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/204181/iphone-xr-128gb-trang-tgdd3.jpg\" onclick=\"return false;\"><img alt=\"Camera trước selfie của điện thoại iPhone Xr 128GB Trắng chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/204181/iphone-xr-128gb-trang-tgdd3.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/204181/iphone-xr-128gb-trang-tgdd3.jpg\" title=\"Camera trước selfie của điện thoại iPhone Xr 128GB Trắng chính hãng\" /></a></p><p>Chưa dừng lại ở đó, máy còn được tích hợp thêm công nghệ Smart HDR giúp tái tạo hình ảnh và cho ra một bức hình với độ sáng tốt nhất.</p><h3>Chiếc iPhone đầu tiên hỗ trợ 2 SIM</h3><p>Nếu như người dùng Android đã quen với việc sử dụng 2 SIM với một SIM cho công việc và một SIM cho data thì phải tới thế hệ iPhone Xr người dùng iOS mới được \"tận hưởng\" tính năng này.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/204181/iphone-xr-128gb-trang-tgdd8.jpg\" onclick=\"return false;\"><img alt=\"2 SIM 2 sóng của điện thoại iPhone Xr 128GB Trắng chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/204181/iphone-xr-128gb-trang-tgdd8.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/204181/iphone-xr-128gb-trang-tgdd8.jpg\" title=\"2 SIM 2 sóng của điện thoại iPhone Xr 128GB Trắng chính hãng\" /></a></p><p>Theo đó thì với chiếc iPhone Xr người dùng ngoài chiếc SIM vật lý quen thuộc thì còn được tích hợp thêm eSIM ẩn sâu bên trong máy.</p><p>Với việc hỗ trợ 2 SIM thì giờ đây bạn có thể tận dụng được tối đa các khuyến mãi tới từ nhà mạng và hạn chế được tính trạng \"mất SIM\" thường xuyên với eSIM.</p><h3>Những trang bị đáng giá khác</h3><p>iPhone Xr 128GB Trắng vẫn sở hữu cho mình màn hình tai thỏ và công nghệ mở khóa bằng khuôn mặt <a href=\"https://www.thegioididong.com/hoi-dap/face-id-la-gi-va-cach-hoat-dong-cua-face-id-1021871\" target=\"_blank\" title=\"Tìm hiểu về tính năng Face ID trên điện thoại iPhone\" type=\"Tìm hiểu về tính năng Face ID trên điện thoại iPhone\">Face ID</a> tiên tiến hàng đầu hiện nay.</p><p>Với hệ thống camera TrueDepth nay bạn có thể tự tạo cho bản thân những bức ảnh ấn tượng với công nghệ thực tế ảo tăng cường AR.</p><p>Một tính năng cao cấp khác là khả năng chống nước và bụi bẩn IP67 cũng xuất hiện trên chiếc smartphone này giúp bạn có thể sử dụng ngay cả trong điều kiện ẩm ướt.</p></article>', 147, 26800000, 27580000, '', '', '2019-06-23 20:45:03', 1, '2019-06-23 20:45:03', 1, 1),
(15, 6, 'iPhone Xr 128GB Đen', 'iphone-xr-128gb-den', 'iphone-xr-128gb-den.jpg', '<h2><a href=\"https://www.thegioididong.com/dtdd/iphone-xr-128gb-den\" target=\"_blank\" title=\"Tham khảo điện thoại iPhone Xr 128GB Đen chính hãng\">iPhone Xr 128GB Đen</a> là chiếc iPhone 2018 có giá thấp nhất tuy nhiên vẫn được trang bị <a href=\"https://www.thegioididong.com/hoi-dap/face-id-la-gi-va-cach-hoat-dong-cua-face-id-1021871\" target=\"_blank\" title=\"Tìm hiểu về tính năng Face ID trên điện thoại iPhone\" type=\"Tìm hiểu về tính năng Face ID trên điện thoại iPhone\">Face ID</a>, màn hình LCD tràn cạnh, camera đơn, thiết kế nhôm kính hỗ trợ sạc không dây và tích hợp chip Apple A12 cực mạnh.</h2><h3>Là chiếc iPhone có thời lượng pin tốt nhất</h3><p>iPhone Xr có thời lượng pin tốt nhất trong số bộ ba <a href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại Apple chính hãng\">iPhone</a> 2018, nhỉnh hơn cả <a href=\"https://www.thegioididong.com/dtdd/iphone-xs-max-256gb\" target=\"_blank\" title=\"Chi tiết điện thoại iPhone XS Max 256GB\" type=\"Chi tiết điện thoại iPhone XS Max 256GB\">iPhone Xs Max</a> tích hợp viên pin dung lượng lớn hơn.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/204184/iphone-xr-128gb-den-tgdd7.jpg\" onclick=\"return false;\"><img alt=\"Thời lượng pin của điện thoại iPhone Xr 128GB Đen chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/204184/iphone-xr-128gb-den-tgdd7.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/204184/iphone-xr-128gb-den-tgdd7.jpg\" title=\"Thời lượng pin của điện thoại iPhone Xr 128GB Đen chính hãng\" /></a></p><p>Thật vậy với dung lượng pin chỉ 2942 mAh nhưng nhờ phải gánh một màn hình độ phân giải không quá cao nên iPhone Xr 128GB Đen vẫn mang lại một thời lượng pin rất ấn tượng.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/204184/iphone-xr-128gb-den-tgdd8.jpg\" onclick=\"return false;\"><img alt=\"Thời lượng pin của điện thoại iPhone Xr 128GB Đen chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/204184/iphone-xr-128gb-den-tgdd8.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/204184/iphone-xr-128gb-den-tgdd8.jpg\" title=\"Thời lượng pin của điện thoại iPhone Xr 128GB Đen chính hãng\" /></a></p><p>Bên cạnh đó cũng không thể quên hệ điều hành iOS hoạt động ổn định giúp bạn có thể để máy qua đêm mà không hề sụt giảm nhiều dung lượng pin.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/204184/iphone-xr-128gb-den-tgdd5.jpg\" onclick=\"return false;\"><img alt=\"Thời lượng pin của điện thoại iPhone Xr 128GB Đen chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/204184/iphone-xr-128gb-den-tgdd5.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/204184/iphone-xr-128gb-den-tgdd5.jpg\" title=\"Thời lượng pin của điện thoại iPhone Xr 128GB Đen chính hãng\" /></a></p><p>Tất nhiên máy cũng hỗ trợ sạc không dây và sạc nhanh tương tự 2 người anh em iPhone Xs và Xs Max.</p><h3>Hiệu năng mạnh mẽ hàng đầu thế giới smartphone</h3><p>iPhone Xr 128GB Đen được trang bị cấu hình mạnh mẽ nhất của Apple trong năm 2018 với con chip <a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-chip-apple-a12-bionic-con-chip-7nm-dau-1171937\" target=\"_blank\" title=\"Tìm hiểu chip iPhone Xr 128GB Đen\">Apple A12 Bionic 6 nhân</a> cùng 3 GB RAM và 128 GB bộ nhớ trong.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/204184/iphone-xr-128gb-den-tgdd1.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình của điện thoại iPhone Xr 128GB Đen chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/204184/iphone-xr-128gb-den-tgdd1.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/204184/iphone-xr-128gb-den-tgdd1.jpg\" title=\"Cấu hình của điện thoại iPhone Xr 128GB Đen chính hãng\" /></a></p><p>Chip A12 bionic được xây dựng trên tiến trình 7nm đầu tiên đem lại hiêu năng vượt trội trong việc xử lý các tác vụ và khả năng tiết kiệm năng lượng tối ưu.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/204184/iphone-xr-128gb-den-tgdd2.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình của điện thoại iPhone Xr 128GB Đen chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/204184/iphone-xr-128gb-den-tgdd2.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/204184/iphone-xr-128gb-den-tgdd2.jpg\" title=\"Cấu hình của điện thoại iPhone Xr 128GB Đen chính hãng\" /></a></p><p>Chưa dừng lại ở đó, máy còn được tích hợp trí thông minh nhân tạo giúp phần cứng tối ưu hiệu suất, nhờ đó mà các thao tác của bạn được xử lý một cách nhanh chóng hơn.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/204184/iphone-xr-128gb-den-tgdd3.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình của điện thoại iPhone Xr 128GB Đen chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/204184/iphone-xr-128gb-den-tgdd3.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/204184/iphone-xr-128gb-den-tgdd3.jpg\" title=\"Cấu hình của điện thoại iPhone Xr 128GB Đen chính hãng\" /></a></p><p>Bên cạnh đó, bộ xử lý đồ họa của máy cũng được Apple thiết kế lại giúp việc chơi game hay dựng hình mượt mà và nhanh chóng hơn gấp nhiều lần.</p><p>Kết hợp với phần cứng mạnh mẽ là hệ điều hành iOS siêu mượt, hứa hẹn iPhone Xr 128GB Đen sẽ trở thành một con quái thú trong làng <a href=\"https://www.thegioididong.com/dtdd\" style=\"margin: 0px; padding: 0px; text-decoration-line: none; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; line-height: 18px; color: rgb(80, 168, 227); outline: none; zoom: 1;\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại tại Thegioididong.com\">smartphone</a> hiện nay.</p><h3>Chiếc iPhone đầu tiên hỗ trợ 2 SIM</h3><p>Nếu như người dùng Android đã quen với việc sử dụng 2 SIM với một SIM cho công việc và một SIM cho data thì phải tới thế hệ iPhone Xr người dùng iOS mới được \"tận hưởng\" tính năng này.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/204184/iphone-xr-128gb-den-tgdd11.jpg\" onclick=\"return false;\"><img alt=\"2 SIM 2 sóng của điện thoại iPhone Xr 128GB Đen chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/204184/iphone-xr-128gb-den-tgdd11.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/204184/iphone-xr-128gb-den-tgdd11.jpg\" title=\"2 SIM 2 sóng của điện thoại iPhone Xr 128GB Đen chính hãng\" /></a></p><p>Theo đó thì với chiếc iPhone Xr người dùng ngoài chiếc SIM vật lý quen thuộc thì còn được tích hợp thêm eSIM ẩn sâu bên trong máy.</p><p>Với việc hỗ trợ 2 SIM thì giờ đây bạn có thể tận dụng được tối đa các khuyến mãi tới từ nhà mạng và hạn chế được tính trạng \"mất SIM\" thường xuyên với eSIM.</p><h3>iPhone camera đơn nhưng vẫn xóa phông \"cực đỉnh\"</h3><p>iPhone Xr 128GB Đen là thế hệ đầu tiên Apple tích hợp camera đơn cho một chiếc iPhone nhưng vẫn cho khả năng xóa phông rất ấn tượng.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/204184/iphone-xr-128gb-den-tgdd10.jpg\" onclick=\"return false;\"><img alt=\"Camera sau của điện thoại iPhone Xr 128GB Đen chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/204184/iphone-xr-128gb-den-tgdd10.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/204184/iphone-xr-128gb-den-tgdd10.jpg\" title=\"Camera sau của điện thoại iPhone Xr 128GB Đen chính hãng\" /></a></p><p>Máy sở hữu camera chính có độ phân giải 12 MP hỗ trợ người chụp ảnh từ cơ bản đến cả nâng cao chuyên nghiệp.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/204184/iphone-xr-128gb-den-tgdd4.jpg\" onclick=\"return false;\"><img alt=\"Camera sau của điện thoại iPhone Xr 128GB Đen chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/204184/iphone-xr-128gb-den-tgdd4.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/204184/iphone-xr-128gb-den-tgdd4.jpg\" title=\"Camera sau của điện thoại iPhone Xr 128GB Đen chính hãng\" /></a></p><p>Máy còn có tính năng Smart HDR: Dù bạn chụp ảnh có độ sáng tối khác biệt nhiều nhưng vẫn giữ được chủ thể.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/204184/iphone-xr-128gb-den-tgdd6.jpg\" onclick=\"return false;\"><img alt=\"Camera trước selfie của điện thoại iPhone Xr 128GB Đen chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/204184/iphone-xr-128gb-den-tgdd6.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/204184/iphone-xr-128gb-den-tgdd6.jpg\" title=\"Camera trước selfie của điện thoại iPhone Xr 128GB Đen chính hãng\" /></a></p><p>Ngoài ra, iPhone Xr còn được hỗ trợ bởi trí thông minh nhân tạo đem đến khả năng tự động điều chỉnh màu sắc, độ sáng và độ tương phản sao cho phù hợp với từng vật thể khác nhau.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/204184/iphone-xr-128gb-den-tgdd9.jpg\" onclick=\"return false;\"><img alt=\"Camera trước selfie của điện thoại iPhone Xr 128GB Đen chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/204184/iphone-xr-128gb-den-tgdd9.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/204184/iphone-xr-128gb-den-tgdd9.jpg\" title=\"Camera trước selfie của điện thoại iPhone Xr 128GB Đen chính hãng\" /></a></p><p>Camera trước độ phân giải 7 MP với khả năng selfie xóa phông đáp ứng tốt mọi nhu cầu tự sướng của giới trẻ hiện nay.</p><h3>Nhiều tùy chọn màu sắc hơn</h3><p>Một lợi thế của chiếc iPhone Xr so với \"những người anh em đắt tiền\" của mình đó chính là máy có nhiều màu sắc hơn cho bạn lựa chọn.</p><p>Apple ra mắt iPhone Xs và Xs Max với 3 tùy chọn màu sắc: bạc, xám và vàng. Trong khi đó iPhone XR sở hữu tới 6 phiên bản màu là đen, trắng, vàng, đỏ, xanh dương và san hô.</p><p>Vậy nên, người dùng sẽ có nhiều sự lựa chọn hơn để phù hợp với sở thích, cá tính hay quan niệm phong thủy về màu sắc của mình.</p><p>iPhone Xr 128GB Đen cũng sở hữu chuẩn chống nước IP67 hàng đầu hiện nay giúp bạn có thể sử dụng trong những điều kiện ẩm ướt.</p></article>', 26, 12800000, 0, '', '', '2019-06-23 20:45:04', 1, '2019-06-23 20:45:04', 1, 1),
(16, 6, 'iPhone 8 Plus 64GB', 'iphone-8-plus-64gb', 'iphone-8-plus-64gb.jpg', '<h2>Thừa hưởng những thiết kế đã đạt đến độ chuẩn mực, thế hệ <a href=\"https://www.thegioididong.com/dtdd/iphone-8-plus\" target=\"_blank\" title=\"Chi tiết điện thoại iPhone 8 Plus\" type=\"Chi tiết điện thoại iPhone 8 Plus\">iPhone 8 Plus</a> thay đổi phong cách bóng bẩy hơn và bổ sung hàng loạt tính năng cao cấp cho trải nghiệm sử dụng vô cùng tuyệt vời.</h2><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/114114/iphone-8-plus-256gb-h1-1.jpg\" onclick=\"return false;\"><img alt=\"Đánh giá điện thoại iPhone 8 Plus 64GB\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-h1-1.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-h1-1.jpg\" title=\"Đánh giá điện thoại iPhone 8 Plus 64GB\"></a></p><h3>Thiết kế từ <a href=\"https://www.thegioididong.com/dtdd?g=kim-loai-va-kinh-cuong-luc\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại thiết kế kim loại và kính\" type=\"Tham khảo các dòng điện thoại thiết kế kim loại và kính\">kính và kim loại</a></h3><p><a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng smartphone tại Thegioididong.com\" type=\"Tham khảo các dòng smartphone tại Thegioididong.com\">Smartphone</a> iPhone 8 Plus giữ nguyên hoàn toàn những đường nét thiết kế đã hoàn thiện từ thế hệ trước nhưng sử dụng phong cách 2 mặt kính cường lực kết hợp bộ khung kim loại.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/114114/iphone-8-plus-256gb-h7-1.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại iPhone 8 Plus 64GB\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-h7-1.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-h7-1.jpg\" title=\"Thiết kế điện thoại iPhone 8 Plus 64GB\"></a></p><p>Mặt lưng kính bo cong 2.5D thời thượng, khung kim loại được gia cố cứng cáp, bền bỉ hơn với 7 lớp xử lý màu sơn, hạn chế bong tróc.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/114114/iphone-8-plus-256gb-h9.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại iPhone 8 Plus 64GB\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-h9.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-h9.jpg\" title=\"Thiết kế điện thoại iPhone 8 Plus 64GB\"></a></p><p>Thay đổi này đồng thời loại bỏ chi tiết đường anten thừa ở mặt lưng như các thế hệ trước, mang đến vẻ đẹp hoàn hảo hơn.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/114114/iphone-8-plus-256gb-h0.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại iPhone 8 Plus 64GB\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-h0.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-h0.jpg\" title=\"Thiết kế điện thoại iPhone 8 Plus 64GB\"></a></p><h3>Camera kép chuyên nghiệp</h3><p>Không có sự thay đổi trong thông số camera (vẫn là cụm camera kép cùng độ phân giải 12 MP so với chiếc điện thoại IPhone 7 Plus), chiếc <a href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại Apple iPhone tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại Apple iPhone tại Thegioididong.com\">điện thoại iPhone</a> này chủ yếu được tập trung nâng cấp về ống kính, cảm biến, vi xử lý hình ảnh để nâng cao chất lượng ảnh chụp.</p><p>Khả năng zoom quang 2X không thay đổi chất lượng cũng như xóa phông chân dung vẫn được tích hợp trên iPhone 8 Plus cùng với <a href=\"https://www.thegioididong.com/tin-tuc/post-1029635\" target=\"_blank\" title=\"Tìm hiểu tính năng PORTRAIT LIGHTING\" type=\"Tìm hiểu tính năng PORTRAIT LIGHTING\">Portrait Lighting</a>, tính năng chụp ảnh sân khấu hoàn toàn mới.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/114114/iphone-8-plus-256gb-h5-1.jpg\" onclick=\"return false;\"><img alt=\"Camera trên điện thoại iPhone 8 Plus 64GB\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-h5-1.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-h5-1.jpg\" title=\"Camera trên điện thoại iPhone 8 Plus 64GB\"></a></p><p>Camera trước vẫn là độ phân giải 7 MP cùng tính năng chụp chân dung ánh sáng sân khấu đặc biệt.</p><h3>Chip Apple A11 cùng với thực tế ảo AR</h3><p>Một trong những điểm nhất quan trọng nhất ở thế hệ iPhone 8 Plus chính là sức mạnh phần cứng, con chip <a href=\"https://www.thegioididong.com/tin-tuc/chi-tiet-a11-bionic-chip-co-nhieu-thanh-phan-apple-tu-trong-nhat-tu-truoc-den-nay-1021653\" target=\"_blank\" title=\"Tìm hiểu chip Apple A11 trên điện thoại iPhone\" type=\"Tìm hiểu chip Apple A11 trên điện thoại iPhone\">Apple A11</a> như trên <a href=\"https://www.thegioididong.com/dtdd/iphone-x-64gb\" target=\"_blank\" title=\"Chi tiết điện thoại iPhone X\" type=\"Chi tiết điện thoại iPhone X\">iPhone X</a> cho hiệu năng vượt trội hơn rất nhiều so với trước đây, đặc biệt tối ưu cho 3D và thực tế ảo tăng cường AR, một trải nghiệm hoàn toàn mới mẻ trên smartphone.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/114114/iphone-8-plus-256gb-h3.jpg\" onclick=\"return false;\"><img alt=\"Tính năng thực tế ảo trên điện thoại iPhone 8 Plus 64GB\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-h3.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-h3.jpg\" title=\"Tính năng thực tế ảo trên điện thoại iPhone 8 Plus 64GB\"></a></p><h3>Màn hình rộng hoàn hảo</h3><p>Kích thước màn hình vẫn là 5.5 inch, giữ nguyên độ phân giải Retina HD sắc nét nhưng thế hệ iPhone 8 Plus lại có thêm những cải tiến đáng giá hơn, về độ phủ màu lớn hiển thị màu sắc chính xác hay công nghệ <a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-cong-nghe-man-hinh-true-tone-992705\" target=\"_blank\" title=\"Tìm hiểu công nghê True Tone\" type=\"Tìm hiểu công nghê True Tone\">True Tone</a> từ iPad Pro 2017.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/114114/iphone-8-plus-256gb2-1.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại iPhone 8 Plus 64GB\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb2-1.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb2-1.jpg\" title=\"Màn hình điện thoại iPhone 8 Plus 64GB\"></a></p><h3>Hỗ trợ sạc nhanh, sạc không dây</h3><p>Nhờ thiết kế mặt lưng kính mà iPhone 8 Plus đã có thể tích hợp thêm công nghệ <a href=\"https://www.thegioididong.com/hoi-dap/cong-nghe-sac-khong-day-761328\" target=\"_blank\" title=\"Tìm hiểu công nghệ sạc không dây\" type=\"Tìm hiểu công nghệ sạc không dây\">sạc không dây</a> tiên tiến cho sử dụng an toàn và thuận tiện hơn rất nhiều.</p><p>Bên cạnh đó, một nâng cấp phải kể đến nữa là khả năng hỗ trợ sạc nhanh đạt 50% chỉ sau 30 phút, tuy nhiên bộ phụ kiện theo kèm máy chưa bao gồm sạc nhanh.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/114114/iphone-8-plus-256gb-h4-1.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPhone 8 Plus 64GB có hỗ trợ sạc không dây\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-h4-1.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-h4-1.jpg\" title=\"Điện thoại iPhone 8 Plus 64GB có hỗ trợ sạc không dây\"></a></p></article>', 187, 21700000, 0, '', '', '2019-06-23 20:45:06', 1, '2019-06-23 20:45:06', 1, 1),
(17, 6, 'iPhone 6s Plus 32GB', 'iphone-6s-plus-32gb', 'iphone-6s-plus-32gb.jpg', '<h2><a href=\"https://www.thegioididong.com/dtdd/iphone-6s-plus-32gb\" target=\"_blank\" title=\"Chi tiết điện thoại iPhone 6s Plus 32GB\" type=\"Chi tiết điện thoại iPhone 6s Plus 32GB\">iPhone 6s Plus 32 GB</a> là phiên bản nâng cấp hoàn hảo từ <a href=\"https://www.thegioididong.com/dtdd/iphone-6-plus-32gb\" target=\"_blank\" title=\"Chi tiết điện thoại iPhone 6 Plus\" type=\"Chi tiết điện thoại iPhone 6 Plus\">iPhone 6 Plus</a> với nhiều tính năng mới hấp dẫn.</h2><h3>Camera được cải tiến</h3><p><a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại tại Thegioididong.com\">Điện thoại</a> iPhone 6s Plus 32 GB được nâng cấp độ phân giải camera sau lên 12 MP (thay vì 8 MP như trên iPhone 6 Plus), camera cho tốc độ lấy nét và chụp nhanh, thao tác chạm để chụp nhẹ nhàng. Chất lượng ảnh trong các điều kiện chụp khác nhau tốt.</p><p><div id=\"imgCpr_1\" class=\"imgCprW\" data-src1=\"https://cdn.tgdd.vn/Products/Images/42/71770/iphone-6s-plus2-1.jpg\" data-src2=\"https://cdn.tgdd.vn/Products/Images/42/71770/iphone-6s-plus1-1.jpg\" >Đang tải...</div></p><p><i>Màu sáng hơn hẳn so với iPhone 6 Plus</i></p><p>Camera trước với độ phân giải 5 MP cho hình ảnh với độ chi tiết rõ nét, đặc biệt máy còn có tính năng Retina Flash, sẽ giúp màn hình sáng lên như đèn Flash để bức ảnh khi bạn chụp trong trời tối được tốt hơn.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/71770/iphone-6s-plus213.gif\" onclick=\"return false;\"><img alt=\"Để bật tính năng Retina Flash, tại camera trước bạn bật đèn Flash lên\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/71770/iphone-6s-plus213.gif\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/71770/iphone-6s-plus213.gif\" title=\"Để bật tính năng Retina Flash, tại camera trước bạn bật đèn Flash lên\"></a></p><p><i>Để bật tính năng Retina Flash, tại camera trước bạn bật đèn Flash lên</i></p><p><div id=\"imgCpr_2\" class=\"imgCprW\" data-src1=\"https://cdn.tgdd.vn/Products/Images/42/71770/iphone-6s-plus1-2.jpg\" data-src2=\"https://cdn.tgdd.vn/Products/Images/42/71770/iphone-6s-plus2-2.jpg\" >Đang tải...</div></p><p><i>Đèn Flash giúp ảnh được sáng hơn</i></p><h3>Thích thú hơn với màn hình rộng</h3><p>Màn hình lớn cùng <span>màu sắc tươi tắn, độ nét cao sẽ mang đến nhiều</span> thích thú khi lướt web, xem phim hay làm việc.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/71770/iphone-6s-plus4-1.jpg\" onclick=\"return false;\"><img alt=\"Màn hình lớn 5.5 inch thoải mái để làm việc và giải trí\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/71770/iphone-6s-plus4-1.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/71770/iphone-6s-plus4-1.jpg\" title=\"Màn hình lớn 5.5 inch thoải mái để làm việc và giải trí\"></a></p><p><i>Màn hình lớn 5.5 inch thoải mái để làm việc và giải trí</i></p><h3>Cảm ứng 3D Touch độc đáo</h3><p>3D Touch là tính năng hoàn toàn mới trên iPhone 6s Plus 32 GB, cho phép người dùng xem trước được các tùy chọn nhanh dựa vào lực nhấn mạnh hay nhẹ mà không cần phải nhấp vào ứng dụng.</p><p>Để sử dụng, bạn chỉ cần nhấn vào màn hình hoặc ứng dụng 1 lực mạnh đến khi máy rung nhẹ là có thể xem được.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/71770/iphone-6s-plus342.gif\" onclick=\"return false;\"><img alt=\"Chọn nhanh các lựa chọn trên camera của máy\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/71770/iphone-6s-plus342.gif\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/71770/iphone-6s-plus342.gif\" title=\"Chọn nhanh các lựa chọn trên camera của máy\"></a></p><p><i>Chọn nhanh các lựa chọn trên camera của máy</i></p><p>Đáng tiếc tính năng 3D Touch này chỉ mới được áp dụng trên các ứng dụng trên dòng <a href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại iPhone tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại iPhone tại Thegioididong.com\">điện thoại iPhone</a> như: danh bạ, camera, mail, máy ảnh ... </p><p>Bạn có thể tìm hiểu thêm tính năng 3D Touch <strong><a href=\"https://www.thegioididong.com/tin-tuc/tong-hop-tat-ca-nhung-tien-ich-3d-touch-dem-den-cho-nguoi-dung-714800\" target=\"_blank\" title=\"Tìm hiểu thêm các chức năng 3D Touch\">tại đây</a></strong>.</p><h3>Sức mạnh của bộ vi xử lý A9 mới nhất</h3><p>iPhone 6s Plus 32 GB sử dụng <a href=\"https://www.thegioididong.com/hoi-dap/chip-xu-ly-apple-a9-tren-iphone-6s-va-6s-plus-733695\" target=\"_blank\" title=\"Tìm hiểu rõ hơn về chip xử lý A9\">vi xử lý A9</a> tốc độ 1.8 GHz (iPhone 6 Plus chỉ với 1.4 GHz), giúp máy chạy cùng lúc nhiều ứng dụng mượt mà.</p><p><div class=video allowfullscreen=\"\" frameborder=\"0\" height=\"395\" scrolling=\"no\" src=\"https://www.youtube.com/embed/ZowcYYl-UM0\" width=\"640\"></div></p><p><i><span>Bạn sẽ thực sự cảm nhận được sức mạnh của iPhone 6s Plus 32 GB khi chiến các game có đồ họa nặng như </span><a href=\"https://www.thegioididong.com/tin-tuc/modern-combat-5-blackout-game-bom-tan-do-bo-len-ca-556327\" target=\"_blank\" title=\"Thông tin thêm về game Modern Combat\">Modern Combat 5</a><span> hay Warhammer 40.000</span></i></p><p><div class=video allowfullscreen=\"\" frameborder=\"0\" height=\"395\" src=\"https://www.youtube.com/embed/338nP7G8TfI\" width=\"640\"></div></p><p><i>Người trợ lý ảo rất hữu dụng trên các dòng máy iPhone (Nguồn: Youtube)</i></p><p><span>Viên pin chỉ có dung lượng 2750 mAh khá thấp, tuy nhiên bạn vẫn có thể an tâm sử dụng máy trong một ngày.</span></p><p>Một chiếc điện thoại vừa thể hiện đẳng cấp của bạn vừa mang lại những nâng cấp tốt hơn như camera, hiệu năng hoạt động mạnh mẽ hơn, tính năng 3D Touch độc đáo, tất cả sẽ là trải nghiệm mới mẻ cho bạn khi chọn mua iPhone 6s Plus 32 GB.</p></article>', 118, 7100000, 0, '', '', '2019-06-23 20:45:07', 1, '2019-06-23 20:45:07', 1, 1);
INSERT INTO `uml_product` (`product_id`, `product_catid`, `product_name`, `product_slug`, `product_img`, `product_detail`, `product_number`, `product_price`, `product_pricesale`, `product_metakey`, `product_metadesc`, `product_createdat`, `product_createdby`, `product_updatedat`, `product_updatedby`, `product_status`) VALUES
(18, 7, 'Samsung Galaxy S10+ 512GB', 'samsung-galaxy-s10-512gb', 'samsung-galaxy-s10-512gb.jpg', '<h2><a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-s10-plus-512gb\" title=\"Tham khảo điện thoại Samsung Galaxy S10+ (512GB) chính hãng\">Samsung Galaxy S10+ (512GB)</a> - phiên bản kỷ niệm 10 năm chiếc Galaxy S đầu tiên ra mắt, là một chiếc <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại chính hãng\">smartphone</a> hội tủ đủ các yếu tố mà bạn cần ở một chiếc máy cao cấp trong năm 2019.</h2><h3>Khác biệt tới từ màn hình <a href=\"https://www.thegioididong.com/hoi-dap/tong-hop-cac-loai-man-hinh-vo-cuc-moi-tren-dien-th-1150900#infinity-o\" target=\"_blank\" title=\"Tìm hiểu công nghệ màn hình Infinity-O trên điện thoại Samsung\" type=\"Tìm hiểu công nghệ màn hình Infinity-O trên điện thoại Samsung\">Infinity-O</a></h3><p>Samsung Galaxy S10+ (512GB) đi theo kiểu thiết kế màn hình Infinity-O với phần camera được đặt phía trong màn hình rất độc đáo.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/198986/samsung-galaxy-s10-plus-512gb-white-1.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại Samsung Galaxy S10+ 512GB chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/198986/samsung-galaxy-s10-plus-512gb-white-1.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/198986/samsung-galaxy-s10-plus-512gb-white-1.jpg\" title=\"Màn hình điện thoại Samsung Galaxy S10+ 512GB chính hãng\"></a></p><p>Kiểu thiết kế mới này mang lại phần viền màn hình mỏng ở tất cả các cạnh, từ đó khiến tổng thể máy không quá lớn so với kích thước màn hình.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/198986/samsung-galaxy-s10-plus-512gb-white-10.jpg\" onclick=\"return false;\"><img alt=\"Mặt trước điện thoại Samsung Galaxy S10+ 512GB chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/198986/samsung-galaxy-s10-plus-512gb-white-10.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/198986/samsung-galaxy-s10-plus-512gb-white-10.jpg\" title=\"Mặt trước điện thoại Samsung Galaxy S10+ 512GB chính hãng\"></a></p><p>Màn hình của máy có kích thước 6.4 inch cùng độ phân giải khủng 2K+ cho bạn thưởng thức những bộ phim hay xem những hình ảnh sắc nét.</p><p><a href=\"https://www.thegioididong.com/tin-tuc/hdr10-la-gi-vi-sao-no-lam-cho-man-hinh-galaxy-s10-dep-hon--1151189\" target=\"_blank\" title=\"Tìm hiểu công nghệ HDR10+\" type=\"Tìm hiểu công nghệ HDR10+\">Công nghệ HDR10+</a> tiên tiến nhất hiện nay cho bạn một trải nghiệm hình ảnh thực sự khác biệt so với phần còn lại của thế giới smartphone.</p><h3>Camera được nâng lên tầm cao mới</h3><p>Những chiếc Galaxy S tới từ <a href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại Samsung chính hãng\">Samsung</a> luôn được người dùng đánh giá cao về camera và với Samsung Galaxy S10+ (512GB) cũng không phải là một ngoại lệ.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/198986/samsung-galaxy-s10-plus-512gb-white-11.jpg\" onclick=\"return false;\"><img alt=\"Giao diện camera điện thoại Samsung Galaxy S10+ 512GB chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/198986/samsung-galaxy-s10-plus-512gb-white-11.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/198986/samsung-galaxy-s10-plus-512gb-white-11.jpg\" title=\"Giao diện camera điện thoại Samsung Galaxy S10+ 512GB chính hãng\"></a></p><p>Máy sở hữu 3 camera với các thông số lần lượt là: ống kính chính góc rộng (77 độ) 12 MP (khẩu độ f/1.5), ống kính phụ tele 12 MP (khẩu độ f/2.4) và ống kính phụ góc siêu rộng (123 độ, f/2.2) 16 MP.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/198986/sieu-pham-galaxy-s-moi-2-512gb11.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera của điện thoại Samsung Galaxy S10+ (512GB) chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/198986/sieu-pham-galaxy-s-moi-2-512gb11.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/198986/sieu-pham-galaxy-s-moi-2-512gb11.jpg\" title=\"Ảnh chụp từ camera của điện thoại Samsung Galaxy S10+ (512GB) chính hãng\"></a></p><p>Trang công nghệ DxOMark - trang chuyên đánh giá về camera cũng đã đưa ra nhận xét về Galaxy S10+ là một trong những chiếc máy có camera tốt nhất trên thị trường nhờ khả năng chụp siêu rộng cũng như hình ảnh sắc nét, màu sắc rực rỡ.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/198986/sieu-pham-galaxy-s-moi-2-512gb10.jpg\" onclick=\"return false;\"><img alt=\"Camera trước selfie của điện thoại Samsung Galaxy S10+ (512GB) chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/198986/sieu-pham-galaxy-s-moi-2-512gb10.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/198986/sieu-pham-galaxy-s-moi-2-512gb10.jpg\" title=\"Camera trước selfie của điện thoại Samsung Galaxy S10+ (512GB) chính hãng\"></a></p><p>Camera trước của máy cũng là cụm camera kép với chiếc ống kính chính 10 MP và ống kính phụ 8 MP hỗ trợ chụp ảnh chân dung và quay phim với độ phân giải 4K.</p><h3>Sở hữu con chip mạnh mẽ nhất năm 2019</h3><p>Con chip Exynos 9820 kết hợp với 8 GB RAM đủ sức cho bạn có thể sử dụng tất cả game và ứng dụng nặng nhất hiện nay một cách mượt mà, bất kể là Liên Quân Mobile, Free Fire hay PUBG.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/198986/samsung-galaxy-s10-plus-512gb-white-8.jpg\" onclick=\"return false;\"><img alt=\"Android trên điện thoại Samsung Galaxy S10+ 512GB chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/198986/samsung-galaxy-s10-plus-512gb-white-8.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/198986/samsung-galaxy-s10-plus-512gb-white-8.jpg\" title=\"Android trên điện thoại Samsung Galaxy S10+ 512GB chính hãng\"></a></p><p>Bên cạnh đó thì với 512 GB bộ nhớ trong thì bạn sẽ có một không gian \"cực kỳ thoải mái\" để tải ứng dụng hay lưu trữ dữ liệu cá nhân.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/198986/samsung-galaxy-s10-plus-512gb-white-12.jpg\" onclick=\"return false;\"><img alt=\"Chơi game trên điện thoại Samsung Galaxy S10+ 512GB chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/198986/samsung-galaxy-s10-plus-512gb-white-12.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/198986/samsung-galaxy-s10-plus-512gb-white-12.jpg\" title=\"Chơi game trên điện thoại Samsung Galaxy S10+ 512GB chính hãng\"></a></p><p>Samsung Galaxy S10+ cũng được tối ưu về <a href=\"https://www.thegioididong.com/tin-tuc/samsung-ra-mat-giao-dien-moi--one-ui-1129869\" target=\"_blank\" title=\"Tìm hiểu về giao diện OneUI trên smartphone Samsung\" type=\"Tìm hiểu về giao diện OneUI trên smartphone Samsung\">giao diện OneUI</a> mới hứa hẹn sẽ giúp máy có được khả năng hoạt động lâu dài hơn các phiên bản tiền nhiệm trước đây.</p><h3>Nhiều tính năng cao cấp khác</h3><p>Trên Samsung Galaxy S10+ còn có một tính năng mới được rất nhiều người yêu thích đó chính là khả năng sạc ngược không dây cho smartphone khác (PowerShare).</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/198986/samsung-galaxy-s10-plus-512gb-powershare.jpg\" onclick=\"return false;\"><img alt=\"Tính năng PowerShare trên Galaxy S10+\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/198986/samsung-galaxy-s10-plus-512gb-powershare.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/198986/samsung-galaxy-s10-plus-512gb-powershare.jpg\" title=\"Tính năng PowerShare trên Galaxy S10+\"></a></p><p>Điều này có thể giúp bạn biến chiếc smartphone của mình thành một cục sạc không dây di động trong những trường hợp khẩn cấp.</p><p><i>Xem thêm: <a href=\"https://www.thegioididong.com/tin-tuc/tinh-nang-powershare-tren-galaxy-s10-la-gi-lam-sao-de-su-dung-no-1151089\" target=\"_blank\" title=\"Tính năng PowerShare trên Galaxy S10 là gì? Làm sao để sử dụng nó?\" type=\"Tính năng PowerShare trên Galaxy S10 là gì? Làm sao để sử dụng nó?\">Tính năng PowerShare trên Galaxy S10 là gì? Làm sao để sử dụng nó?</a></i></p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/198986/samsung-galaxy-s10-plus-512gb-white-2.jpg\" onclick=\"return false;\"><img alt=\"Đánh giá điện thoại Samsung Galaxy S10+ 512GB chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/198986/samsung-galaxy-s10-plus-512gb-white-2.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/198986/samsung-galaxy-s10-plus-512gb-white-2.jpg\" title=\"Đánh giá điện thoại Samsung Galaxy S10+ 512GB chính hãng\"></a></p><p>Ngoài ra tiêu chuẩn kháng nước và bụi bẩn IP68 cao cấp nhất hiện nay vẫn xuất hiện trên máy đảm bảo bạn có thể sử dụng dưới các điều kiện khắc nghiệt mà máy không gặp vấn đề gì.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/198986/samsung-galaxy-s10-plus-512gb-white-6.jpg\" onclick=\"return false;\"><img alt=\"Vân tay trên điện thoại Samsung Galaxy S10+ 512GB chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/198986/samsung-galaxy-s10-plus-512gb-white-6.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/198986/samsung-galaxy-s10-plus-512gb-white-6.jpg\" title=\"Vân tay trên điện thoại Samsung Galaxy S10+ 512GB chính hãng\"></a></p><p><a href=\"https://www.thegioididong.com/hoi-dap/cong-nghe-quet-van-tay-song-sieu-am-la-gi-912419\" target=\"_blank\" title=\"Tìm hiểu tính năng vân tay siêu âm \" type=\"Tìm hiểu tính năng vân tay siêu âm \">Vân tay siêu âm</a> hiện đại và tiên tiến cũng là 1 tính năng hấp dẫn trên S10+ giúp mở khoá máy nhanh chóng và đẳng cấp.</p></article>', 49, 10200000, 0, '', '', '2019-06-23 20:47:22', 1, '2019-06-23 20:47:22', 1, 1),
(19, 7, 'Samsung Galaxy S10+ 128GB Bạc Đa Sắc', 'samsung-galaxy-s10-128gb-bac-da-sac', 'samsung-galaxy-s10-128gb-bac-da-sac.jpg', '<h2><a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-s10-plus\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung Galaxy S10+ chính hãng\" type=\"Tham khảo điện thoại Samsung Galaxy S10+ chính hãng\">Samsung Galaxy S10+</a> đã ra mắt và thực sự người dùng khó có thể cường lại sức hút kỳ lạ tới từ siêu phẩm này của Samsung và giờ đây hãng sản xuất smartphone tới từ Hàn Quốc tiếp tục tung ra phiên bản <a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-s10-plus-128gb-bac-da-sac\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung Galaxy S10+ 128GB Bạc Đa Sắc chính hãng\">Samsung Galaxy S10+ 128GB Bạc Đa Sắc</a> thậm chí còn thu hút hơn các phiên bản cũ.</h2><h3>Phiên bản giới hạn với bộ phụ kiện cao cấp</h3><p>Khác biệt hoàn toàn với phiên bản S10+ thông thường, chiếc điện thoại S10+ Park Hang Seo (Bạc Đa Sắc) này sở hữu bộ phụ kiện độc đáo không máy nào có được.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-24.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Phụ kiện theo máy\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-24.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-24.jpg\" title=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Phụ kiện theo máy\"></a></p><p>Điểm nhấn của phiên bản này là chiếc ốp lưng màu đỏ, in hình người thầy đáng kính của đội tuyển Việt Nam.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-26.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Phụ kiện theo máy\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-26.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-26.jpg\" title=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Phụ kiện theo máy\"></a></p><p>Hơn nữa, máy còn độc đáo ở chỗ \"màu bạc đa sắc\" sẽ cho từng màu khác nhau khi chuyển đổi góc máy.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-25.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Thiết kế mặt lưng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-25.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-25.jpg\" title=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Thiết kế mặt lưng\"></a></p><p>Từng màu vàng, bạc, hay đỏ,... sẽ xuất hiện giúp người dùng thêm độc đáo và không bị \"đụng hàng\".</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-5.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Thiết kế mặt lưng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-5.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-5.jpg\" title=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Thiết kế mặt lưng\"></a></p><h3>Thiết kế gây ấn tượng từ cái nhìn đầu tiên</h3><p>Samsung Galaxy S10+ sở hữu màn hình Infinity-O tràn viền với màu sắc vô cùng sống động chắc chắn sẽ làm tan chảy bất kì con tim nào.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-27.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Thiết kế màn hình\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-27.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-27.jpg\" title=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Thiết kế màn hình\"></a></p><p>Nhờ thiết kế đục lỗ, Galaxy S10+ có màn hình tràn viền thực sự, nhìn đã mắt và tỷ lệ màn hình/mặt trước của máy là 89%, cao hơn nhiều so với thế hệ Galaxy S9 cũ.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-1.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Thiết kế màn hình\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-1.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-1.jpg\" title=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Thiết kế màn hình\"></a></p><p>Công nghệ màn hình <a href=\"https://www.thegioididong.com/tin-tuc/man-hinh-dynamic-amoled-la-gi-no-co-cong-dung-gi-noi-bat-1153075\" target=\"_blank\" title=\"Tìm hiểu về tấm nền Dynamic AMOLED\" type=\"Tìm hiểu về tấm nền Dynamic AMOLED\">Dynamic AMOLED</a>, tỷ lệ màn hình 19.5:9, viền bezel cực mỏng, có thể nói là mỏng nhất từ trước đến nay từng thấy, trải nghiệm hình ảnh trên S10+ là cực kỳ đẳng cấp và đáng giá.</p><p>Một nâng cấp tuy nhỏ nhưng khá quan trọng khác là màn hình trên Galaxy S10+ hỗ trợ chuẩn hình ảnh tiên tiến nhất hiện nay <a href=\"https://www.thegioididong.com/tin-tuc/hdr10-la-gi-vi-sao-no-lam-cho-man-hinh-galaxy-s10-dep-hon--1151189\" target=\"_blank\" title=\"Tìm hiểu về công nghệ HDR10+\" type=\"Tìm hiểu về công nghệ HDR10+\">công nghệ HDR10+</a>.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-7.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Thiết kế màn hình\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-7.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-7.jpg\" title=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Thiết kế màn hình\"></a></p><p>Điều này sẽ giúp màu sắc tái hiện sống động hơn và đặc biệt độ sâu của màu đen nhìn đã hơn, sướng mắt hơn.</p><h3>Camera được nâng cấp mạnh mẽ</h3><p>Samsung đã \"bắt trend\" rất nhanh ở trào lưu 3 camera khi Galaxy S10+ được trang bị thêm một camera góc rộng siêu rộng nữa bên cạnh camera tele và camera tiêu chuẩn.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-8.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Giao diện camera\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-8.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-8.jpg\" title=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Giao diện camera\"></a></p><p>Camera góc siêu rộng 16 MP trên S10+ có góc chụp tới 123 độ, tiêu cự 12mm, khẩu độ f/2.2, là một trong những camera góc rộng nhất hiện nay trên <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại chính hãng\">smartphone</a>.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-13.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Ảnh chụp góc rộng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-13.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-13.jpg\" title=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Ảnh chụp góc rộng\"></a></p><p>Với cụm ba máy ảnh, Nếu bạn muốn lấy nhiều cảnh vật hơn, bạn có thể chuyển qua ống kính siêu rộng, nếu bạn muốn chụp chân dung, bạn chuyển qua ống kính tele.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd9.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera của điện thoại Samsung Galaxy S10+ 128GB Bạc Đa Sắc chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd9.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd9.jpg\" title=\"Ảnh chụp từ camera của điện thoại Samsung Galaxy S10+ 128GB Bạc Đa Sắc chính hãng\"></a></p><p>Thậm chí nếu bạn muốn điều khiển toàn bộ thông số máy ảnh, bạn có thể chọn chế độ chuyên nghiệp trên phần mềm chụp ảnh. S10+ có thể phơi sáng tối đa 10 giây, đủ để bạn có được những cảnh chụp đêm hay phong cảnh huyền bí.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd10.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera của điện thoại Samsung Galaxy S10+ 128GB Bạc Đa Sắc chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd10.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd10.jpg\" title=\"Ảnh chụp từ camera của điện thoại Samsung Galaxy S10+ 128GB Bạc Đa Sắc chính hãng\"></a></p><p>Với camera selfie, S10+ có đến 2 camera gồm 1 camera chính 10 MP, khẩu độ f/1.9, tiêu cự 26mm, điểm ảnh 1.22µm, hỗ trợ lấy nét Dual Pixel PDAF.</p><p>Bên cạnh đó là 1 camera 8 MP, khẩu độ f/2.2, tiêu cự 22mm (wide), điểm ảnh 1.12µm, chuyên để thu độ sâu phục vụ việc xóa phông.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd11.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera của điện thoại Samsung Galaxy S10+ 128GB Bạc Đa Sắc chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd11.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd11.jpg\" title=\"Ảnh chụp từ camera của điện thoại Samsung Galaxy S10+ 128GB Bạc Đa Sắc chính hãng\"></a></p><p>Các chế độ làm đẹp hay xóa phông đều làm việc hiệu quả, tạo ra những bức ảnh selfie \"lung linh\" hơn nhưng không cà mặt quá đà như trước nữa.</p><h3>Hiệu năng hàng đầu thế giới smartphone</h3><p>Cung cấp sức mạnh cho <a href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại Samsung chính hãng\">Samsung</a> Galaxy S10+ 128GB Bạc Đa Sắc là con chip hàng đầu tới từ Samsung Exynos 9820 8 nhân 64-bit cùng 8 GB RAM và 128 GB bộ nhớ trong.</p><p>Trong sử dụng thực tế, không bất ngờ khi hiệu năng của S10+ đáp ứng rất nhanh và mượt mà, các ứng dụng mở gần như ngay tức thì, cuộn trang và các hiệu ứng chuyển động trên giao diện phần mềm diễn ra trơn tru.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-28.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Trải nghiệm chơi game\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-28.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-28.jpg\" title=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Trải nghiệm chơi game\"></a></p><p>Nếu kết hợp với đế Galaxy DeX, thậm chí bạn hoàn toàn có thể để laptop ở nhà mà vẫn có thể đảm bảo công việc văn phòng chạy trơn tru như bình thường.</p><p>Ngoài ra máy còn sở hữu chuẩn kết nối Wi-Fi 6 hoàn toàn mới đảm bảo chiếc smartphone của bạn có thể kết nối internet nhanh nhất có thể.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-14.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Trên tay và trải nghiệm\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-14.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-14.jpg\" title=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Trên tay và trải nghiệm\"></a></p><p>Thời lượng pin 4100mAh sẽ đủ để bạn dùng từ sáng đến tối mà không cần phải sạc giữa chừng, thậm chí còn dư để có thể sạc không dây cho các thiết bị khác khi cần.</p><h3>Nhiều trang bị cao cấp khác nữa</h3><p>Samsung là một hãng smartphone hiếm hoi vẫn giữ được những chất riêng cho chiếc smartphone của mình.</p><p>Bạn sẽ vẫn có jack cắm tai nghe 3.5 mm để có thể sử dụng với các tai nghe phổ biến hiện nay mà không cần thông qua jack chuyển.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-3.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Jack 3.5 mm\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-3.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-3.jpg\" title=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Jack 3.5 mm\"></a></p><p>Bạn vẫn sẽ có tiêu chuẩn chống nước và bụi bẩn cao cấp nhất hiện nay IP68 giúp máy có thể chịu đựng được trong các môi trường khắc nghiệt.</p><p>Và kèm theo đó là một bộ giải mã âm thanh cực kỳ đáng giá hỗ trợ chuẩn Dolby Atmos/AKG để phục vụ các tín đồ yêu âm nhạc.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-9.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Vân tay siêu âm\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-9.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-9.jpg\" title=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Vân tay siêu âm\"></a></p><p>Samsung Galaxy S10+ 128GB Bạc Đa Sắc cũng là một trong những smartphone đầu tiên sở hữu công nghệ cảm biến <a href=\"https://www.thegioididong.com/hoi-dap/cong-nghe-quet-van-tay-song-sieu-am-la-gi-912419\" target=\"_blank\" title=\"Tìm hiểu công nghệ vân tay siêu âm\" type=\"Tìm hiểu công nghệ vân tay siêu âm\">vân tay siêu âm</a> bên trong màn hình với độ chính xác rất cao.</p></article>', 156, 13400000, 0, '', '', '2019-06-23 20:47:23', 1, '2019-06-23 20:47:23', 1, 1),
(20, 7, 'Samsung Galaxy S10+', 'samsung-galaxy-s10', 'samsung-galaxy-s10.jpg', '<h2><a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-s10-plus\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung Galaxy S10+ chính hãng\" type=\"Tham khảo điện thoại Samsung Galaxy S10+ chính hãng\">Samsung Galaxy S10+</a> là một trong những chiếc <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại chính hãng\">smartphone</a> được trông chờ nhiều nhất trong năm 2019 và không phụ sự kỳ vọng của mọi người thì chiếc Galaxy S thứ 10 của Samsung thực sự gây ấn tượng mạnh cho người dùng.</h2><h3>Thiết kế sang trọng, bóng bẩy</h3><p>Với một chiếc máy cao cấp như Samsung Galaxy S10+ thì việc đầu tiên cần có là máy phải sở hữu một vẻ ngoài \"lộng lẫy\", thu hút mọi ánh nhìn.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/179530/samsung-galaxy-s10-plus-white-5.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại Samsung Galaxy S10+ chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/179530/samsung-galaxy-s10-plus-white-5.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/179530/samsung-galaxy-s10-plus-white-5.jpg\" title=\"Thiết kế điện thoại Samsung Galaxy S10+ chính hãng\"></a></p><p>Và thậm chí <a href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại Samsung chính hãng\">Samsung</a> còn làm tốt hơn như thế khi Samsung Galaxy S10+ sở hữu thiết kế 2 mặt kính đẹp mắt cùng việc loại bỏ đi cảm biến vân tay ở mặt lưng giúp máy liền mạch hơn.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/179530/samsung-galaxy-s10-plus-white-3.jpg\" onclick=\"return false;\"><img alt=\"Mặt lưng điện thoại Samsung Galaxy S10+ chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/179530/samsung-galaxy-s10-plus-white-3.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/179530/samsung-galaxy-s10-plus-white-3.jpg\" title=\"Mặt lưng điện thoại Samsung Galaxy S10+ chính hãng\"></a></p><p>Trên Galaxy S10+ Samsung đã sở hữu công nghệ màn hình mới mà hãng gọi là <a href=\"https://www.thegioididong.com/hoi-dap/tong-hop-cac-loai-man-hinh-vo-cuc-moi-tren-dien-th-1150900#infinity-o\" target=\"_blank\" title=\"Tìm hiểu công nghệ màn hình Infinity-O trên điện thoại Samsung\" type=\"Tìm hiểu công nghệ màn hình Infinity-O trên điện thoại Samsung\">Infinity-O</a>, đảm bảo được viền màn hình mỏng ở tất cả cách cạnh khiến kích thước máy không lớn hơn S9+ nhưng kích thước màn hình lại lớn hơn.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/179530/samsung-galaxy-s10-plus-white-1.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại Samsung Galaxy S10+ chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/179530/samsung-galaxy-s10-plus-white-1.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/179530/samsung-galaxy-s10-plus-white-1.jpg\" title=\"Màn hình điện thoại Samsung Galaxy S10+ chính hãng\"></a></p><h3>Trải nghiệm màn hình vô cực độc đáo</h3><p>Samsung Galaxy S10+ sở hữu màn hình có kích thước 6.4 inch, sử dụng tấm nền <a href=\"https://www.thegioididong.com/tin-tuc/man-hinh-dynamic-amoled-la-gi-no-co-cong-dung-gi-noi-bat-1153075\" target=\"_blank\" title=\"Tìm hiểu về tấm nền Dynamic AMOLED\" type=\"Tìm hiểu về tấm nền Dynamic AMOLED\">Dynamic AMOLED</a> với độ phân giải lên tới 2K+ cho hình ảnh hiển thị sắc nét và sống động.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/179530/samsung-galaxy-s10-plus-white-10.jpg\" onclick=\"return false;\"><img alt=\"Mặt trước điện thoại Samsung Galaxy S10+ chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/179530/samsung-galaxy-s10-plus-white-10.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/179530/samsung-galaxy-s10-plus-white-10.jpg\" title=\"Mặt trước điện thoại Samsung Galaxy S10+ chính hãng\"></a></p><p>Đặc biệt màn hình này còn có sự hỗ trợ của <a href=\"https://www.thegioididong.com/tin-tuc/hdr10-la-gi-vi-sao-no-lam-cho-man-hinh-galaxy-s10-dep-hon--1151189\" target=\"_blank\" title=\"Tìm hiểu về công nghệ HDR10+\" type=\"Tìm hiểu về công nghệ HDR10+\">công nghệ HDR10+</a>, nó cho phép màn hình hiển thị nhiều màu sáng hơn hoặc nhiều màu tối hơn phụ thuộc vào những gì đang hiển thị trên màn hình.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/179530/samsung-galaxy-s10-plus-white-7.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại Samsung Galaxy S10+ chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/179530/samsung-galaxy-s10-plus-white-7.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/179530/samsung-galaxy-s10-plus-white-7.jpg\" title=\"Màn hình điện thoại Samsung Galaxy S10+ chính hãng\"></a></p><p>Và với nội dung phù hợp, người sẽ bị bất ngờ bởi chất lượng hình ảnh mà video có thể mang lại.</p><h3>Cảm biến <a href=\"https://www.thegioididong.com/hoi-dap/cong-nghe-quet-van-tay-song-sieu-am-la-gi-912419\" target=\"_blank\" title=\"Tìm hiểu công nghệ vân tay siêu âm\" type=\"Tìm hiểu công nghệ vân tay siêu âm\">vân tay siêu âm</a> bên dưới màn hình</h3><p>Galaxy S10 là những điện thoại đầu tiên sử dụng công nghệ vân tay siêu âm dưới màn hình của Qualcomm, nó sử dụng sóng âm để đọc bản in ngón tay bạn.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/179530/samsung-galaxy-s10-plus-white-6.jpg\" onclick=\"return false;\"><img alt=\"Vân tay siêu âm điện thoại Samsung Galaxy S10+ chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/179530/samsung-galaxy-s10-plus-white-6.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/179530/samsung-galaxy-s10-plus-white-6.jpg\" title=\"Vân tay siêu âm điện thoại Samsung Galaxy S10+ chính hãng\"></a></p><p>Công nghệ này có thể hoạt động bằng cả trong điều kiện tay của bạn dính nước, kem dưỡng da và dầu mỡ, vào ban đêm hoặc dưới ánh sáng ban ngày.</p><p>Theo Samsung thì nó nhanh hơn và an toàn hơn nhiều so với cảm biến vân tay quang học mà bạn đã thấy ở các điện thoại khác trước đây.</p><h3>Camera chất lượng hàng đầu thế giới</h3><p>Nếu bạn là một tín đồ của camera phone thì Samsung Galaxy S10+ là chiếc smartphone sinh ra để dành cho bạn.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/179530/samsung-galaxy-s10-plus-white-11.jpg\" onclick=\"return false;\"><img alt=\"Giao diện camera điện thoại Samsung Galaxy S10+ chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/179530/samsung-galaxy-s10-plus-white-11.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/179530/samsung-galaxy-s10-plus-white-11.jpg\" title=\"Giao diện camera điện thoại Samsung Galaxy S10+ chính hãng\"></a></p><p>Máy sở hữu cụm 3 camera sau (cảm biến chính 12 MP và hai cảm biến phụ 12 MP và 16 MP) với một camera có thể thay đổi khẩu độ, một camera zoom phục vụ việc xóa phông và một camera góc siêu rộng cho chụp ảnh phong cảnh.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/179530/samsung-galaxy-s10-plus-green11.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera của điện thoại Samsung Galaxy S10+ chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/179530/samsung-galaxy-s10-plus-green11.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/179530/samsung-galaxy-s10-plus-green11.jpg\" title=\"Ảnh chụp từ camera của điện thoại Samsung Galaxy S10+ chính hãng\"></a></p><p>Như vậy với chiếc Samsung Galaxy S10+ thì bạn có thể tự tin ghi lại những khoảnh khắc đẹp trong tất cả các điều kiện khác nhau với chất lượng \"tuyệt vời\".</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/179530/samsung-galaxy-s10-plus-green12.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera của điện thoại Samsung Galaxy S10+ chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/179530/samsung-galaxy-s10-plus-green12.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/179530/samsung-galaxy-s10-plus-green12.jpg\" title=\"Ảnh chụp từ camera của điện thoại Samsung Galaxy S10+ chính hãng\"></a></p><p>Khả năng quay video của máy cũng là điểm nổi bật với nhiều cải tiến đem đến các khung hình mượt mà, ổn định hình ảnh chất lượng.</p><h3>Camera selfie kép lần đầu tiên xuất hiện</h3><p>Samsung Galaxy S10+ là chiếc máy cao cấp đầu tiên của Samsung sở hữu cho mình camera selfie kép.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/179530/samsung-galaxy-s10-plus-green10.jpg\" onclick=\"return false;\"><img alt=\"Camera trước selfie của điện thoại Samsung Galaxy S10+ chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/179530/samsung-galaxy-s10-plus-green10.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/179530/samsung-galaxy-s10-plus-green10.jpg\" title=\"Camera trước selfie của điện thoại Samsung Galaxy S10+ chính hãng\"></a></p><p>Máy có bộ đôi camera trước với ống kính kính 10 MP và ống kính phụ 8 MP cho phép bạn có thể chụp ảnh xóa phông ngay với camera selfie.</p><h3>Hiệu năng mạnh mẽ nhất thế giới Android</h3><p>Một chiếc máy đầu bảng như Samsung Galaxy S10+ thì chắc chắn phải được trang bị những gì tốt nhất của năm 2019 rồi.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/179530/samsung-galaxy-s10-plus-white-12.jpg\" onclick=\"return false;\"><img alt=\"Chơi game trên điện thoại Samsung Galaxy S10+ chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/179530/samsung-galaxy-s10-plus-white-12.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/179530/samsung-galaxy-s10-plus-white-12.jpg\" title=\"Chơi game trên điện thoại Samsung Galaxy S10+ chính hãng\"></a></p><p>Cung cấp sức mạnh cho máy là con chip Exynos 9820 với 8 GB RAM cùng 128 GB bộ nhớ trong cho bạn thoải mái lưu trữ dữ liệu cá nhân.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/179530/samsung-galaxy-s10-plus-white-8.jpg\" onclick=\"return false;\"><img alt=\"Giao diện Android trên điện thoại Samsung Galaxy S10+ chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/179530/samsung-galaxy-s10-plus-white-8.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/179530/samsung-galaxy-s10-plus-white-8.jpg\" title=\"Giao diện Android trên điện thoại Samsung Galaxy S10+ chính hãng\"></a></p><p>Bên cạnh đó Samsung cũng giới thiệu tính năng quản lý hiệu suất thông minh cho phép Samsung Galaxy S10+ có thể mở nhanh các ứng dụng sử dụng nhiều và hạn chế các ứng dụng chạy ngầm ít sử dụng.</p><h3>Nhiều tính năng cao cấp khác</h3><p>Trên Samsung Galaxy S10+ còn có một tính năng được rất nhiều người yêu thích đó chính là khả năng <a href=\"https://www.thegioididong.com/tin-tuc/tinh-nang-powershare-tren-galaxy-s10-la-gi-lam-sao-de-su-dung-no-1151089\" target=\"_blank\" title=\"Tìm hiểu tính năng sạc ngược PowerShare\" type=\"Tìm hiểu tính năng sạc ngược PowerShare\">sạc ngược không dây (PowerShare)</a> cho smartphone khác (có hỗ trợ sạc không dây tương ứng).</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/179530/samsung-galaxy-s10-plus-powershare.jpg\" onclick=\"return false;\"><img alt=\"PowerShare trên điện thoại Samsung Galaxy S10+ chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/179530/samsung-galaxy-s10-plus-powershare.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/179530/samsung-galaxy-s10-plus-powershare.jpg\" title=\"PowerShare trên điện thoại Samsung Galaxy S10+ chính hãng\"></a></p><p>Điều này có thể giúp bạn biến chiếc smartphone của mình thành một cục sạc không dây di động trong những trường hợp khẩn cấp.</p><p>Ngoài ra tiêu chuẩn kháng nước và bụi bẩn IP68 cao cấp nhất hiện nay vẫn xuất hiện trên máy đảm bảo bạn có thể sử dụng dưới các điều kiện khắc nghiệt mà máy không gặp vấn đề gì.</p></article>', 74, 19900000, 20680000, '', '', '2019-06-23 20:47:25', 1, '2019-06-23 20:47:25', 1, 1);
INSERT INTO `uml_product` (`product_id`, `product_catid`, `product_name`, `product_slug`, `product_img`, `product_detail`, `product_number`, `product_price`, `product_pricesale`, `product_metakey`, `product_metadesc`, `product_createdat`, `product_createdby`, `product_updatedat`, `product_updatedby`, `product_status`) VALUES
(21, 7, 'Samsung Galaxy Note 9 128GB', 'samsung-galaxy-note-9-128gb', 'samsung-galaxy-note-9-128gb.jpg', '<h2>Mang lại sự cải tiến đặc biệt trong cây bút S-Pen, siêu phẩm <a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-note-9\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung Galaxy Note 9 tại Thegioididong.com\" type=\"Tham khảo điện thoại Samsung Galaxy Note 9 tại Thegioididong.com\">Samsung Galaxy Note 9</a> còn sở hữu dung lượng pin khủng lên tới 4.000 mAh cùng hiệu năng mạnh mẽ vượt bậc, xứng đáng là một trong những chiếc <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại tại Thegioididong.com\">điện thoại</a> cao cấp nhất của Samsung.</h2><p><div class=video allow=\"autoplay; encrypted-media\" allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"https://www.youtube.com/embed/miurMkZzJfs\" width=\"560\"></div></p><h3>Một chút thay đổi trong thiết kế</h3><p>Chiếc <a href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại Samsung tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại Samsung tại Thegioididong.com\">điện thoại Samsung</a> mới vẫn thừa hưởng lối thiết kế vô cùng quyến rũ từ đàn anh của mình với thân hình mạnh mẽ và được cách điệu bởi những đường cong mềm mại.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/154897/samsung-galaxy-note-9-6-1.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại Samsung Galaxy Note 9\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/154897/samsung-galaxy-note-9-6-1.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/154897/samsung-galaxy-note-9-6-1.jpg\" title=\"Thiết kế điện thoại Samsung Galaxy Note 9\"></a></p><p>Điểm thay đổi đáng chú ý phải kể đến mặt lưng khi mà <a href=\"https://www.thegioididong.com/hoi-dap/cam-bien-van-tay-tren-smartphone-la-gi-908163\" target=\"_blank\" title=\"Tìm hiểu cảm biến vân tay trên điện thoại\" type=\"Tìm hiểu cảm biến vân tay trên điện thoại\">cảm biến vân tay</a> của máy đã được đặt dưới cụm <a href=\"https://www.thegioididong.com/tin-tuc/cung-tim-hieu-ve-cac-loai-camera-kep-hien-co-tren-smartphone-1079186\" target=\"_blank\" title=\"Tìm hiểu các loại camera kép trên điện thoại\" type=\"Tìm hiểu các loại camera kép trên điện thoại\">camera kép</a> với một vị trí thuận lợi để bạn có thể mở khóa máy một cách dễ dàng.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/154897/samsung-galaxy-note-98-1.png\" onclick=\"return false;\"><img alt=\"Cảm biến vân tay trên điện thoại Samsung Galaxy Note 9\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/154897/samsung-galaxy-note-98-1.png\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/154897/samsung-galaxy-note-98-1.png\" title=\"Cảm biến vân tay trên điện thoại Samsung Galaxy Note 9\"></a></p><p>Bên cạnh đó Samsung đã tối giản cạnh viền trên dưới để mang lại cho bạn một trải nghiệm tuyệt vời hơn nữa với một màn hình có kích thước khủng lên đến 6.4 inch.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/154897/samsung-galaxy-note-9-1-2.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại Samsung Galaxy Note 9\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/154897/samsung-galaxy-note-9-1-2.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/154897/samsung-galaxy-note-9-1-2.jpg\" title=\"Màn hình điện thoại Samsung Galaxy Note 9\"></a></p><p>Nhìn chung với chỉ một chút thay đổi nhỏ trong thiết kế cũng đủ để giúp Galaxy Note 9 trở nên nổi bật và quyến rũ, cũng như khắc phục được những thiếu sót từ đàn anh Note 8.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/154897/samsung-galaxy-note-9-3-1.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại Samsung Galaxy Note 9\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/154897/samsung-galaxy-note-9-3-1.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/154897/samsung-galaxy-note-9-3-1.jpg\" title=\"Thiết kế điện thoại Samsung Galaxy Note 9\"></a></p><h3>Hiệu năng luôn ở đỉnh cao</h3><p>Là một flagship cao cấp nên những gì mạnh mẽ nhất đều được hội tụ trên Galaxy Note 9 với con chip <a href=\"https://www.thegioididong.com/tin-tuc/chip-snapdragon-845-manh-hon-25-tap-trung-ai-bao-mat-cao-1048166\" target=\"_blank\" title=\"Tìm hiểu về chip Snapdragon 845\" type=\"Tìm hiểu về chip Snapdragon 845\">Snapdragon 845</a> hoặc <a href=\"https://www.thegioididong.com/tin-tuc/samsung-trinh-lang-vi-xu-ly-exynos-9810-danh-cho-galaxy-s9-1040294\" target=\"_blank\" title=\"Tìm hiểu về chip Exynos 9810\" type=\"Tìm hiểu về chip Exynos 9810\">Exynos 9810</a> tùy vào từng thị trường khác nhau.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/154897/samsung-galaxy-note-9-gaming-2.gif\" onclick=\"return false;\"><img alt=\"Chơi game trên điện thoại Samsung Galaxy Note 9\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/154897/samsung-galaxy-note-9-gaming-2.gif\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/154897/samsung-galaxy-note-9-gaming-2.gif\" title=\"Chơi game trên điện thoại Samsung Galaxy Note 9\"></a></p><p>Đi kèm theo đó là bộ nhớ RAM lên đến 6 GB đã giúp Note 9 trở thành một trong những smartphone mạnh mẽ nhất hiện nay đủ để chiến mọi thể loại game nặng nhất.<a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/154897/samsung-galaxy-note-9-gaming-3.gif\" onclick=\"return false;\"><img alt=\"Chơi game trên điện thoại Samsung Galaxy Note 9\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/154897/samsung-galaxy-note-9-gaming-3.gif\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/154897/samsung-galaxy-note-9-gaming-3.gif\" title=\"Chơi game trên điện thoại Samsung Galaxy Note 9\"></a></p><p>Máy sở hữu phiên bản 128 GB và có cả một phiên bản cao cấp nhất, vô cùng mạnh mẽ khác đó chính là bộ nhớ trong lên tới 512 GB đi kèm với RAM 8 GB.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/183507/galaxy-note-9-6.jpg\" onclick=\"return false;\"><img alt=\"Giao diện Android điện thoại Samsung Galaxy Note 9\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/183507/galaxy-note-9-6.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/183507/galaxy-note-9-6.jpg\" title=\"Giao diện Android điện thoại Samsung Galaxy Note 9\"></a></p><p>Đáng tiếc máy vẫn chỉ được sẵn <a href=\"https://www.thegioididong.com/hoi-dap/co-gi-dac-biet-o-android-81-oreo-1073208\" target=\"_blank\" title=\"Tìm hiểu về hệ điều hành Android 8.1 Oreo\" type=\"Tìm hiểu về hệ điều hành Android 8.1 Oreo\">Android 8.1 (Oreo)</a> và hứa hẹn trong thời gian tới sẽ được cập nhật nhanh chóng lên phiên bản Android mới nhất với nhiều tính năng mới mẻ.</p><p><div class=video allow=\"autoplay; encrypted-media\" allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"https://www.youtube.com/embed/vniaKXHpD8E\" width=\"560\"></div></p><h3>Bút S-Pen cải tiến đến vi diệu</h3><p>Một sự thay đổi khiến bạn phải thích thú có lẽ là chiếc bút S-Pen đi kèm theo máy với nhiều màu sắc nổi bật.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/154897/samsung-galaxy-note-9-13.jpg\" onclick=\"return false;\"><img alt=\"Bút S Pen trên điện thoại Samsung Galaxy Note 9\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/154897/samsung-galaxy-note-9-13.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/154897/samsung-galaxy-note-9-13.jpg\" title=\"Bút S Pen trên điện thoại Samsung Galaxy Note 9\"></a></p><p>Chưa dừng ở đó, chiếc bút này còn được trang bị khả năng đặc biệt giúp điều khiển Note 9 dễ dàng thông qua Bluetooth.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/154897/samsung-galaxy-note-9-12-1.jpg\" onclick=\"return false;\"><img alt=\"Điều khiển bút S Pen trên điện thoại Samsung Galaxy Note 9\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/154897/samsung-galaxy-note-9-12-1.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/154897/samsung-galaxy-note-9-12-1.jpg\" title=\"Điều khiển bút S Pen trên điện thoại Samsung Galaxy Note 9\"></a></p><p>Cụ thể hơn, chỉ với một thao tác nhấn đơn giản là bạn có thể dễ dàng chụp ảnh selfie, trình chiếu bài thuyết trình, ngưng/phát video cùng nhiều công năng khác nữa.</p><h3>Camera thay đổi <a href=\"https://www.thegioididong.com/hoi-dap/khau-do-la-gi-tai-sao-khau-do-lai-quan-trong-vo-1098341\" target=\"_blank\" title=\"Tìm hiểu về khẩu độ\" type=\"Tìm hiểu về khẩu độ\">khẩu độ</a> dễ dàng và tích hợp AI</h3><p>Galaxy Note 9 sở hữu bộ đôi camera kép có cùng độ phân giải 12 MP có khả năng thay đổi khẩu độ như <a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-s9-plus\" target=\"_blank\" title=\"Tham khảo điện thoại Galaxy S9+ tại Thegioididong.com\" type=\"Tham khảo điện thoại Galaxy S9+ tại Thegioididong.com\">Galaxy S9+</a> và được tích hợp thêm công nghệ AI giúp chất lượng ảnh chụp đẹp hơn đáng kể.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/154897/samsung-galaxy-note-9-7-1.jpg\" onclick=\"return false;\"><img alt=\"Bút S Pen trên điện thoại Samsung Galaxy Note 9\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/154897/samsung-galaxy-note-9-7-1.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/154897/samsung-galaxy-note-9-7-1.jpg\" title=\"Bút S Pen trên điện thoại Samsung Galaxy Note 9\"></a></p><p>Theo như những gì mà Samsung giới thiệu thì AI trên Note 9 có khả năng phân loại nhiều đối tượng, tự động điều chỉnh màu sắc, độ tương phản và độ sáng trong bối cảnh chụp ảnh khác nhau.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/183507/galaxy-note-9-15.jpg\" onclick=\"return false;\"><img alt=\"Camera điện thoại Samsung Galaxy Note 9\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/183507/galaxy-note-9-15.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/183507/galaxy-note-9-15.jpg\" title=\"Camera điện thoại Samsung Galaxy Note 9\"></a></p><p>Chất lượng ảnh chụp của máy vô cùng ấn tượng với các chi tiết được thể hiện rõ ràng, màu sắc cân đối và độ tương phản cao.</p><p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Files/2018/08/09/1108117/galaxy-note-9-hands-on-47_800x450.jpg\" onclick=\"return false;\"><img alt=\"Giao diện camera điện thoại Samsung Galaxy Note 9\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Files/2018/08/09/1108117/galaxy-note-9-hands-on-47_800x450.jpg\" title=\"Giao diện camera điện thoại Samsung Galaxy Note 9\"></a></p><p>Cũng như trên Galaxy S9+ thì Note 9 cũng được trang bị khả năng quay phim <a href=\"https://www.thegioididong.com/hoi-dap/super-slow-motion-960fps-la-gi-956676\" target=\"_blank\" title=\"Tìm hiểu về quay phim Super Slow Motion\" type=\"Tìm hiểu về quay phim Super Slow Motion\">Super Slow Motion</a> giúp bạn ghi lại những khoảnh khắc đáng nhớ trong cuộc sống.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/183507/galaxy-note-9-17.jpg\" onclick=\"return false;\"><img alt=\"Quay phim Super Slow Motion trên Samsung Galaxy Note 9\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/183507/galaxy-note-9-17.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/183507/galaxy-note-9-17.jpg\" title=\"Quay phim Super Slow Motion trên Samsung Galaxy Note 9\"></a></p><p><div class=\'ratingbox\'><div class=\'item\'><p><b>❝</b>Đã mua và cảm thấy rất hài lòng về máy. Cấu hình ngon cộng thêm camera rất đẹp. Pin xài tương đối ổn, cảm biến vân tay nhạy. Nói chung là siêu phẩm nên không có gì phải phàn nàn.<b>❞</b></p><label class=\'line\'></label><span class=\'info\'><b>Chị Loan</b> - <span>Mua online trên website - 1 tuần trước</span><label class=\'sttB\'><i class=\'iconcom-checkbuy\'></i>Đã mua tại Thế giới di động</label></span></div></div></p><h3>Dung lượng pin được cải tiến</h3><p>Sau những gì mà người dùng mong mỏi thì nay Note 9 đã có một viên pin to hơn, mạnh mẽ và bền bỉ hơn với dung lượng lên đến 4000 mAh.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/154897/samsung-galaxy-note-9-5-1.jpg\" onclick=\"return false;\"><img alt=\"Đánh giá điện thoại Samsung Galaxy Note 9\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/154897/samsung-galaxy-note-9-5-1.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/154897/samsung-galaxy-note-9-5-1.jpg\" title=\"Đánh giá điện thoại Samsung Galaxy Note 9\"></a></p><p>Tuy không thuộc dạng khủng nhất nhưng từng đó cũng đủ để bạn có thể hài lòng với các trải nghiệm hằng ngày một cách trọn vẹn.</p><p>Hơi thất vọng nhẹ khi máy vẫn chỉ được trang bị công nghệ sạc nhanh <a href=\"https://www.thegioididong.com/tin-tuc/su-loi-hai-cua-tinh-nang-sac-nhanh-quick-charge-2--625393\" target=\"_blank\" title=\"Tìm hiểu về công nghệ Quick Charge 2.0\" type=\"Tìm hiểu về công nghệ Quick Charge 2.0\">Quick Charge 2.0</a> nên thời gian sạc vẫn chưa được tối ưu nhất.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/183507/galaxy-note-9-20.jpg\" onclick=\"return false;\"><img alt=\"Cổng sạc trên điện thoại Samsung Galaxy Note 9\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/183507/galaxy-note-9-20.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/183507/galaxy-note-9-20.jpg\" title=\"Cổng sạc trên điện thoại Samsung Galaxy Note 9\"></a></p><h3>Hỗ trợ nhiều tính năng đặc biệt khác</h3><p>Tính năng <a href=\"https://www.thegioididong.com/tin-tuc/samsung-dex-phu-kien-bien-galaxy-s8-thanh-may-tinh-de-ban-966856\" target=\"_blank\" title=\"Tìm hiểu về Samsung DeX trên điện thoại\" type=\"Tìm hiểu về Samsung DeX trên điện thoại\">Samsung DeX</a> vẫn được hỗ trợ trên Note 9 nhưng đã được Samsung cải tiến thông qua một sợi cáp nhỏ gọn mang tên DeX HDMI.</p><p>Nhờ đó mà việc kết nối điện thoại với một màn hình bất kỳ đem đến một trải nghiệm như máy tính trở nên thuận tiện hơn bao giờ hết.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/183507/galaxy-note-9-19.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Note 9\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/183507/galaxy-note-9-19.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/183507/galaxy-note-9-19.jpg\" title=\"Samsung Galaxy Note 9\"></a></p><p>Ngoài ra, những tính năng như: <a href=\"https://www.thegioididong.com/hoi-dap/cong-nghe-quet-mong-mat-771818\" target=\"_blank\" title=\"Tìm hiểu công nghệ mở khoá bằng mống mắt\" type=\"Tìm hiểu công nghệ mở khoá bằng mống mắt\">mở khóa bằng mống mắt</a>, <a href=\"https://www.thegioididong.com/hoi-dap/cai-dat-mo-khoa-bang-khuon-mat-tren-samsung-1033322\" target=\"_blank\" title=\"Hướng dẫn cách cài đặt bảo mật khuôn mặt trên điện thoại Samsung\" type=\"Hướng dẫn cách cài đặt bảo mật khuôn mặt trên điện thoại Samsung\">bảo mật khuôn mặt</a>, <a href=\"https://www.thegioididong.com/tin-tuc/cach-su-dung-samsung-pay-trong-vai-buoc-1028869\" target=\"_blank\" title=\"Tìm hiểu về cách thanh toán Samsung Pay trên điện thoại\" type=\"Tìm hiểu về cách thanh toán Samsung Pay trên điện thoại\">Samsung Pay</a> hay <a href=\"https://www.thegioididong.com/hoi-dap/chong-nuoc-va-chong-bui-tren-smart-phone-771130\" target=\"_blank\" title=\"Tìm hiểu về tính năng chống nước trên điện thoại\" type=\"Tìm hiểu về tính năng chống nước trên điện thoại\">khả năng chống nước IP68</a> vẫn được giữ lại trên Galaxy Note 9.</p><p><div class=\'ratingbox\'><div class=\'item\'><p><b>❝</b>Siêu phẩm xứng đáng, quà tặng giá trị, màn hình đẹp, camera xuất sắc. Nhân viên chi nhánh 32K QL1K Thủ Đức dễ thương nhiệt tình. Mình chọn Thế Giới Di Động!<b>❞</b></p><label class=\'line\'></label><span class=\'info\'><b>Anh Đỗ Cao Thái</b> - Mua online trên website - 1 tuần trước<label class=\'sttB\'><i class=\'iconcom-checkbuy\'></i>Đã mua tại Thế giới di động</label></span></div></div></p></article>', 175, 7200000, 0, '', '', '2019-06-23 20:47:26', 1, '2019-06-23 20:47:26', 1, 1),
(22, 7, 'Samsung Galaxy Note 9 512GB', 'samsung-galaxy-note-9-512gb', 'samsung-galaxy-note-9-512gb.jpg', '<h2>Sau thành công vang dội của <a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-note8\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung Galaxy Note 8 tại Thegioididong.com\" type=\"Tham khảo điện thoại Samsung Galaxy Note 8 tại Thegioididong.com\">Galaxy Note 8</a> thì Samsung chính thức giới thiệu phiên bản tiếp theo tới người dùng cái tên <a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-note-9-512gb\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung Galaxy Note 9 bản 512GB\" type=\"Tham khảo điện thoại Samsung Galaxy Note 9 bản 512GB\">Samsung Galaxy Note 9 512GB</a> mang trong mình hàng hoạt các thay đổi đột phá với điểm nhấn đặc biệt đến từ chiếc bút S-Pen thần thánh cùng một viên pin dung lượng khổng lồ tới 4.000 mAh.</h2><h3>Bút S-Pen cải tiến đến vi diệu</h3><p>Một sự thay đổi khiến bạn phải thích thú có lẽ là chiếc bút S-Pen đi kèm theo <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại tại Thegioididong.com\">điện thoại</a> với nhiều màu sắc nổi bật.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/154897/samsung-galaxy-note-9-13.jpg\" onclick=\"return false;\"><img alt=\"Bút S Pen trên điện thoại Samsung Galaxy Note 9\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/154897/samsung-galaxy-note-9-13.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/154897/samsung-galaxy-note-9-13.jpg\" title=\"Bút S Pen trên điện thoại Samsung Galaxy Note 9\"></a></p><p>Chưa dừng ở đó, chiếc bút này còn được trang bị khả năng đặc biệt giúp điều khiển Note 9 dễ dàng thông qua Bluetooth.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/154897/samsung-galaxy-note-9-12-1.jpg\" onclick=\"return false;\"><img alt=\"Điều khiển bút S Pen trên điện thoại Samsung Galaxy Note 9\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/154897/samsung-galaxy-note-9-12-1.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/154897/samsung-galaxy-note-9-12-1.jpg\" title=\"Điều khiển bút S Pen trên điện thoại Samsung Galaxy Note 9\"></a></p><p>Cụ thể hơn, chỉ với một thao tác nhấn đơn giản là bạn có thể dễ dàng chụp ảnh selfie, trình chiếu bài thuyết trình, ngưng/phát video cùng nhiều công năng khác nữa.</p><h3>Hiệu năng luôn ở đỉnh cao</h3><p>Là một flagship cao cấp nên những gì mạnh mẽ nhất đều được hội tụ trên Galaxy Note 9 với con chip <a href=\"https://www.thegioididong.com/tin-tuc/chip-snapdragon-845-manh-hon-25-tap-trung-ai-bao-mat-cao-1048166\" target=\"_blank\" title=\"Tìm hiểu về chip Snapdragon 845\" type=\"Tìm hiểu về chip Snapdragon 845\">Snapdragon 845</a> hoặc <a href=\"https://www.thegioididong.com/tin-tuc/samsung-trinh-lang-vi-xu-ly-exynos-9810-danh-cho-galaxy-s9-1040294\" target=\"_blank\" title=\"Tìm hiểu về chip Exynos 9810\" type=\"Tìm hiểu về chip Exynos 9810\">Exynos 9810</a> tùy vào từng thị trường khác nhau.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/154897/samsung-galaxy-note-9-gaming-2.gif\" onclick=\"return false;\"><img alt=\"Chơi game trên điện thoại Samsung Galaxy Note 9\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/154897/samsung-galaxy-note-9-gaming-2.gif\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/154897/samsung-galaxy-note-9-gaming-2.gif\" title=\"Chơi game trên điện thoại Samsung Galaxy Note 9\"></a></p><p>Đi kèm theo đó là bộ nhớ RAM lên đến 8 GB đã giúp Note 9 trở thành một trong những smartphone mạnh mẽ nhất hiện nay đủ để chiến mọi thể loại game nặng nhất.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/154897/samsung-galaxy-note-9-gaming-3.gif\" onclick=\"return false;\"><img alt=\"Chơi game trên điện thoại Samsung Galaxy Note 9\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/154897/samsung-galaxy-note-9-gaming-3.gif\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/154897/samsung-galaxy-note-9-gaming-3.gif\" title=\"Chơi game trên điện thoại Samsung Galaxy Note 9\"></a></p><p>Hơn nữa, một sự nâng cấp vô cùng mạnh mẽ khác chính là bộ nhớ trong của máy đã được nâng lên tới 512 GB cho phiên bản cao cấp nhất.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/183507/galaxy-note-9-6.jpg\" onclick=\"return false;\"><img alt=\"Giao diện Android điện thoại Samsung Galaxy Note 9\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/183507/galaxy-note-9-6.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/183507/galaxy-note-9-6.jpg\" title=\"Giao diện Android điện thoại Samsung Galaxy Note 9\"></a></p><p>Đáng tiếc máy vẫn chỉ được sẵn <a href=\"https://www.thegioididong.com/hoi-dap/co-gi-dac-biet-o-android-81-oreo-1073208\" target=\"_blank\" title=\"Tìm hiểu về hệ điều hành Android 8.1 Oreo\" type=\"Tìm hiểu về hệ điều hành Android 8.1 Oreo\">Android 8.1 (Oreo)</a> và hứa hẹn trong thời gian tới sẽ được cập nhật nhanh chóng lên phiên bản Android mới nhất với nhiều tính năng mới mẻ.</p><p><div class=video allow=\"autoplay; encrypted-media\" allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"https://www.youtube.com/embed/vniaKXHpD8E\" width=\"560\"></div></p><h3>Camera thay đổi <a href=\"https://www.thegioididong.com/hoi-dap/khau-do-la-gi-tai-sao-khau-do-lai-quan-trong-vo-1098341\" target=\"_blank\" title=\"Tìm hiểu về khẩu độ\" type=\"Tìm hiểu về khẩu độ\">khẩu độ</a> dễ dàng và tích hợp AI</h3><p>Galaxy Note 9 sở hữu bộ đôi camera kép với cùng độ phân giải 12 MP có khả năng thay đổi khẩu độ như <a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-s9-plus\" target=\"_blank\" title=\"Tham khảo điện thoại Galaxy S9+ tại Thegioididong.com\" type=\"Tham khảo điện thoại Galaxy S9+ tại Thegioididong.com\">Galaxy S9+</a> và được tích hợp thêm công nghệ AI giúp chất lượng ảnh chụp đẹp hơn đáng kể.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/154897/samsung-galaxy-note-9-7-1.jpg\" onclick=\"return false;\"><img alt=\"Bút S Pen trên điện thoại Samsung Galaxy Note 9\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/154897/samsung-galaxy-note-9-7-1.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/154897/samsung-galaxy-note-9-7-1.jpg\" title=\"Bút S Pen trên điện thoại Samsung Galaxy Note 9\"></a></p><p>Theo như những gì mà Samsung giới thiệu thì AI trên Note 9 có khả năng phân loại nhiều đối tượng, tự động điều chỉnh màu sắc, độ tương phản và độ sáng trong bối cảnh chụp ảnh khác nhau.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/183507/galaxy-note-9-15.jpg\" onclick=\"return false;\"><img alt=\"Camera điện thoại Samsung Galaxy Note 9\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/183507/galaxy-note-9-15.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/183507/galaxy-note-9-15.jpg\" title=\"Camera điện thoại Samsung Galaxy Note 9\"></a></p><p>Chất lượng ảnh chụp của máy vô cùng ấn tượng với các chi tiết được thể hiện rõ ràng, màu sắc cân đối và độ tương phản cao.</p><p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Files/2018/08/09/1108117/galaxy-note-9-hands-on-47_800x450.jpg\" onclick=\"return false;\"><img alt=\"Giao diện camera điện thoại Samsung Galaxy Note 9\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Files/2018/08/09/1108117/galaxy-note-9-hands-on-47_800x450.jpg\" title=\"Giao diện camera điện thoại Samsung Galaxy Note 9\"></a></p><p>Cũng như trên Galaxy S9+ thì Note 9 cũng được trang bị khả năng quay phim <a href=\"https://www.thegioididong.com/hoi-dap/super-slow-motion-960fps-la-gi-956676\" target=\"_blank\" title=\"Tìm hiểu về quay phim Super Slow Motion\" type=\"Tìm hiểu về quay phim Super Slow Motion\">Super Slow Motion</a> giúp bạn ghi lại những khoảnh khắc đáng nhớ trong cuộc sống.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/183507/galaxy-note-9-17.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Note 9\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/183507/galaxy-note-9-17.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/183507/galaxy-note-9-17.jpg\" title=\"Samsung Galaxy Note 9\"></a></p><h3>Dung lượng pin được cải tiến</h3><p>Sau những gì mà người dùng mong mỏi thì nay Note 9 đã có một viên pin to hơn, mạnh mẽ và bền bỉ hơn với dung lượng lên đến 4000 mAh.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/154897/samsung-galaxy-note-9-5-1.jpg\" onclick=\"return false;\"><img alt=\"Đánh giá điện thoại Samsung Galaxy Note 9\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/154897/samsung-galaxy-note-9-5-1.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/154897/samsung-galaxy-note-9-5-1.jpg\" title=\"Đánh giá điện thoại Samsung Galaxy Note 9\"></a></p><p>Tuy không thuộc dạng khủng nhất nhưng từng đó cũng đủ để bạn có thể hài lòng với các trải nghiệm hằng ngày một cách trọn vẹn.</p><p>Hơi thất vọng nhẹ khi máy vẫn chỉ được trang bị công nghệ sạc nhanh <a href=\"https://www.thegioididong.com/tin-tuc/su-loi-hai-cua-tinh-nang-sac-nhanh-quick-charge-2--625393\" target=\"_blank\" title=\"Tìm hiểu về công nghệ Quick Charge 2.0\" type=\"Tìm hiểu về công nghệ Quick Charge 2.0\">Quick Charge 2.0</a> nên thời gian sạc vẫn chưa được tối ưu nhất.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/183507/galaxy-note-9-20.jpg\" onclick=\"return false;\"><img alt=\"Cổng sạc trên điện thoại Samsung Galaxy Note 9\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/183507/galaxy-note-9-20.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/183507/galaxy-note-9-20.jpg\" title=\"Cổng sạc trên điện thoại Samsung Galaxy Note 9\"></a></p><p><div class=video allow=\"autoplay; encrypted-media\" allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"https://www.youtube.com/embed/miurMkZzJfs\" width=\"560\"></div></p><h3>Một chút thay đổi trong thiết kế</h3><p>Chiếc <a href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại Samsung tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại Samsung tại Thegioididong.com\">điện thoại Samsung</a> mới vẫn thừa hưởng lối thiết kế vô cùng quyến rũ từ đàn anh của mình với thân hình mạnh mẽ và được cách điệu bởi những đường cong mềm mại.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/154897/samsung-galaxy-note-9-6-1.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại Samsung Galaxy Note 9\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/154897/samsung-galaxy-note-9-6-1.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/154897/samsung-galaxy-note-9-6-1.jpg\" title=\"Thiết kế điện thoại Samsung Galaxy Note 9\"></a></p><p>Điểm thay đổi đáng chú ý phải kể đến mặt lưng khi mà <a href=\"https://www.thegioididong.com/hoi-dap/cam-bien-van-tay-tren-smartphone-la-gi-908163\" target=\"_blank\" title=\"Tìm hiểu cảm biến vân tay trên điện thoại\" type=\"Tìm hiểu cảm biến vân tay trên điện thoại\">cảm biến vân tay</a> của máy đã được đặt dưới cụm <a href=\"https://www.thegioididong.com/tin-tuc/cung-tim-hieu-ve-cac-loai-camera-kep-hien-co-tren-smartphone-1079186\" target=\"_blank\" title=\"Tìm hiểu các loại camera kép trên điện thoại\" type=\"Tìm hiểu các loại camera kép trên điện thoại\">camera kép</a> với một vị trí thuận lợi để bạn có thể mở khóa máy một cách dễ dàng.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/154897/samsung-galaxy-note-98-1.png\" onclick=\"return false;\"><img alt=\"Cảm biến vân tay trên điện thoại Samsung Galaxy Note 9\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/154897/samsung-galaxy-note-98-1.png\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/154897/samsung-galaxy-note-98-1.png\" title=\"Cảm biến vân tay trên điện thoại Samsung Galaxy Note 9\"></a></p><p>Bên cạnh đó Samsung đã tối giản cạnh viền trên dưới để mang lại cho bạn một trải nghiệm tuyệt vời hơn nữa với một màn hình có kích thước khủng lên đến 6.4 inch.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/154897/samsung-galaxy-note-9-1-2.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại Samsung Galaxy Note 9\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/154897/samsung-galaxy-note-9-1-2.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/154897/samsung-galaxy-note-9-1-2.jpg\" title=\"Màn hình điện thoại Samsung Galaxy Note 9\"></a></p><p>Nhìn chung với chỉ một chút thay đổi nhỏ trong thiết kế cũng đủ để giúp Galaxy Note 9 trở nên nổi bật và quyến rũ, cũng như khắc phục được những thiếu sót từ đàn anh Note 8.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/154897/samsung-galaxy-note-9-3-1.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại Samsung Galaxy Note 9\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/154897/samsung-galaxy-note-9-3-1.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/154897/samsung-galaxy-note-9-3-1.jpg\" title=\"Thiết kế điện thoại Samsung Galaxy Note 9\"></a></p><p><div class=\'ratingbox\'><div class=\'item\'><p><b>❝</b>Đã mua và cảm thấy rất hài lòng về máy. Cấu hình ngon cộng thêm camera rất đẹp. Pin xài tương đối ổn, cảm biến vân tay nhạy. Nói chung là siêu phẩm nên không có gì phải phàn nàn.<b>❞</b></p><label class=\'line\'></label><span class=\'info\'><b>Loan</b> - <span>Mua online trên website - 1 tuần trước</span><label class=\'sttB\'><i class=\'iconcom-checkbuy\'></i>Đã mua tại Thế giới di động</label></span></div></div></p><h3>Hỗ trợ nhiều tính năng đặc biệt khác</h3><p>Tính năng <a href=\"https://www.thegioididong.com/tin-tuc/samsung-dex-phu-kien-bien-galaxy-s8-thanh-may-tinh-de-ban-966856\" target=\"_blank\" title=\"Tìm hiểu về Samsung DeX trên điện thoại\" type=\"Tìm hiểu về Samsung DeX trên điện thoại\">Samsung DeX</a> vẫn được hỗ trợ trên Note 9 nhưng đã được Samsung cải tiến thông qua một sợi cáp nhỏ gọn mang tên DeX HDMI.</p><p>Nhờ đó mà việc kết nối điện thoại với một màn hình bất kỳ đem đến một trải nghiệm như máy tính trở nên thuận tiện hơn bao giờ hết.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/183507/galaxy-note-9-19.jpg\" onclick=\"return false;\"><img alt=\"Dex HDMI kết nối với điện thoại Samsung Galaxy Note 9\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/183507/galaxy-note-9-19.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/183507/galaxy-note-9-19.jpg\" title=\"Dex HDMI kết nối với điện thoại Samsung Galaxy Note 9\"></a></p><p>Ngoài ra, những tính năng như: <a href=\"https://www.thegioididong.com/hoi-dap/cong-nghe-quet-mong-mat-771818\" target=\"_blank\" title=\"Tìm hiểu công nghệ mở khoá bằng mống mắt\" type=\"Tìm hiểu công nghệ mở khoá bằng mống mắt\">mở khóa bằng mống mắt</a>, <a href=\"https://www.thegioididong.com/hoi-dap/cai-dat-mo-khoa-bang-khuon-mat-tren-samsung-1033322\" target=\"_blank\" title=\"Hướng dẫn cách cài đặt bảo mật khuôn mặt trên điện thoại Samsung\" type=\"Hướng dẫn cách cài đặt bảo mật khuôn mặt trên điện thoại Samsung\">bảo mật khuôn mặt</a>, <a href=\"https://www.thegioididong.com/tin-tuc/cach-su-dung-samsung-pay-trong-vai-buoc-1028869\" target=\"_blank\" title=\"Tìm hiểu về cách thanh toán Samsung Pay trên điện thoại\" type=\"Tìm hiểu về cách thanh toán Samsung Pay trên điện thoại\">Samsung Pay</a> hay <a href=\"https://www.thegioididong.com/hoi-dap/chong-nuoc-va-chong-bui-tren-smart-phone-771130\" target=\"_blank\" title=\"Tìm hiểu về tính năng chống nước trên điện thoại\" type=\"Tìm hiểu về tính năng chống nước trên điện thoại\">khả năng chống nước IP68</a> vẫn được giữ lại trên Galaxy Note 9.</p><p><div class=\'ratingbox\'><div class=\'item\'><p><b>❝</b>Siêu phẩm xứng đáng, quà tặng giá trị, màn hình đẹp, camera xuất sắc. Nhân viên chi nhánh 32K QL1K Thủ Đức dễ thương nhiệt tình. Mình chọn Thế Giới Di Động!<b>❞</b></p><label class=\'line\'></label><span class=\'info\'><b>Anh Đỗ Cao Thái</b> - Mua online trên website - 1 tuần trước<label class=\'sttB\'><i class=\'iconcom-checkbuy\'></i>Đã mua tại Thế giới di động</label></span></div></div></p></article>', 177, 18700000, 19480000, '', '', '2019-06-23 20:47:27', 1, '2019-06-23 20:47:27', 1, 1),
(23, 7, 'Samsung Galaxy S10', 'samsung-galaxy-s10', 'samsung-galaxy-s10.jpg', '<h2><a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-s10\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung Galaxy S10 chính hãng\" type=\"Tham khảo điện thoại Samsung Galaxy S10 chính hãng\">Samsung Galaxy S10</a> là chiếc <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại chính hãng\">smartphone</a> kỉ niệm 10 năm ngày kể từ ngày đầu tiên Samsung ra mắt chiếc Galaxy S và không phụ sự chờ đợi của người dùng thì Samsung Galaxy S10 thực sự rất ấn tượng.</h2><h3>Thiết kế lột xác, tạo xu thế mới</h3><p>Cái nhìn sẽ là điểm ấn tượng đầu tiên trên Samsung Galaxy S10 khi Samsung mang đến cho chúng ta một thiết bị cao cấp và sang trọng như món đồ trang sức.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/161554/samsung-galaxy-s10-white-2-1.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại Samsung Galaxy S10 chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/161554/samsung-galaxy-s10-white-2-1.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/161554/samsung-galaxy-s10-white-2-1.jpg\" title=\"Thiết kế điện thoại Samsung Galaxy S10 chính hãng\"></a></p><p>Điểm đặc biệt là viền trên và dưới trên chiếc smartphone này được làm mỏng hơn, ấn tượng hơn rất nhiều so với Galaxy S9 hay Note 9.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/161554/samsung-galaxy-s10-white-7.jpg\" onclick=\"return false;\"><img alt=\"Mặt trước điện thoại Samsung Galaxy S10 chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/161554/samsung-galaxy-s10-white-7.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/161554/samsung-galaxy-s10-white-7.jpg\" title=\"Mặt trước điện thoại Samsung Galaxy S10 chính hãng\"></a></p><p>Để làm được điều đó, Samsung đã đưa cụm camera vào bên trong màn hình, một điều mà chưa một hãng nào làm trước đó.</p><p>Thiết kế mới mang lại cho người dùng trải nghiệm mới mẻ và có lẽ Samsung sẽ là hãng đầu tiên khơi mào cho phong cách thiết kế camera trong màn hình sau này.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/161554/samsung-galaxy-s10-white-10.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại Samsung Galaxy S10 chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/161554/samsung-galaxy-s10-white-10.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/161554/samsung-galaxy-s10-white-10.jpg\" title=\"Màn hình điện thoại Samsung Galaxy S10 chính hãng\"></a></p><p>Ngoài ra màn hình của S10 được cải tiến nhờ tấm nền <a href=\"https://www.thegioididong.com/tin-tuc/man-hinh-dynamic-amoled-la-gi-no-co-cong-dung-gi-noi-bat-1153075\" target=\"_blank\" title=\"Tìm hiểu về tấm nền Dynamic AMOLED\" type=\"Tìm hiểu về tấm nền Dynamic AMOLED\">Dynamic AMOLED</a> cùng <a href=\"https://www.thegioididong.com/tin-tuc/hdr10-la-gi-vi-sao-no-lam-cho-man-hinh-galaxy-s10-dep-hon--1151189\" target=\"_blank\" title=\"Tìm hiểu về công nghệ HDR10+\" type=\"Tìm hiểu về công nghệ HDR10+\">công nghệ HDR10+</a> giúp hình ảnh sáng, rực rỡ hơn, tối ưu mọi chi tiết hình ảnh được hiển thị.</p><p>Màn hình S10 còn đặc biệt ở thiết kế, chứa camera selfie nằm bên trong màn hình độc nhất vô nhị. Kiểu thiết kế này được Samsung ưu ái gọi là \"<a href=\"https://www.thegioididong.com/hoi-dap/tong-hop-cac-loai-man-hinh-vo-cuc-moi-tren-dien-th-1150900#infinity-o\" target=\"_blank\" title=\"Tìm hiểu công nghệ màn hình Infinity-O trên điện thoại Samsung\" type=\"Tìm hiểu công nghệ màn hình Infinity-O trên điện thoại Samsung\">Infinity-O</a>\".</p><h3><a href=\"https://www.thegioididong.com/hoi-dap/cong-nghe-quet-van-tay-song-sieu-am-la-gi-912419\" target=\"_blank\" title=\"Tìm hiểu công nghệ vân tay siêu âm\" type=\"Tìm hiểu công nghệ vân tay siêu âm\">Cảm biến vân tay siêu âm</a> bên dưới màn hình</h3><p>Galaxy S10 là những điện thoại đầu tiên sử dụng công nghệ vân tay siêu âm dưới màn hình của Qualcomm, nó sử dụng sóng âm để đọc bản in ngón tay bạn.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/161554/samsung-galaxy-s10-white-6.jpg\" onclick=\"return false;\"><img alt=\"Vân tay siêu âm Samsung Galaxy S10 chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/161554/samsung-galaxy-s10-white-6.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/161554/samsung-galaxy-s10-white-6.jpg\" title=\"Vân tay siêu âm Samsung Galaxy S10 chính hãng\"></a></p><p>Công nghệ này có thể hoạt động bằng cả trong điều kiện tay của bạn dính nước, kem dưỡng da và dầu mỡ, vào ban đêm hoặc dưới ánh sáng ban ngày.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/161554/samsung-galaxy-s10-white-9.jpg\" onclick=\"return false;\"><img alt=\"Vân tay siêu âm Samsung Galaxy S10 chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/161554/samsung-galaxy-s10-white-9.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/161554/samsung-galaxy-s10-white-9.jpg\" title=\"Vân tay siêu âm Samsung Galaxy S10 chính hãng\"></a></p><p>Theo Samsung thì nó nhanh hơn và an toàn hơn nhiều so với cảm biến vân tay quang học mà bạn đã thấy ở các điện thoại khác trước đây.</p><h3>Giờ dòng Galaxy S đã có tới 3 camera</h3><p>Trên chiếc <a href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại Samsung chính hãng\">điện thoại Samsung</a> này thì hãng đã đầu tư khá mạnh mẽ về camera khi không chỉ có nguyên cụm camera kép của <a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-note-9\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung Galaxy Note 9 chính hãng\">Note 9</a> mà còn có một cảm biến góc rộng giúp cung cấp trường nhìn lên đến 123 độ. Bộ camera của máy gồm cảm biến chính 12 MP và 2 cảm biến phụ 12 MP + 16 MP.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/161554/samsung-galaxy-s10-white-4.jpg\" onclick=\"return false;\"><img alt=\"Cụm camera sau Samsung Galaxy S10 chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/161554/samsung-galaxy-s10-white-4.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/161554/samsung-galaxy-s10-white-4.jpg\" title=\"Cụm camera sau Samsung Galaxy S10 chính hãng\"></a></p><p>Samsung còn cung cấp thêm tính năng Super Steady Video để hỗ trợ chống rung trong các cảnh quay thể thao, hay tính năng quay video HDR10+ với hình ảnh sinh động hơn, độ sâu ảnh tốt hơn.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/161554/samsung-galaxy-s10-white-11-1.jpg\" onclick=\"return false;\"><img alt=\"Giao diện camera trên Galaxy S10 chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/161554/samsung-galaxy-s10-white-11-1.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/161554/samsung-galaxy-s10-white-11-1.jpg\" title=\"Giao diện camera trên Galaxy S10 chính hãng\"></a></p><p>Galaxy S10 cũng có Chế độ Instagram, và bạn có thể chia sẻ ảnh trên Instagram với một lần chạm.</p><p>Ở mặt trước, Galaxy S10+ có camera 10 MP Dual Pixel với khả năng tự động lấy nét và quay video với độ phân giải lên tới 4K.</p><h3>Hiệu năng mạnh mẽ hàng đầu hiện nay</h3><p>Samsung Galaxy S10 được trang bị vi xử lý Exynos 9820 cho hiệu năng nhanh hơn đến 30% và cho tốc độ xử lý đồ họa nhanh hơn đến 37% so với thế hệ trước.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/161554/samsung-galaxy-s10-white-12.jpg\" onclick=\"return false;\"><img alt=\"Chơi game trên Galaxy S10 chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/161554/samsung-galaxy-s10-white-12.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/161554/samsung-galaxy-s10-white-12.jpg\" title=\"Chơi game trên Galaxy S10 chính hãng\"></a></p><p>Và điều đặc biệt trong đó là tính năng quản lý hiệu suất thông minh trên 2 thiết bị này, tức là máy sẽ phân tích và thống kê các ứng dụng sử dụng nhiều, từ đó tập trung tài nguyên cho chúng.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/161554/samsung-galaxy-s10-white-8.jpg\" onclick=\"return false;\"><img alt=\"Giao diện Android trên Galaxy S10 chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/161554/samsung-galaxy-s10-white-8.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/161554/samsung-galaxy-s10-white-8.jpg\" title=\"Giao diện Android trên Galaxy S10 chính hãng\"></a></p><p>Điều này giúp tối ưu tài nguyên CPU, RAM, và thiết bị của bạn sẽ hoạt động nhanh hơn, trơn tru hơn ở mọi lúc, mọi thời điểm. Galaxy S10 xứng tầm đẳng cấp, mọi thể loại game 3D, game nặng như Asphalt 8, PUBG,... cũng không là trở ngại cho máy.</p><h3>Dung lượng pin lớn, khả năng sạc ngược độc đáo</h3><p>Samsung Galaxy S10 sở hữu viên pin có dung lượng 3.400 mAh hứa hẹn có thể đáp ứng cho bạn nhu cầu sử dụng trong khoảng 1 ngày.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/161554/sieu-pham-galaxy-s-white9.jpg\" onclick=\"return false;\"><img alt=\"Thời lượng pin của điện thoại Samsung Galaxy S10 chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/161554/sieu-pham-galaxy-s-white9.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/161554/sieu-pham-galaxy-s-white9.jpg\" title=\"Thời lượng pin của điện thoại Samsung Galaxy S10 chính hãng\"></a></p><p>Máy cũng hỗ trợ công nghệ sạc nhanh không dây thế hệ 2 sẽ giúp tiết kiệm tối đa thời gian sạc.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/161554/samsung-galaxy-s10-powershare.jpg\" onclick=\"return false;\"><img alt=\"Tính năng PowerShare trên Galaxy S10 chính hãng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/161554/samsung-galaxy-s10-powershare.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/161554/samsung-galaxy-s10-powershare.jpg\" title=\"Tính năng PowerShare trên Galaxy S10 chính hãng\"></a></p><p>Điều đặc biệt lần đầu tiên Samsung trang bị chính là khả năng <a href=\"https://www.thegioididong.com/tin-tuc/tinh-nang-powershare-tren-galaxy-s10-la-gi-lam-sao-de-su-dung-no-1151089\" target=\"_blank\" title=\"Tìm hiểu tính năng sạc ngược PowerShare\" type=\"Tìm hiểu tính năng sạc ngược PowerShare\">sạc ngược không dây (PowerShare)</a>.</p><p>Bạn có thể sử dụng tính năng này để sạc cho bất kỳ thiết bị hỗ trợ sạc không dây khác, từ tai nghe không dây, điện thoại không dây đến pin dự phòng không dây...</p></article>', 11, 17100000, 0, '', '', '2019-06-23 20:47:29', 1, '2019-06-23 20:47:29', 1, 1);
INSERT INTO `uml_product` (`product_id`, `product_catid`, `product_name`, `product_slug`, `product_img`, `product_detail`, `product_number`, `product_price`, `product_pricesale`, `product_metakey`, `product_metadesc`, `product_createdat`, `product_createdby`, `product_updatedat`, `product_updatedby`, `product_status`) VALUES
(24, 7, 'Samsung Galaxy A80', 'samsung-galaxy-a80', 'samsung-galaxy-a80.jpg', '<h2><a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-a80\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung Galaxy A80 chính hãng\">Samsung Galaxy A80</a> là chiếc smartphone mang trong mình rất nhiều đột phá của Samsung và hứa hẹn sẽ là \"ngọn cờ đầu\" cho những chiếc <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại chính hãng\">smartphone</a> sở hữu một màn hình tràn viền thật sự.</h2><h3>Camera vừa trượt vừa xoay đầu tiên trên thế giới</h3><p>Thực tế thì xu hướng màn hình tràn viền đã bắt đầu cách đây khá lâu nhưng có rất ít các hãng có thể mang một màn hình tràn viền đúng nghĩa lên chiếc smartphone của mình.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/201228/samsung-galaxy-a80-tgdd-4.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Samsung Galaxy A80 | Cụm camera xoay\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/201228/samsung-galaxy-a80-tgdd-4.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/201228/samsung-galaxy-a80-tgdd-4.jpg\" title=\"Điện thoại Samsung Galaxy A80 | Cụm camera xoay\"></a></p><p>Và Samsung Galaxy A80 là một hướng đi mới của <a href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại Samsung chính hãng\">Samsung</a> về việc làm smartphone tràn viền khi hãng bố trí cụm camera chính (cũng chính là camera selfie) có thể vừa xoay vừa trượt.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/201228/samsung-galaxy-a80-tgdd-6.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Samsung Galaxy A80 | Cụm camera xoay selfie\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/201228/samsung-galaxy-a80-tgdd-6.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/201228/samsung-galaxy-a80-tgdd-6.jpg\" title=\"Điện thoại Samsung Galaxy A80 | Cụm camera xoay selfie\"></a></p><p>Cụm camera trên máy sẽ tự động trượt lên đồng thời sở hữu một cơ chế xoay giúp nó xoay 180 độ về phía trước những lúc người dùng cần tới camera selfie.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/201228/samsung-galaxy-a80-tgdd-5.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Samsung Galaxy A80 | Giao diện selfie\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/201228/samsung-galaxy-a80-tgdd-5.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/201228/samsung-galaxy-a80-tgdd-5.jpg\" title=\"Điện thoại Samsung Galaxy A80 | Giao diện selfie\"></a></p><p>Và khi không sử dụng nữa thì máy lại tự động trượt xuống và xoay camera 180 độ trở về vị trí cũ.</p><p>Điều này giúp bạn có một camera selfie \"cực kỳ\" chất lượng khi dùng luôn cụm 3 camera chính.</p><h3>Chất lượng camera không kém flagship</h3><p>Samsung Galaxy A80 là chiếc smartphone dòng A đầu tiên được Samsung ưu ái mang lên <a href=\"https://www.thegioididong.com/tin-tuc/cam-bien-camera-time-of-flight-tof-la-gi-no-duoc-dung-lam-gi-1154334\" target=\"_blank\" title=\"Cảm biến TOF 3D tìm hiểu công dụng\" type=\"Cảm biến TOF 3D tìm hiểu công dụng\">cảm biến 3D ToF</a> giúp tăng khả năng nhận diện độ sâu, hỗ trợ quay video xoá phông và các tính năng AR thú vị.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/201228/samsung-galaxy-a80-tgdd-10.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Samsung Galaxy A80 | Ảnh chụp phong cảnh\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/201228/samsung-galaxy-a80-tgdd-10.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/201228/samsung-galaxy-a80-tgdd-10.jpg\" title=\"Điện thoại Samsung Galaxy A80 | Ảnh chụp phong cảnh\"></a></p><p>Có thể bạn chưa biết thì cảm biến 3D ToF này chỉ được trang bị trên phiên bản Galaxy S10+ phiên bản 5G trước đó.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/201228/samsung-galaxy-a80-tgdd-1.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Samsung Galaxy A80 | Ảnh chụp thiếu sáng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/201228/samsung-galaxy-a80-tgdd-1.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/201228/samsung-galaxy-a80-tgdd-1.jpg\" title=\"Điện thoại Samsung Galaxy A80 | Ảnh chụp thiếu sáng\"></a></p><p>Bên cạnh đó cũng không thể bỏ qua camera chính với độ phân giải lên tới 48 MP cùng một camera góc rộng 8 MP giúp bạn có thể chụp mọi cảnh vật mà không phải di chuyển ra xa.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/201228/samsung-galaxy-a80-tgdd-9.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Samsung Galaxy A80 | Ảnh chụp bầu trời\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/201228/samsung-galaxy-a80-tgdd-9.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/201228/samsung-galaxy-a80-tgdd-9.jpg\" title=\"Điện thoại Samsung Galaxy A80 | Ảnh chụp bầu trời\"></a></p><p>Chất lượng ảnh cho ra từ máy cũng rất ấn tượng, khả năng lấy nét nhanh, <a href=\"https://www.thegioididong.com/tin-tuc/chup-anh-bang-ai-ban-da-biet-het-cong-dung--cac-ung-dung-hay-ho-nay--1150253\" target=\"_blank\" title=\"Tìm hiểu về công nghệ trí tuệ nhân tạo AI\" type=\"Tìm hiểu về công nghệ trí tuệ nhân tạo AI\">công nghệ AI</a> hỗ trợ nhận diện cảnh chụp giúp bạn luôn có được những bức hình ưng ý nhất.</p><h3>Smartphone đầu tiên sở hữu con chip <a href=\"https://Snapdragon 730 được sản xuất trên tiến trình 8 nm LPP, có NPU https://www.thegioididong.com › tin tức › Tin nhanh 24/7\" target=\"_blank\" title=\"Tìm hiểu về chip Snapdragon 730\" type=\"Tìm hiểu về chip Snapdragon 730\">Snapdragon 730</a></h3><p>Samsung Galaxy A80 là chiếc smartphone đầu tiên sử dụng con chip mới của Qualcomm được sản xuất dựa trên tiến trình 8nm giúp tiết kiệm điện năng đáng kể.</p><p>Đặc biệt con chip này được tối ưu hóa cho việc chơi game nên chắc chắn sẽ làm hài lòng các game thủ game mobile.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/201228/samsung-galaxy-a80-tgdd-3.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Samsung Galaxy A80 | Giao diện Android\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/201228/samsung-galaxy-a80-tgdd-3.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/201228/samsung-galaxy-a80-tgdd-3.jpg\" title=\"Điện thoại Samsung Galaxy A80 | Giao diện Android\"></a></p><p>Samsung Galaxy A80 cũng được hỗ trợ bởi trí tuệ nhân tạo (AI), cho phép điều chỉnh pin, CPU và RAM của dựa trên hành vi sử dụng của người dùng từ đó giúp máy khởi động ứng dụng nhanh và mượt mà hơn.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/201228/samsung-galaxy-a80-tgdd-2.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Samsung Galaxy A80 | Trải nghiệm và đánh giá\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/201228/samsung-galaxy-a80-tgdd-2.jpg\" class=\'lazy\' data-original=\"https://cdn.tgdd.vn/Products/Images/42/201228/samsung-galaxy-a80-tgdd-2.jpg\" title=\"Điện thoại Samsung Galaxy A80 | Trải nghiệm và đánh giá\"></a></p><p>Màn hình lớn lên tới 6.7 inch cùng trải nghiệm tràn viền thực thụ mang lại một trải nghiệm rất ấn tượng và thích mắt.</p><h3>Dung lượng pin lớn, hỗ trợ sạc nhanh</h3><p>Samsung Galaxy A80 được cung cấp năng lượng bởi viên pin 3.700 mAh với hỗ trợ sạc siêu nhanh 25W, hỗ trợ chế độ tiết kiệm năng lượng cAdaptive Power Saving.</p><p><a href=\"https://www.thegioididong.com/tin-tuc/cam-bien-van-tay-duoi-man-hinh-huawei-p20-pro-danh-gia-cao-1088856\" target=\"_blank\" title=\"Tìm hiểu về cảm biến vân tay dưới màn hình\" type=\"Tìm hiểu về cảm biến vân tay dưới màn hình\">Cảm biến vân tay dưới màn hình</a> cũng xuất hiện trên Samsung Galaxy A80 giúp người dùng được trải nghiệm những công nghệ bảo mật tiên tiến nhất hiện nay.</p></article>', 86, 24200000, 0, '', '', '2019-06-23 20:47:30', 1, '2019-06-23 20:47:30', 1, 1),
(25, 10, 'OPPO Reno 10x Zoom Edition', 'oppo-reno-10x-zoom-edition', 'oppo-reno-10x-zoom-edition.jpg', '<h2>Những chiếc flagship trong năm 2019 không chỉ có một camera chụp ảnh đẹp, chụp xóa phông ảo diệu mà còn phải \"chụp thật xa\" và với chiếc <a href=\"https://www.thegioididong.com/dtdd/oppo-reno-10x-zoom-edition\" target=\"_blank\" title=\"Tham khảo điện thoại OPPO Reno 10x Zoom Edition chính hãng\">OPPO Reno 10x Zoom Edition</a> chính thức gia nhập thị trường \"<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại chính hãng\">smartphone</a> có camera siêu zoom\".</h2><h3>Khi chụp ảnh đẹp thôi là chưa đủ</h3><p>Bên cạnh cuộc chạy đua về cấu hình thì camera là điểm nóng của các hãng khi liên tục có những cải tiến mạnh mẽ về camera phone.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/201235/oppo-reno-10x-zoom-edition9.jpg\" onclick=\"return false;\"><img alt=\"Camera sau của điện thoại OPPO Reno 10x Zoom Edition chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/201235/oppo-reno-10x-zoom-edition9.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/201235/oppo-reno-10x-zoom-edition9.jpg\" title=\"Camera sau của điện thoại OPPO Reno 10x Zoom Edition chính hãng\"></a></p><p>Sau khi chiếc <a href=\"https://www.thegioididong.com/dtdd/huawei-p30\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại Huawei P30 Pro chính hãng\">Huawei P30 Pro</a> ra mắt không lâu với khả năng zoom lên tới 10X hay 50X thì thậm chí chiếc OPPO Reno 10x Zoom Edition còn làm tốt hơn thế.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/201235/oppo-reno-10x-zoom-edition6.jpg\" onclick=\"return false;\"><img alt=\"Camera sau của điện thoại OPPO Reno 10x Zoom Edition chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/201235/oppo-reno-10x-zoom-edition6.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/201235/oppo-reno-10x-zoom-edition6.jpg\" title=\"Camera sau của điện thoại OPPO Reno 10x Zoom Edition chính hãng\"></a></p><p>Máy sở hữu cụm 3 camera với độ phân giải camera chính là 48 MP, một camera góc rộng 8 MP F/2.2 và một ống kính tiềm vọng 13 MP khẩu độ F/3.0 với khả năng zoom hybrid 10X.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/201235/oppo-reno-10x-zoom-edition5.jpg\" onclick=\"return false;\"><img alt=\"Camera sau của điện thoại OPPO Reno 10x Zoom Edition chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/201235/oppo-reno-10x-zoom-edition5.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/201235/oppo-reno-10x-zoom-edition5.jpg\" title=\"Camera sau của điện thoại OPPO Reno 10x Zoom Edition chính hãng\"></a></p><p>Với OPPO Reno 10x Zoom Edition thì bạn có thể sử dụng máy như một chiếc \"kính viễn vọng\" khi có thể quan sát được những vật ở rất xa mà mắt thường cũng khó có thể thấy được.</p><p>Hãy thử tưởng tượng bạn đang đứng ở xa và muốn chụp lấy phần phía trên của một tòa nhà hay một ngọn núi thì tính năng siêu zoom trên Reno 10x Zoom sẽ rất tuyệt vời.</p><h3>Camera trước thoắt ẩn thoắt hiện</h3><p>Không chỉ có camera sau mà camera trước trên máy cũng là một bước sáng tạo của OPPO so với những chiếc máy khác đang có trên thị trường.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/201235/oppo-reno-10x-zoom-edition4.jpg\" onclick=\"return false;\"><img alt=\"Camera trước selfie của điện thoại OPPO Reno 10x Zoom Edition chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/201235/oppo-reno-10x-zoom-edition4.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/201235/oppo-reno-10x-zoom-edition4.jpg\" title=\"Camera trước selfie của điện thoại OPPO Reno 10x Zoom Edition chính hãng\"></a></p><p>OPPO Reno 10x Zoom Edition sở hữu camera selfie ẩn trong khung và mở lên bằng cơ chế pop-up sử dụng động cơ để nâng lên.</p><p>Cơ chế này sẽ khác so với việc nâng toàn bộ cụm camera như trên <a href=\"https://www.thegioididong.com/dtdd/oppo-find-x\" target=\"_blank\" title=\"Chi tiết điện thoại OPPO Find X tại Thegioididong.com\" type=\"Chi tiết điện thoại OPPO Find X tại Thegioididong.com\">OPPO Find X</a> nên theo OPPO thì nhờ đó nó sẽ có độ bền cao hơn.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/201235/oppo-reno-10x-zoom-edition7.jpg\" onclick=\"return false;\"><img alt=\"Camera trước selfie của điện thoại OPPO Reno 10x Zoom Edition chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/201235/oppo-reno-10x-zoom-edition7.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/201235/oppo-reno-10x-zoom-edition7.jpg\" title=\"Camera trước selfie của điện thoại OPPO Reno 10x Zoom Edition chính hãng\"></a></p><p>Tốc độ nâng lên hạ xuống của cụm camera này cũng rất nhanh giúp bạn cảm thấy hầu như không có sự khác biệt so với việc có một camera luôn xuất hiện phía trước.</p><h3>Nhiều công nghệ tiên tiến khác</h3><p>Chiếc <a href=\"https://www.thegioididong.com/dtdd-oppo\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại OPPO chính hãng\">điện thoại OPPO</a> này sở hữu cấu hình mạnh mẽ nhất thế giới Android trong năm 2019 với con chip <a href=\"https://www.thegioididong.com/tin-tuc/snapdragon-855-tong-hop-thong-tin-ve-chip-cao-cap-cua-qualcomm-1110568\" target=\"_blank\" title=\"Tìm hiểu chip Snapdragon 855 8 nhân 64-bit\">Snapdragon 855 8 nhân 64-bit</a> kết hợp với 8 GB RAM và 256 GB bộ nhớ trong.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/201235/oppo-reno-10x-zoom-edition2.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình của điện thoại OPPO Reno 10x Zoom Edition chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/201235/oppo-reno-10x-zoom-edition2.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/201235/oppo-reno-10x-zoom-edition2.jpg\" title=\"Cấu hình của điện thoại OPPO Reno 10x Zoom Edition chính hãng\"></a></p><p>Cảm biến vân tay bên trong màn hình cũng xuất hiện trên máy song song với khả năng mở khóa bằng khuôn mặt giúp bạn dễ dàng sử dụng mọi lúc mọi nơi.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/201235/oppo-reno-10x-zoom-edition3.jpg\" onclick=\"return false;\"><img alt=\"Cảm biến vân tay trên điện thoại OPPO Reno 10x Zoom Edition chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/201235/oppo-reno-10x-zoom-edition3.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/201235/oppo-reno-10x-zoom-edition3.jpg\" title=\"Cảm biến vân tay trên điện thoại OPPO Reno 10x Zoom Edition chính hãng\"></a></p><p>Máy có pin dung lượng 4065 mAh, hỗ trợ sạc nhanh VOOC 3.0 50W cho tốc độ sạc đầy pin nhanh hơn khoảng 30% so với VOOC 2.0 trước đó.</p><h3>Không chỉ là smartphone mà còn là món đồ thời trang</h3><p>Không chỉ có nhiều công nghệ tiên tiến mà thiết kế trên OPPO Reno 10x Zoom Edition cũng sẽ khiến bạn \"yêu từ cái nhìn đầu tiên\".</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/201235/oppo-reno-10x-zoom-edition1.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế của điện thoại OPPO Reno 10x Zoom Edition chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/201235/oppo-reno-10x-zoom-edition1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/201235/oppo-reno-10x-zoom-edition1.jpg\" title=\"Thiết kế của điện thoại OPPO Reno 10x Zoom Edition chính hãng\"></a></p><p>Đầu tiên phải kể đến thiết kế khung viền kim loại cùng 2 mặt kính cường lực <a href=\"https://www.thegioididong.com/tin-tuc/corning-ra-mat-gorilla-glass-6-chiu-va-dap-o-do-cao-1-met-1102577\" target=\"_blank\" title=\"Tìm hiểu Corning Gorilla Glass 6\">Corning Gorilla Glass 6</a> mới nhất cho bạn một cái nhìn rất hào nhoáng và sang trọng.</p><p>Điểm nhấn tới từ cụm 3 camera ở mặt lưng đặt dọc cùng logo và các thông tin sản phẩm được đặt dọc theo một đường chạy xuyên suốt vị trí chính giữa trông thiết kế này rất hợp lý và đẹp mắt.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/201235/oppo-reno-10x-zoom-edition8.jpg\" onclick=\"return false;\"><img alt=\"Thời lượng pin của điện thoại OPPO Reno 10x Zoom Edition chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/201235/oppo-reno-10x-zoom-edition8.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/201235/oppo-reno-10x-zoom-edition8.jpg\" title=\"Thời lượng pin của điện thoại OPPO Reno 10x Zoom Edition chính hãng\"></a></p><p>Mặt lưng cũng được hoàn thiện cong 3D về các cạnh giúp máy ôm tay khi cầm nắm sử dụng trong thời gian dài.</p></article>', 197, 29200000, 0, '', '', '2019-06-23 20:57:35', 1, '2019-06-23 20:57:35', 1, 1),
(26, 10, 'OPPO R17 Pro', 'oppo-r17-pro', 'oppo-r17-pro.jpg', '<h2><a href=\"https://www.thegioididong.com/dtdd/oppo-r17-pro\" target=\"_blank\" title=\"Chi tiết điện thoại OPPO R17 Pro\" type=\"Chi tiết điện thoại OPPO R17 Pro\">OPPO R17 Pro</a> được xem là chiếc <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo smartphone tại Thegioididong.com\" type=\"Tham khảo smartphone tại Thegioididong.com\">smartphone</a> sự hiện thân của cái đẹp khi được khoác lên mình một thiết kế trẻ trung và hiện đại, cùng với đó là hàng loạt các trang bị cao cấp đi từ cấu hình cho đến camera.</h2><h3>Thiết kế thời trang, phá cách</h3><p>OPPO R17 Pro sở hữu cho mình lối thiết kế mới sẽ khiến bạn phải mê mẩn đến từ chiếc tai thỏ hình giọt nước vô cùng quyến rũ.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/186395/oppo-r17-pro-6-3.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại OPPO R17 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/186395/oppo-r17-pro-6-3.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/186395/oppo-r17-pro-6-3.jpg\" title=\"Màn hình điện thoại OPPO R17 Pro chính hãng\"></a></p><p>Thân hình uyển chuyển với các đường cong mềm mại và cực kì thu hút khi màn hình của máy gần như chiếm trọn mặt trước.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/186395/oppo-r17-pro-9-1.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại OPPO R17 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/186395/oppo-r17-pro-9-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/186395/oppo-r17-pro-9-1.jpg\" title=\"Thiết kế điện thoại OPPO R17 Pro chính hãng\"></a></p><p>Ấn tượng hơn với mặt lưng kính, trong sự kết hợp hài hòa giữa màu xanh và tím tạo nên hiệu ứng gradient đa sắc độ khi bạn ngắm nhìn ở các góc nghiêng khác nhau.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/186395/oppo-r17-pro-2-3.jpg\" onclick=\"return false;\"><img alt=\"Đánh giá điện thoại OPPO R17 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/186395/oppo-r17-pro-2-3.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/186395/oppo-r17-pro-2-3.jpg\" title=\"Đánh giá điện thoại OPPO R17 Pro chính hãng\"></a></p><p>Không gian trải nghiệm phía trước có kích thước khủng lên đến 6.4 inch và trở thành một trong những smartphone có màn hình lớn nhất hiện nay.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/186395/oppo-r17-pro-1-4.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại OPPO R17 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/186395/oppo-r17-pro-1-4.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/186395/oppo-r17-pro-1-4.jpg\" title=\"Màn hình điện thoại OPPO R17 Pro chính hãng\"></a></p><p>Về khả năng hiển thị R17 Pro hứa hẹn sẽ mang lại cho bạn một trải nghiệm tuyệt vời nhất với tấm nền <a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-amoled-la-gi-905766\" target=\"_blank\" title=\"AMOLED\" type=\"AMOLED\">AMOLED</a> rực rỡ cùng độ phân giải <a href=\"https://www.thegioididong.com/tin-tuc/do-phan-giai-man-hinh-qhd-hd-fullhd-2k-4k-la-gi--592178\" target=\"_blank\" title=\"Full HD+\" type=\"Full HD+\">Full HD+</a> siêu nét.</p><h3>Cấu hình với chip <a href=\"https://www.thegioididong.com/tin-tuc/snapdragon-710-se-la-vi-xu-ly-dau-tien-thuoc-series-700-1079732\" target=\"_blank\" title=\"Snapdragon 710\" type=\"Snapdragon 710\">Snapdragon 710</a> mạnh mẽ</h3><p>Sức mạnh của R17 Pro sẽ không làm bạn thất vọng khi mang trong mình con chip Snapdragon 710 kết hợp 8 GB RAM cùng 128 GB bộ nhớ trong.</p><p><div class=video allow=\"accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"https://www.youtube.com/embed/CRY2W2l07t4\" width=\"560\"></div></p><p>Với những thông số trên thì mọi tác vụ hằng ngày của bạn như: chơi game, xem phim, lướt web sẽ được xử lý một cách nhanh chóng và đơn giản.</p><p><div class=video allow=\"accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"https://www.youtube.com/embed/-SPoQK3SheU\" width=\"560\"></div></p><p>Hơn nữa, giao diện <a href=\"https://www.thegioididong.com/hoi-dap/he-dieu-hanh-coloros-tren-oppo-la-ma-quen-1073718\" target=\"_blank\" title=\"ColorOS 5.2\" type=\"ColorOS 5.2\">ColorOS 5.2</a> được tùy biến thông minh sẽ giúp các trải nghiệm của bạn trở nên thú vị hơn rất nhiều.</p><h3>Điểm nhấn đặc biệt về camera</h3><p><a href=\"https://www.thegioididong.com/dtdd-oppo\" target=\"_blank\" title=\"Các dòng điện thoại OPPO đang kinh doanh tại thegioididong.com\" type=\"Các dòng điện thoại OPPO đang kinh doanh tại thegioididong.com\">OPPO</a> luôn cho thấy sự chịu chơi của mình khi trang bị cho R17 Pro cụm camera với 3 thấu kính có độ phân giải lần lượt là 20 MP, 12 MP và <a href=\"https://www.thegioididong.com/tin-tuc/tren-tay-oppo-r17-pro-chip-snapdragon-710-camera-3d-1127832\" target=\"_blank\" title=\"Tìm hiểu công nghệ TOF 3D camera\">TOF 3D</a>.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/186395/oppo-r17-pro-3-3.jpg\" onclick=\"return false;\"><img alt=\"Camera sau điện thoại OPPO R17 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/186395/oppo-r17-pro-3-3.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/186395/oppo-r17-pro-3-3.jpg\" title=\"Camera sau điện thoại OPPO R17 Pro chính hãng\"></a></p><p>Chi tiết hơn, camera chính góc rộng 12 MP được tích hợp ổn định hình ảnh quang học và có thể thay đổi khẩu độ giữa F/2.4 và F/1.5 như trên <a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-note-9\" target=\"_blank\" title=\"Samsung Galaxy Note 9\" type=\"Samsung Galaxy Note 9\">Samsung Galaxy Note 9</a>.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/186395/oppo-r17-pro-7-2.jpg\" onclick=\"return false;\"><img alt=\"Giao diện camera điện thoại OPPO R17 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/186395/oppo-r17-pro-7-2.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/186395/oppo-r17-pro-7-2.jpg\" title=\"Giao diện camera điện thoại OPPO R17 Pro chính hãng\"></a></p><p>Còn với camera phụ 20 MP sẽ được sử dụng để phóng to ảnh cũng như tạo các hiệu ứng làm mờ hỗ trợ cho tính năng <a href=\"https://www.thegioididong.com/tin-tuc/tim-hieu-xoa-phong-huong-dan-xoa-phong-tren-moi-smartphone-892585\" target=\"_blank\" title=\"chụp ảnh xóa phông\" type=\"chụp ảnh xóa phông\">chụp ảnh xóa phông</a>.</p><p><div class=video allow=\"accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"https://www.youtube.com/embed/cg6hTbrbp_c\" width=\"560\"></div></p><p>Chưa dừng lại ở đó, R17 Pro còn được trang bị cảm biến TOF 3D camera chụp được ảnh 3D và tương tác ảo AR nhờ tích hợp thêm mô-đun cảm biến chiều sâu 3D.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/186395/dtdd-oppo-r17-pro-9.jpg\" onclick=\"return false;\"><img alt=\"Cụm camera trước trên điện thoại OPPO R17 pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/186395/dtdd-oppo-r17-pro-9.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/186395/dtdd-oppo-r17-pro-9.jpg\" title=\"Cụm camera trước trên điện thoại OPPO R17 pro chính hãng\"></a></p><p>Camera trước cũng để lại ấn tượng không kém với độ phân giải 25 MP cực khủng tích hợp <a href=\"https://www.thegioididong.com/tin-tuc/cac-tinh-nang-chup-anh-camera-truoc-769270#lamdep\" target=\"_blank\" title=\"chế độ làm đẹp\" type=\"chế độ làm đẹp\">chế độ làm đẹp</a> nhiều cấp độ, hứa hẹn sẽ là một lựa chọn tuyệt vời cho bạn mỗi khi chụp ảnh tự sướng.</p><h3>Công nghệ SuperVOOC sạc siêu nhanh</h3><p>OPPO R17 Pro được ưu ái tích hợp chuẩn <a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-ve-cong-nghe-sac-nhanh-vooc-cua-oppo-918283\" target=\"_blank\" title=\"sạc nhanh VOOC\" type=\"sạc nhanh VOOC\">sạc nhanh SuperVOOC</a> với công suất lên đến 50W cho phép bạn sạc từ 0% đến 40% chỉ trong 10 phút.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/186395/oppo-r17-pro-4-3.jpg\" onclick=\"return false;\"><img alt=\"Cổng sạc điện thoại OPPO R17 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/186395/oppo-r17-pro-4-3.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/186395/oppo-r17-pro-4-3.jpg\" title=\"Cổng sạc điện thoại OPPO R17 Pro chính hãng\"></a></p><p>Bên cạnh đó, một công nghệ đặc biệt khác cũng phải kể đến đó chính là <a href=\"https://www.thegioididong.com/tin-tuc/clear-id-la-gi-cam-bien-van-tay-duoi-man-hinh-co-gi-moi-1057643\" target=\"_blank\" title=\"cảm biến vân tay siêu âm\" type=\"cảm biến vân tay siêu âm\">cảm biến vân tay siêu âm</a> bên trong màn hình vô cùng hiện đại và tương lai.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/186395/oppo-r17-pro-8-1.jpg\" onclick=\"return false;\"><img alt=\"Vân tay siêu âm trên điện thoại OPPO R17 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/186395/oppo-r17-pro-8-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/186395/oppo-r17-pro-8-1.jpg\" title=\"Vân tay siêu âm trên điện thoại OPPO R17 Pro chính hãng\"></a></p><p>Ngoài ra, pin của máy có dung lượng cao lên đến 3700 mAh đáp ứng cho bạn thời gian trải nghiệm dư giả và thoải mái trong 1 ngày sử dụng.</p></article>', 18, 5800000, 6580000, '', '', '2019-06-23 20:57:36', 1, '2019-06-23 20:57:36', 1, 1),
(27, 10, 'OPPO Find X', 'oppo-find-x', 'oppo-find-x.jpg', '<h2><a href=\"https://www.thegioididong.com/dtdd/oppo-find-x\" target=\"_blank\" title=\"Chi tiết điện thoại OPPO Find X tại Thegioididong.com\" type=\"Chi tiết điện thoại OPPO Find X tại Thegioididong.com\">OPPO Find X</a> tạo nên một cú hích lớn trong làng <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Các dòng smartphone đang kinh doanh tại Thegioididong.com\" type=\"Các dòng smartphone đang kinh doanh tại Thegioididong.com\">smartphone</a> hiện nay khi mang trong mình nhiều tính năng đột phá mà nổi bật nhất đến từ thiết kế sáng tạo và một hiệu năng đỉnh cao.</h2><h3>Thiết kế đến từ tương lai</h3><p>Chiếc <a href=\"https://www.thegioididong.com/dtdd-oppo\" target=\"_blank\" title=\"Các dòng điện thoại OPPO tại Thegioididong.com\" type=\"Các dòng điện thoại OPPO tại Thegioididong.com\">điện thoại OPPO</a> mới đã tạo nên sự khác biệt cho riêng mình nhờ lối thiết kế dạng trượt phần camera giúp không gian hiển thị mặt trước chiếm gần như là trọn vẹn.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/165189/oppo-find-x-1-3.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại OPPO Find X\" src=\"https://cdn.tgdd.vn/Products/Images/42/165189/oppo-find-x-1-3.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/165189/oppo-find-x-1-3.jpg\" title=\"Màn hình điện thoại OPPO Find X\"></a></p><p><em>Mặt trước OPPO Find X chiếm diện tích khoảng 87.0%</em></p><p>Và theo nhận định của nhiều chuyên gia cơ chế trượt của máy hoạt động khá trơn tru và bền bỉ trong thời gian dài.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/165189/oppo-find-x-6.gif\" onclick=\"return false;\"><img alt=\"Cơ chế trượt trên điện thoại OPPO Find X\" src=\"https://cdn.tgdd.vn/Products/Images/42/165189/oppo-find-x-6.gif\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/165189/oppo-find-x-6.gif\" title=\"Cơ chế trượt trên điện thoại OPPO Find X\"></a></p><p><em>Cơ chế trượt của OPPO Find X được điều khiển thông qua thao tác trên màn hình </em></p><p>Tuy nhiên, vấn đề bụi bẩn và nước sẽ là mối lo ngại đáng kể với hệ thống trượt này.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/165189/oppo-find-x-9-1.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế trượt trên điện thoại OPPO Find X\" src=\"https://cdn.tgdd.vn/Products/Images/42/165189/oppo-find-x-9-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/165189/oppo-find-x-9-1.jpg\" title=\"Thiết kế trượt trên điện thoại OPPO Find X\"></a></p><p><em>Cơ chế trượt OPPO Find X để lộ nhiều khoảng trống dễ bị vào bụi</em></p><p>Tổng thể thân máy được làm sắc sảo đến từng chi tiết trong sự kết hợp hài hòa giữa <a href=\"https://www.thegioididong.com/dtdd?g=kim-loai-va-kinh-cuong-luc\" target=\"_blank\" title=\"Điện thoại có thiết kế khung kim loại và mặt kính tại Thegioididong.com\" type=\"Điện thoại có thiết kế khung kim loại và mặt kính tại Thegioididong.com\">khung kim loại và 2 mặt kính</a> cao cấp.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/165189/oppo-find-x-7-1.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại OPPO Find X\" src=\"https://cdn.tgdd.vn/Products/Images/42/165189/oppo-find-x-7-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/165189/oppo-find-x-7-1.jpg\" title=\"Thiết kế điện thoại OPPO Find X\"></a></p><p><em>OPPO Find X sở hữu thiết kế sang trọng và hiện đại</em></p><p>Dù sở hữu kích thước màn hình lên đến 6.42 inch nhưng máy vẫn khá vừa vặn mỗi khi cầm nắm.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/165189/oppo-find-x-2-2.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại OPPO Find X\" src=\"https://cdn.tgdd.vn/Products/Images/42/165189/oppo-find-x-2-2.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/165189/oppo-find-x-2-2.jpg\" title=\"Màn hình điện thoại OPPO Find X\"></a></p><p><em>OPPO Find X sở hữu tỷ lệ màn hình 19.5:9</em></p><p>Lấy màu đen làm tông chủ đạo khi kết hợp với các màu khác giúp OPPO Find X trở nên lung linh mờ ảo nhưng không kém phần quyến rũ.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/165189/oppo-find-x-13-3.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại OPPO Find X\" src=\"https://cdn.tgdd.vn/Products/Images/42/165189/oppo-find-x-13-3.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/165189/oppo-find-x-13-3.jpg\" title=\"Thiết kế điện thoại OPPO Find X\"></a></p><p><em>Màu sắc lấp lánh ở các góc nghiêng khác nhau trên OPPO Find X</em></p><h3>Hiệu năng hàng đầu hiện nay</h3><p>Sức mạnh đến từ con chip <a href=\"https://www.thegioididong.com/tin-tuc/chip-snapdragon-845-manh-hon-25-tap-trung-ai-bao-mat-cao-1048166\" target=\"_blank\" title=\"Snapdragon 845\" type=\"Snapdragon 845\">Snapdragon 845</a> cùng 8 GB RAM sẽ là thông số đáng mơ ước trên một chiếc smartphone và nay đã có mặt trên OPPO Find X.</p><p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Files/2018/08/06/1107210/hieu-nang-oppo-find-x-1_600x338.gif\" onclick=\"return false;\"><img alt=\"Chơi game Liên Quân trên điện thoại OPPO Find X\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Files/2018/08/06/1107210/hieu-nang-oppo-find-x-1_600x338.gif\" title=\"Chơi game Liên Quân trên điện thoại OPPO Find X\"></a></p><p><em>Trải nghiệm cấu hình cực khủng trên OPPO Find X</em></p><p>Với cấu hình mạnh mẽ như trên ắt hẳn mọi tựa game nặng hiện nay sẽ bị khuất phục một cách nhanh chóng.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/165189/oppo-find-x-5.jpg\" onclick=\"return false;\"><img alt=\"Giao diện điện thoại OPPO Find X\" src=\"https://cdn.tgdd.vn/Products/Images/42/165189/oppo-find-x-5.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/165189/oppo-find-x-5.jpg\" title=\"Giao diện điện thoại OPPO Find X\"></a></p><p><em>Color OS trên OPPO Find X thông minh tiện dụng</em></p><p>Hơn thế nữa, với sự tùy biến cao đến từ hệ điều hành Color OS thì mọi thao tác sử dụng hằng ngày của bạn sẽ trở nên đơn giản hơn rất nhiều.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/165189/oppo-find-x-10-1.jpg\" onclick=\"return false;\"><img alt=\"Chơi game trên điện thoại OPPO Find X\" src=\"https://cdn.tgdd.vn/Products/Images/42/165189/oppo-find-x-10-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/165189/oppo-find-x-10-1.jpg\" title=\"Chơi game trên điện thoại OPPO Find X\"></a></p><h3>Hệ thống camera đỉnh cao</h3><p>Cụm camera kép ở phía sau của máy bao gồm một ống kính chính 16 MP và một ống kính phụ 20 MP.</p><p>Nhiều công nghệ chụp ảnh hiện đại nhất như: <a href=\"https://www.thegioididong.com/hoi-dap/che-do-chong-rung-quang-hoc-ois-chup-anh-tren-smar-906416\" target=\"_blank\" title=\"zoom quang học\" type=\"zoom quang học\">zoom quang học</a>, <a href=\"https://www.thegioididong.com/tin-tuc/tim-hieu-xoa-phong-huong-dan-xoa-phong-tren-moi-smartphone-892585\" target=\"_blank\" title=\"chụp ảnh xóa phông\" type=\"chụp ảnh xóa phông\">chụp ảnh xóa phông</a> cũng được tích hợp trên bộ đôi camera kép của máy.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/165189/oppo-find-x-4-2.jpg\" onclick=\"return false;\"><img alt=\"Khả năng chụp hình camera sau trên điện thoại OPPO Find X\" src=\"https://cdn.tgdd.vn/Products/Images/42/165189/oppo-find-x-4-2.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/165189/oppo-find-x-4-2.jpg\" title=\"Khả năng chụp hình camera sau trên điện thoại OPPO Find X\"></a></p><p><em>Camera sau của OPPO Find X</em></p><p>Với độ phân giải 25 MP trên camera trước thì OPPO Find X sẽ là cái tên nằm trong danh sách những smartphone chụp ảnh selfie đẹp và có phần ảo diệu.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/165189/oppo-find-x-5-2.jpg\" onclick=\"return false;\"><img alt=\"Camera selfie trên điện thoại OPPO Find X\" src=\"https://cdn.tgdd.vn/Products/Images/42/165189/oppo-find-x-5-2.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/165189/oppo-find-x-5-2.jpg\" title=\"Camera selfie trên điện thoại OPPO Find X\"></a></p><p><em>Cụm camera trước trên OPPO Find X</em></p><h3>Công nghệ sạc nhanh VOOC độc quyền</h3><p>OPPO Find X được trang bị công nghệ <a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-ve-cong-nghe-sac-nhanh-vooc-cua-oppo-918283\" target=\"_blank\" title=\"sạc nhanh VOOC\" type=\"sạc nhanh VOOC\">sạc nhanh VOOC</a> do hãng phát triển đáp ứng thời gian sạc nhanh chóng chỉ rơi vào khoảng 30 phút.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/165189/oppo-find-x-13-2.jpg\" onclick=\"return false;\"><img alt=\"Cổng kết nối trên điện thoại OPPO Find X\" src=\"https://cdn.tgdd.vn/Products/Images/42/165189/oppo-find-x-13-2.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/165189/oppo-find-x-13-2.jpg\" title=\"Cổng kết nối trên điện thoại OPPO Find X\"></a></p><p><em>Giao tiếp trên OPPO Find X thông qua cổng <a href=\"https://www.thegioididong.com/hoi-dap/usb-typec-chuan-muc-cong-ket-noi-moi-723760\" target=\"_blank\" title=\"USB Type C\" type=\"USB Type C\">USB Type C</a></em></p><p>Công nghệ này được xem như đối thủ của Qualcomm với công nghệ Quick charge.</p><h3>Liệu OPPO Find X có đáng để bạn móc hầu bao?</h3><p>Không thể phủ nhận, Find X chứa đựng nhiều tính năng cao cấp đến từ phần cứng cũng như phần mềm, nhưng cái giá để bạn có thể sở hữu sẽ khá chát và con số được tính bằng ngàn đô.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/165189/oppo-find-x-12-1.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại OPPO Find X\" src=\"https://cdn.tgdd.vn/Products/Images/42/165189/oppo-find-x-12-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/165189/oppo-find-x-12-1.jpg\" title=\"Thiết kế điện thoại OPPO Find X\"></a></p><p>Tuy nhiên, nếu bạn là người khá giả đam mê cái đẹp hay quan tâm đến hiệu năng và thích thú với hệ thống trượt thông minh thì Find X sẽ khiến bạn không phải thất vọng.</p></article>', 104, 11200000, 0, '', '', '2019-06-23 20:57:37', 1, '2019-06-23 20:57:37', 1, 1),
(28, 10, 'OPPO Reno', 'oppo-reno', 'oppo-reno.jpg', '<h2>Những năm gần đây OPPO luôn tạo được dấu ăn trên các sản phẩm mới của mình và <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại chính hãng\">smartphone</a> vừa ra mắt <a href=\"https://www.thegioididong.com/dtdd/oppo-reno\" target=\"_blank\" title=\"Tham khảo điện thoại OPPO Reno chính hãng\">OPPO Reno</a> là một ví dụ điển hình.</h2><h3>Cụm camera selfie độc đáo</h3><p>Với <a href=\"https://www.thegioididong.com/dtdd/oppo-find-x\" target=\"_blank\" title=\"Chi tiết điện thoại OPPO Find X tại Thegioididong.com\" type=\"Chi tiết điện thoại OPPO Find X tại Thegioididong.com\">OPPO Find X</a> thì OPPO đã đánh dấu cách tiếp cận mới cho một chiếc máy tràn viền và trên OPPO Reno thì OPPO lại một lần nữa khiến nhiều người không khỏi ngạc nhiên.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/200438/oppo-reno-tgdd-12.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại OPPO Reno | Cụm camera\" src=\"https://cdn.tgdd.vn/Products/Images/42/200438/oppo-reno-tgdd-12.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/200438/oppo-reno-tgdd-12.jpg\" title=\"Điện thoại OPPO Reno | Cụm camera\"></a></p><p>Máy vẫn sở hữu camera trước có khả năng thò ra thụt vào nhưng giờ đây không phải là nguyên cụm mà chỉ là cơ chế nâng một bên và một bên cố định.</p><p>Việc sử dụng thiết kế module camera trước mới giúp độ bền cơ học của OPPO Reno trở nên đảm bảo hơn so với cơ chế trượt của Find X trước đây.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/200438/oppo-reno-tgdd-13.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại OPPO Reno | Cụm camera\" src=\"https://cdn.tgdd.vn/Products/Images/42/200438/oppo-reno-tgdd-13.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/200438/oppo-reno-tgdd-13.jpg\" title=\"Điện thoại OPPO Reno | Cụm camera\"></a></p><p>Bạn cũng không cần quá lo lắng khi <a href=\"https://www.thegioididong.com/dtdd-oppo\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại OPPO chính hãng\">OPPO</a> đã cải thiện tốc độ mở camera trước nhanh hơn tới 28.5% so với các thế hệ trước giúp bạn không cảm thấy khó khăn khi sử dụng camera pop-up.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/200438/oppo-reno-tgdd-2.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại OPPO Reno | Ảnh chụp đêm\" src=\"https://cdn.tgdd.vn/Products/Images/42/200438/oppo-reno-tgdd-2.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/200438/oppo-reno-tgdd-2.jpg\" title=\"Điện thoại OPPO Reno | Ảnh chụp đêm\"></a></p><p>Với độ phân giải lên tới 16 MP kết hợp với công nghệ làm đẹp bằng AI thì OPPO Reno tự tin sẽ làm hài lòng cả những người dùng khó tính nhất.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/200438/oppo-reno-tgdd-4.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại OPPO Reno | Ảnh chụp selfie\" src=\"https://cdn.tgdd.vn/Products/Images/42/200438/oppo-reno-tgdd-4.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/200438/oppo-reno-tgdd-4.jpg\" title=\"Điện thoại OPPO Reno | Ảnh chụp selfie\"></a></p><p>Camera trước này chắc chắn sẽ mang lại cho bạn những trải nghiệm cực kỳ hấp dẫn mà không một chiếc smartphone nào sở hữu trên thị trường hiện nay.</p><h3>Trải nghiệm màn hình tràn viền thực thụ</h3><p>Nhờ có camera trước được dấu bên trong mà OPPO Reno sẽ được trang bị một màn hình tràn viền thật sự, không bị đục bất cứ phần nào trên màn hình.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/200438/oppo-reno-tgdd-6.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại OPPO Reno | Màn hình siêu tràn\" src=\"https://cdn.tgdd.vn/Products/Images/42/200438/oppo-reno-tgdd-6.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/200438/oppo-reno-tgdd-6.jpg\" title=\"Điện thoại OPPO Reno | Màn hình siêu tràn\"></a></p><p>Kích thước lên tới 6.4 inch cùng tấm nền <a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-amoled-la-gi-905766\" target=\"_blank\" title=\"Tìm hiểu màn hình AMOLED\">AMOLED</a> chất lượng cao giúp bạn có một thiết bị hiển thị rất đẹp và ấn tượng.</p><p>Tỉ lệ màn hình chiếm tới hơn 93% mặt trước giúp tổng thể máy không quá lớn và có thể sử dụng được bằng một tay.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/200438/oppo-reno-tgdd-11.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại OPPO Reno | Màn hình siêu tràn\" src=\"https://cdn.tgdd.vn/Products/Images/42/200438/oppo-reno-tgdd-11.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/200438/oppo-reno-tgdd-11.jpg\" title=\"Điện thoại OPPO Reno | Màn hình siêu tràn\"></a></p><p>Ở mặt trước và mặt sau của OPPO Reno đều được nhà sản xuất bảo vệ bằng kính cường lực Gorilla Class thế hệ thứ 6, chống xước và chống va đập tốt hơn.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/200438/oppo-reno-tgdd-9.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại OPPO Reno | Thiết kế mặt lưng\" src=\"https://cdn.tgdd.vn/Products/Images/42/200438/oppo-reno-tgdd-9.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/200438/oppo-reno-tgdd-9.jpg\" title=\"Điện thoại OPPO Reno | Thiết kế mặt lưng\"></a></p><p>Bên cạnh đó, mặt kính sau cũng được làm dạng mờ để hạn chế việc bám vân tay trong quá trình sử dụng.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/200438/oppo-reno-tgdd-7.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại OPPO Reno | Thiết kế mặt lưng\" src=\"https://cdn.tgdd.vn/Products/Images/42/200438/oppo-reno-tgdd-7.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/200438/oppo-reno-tgdd-7.jpg\" title=\"Điện thoại OPPO Reno | Thiết kế mặt lưng\"></a></p><p>Trên màn hình này cũng được tích hợp cảm biến vân tay, giúp người dùng có trải nghiệm mở khoá mới hiện đại và cao cấp hơn.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/200438/oppo-reno-tgdd-29.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại OPPO Reno | Vân tay dưới màn hình\" src=\"https://cdn.tgdd.vn/Products/Images/42/200438/oppo-reno-tgdd-29.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/200438/oppo-reno-tgdd-29.jpg\" title=\"Điện thoại OPPO Reno | Vân tay dưới màn hình\"></a></p><h3>Nhiều trang bị không kém smartphone cao cấp</h3><p>OPPO Reno sở hữu cho mình camera chính với độ phân giải lên tới 48 MP cùng camera phụ 5 MP hỗ trợ chụp ảnh xóa phông khá ảo diệu.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/200438/oppo-reno-tgdd-1.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại OPPO Reno | Ảnh chụp camera sau\" src=\"https://cdn.tgdd.vn/Products/Images/42/200438/oppo-reno-tgdd-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/200438/oppo-reno-tgdd-1.jpg\" title=\"Điện thoại OPPO Reno | Ảnh chụp camera sau\"></a></p><p>Máy được cài sẵn Android 9 Pie khi xuất xưởng với nhiều tùy biến hấp dẫn hứa hẹn sẽ giúp bạn khai thác tối đa công dụng của máy.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/200438/oppo-reno-tgdd-27.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại OPPO Reno | Giao diện Android\" src=\"https://cdn.tgdd.vn/Products/Images/42/200438/oppo-reno-tgdd-27.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/200438/oppo-reno-tgdd-27.jpg\" title=\"Điện thoại OPPO Reno | Giao diện Android\"></a></p><p>Và không thể không nhắc tới công nghệ sạc pin siêu nhanh VOOC 3.0 với thuật toán sạc VFC mới tăng đáng kể tốc độ sạc đầy pin.</p><p>Theo OPPO thì máy có thể sạc nhanh hơn 23.8% so với những chiếc máy sở hữu công nghệ VOOC 2.0 cũ.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/200438/oppo-reno-tgdd-30.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại OPPO Reno | Khả năng chơi game\" src=\"https://cdn.tgdd.vn/Products/Images/42/200438/oppo-reno-tgdd-30.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/200438/oppo-reno-tgdd-30.jpg\" title=\"Điện thoại OPPO Reno | Khả năng chơi game\"></a></p><p>Ngoài ra với cấu hình RAM đến 6 GB, cùng chip Snapdragon 710, OPPO Reno có thể chiến tốt cả dòng game hiện nay với độ ổn định khá cao.</p></article>', 47, 11700000, 0, '', '', '2019-06-23 20:57:38', 1, '2019-06-23 20:57:38', 1, 1);
INSERT INTO `uml_product` (`product_id`, `product_catid`, `product_name`, `product_slug`, `product_img`, `product_detail`, `product_number`, `product_price`, `product_pricesale`, `product_metakey`, `product_metadesc`, `product_createdat`, `product_createdby`, `product_updatedat`, `product_updatedby`, `product_status`) VALUES
(29, 10, 'OPPO F11 Pro Xám 128GB', 'oppo-f11-pro-xam-128gb', 'oppo-f11-pro-xam-128gb.jpg', '<h2>OPPO F11 Pro là chiếc <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại chính hãng\" type=\"smartphone\">smartphone</a> tầm trung mới nhất sở hữu nhiều công nghệ độc đáo của <a href=\"https://www.thegioididong.com/dtdd-oppo\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại OPPO chính hãng\" type=\"Tham khảo các dòng điện thoại OPPO chính hãng\">OPPO</a> và đã được rất nhiều người dùng lựa chọn, mới đây thì hãng tiếp tục tung ra phiên bản <a href=\"https://www.thegioididong.com/dtdd/oppo-f11-pro-xam-128gb\" target=\"_blank\" title=\"Tham khảo điện thoại OPPO F11 Pro Xám 128GB chính hãng\">OPPO F11 Pro Xám 128GB</a> giúp người dùng có nhiều lựa chọn hơn.</h2><h3>Thiết kế <a href=\"https://www.thegioididong.com/dtdd?f=man-hinh-tran-vien\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại có thiết kế màn hình tràn viền\">màn hình tràn viền</a> ấn tượng</h3><p>Trong thời đại điện thoại đang ngày càng trở nên giống nhau, nhưng những thay đổi OPPO F11 Pro đã giúp chiếc smartphone này trở nên nổi bật so với các đối thủ của mình.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/202704/oppo-f11-pro-xam-128gb-tgdd-19.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại OPPO F11 Pro Xám 128GB | Thiết kế\" src=\"https://cdn.tgdd.vn/Products/Images/42/202704/oppo-f11-pro-xam-128gb-tgdd-19.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/202704/oppo-f11-pro-xam-128gb-tgdd-19.jpg\" title=\"Điện thoại OPPO F11 Pro Xám 128GB | Thiết kế\" /></a></p><p>OPPO F11 Pro Xám 128GB sở hữu cho mình màn hình có kích thước lên tới 6.5 inch tràn viền đúng nghĩa khi camera đã được ẩn sâu bên trong.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/202704/oppo-f11-pro-xam-128gb-tgdd-6.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại OPPO F11 Pro Xám 128GB | Màn hình tràn viền\" src=\"https://cdn.tgdd.vn/Products/Images/42/202704/oppo-f11-pro-xam-128gb-tgdd-6.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/202704/oppo-f11-pro-xam-128gb-tgdd-6.jpg\" title=\"Điện thoại OPPO F11 Pro Xám 128GB | Màn hình tràn viền\" /></a></p><p>Bạn sẽ có một trải nghiệm hoàn toàn khác so với những chiếc smartphone đã ra mắt trước đây khi giờ đây toàn bộ mặt trước tập trung cho hiển thị.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/202704/oppo-f11-pro-xam-128gb-tgdd-18.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại OPPO F11 Pro Xám 128GB | Mặt lưng\" src=\"https://cdn.tgdd.vn/Products/Images/42/202704/oppo-f11-pro-xam-128gb-tgdd-18.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/202704/oppo-f11-pro-xam-128gb-tgdd-18.jpg\" title=\"Điện thoại OPPO F11 Pro Xám 128GB | Mặt lưng\" /></a></p><p>Mặt lưng của máy còn được hoàn thiện theo phong cách gradient giúp chiếc smartphone này càng trở nên nổi bật hơn.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/202704/oppo-f11-pro-xam-128gb-tgdd-17.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại OPPO F11 Pro Xám 128GB | Thiết kế màu sắc\" src=\"https://cdn.tgdd.vn/Products/Images/42/202704/oppo-f11-pro-xam-128gb-tgdd-17.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/202704/oppo-f11-pro-xam-128gb-tgdd-17.jpg\" title=\"Điện thoại OPPO F11 Pro Xám 128GB | Thiết kế màu sắc\" /></a></p><p>Phiên bản màu xám mới ra mắt hứa hẹn sẽ là lựa chọn hàng đầu dành cho giới trẻ để có thể khoe được cá tính riêng của mình.</p><h3>Camera \"thò ra thụt vào\" độc đáo</h3><p>Camera sẽ tự bật lên khi sử dụng ứng dụng camera hay bất cứ ứng dụng nào có dùng camera trước như Facebook, Zalo... Khi thoát khỏi ứng dụng nó sẽ tự rút lại.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/202704/oppo-f11-pro-xam-128gb-tgdd-13.png\" onclick=\"return false;\"><img alt=\"Điện thoại OPPO F11 Pro Xám 128GB | Thiết kế camera\" src=\"https://cdn.tgdd.vn/Products/Images/42/202704/oppo-f11-pro-xam-128gb-tgdd-13.png\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/202704/oppo-f11-pro-xam-128gb-tgdd-13.png\" title=\"Điện thoại OPPO F11 Pro Xám 128GB | Thiết kế camera\" /></a></p><p>Camera selfie được đặt tại vị trí chính giữa giúp các bức ảnh selfie dễ canh hình hơn, không bị lệch và bạn chỉ cần mất vài giây để bật camera lên để tự sướng thôi.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/202704/oppo-f11-pro-xam-128gb-tgdd-1.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại OPPO F11 Pro Xám 128GB | Ảnh chụp AI\" src=\"https://cdn.tgdd.vn/Products/Images/42/202704/oppo-f11-pro-xam-128gb-tgdd-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/202704/oppo-f11-pro-xam-128gb-tgdd-1.jpg\" style=\"text-align: center;\" title=\"Điện thoại OPPO F11 Pro Xám 128GB | Ảnh chụp AI\" /></a></p><p>Không chỉ thế, camera selfie hỗ trợ trí tuệ nhân tạo AI với công nghệ Beautification được OPPO đánh giá cao, cho phép mang đến những bức ảnh selfie tốt nhất.</p><p>Phần mềm máy ảnh cũng sẽ có nhiều hiệu ứng khác nhau để làm cho ảnh selfie của bạn trông tuyệt vời hơn nữa.</p><h3>Không chỉ có camera trước mà camera sau cũng rất tốt</h3><p>OPPO F11 Pro Xám 128GB là một trong những chiếc smartphone sở hữu camera chính với độ phân giải lớn nhất hiện nay với 48 MP.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/202704/oppo-f11-pro-xam-128gb-tgdd-20.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại OPPO F11 Pro Xám 128GB | Camera kép sau\" src=\"https://cdn.tgdd.vn/Products/Images/42/202704/oppo-f11-pro-xam-128gb-tgdd-20.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/202704/oppo-f11-pro-xam-128gb-tgdd-20.jpg\" title=\"Điện thoại OPPO F11 Pro Xám 128GB | Camera kép sau\" /></a></p><p>Bên cạnh đó máy cũng được trang bị thêm một camera 5 MP hỗ trợ người dùng trong việc chụp các bức ảnh xóa phông khá ảo diệu.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/202704/oppo-f11-pro-xam-128gb-tgdd-7.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại OPPO F11 Pro Xám 128GB | Ảnh chụp từ camera\" src=\"https://cdn.tgdd.vn/Products/Images/42/202704/oppo-f11-pro-xam-128gb-tgdd-7.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/202704/oppo-f11-pro-xam-128gb-tgdd-7.jpg\" title=\"Điện thoại OPPO F11 Pro Xám 128GB | Ảnh chụp từ camera\" /></a></p><p>Phần mềm của máy được OPPO đầu tư mạnh mẽ với rất nhiều tính năng chỉ xuất hiện trên những chiếc máy cao cấp.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/202704/oppo-f11-pro-xam-128gb-tgdd-8.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại OPPO F11 Pro Xám 128GB | Ảnh chụp từ camera\" src=\"https://cdn.tgdd.vn/Products/Images/42/202704/oppo-f11-pro-xam-128gb-tgdd-8.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/202704/oppo-f11-pro-xam-128gb-tgdd-8.jpg\" title=\"Điện thoại OPPO F11 Pro Xám 128GB | Ảnh chụp từ camera\" /></a></p><p>Chúng ta sẽ có công nghệ Ultra Night giúp người dùng chụp ảnh đêm tốt hơn, tính Tetracell giúp cải thiện khả năng chụp chân dung và thậm chí bạn còn có tính năng giúp kiểm soát màu da của con người.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/202704/oppo-f11-pro-xam-128gb-tgdd-15.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại OPPO F11 Pro Xám 128GB | Ảnh chụp đêm\" src=\"https://cdn.tgdd.vn/Products/Images/42/202704/oppo-f11-pro-xam-128gb-tgdd-15.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/202704/oppo-f11-pro-xam-128gb-tgdd-15.jpg\" title=\"Điện thoại OPPO F11 Pro Xám 128GB | Ảnh chụp đêm\" /></a></p><p>Nhìn chung OPPO F11 Pro Xám 128GB sở hữu một cụm camera chính chất lượng, có thể đáp ứng tốt cho người dùng trong hầu hết các nhu cầu hằng ngày.</p><h3>Sạc nhanh siêu ấn tượng</h3><p>OPPO F11 Pro Xám 128GB sở hữu cho mình viên pin có dung lượng lên tới 4000 mAh giúp bạn thoải mái sử dụng từ sáng tới tối.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/202704/oppo-f11-pro-xam-128gb-tgdd-12.png\" onclick=\"return false;\"><img alt=\"Điện thoại OPPO F11 Pro Xám 128GB | Cổng sạc trên máy\" src=\"https://cdn.tgdd.vn/Products/Images/42/202704/oppo-f11-pro-xam-128gb-tgdd-12.png\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/202704/oppo-f11-pro-xam-128gb-tgdd-12.png\" title=\"Điện thoại OPPO F11 Pro Xám 128GB | Cổng sạc trên máy\" /></a></p><p>Và đặc biệt máy được nâng cấp công nghệ sạc nhanh VOOC lên phiên bản 3.0 giúp máy có thể sạc đầy viên <a href=\"https://www.thegioididong.com/dtdd?f=pin-khung-3000-mah\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại có dung lượng pin \">pin \"siêu khủng\"</a> của mình chỉ trong 80 phút.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/202704/oppo-f11-pro-xam-128gb-tgdd-2.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại OPPO F11 Pro Xám 128GB | Đánh giá\" src=\"https://cdn.tgdd.vn/Products/Images/42/202704/oppo-f11-pro-xam-128gb-tgdd-2.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/202704/oppo-f11-pro-xam-128gb-tgdd-2.jpg\" title=\"Điện thoại OPPO F11 Pro Xám 128GB | Đánh giá\" /></a></p><p>Đồng thời, với 5 lớp bảo vệ trong <a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-ve-cong-nghe-sac-nhanh-vooc-cua-oppo-918283\" target=\"_blank\" title=\"Tìm hiểu công nghệ sạc nhanh VOOC\" type=\"Tìm hiểu công nghệ sạc nhanh VOOC\">công nghệ VOOC</a>, công nghệ <a href=\"https://www.thegioididong.com/dtdd?f=sac-pin-nhanh\" target=\"_blank\" title=\"Tham khảo giá điện thoại có công nghệ sạc nhanh\" type=\"Tham khảo giá điện thoại có công nghệ sạc nhanh\">sạc nhanh</a> của OPPO đảm bảo sự an toàn cho người dùng.</p></article>', 63, 17200000, 0, '', '', '2019-06-23 20:57:40', 1, '2019-06-23 20:57:40', 1, 1),
(30, 10, 'OPPO F11 Pro 128GB', 'oppo-f11-pro-128gb', 'oppo-f11-pro-128gb.jpg', '<h2>Lâu nay, những chiếc điện thoại của <a href=\"https://www.thegioididong.com/dtdd-oppo\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại OPPO chính hãng\">OPPO</a> thường được đánh giá cao ở camera selfie và với <a href=\"https://www.thegioididong.com/dtdd/oppo-f11-pro-128gb\" target=\"_blank\" title=\"Tham khảo điện thoại OPPO F11 Pro 128GB chính hãng\">OPPO F11 Pro 128GB</a> thì ngoài thế mạnh về camera trước với hệ thống trượt, OPPO làm người ta phải ấn tượng thêm cả về camera sau.</h2><h3>Màn hình tràn viền không khuyết điểm</h3><p>Không “tai thỏ”, không “nốt ruồi” giúp cho OPPO F11 Pro 128GB khác biệt hoàn toàn với nhiều <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại chính hãng\">smartphone</a> trên thị trường hiện nay.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/202703/oppo-f11-pro-128gb-tgdd-13.jpg\" onclick=\"return false;\"><img alt=\"Màn hình của điện thoại OPPO F11 Pro 128GB chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-13.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-13.jpg\" title=\"Màn hình của điện thoại OPPO F11 Pro 128GB chính hãng\"></a></p><p>Camera selfie nằm ẩn bên trong cơ chế trượt, từ đó chúng ta có một <a href=\"https://www.thegioididong.com/dtdd?f=man-hinh-tran-vien\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại có thiết kế màn hình tràn viền\">màn hình tràn viền</a> cực kỳ ấn tượng giúp người dùng có một diện tích trải nghiệm tuyệt vời.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/202703/oppo-f11-pro-128gb-tgdd-7.jpg\" onclick=\"return false;\"><img alt=\"Màn hình của điện thoại OPPO F11 Pro 128GB chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-7.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-7.jpg\" title=\"Màn hình của điện thoại OPPO F11 Pro 128GB chính hãng\"></a></p><p>OPPO F11 Pro 128GB được trang bị màn hình 6.5 inch, độ phân giải Full HD+, tỉ lệ 19.5:9, cộng với viền mỏng, nó giúp chiếc máy dễ dàng nằm gọn trong lòng bàn tay người dùng.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/202703/oppo-f11-pro-128gb-tgdd-18.jpg\" onclick=\"return false;\"><img alt=\"Màn hình của điện thoại OPPO F11 Pro 128GB chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-18.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-18.jpg\" title=\"Màn hình của điện thoại OPPO F11 Pro 128GB chính hãng\"></a></p><p>Đồng thời, góc nhìn và độ sáng của máy cũng được đánh giá là khá tốt khi người dùng trải nghiệm ngoài trời.</p><h3>Camera selfie trượt ấn tượng</h3><p>F11 Pro 128GB là máy sở hữu camera selfie 16 MP có cơ chế “ thò thụt” khá độc đáo và tiện lợi cho người dùng.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/202703/oppo-f11-pro-128gb-tgdd-12.jpg\" onclick=\"return false;\"><img alt=\"Camera trước selfie của điện thoại OPPO F11 Pro 128GB chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-12.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-12.jpg\" title=\"Camera trước selfie của điện thoại OPPO F11 Pro 128GB chính hãng\"></a></p><p>OPPO gọi đây là cơ chế pop-up (bật lên), lúc nào cần thì camera sẽ tự động được bật lên và sẽ ẩn đi khi không sử dụng.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/202703/oppo-f11-pro-128gb-tgdd-11.jpg\" onclick=\"return false;\"><img alt=\"Camera trước selfie của điện thoại OPPO F11 Pro 128GB chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-11.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-11.jpg\" title=\"Camera trước selfie của điện thoại OPPO F11 Pro 128GB chính hãng\"></a></p><p>Camera selfie được đặt tại vị trí chính giữa giúp các bức ảnh selfie dễ canh hình hơn, không bị lệch và bạn chỉ cần mất vài giây để bật camera lên để tự sướng thôi.</p><p>Không chỉ thế, camera selfie hỗ trợ trí tuệ nhân tạo AI với công nghệ Beautification được OPPO đánh giá cao, cho phép mang đến những bức ảnh selfie tốt nhất.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/202703/oppo-f11-pro-128gb-tgdd-20.jpg\" onclick=\"return false;\"><img alt=\"Camera trước selfie của điện thoại OPPO F11 Pro 128GB chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-20.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-20.jpg\" title=\"Camera trước selfie của điện thoại OPPO F11 Pro 128GB chính hãng\"></a></p><p>Phần mềm máy ảnh cũng sẽ có nhiều hiệu ứng khác nhau để làm cho ảnh selfie của bạn trông tuyệt vời hơn nữa.</p><h3>Camera sau lên đến 48 MP</h3><p>Không chỉ có camera trước mà OPPO F11 Pro 128 GB còn sở hữu camera chính với độ phân giải lên đến 48 MP và nhiều tính năng chụp hình thú vị khác.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/202703/oppo-f11-pro-128gb-tgdd-15.jpg\" onclick=\"return false;\"><img alt=\"Camera sau của điện thoại OPPO F11 Pro 128GB chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-15.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-15.jpg\" title=\"Camera sau của điện thoại OPPO F11 Pro 128GB chính hãng\"></a></p><p>Bên cạnh đó F11 Pro còn được trang bị cảm biến độ sâu 5 MP với khẩu độ f/2.4, sử dụng cảm biến của Sony giúp camera nhận được nhiều ánh sáng hơn và thu được nhiều chi tiết hơn.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/202703/oppo-f11-pro-128gb-tgdd-6.jpg\" onclick=\"return false;\"><img alt=\"Camera sau của điện thoại OPPO F11 Pro 128GB chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-6.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-6.jpg\" title=\"Camera sau của điện thoại OPPO F11 Pro 128GB chính hãng\"></a></p><p>Ngoài độ phân giải cao, OPPO F11 Pro còn trang bị quá nhiều công nghệ tiên tiến trên một chiếc camera như: AI Ultra-clear Engine giúp người dùng luôn có được những bức ảnh tuyệt vời dù chụp đủ sáng hay thiếu sáng.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/202703/oppo-f11-pro-128gb-tgdd-5.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera của điện thoại OPPO F11 Pro 128GB chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-5.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-5.jpg\" title=\"Ảnh chụp từ camera của điện thoại OPPO F11 Pro 128GB chính hãng\"></a></p><p>Công nghệ Tetracell xuất hiện trên máy cũng sẽ cải thiện khả năng chụp ảnh chân dung vào ban đêm.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/202703/oppo-f11-pro-128gb-tgdd-4.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera của điện thoại OPPO F11 Pro 128GB chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-4.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-4.jpg\" title=\"Ảnh chụp từ camera của điện thoại OPPO F11 Pro 128GB chính hãng\"></a></p><p>Chế độ Ultra Night có chức năng mang đến những bức ảnh tuyệt vời trong nhiều cảnh đêm khác nhau và kèm theo tính năng Color Mapping nhằm kiểm soát màu da của con người.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/202703/oppo-f11-pro-128gb-tgdd-3.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera của điện thoại OPPO F11 Pro 128GB chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-3.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-3.jpg\" title=\"Ảnh chụp từ camera của điện thoại OPPO F11 Pro 128GB chính hãng\"></a></p><p>Tất cả những gì bạn cần làm là cầm chiếc OPPO F11 Pro lên và chụp, mọi việc còn lại đã có máy xử lý tự động cho bạn.</p><h3><a href=\"https://www.thegioididong.com/dtdd?f=pin-khung-3000-mah\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại có dung lượng pin \">Pin \"siêu khủng\"</a> kèm <a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-ve-cong-nghe-sac-nhanh-vooc-cua-oppo-918283\" target=\"_blank\" title=\"Tìm hiểu công nghệ sạc nhanh VOOC\" type=\"Tìm hiểu công nghệ sạc nhanh VOOC\">công nghệ VOOC</a></h3><p>OPPO F11 Pro được nâng cấp công nghệ <a href=\"https://www.thegioididong.com/dtdd?f=sac-pin-nhanh\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại có tính năng sạc pin nhanh\">sạc nhanh</a> VOOC 3.0 giúp máy có thể được sạc đầy chỉ trong vòng 80 phút.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/202703/oppo-f11-pro-128gb-tgdd-10.jpg\" onclick=\"return false;\"><img alt=\"Thời lượng pin của điện thoại OPPO F11 Pro 128GB chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-10.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-10.jpg\" title=\"Thời lượng pin của điện thoại OPPO F11 Pro 128GB chính hãng\"></a></p><p>Với sự nâng cấp này sẽ cho máy sạc nhanh hơn 20% so với \"đàn em\" OPPO F9 đã ra mắt trước đó.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/202703/oppo-f11-pro-128gb-tgdd-9.jpg\" onclick=\"return false;\"><img alt=\"Thời lượng pin của điện thoại OPPO F11 Pro 128GB chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-9.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-9.jpg\" title=\"Thời lượng pin của điện thoại OPPO F11 Pro 128GB chính hãng\"></a></p><p>Viên pin dung lượng lên tới 4000 mAh hứa hẹn cũng sẽ cho bạn thoải mái sử dụng cả ngày mà không cần lo lắng về việc phải sạc pin.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/202703/oppo-f11-pro-128gb-tgdd-8.jpg\" onclick=\"return false;\"><img alt=\"Thời lượng pin của điện thoại OPPO F11 Pro 128GB chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-8.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-8.jpg\" title=\"Thời lượng pin của điện thoại OPPO F11 Pro 128GB chính hãng\"></a></p><p>Đồng thời, với 5 lớp bảo vệ trong công nghệ VOOC, công nghệ sạc nhanh của OPPO đảm bảo sự an toàn cho người dùng.</p></article>', 14, 12700000, 0, '', '', '2019-06-23 20:57:41', 1, '2019-06-23 20:57:41', 1, 1),
(31, 10, 'OPPO F11 Pro 64GB', 'oppo-f11-pro-64gb', 'oppo-f11-pro-64gb.jpg', '<h2><a href=\"https://www.thegioididong.com/dtdd/oppo-f11-pro\" target=\"_blank\" title=\"Tham khảo điện thoại OPPO F11 Pro chính hãng\">OPPO F11 Pro</a> là chiếc <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng smartphone chính hãng tại Thegioididong.com\" type=\"Tham khảo các dòng smartphone chính hãng tại Thegioididong.com\">smartphone</a> mới nhất thuộc dòng F-series - dòng sản phẩm được rất nhiều người dùng yêu thích, máy mang trong mình sự thay đổi hoàn toàn về thiết kế và đặc biệt sở hữu những tính năng độc đáo.</h2><h3>Thiết kế độc đáo, gây thu hút mọi người</h3><p>Chắc chắn bạn sẽ bị \"mê hoặc\" bởi vẻ ngoài của OPPO F11 Pro khi <a href=\"https://www.thegioididong.com/dtdd-oppo\" target=\"_blank\" title=\"Tham khảo điện thoại OPPO chính hãng\">OPPO</a> đã khoác lên máy thiết kế mặt lưng bóng bẩy cùng hiệu ứng chuyển màu độc đáo hứa hẹn sẽ khiến giới trẻ \"phát cuồng\".</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/198791/oppo-f11-pro2.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế của điện thoại OPPO F11 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/198791/oppo-f11-pro2.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/198791/oppo-f11-pro2.jpg\" title=\"Thiết kế của điện thoại OPPO F11 Pro chính hãng\"></a></p><p>Phần viền máy được làm bo cong ôm sát cung cấp khả năng trải nghiệm cầm nắm thoải mái, dù điện thoại có kích thước khá lớn.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/198791/oppo-f11-pro3.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế của điện thoại OPPO F11 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/198791/oppo-f11-pro3.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/198791/oppo-f11-pro3.jpg\" title=\"Thiết kế của điện thoại OPPO F11 Pro chính hãng\"></a></p><h3>Cơ chế camera nhô ra thụt vào độc đáo</h3><p>OPPO F11 Pro sở hữu camera trước độ phân giải 16 MP có thể với cơ chế mới rất ấn tượng.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/198791/oppo-f11-pro1.jpg\" onclick=\"return false;\"><img alt=\"Camera trước selfie của điện thoại OPPO F11 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/198791/oppo-f11-pro1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/198791/oppo-f11-pro1.jpg\" title=\"Camera trước selfie của điện thoại OPPO F11 Pro chính hãng\"></a></p><p>OPPO gọi đây là cơ chế pop-up (bật lên), lúc nào cần thì camera sẽ tự động được bật lên và sẽ ẩn đi khi không sử dụng.</p><p>Điều này mang lại cho bạn một diện tích hiển thị chiếm tới 90.9% mặt trước cho bạn một trải nghiệm màn hình liền mạch và thoáng đãng.</p><p>Chế độ làm đẹp bằng AI hay những công nghệ mà những chiếc smartphone OPPO đã mang đến thì bạn hoàn toàn có thể tự tin với khả năng selfie của OPPO F11 Pro.</p><h3>Công nghệ <a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-ve-cong-nghe-sac-nhanh-vooc-cua-oppo-918283\" target=\"_blank\" title=\"Tìm hiểu công nghệ sạc nhanh VOOC của OPPO \" type=\"Tìm hiểu công nghệ sạc nhanh VOOC của OPPO \">sạc nhanh VOOC</a> 3.0 độc quyền</h3><p>OPPO sở hữu riêng cho mình công nghệ sạc nhanh VOOC cực kỳ ấn tượng và trên F11 Pro thì đã được nâng cấp lên phiên bản 3.0.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/198791/oppo-f11-pro4.jpg\" onclick=\"return false;\"><img alt=\"Thời lượng pin của điện thoại OPPO F11 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/198791/oppo-f11-pro4.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/198791/oppo-f11-pro4.jpg\" title=\"Thời lượng pin của điện thoại OPPO F11 Pro chính hãng\"></a></p><p>Viên pin dung lượng 4.000 mAh trên OPPO F11 Pro có thể giúp bạn thoải mái sử dụng lên tới gần 2 ngày.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/198791/oppo-f11-pro5.jpg\" onclick=\"return false;\"><img alt=\"Thời lượng pin của điện thoại OPPO F11 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/198791/oppo-f11-pro5.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/198791/oppo-f11-pro5.jpg\" title=\"Thời lượng pin của điện thoại OPPO F11 Pro chính hãng\"></a></p><p>Và nhờ sự hỗ trợ của sạc nhanh VOOC 3.0 thì bạn chỉ mất 40 phút để nạp đầy năng lượng cho viên pin dung lượng khủng của máy.</p><h3>Camera đáng đồng tiền bát gạo</h3><p>Đã rất lâu rồi OPPO mới lại đem lên chiếc smartphone của mình camera chính với độ phân giải lên tới 48 MP.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/198791/oppo-f11-pro6.jpg\" onclick=\"return false;\"><img alt=\"Camera sau của điện thoại OPPO F11 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/198791/oppo-f11-pro6.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/198791/oppo-f11-pro6.jpg\" title=\"Camera sau của điện thoại OPPO F11 Pro chính hãng\"></a></p><p>Ngoài ra máy cũng có thêm một camera 5 MP hỗ trợ chụp ảnh xóa phông hứa hẹn sẽ cho bạn những bức ảnh để \"sống ảo vô cùng đẹp mắt\".</p><p>OPPO tuyên bố camera trên smartphone mới của họ có khả năng chụp trong điều kiện thiếu sáng ấn tượng với chế độ Ultra Night Mode.</p><h3>Hiệu năng khá trong phân khúc</h3><p>Cung cấp sức mạnh cho chiếc smartphone này chính là con chip <a href=\"https://www.thegioididong.com/tin-tuc/mediatek-helio-p70-ra-mat-hieu-suat-tot-hon-13--cai-tien-ai-1126674\" target=\"_blank\" title=\"Tìm hiểu về dòng chip Helio P70\" type=\"Tìm hiểu về dòng chip Helio P70\">MediaTek Helio P70 8 nhân</a> đủ để trải nghiệm các dòng game hot: PUBG, Liên Quân ở mức cấu hình tầm trung và đặc biệt máy sở hữu tới 6 GB RAM.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/198791/oppo-f11-pro7.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình của điện thoại OPPO F11 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/198791/oppo-f11-pro7.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/198791/oppo-f11-pro7.jpg\" title=\"Cấu hình của điện thoại OPPO F11 Pro chính hãng\"></a></p><p>Với dung lượng RAM khủng như vậy thì bạn có thể tự tin sử dụng nhiều ứng dụng cùng lúc mà máy vẫn chuyển qua lại một cách mượt mà.</p><p>Một điểm cộng khác là máy chạy sẵn <a href=\"https://www.thegioididong.com/hoi-dap/top-5-tinh-nang-moi-duoc-yeu-thich-nhat-tren-and-1107119\" target=\"_blank\" title=\"Tìm hiểu Android 9.0 (Pie)\">Android 9.0 (Pie)</a> ngay khi bán ra cho bạn nhiều trải nghiệm mới mẻ.</p></article>', 145, 5900000, 6680000, '', '', '2019-06-23 20:57:43', 1, '2019-06-23 20:57:43', 1, 1),
(32, 10, 'OPPO F11', 'oppo-f11', 'oppo-f11.jpg', '<h2><a href=\"https://www.thegioididong.com/dtdd/oppo-f11\" target=\"_blank\" title=\"Tham khảo điện thoại OPPO F11 chính hãng\">OPPO F11</a> gây ấn tượng với người dùng bởi thiết kế <a href=\"https://www.thegioididong.com/dtdd?f=man-hinh-tran-vien\" target=\"_blank\" title=\"Tham khảo giá điện thoại màn hình tràn viền tại Thegioididong.com\" type=\"Tham khảo giá điện thoại màn hình tràn viền tại Thegioididong.com\">màn hình tràn viền</a> hình giọt nước và camera sau khủng đến 48 MP.</h2><h3>Thiết kế màn hình tràn viền thực thụ</h3><p>Với các nhà sản xuất <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại chính hãng\">smartphone</a> thì màn hình giọt nước để chứa camera selfie đang là xu hướng mới mẻ và vẫn tiếp tục phát triển.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/199801/oppo-f11-tgdd-1-1.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại OPPO F11 - Thiết kế tổng thể\" src=\"https://cdn.tgdd.vn/Products/Images/42/199801/oppo-f11-tgdd-1-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/199801/oppo-f11-tgdd-1-1.jpg\" title=\"Điện thoại OPPO F11 - Thiết kế tổng thể\"></a></p><p>Tuy nhiên với OPPO F11, màn hình giọt nước đã được tối ưu lại, giúp nhỏ gọn hơn, đem lại màn hình tràn viền hoàn hảo.</p><p>OPPO F11 có bộ khung viền cũng được bo cong để thoải mái cầm nắm trên tay mà không cho cảm giác cấn tay, khó chịu.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/199801/oppo-f11-tgdd-10-1.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại OPPO F11 - Thiết kế màn hình tràn viền giọt nước\" src=\"https://cdn.tgdd.vn/Products/Images/42/199801/oppo-f11-tgdd-10-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/199801/oppo-f11-tgdd-10-1.jpg\" title=\"Điện thoại OPPO F11 - Thiết kế màn hình tràn viền giọt nước\"></a></p><p>Mặt sau của OPPO F11 có hiệu ứng đổi màu đẹp mắt, bạn có thể thấy các màu như tím, hay xanh trên chiếc smartphone này cho dù nhìn từ hướng nào.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/199801/oppo-f11-tgdd-3-1.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại OPPO F11 - Thiết kế mặt lưng\" src=\"https://cdn.tgdd.vn/Products/Images/42/199801/oppo-f11-tgdd-3-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/199801/oppo-f11-tgdd-3-1.jpg\" title=\"Điện thoại OPPO F11 - Thiết kế mặt lưng\"></a></p><h3>Camera sau khủng đến 48 MP</h3><p>Chiếc <a href=\"https://www.thegioididong.com/dtdd-oppo\" target=\"_blank\" title=\"Tham khảo điện thoại OPPO chính hãng\">điện thoại OPPO</a> sở hữu một camera selfie 16 MP chất lượng ngay điểm giọt nước trên màn hình.</p><p>Tất nhiên camera này cũng được tích hợp <a href=\"https://www.thegioididong.com/hoi-dap/cong-nghe-selfie-ai-beauty-la-gi-1049958\" target=\"_blank\" title=\"Tìm hiểu công nghệ A.I Beauty\" type=\"Tìm hiểu công nghệ A.I Beauty\">công nghệ AI</a> và nhiều chế độ làm đẹp để bạn luôn có được những hình ảnh đẹp nhất, chia sẻ với bạn bè.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/199801/oppo-f11-tgdd-16.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại OPPO F11 - Chế độ làm đẹp AI\" src=\"https://cdn.tgdd.vn/Products/Images/42/199801/oppo-f11-tgdd-16.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/199801/oppo-f11-tgdd-16.jpg\" title=\"Điện thoại OPPO F11 - Chế độ làm đẹp AI\"></a></p><p>Camera chính của máy có độ phân giải lớn 48 MP kết hợp với một camera 5 MP có nhiệm vụ đo độ sâu trường ảnh hỗ trợ người dùng trong chụp ảnh xóa phông.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/199801/oppo-f11-tgdd-4-1.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại OPPO F11 - Cụm camera kép mặt sau\" src=\"https://cdn.tgdd.vn/Products/Images/42/199801/oppo-f11-tgdd-4-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/199801/oppo-f11-tgdd-4-1.jpg\" title=\"Điện thoại OPPO F11 - Cụm camera kép mặt sau\"></a></p><p>Máy cũng có chế độ chụp ảnh ban đêm chuyên dụng cho phép bạn có các bức ảnh sáng hơn, bớt nhiễu hơn và sắc nét dù là cầm tay.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/199801/oppo-f11-tgdd-17.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại OPPO F11 - Ảnh chụp ban đêm\" src=\"https://cdn.tgdd.vn/Products/Images/42/199801/oppo-f11-tgdd-17.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/199801/oppo-f11-tgdd-17.jpg\" title=\"Điện thoại OPPO F11 - Ảnh chụp ban đêm\"></a></p><h3>Màn hình lớn, hiển thị sắc nét</h3><p>OPPO F11 sở hữu màn hình với kích thước lớn 6.5 inch, độ phân giải <a href=\"https://www.thegioididong.com/tin-tuc/do-phan-giai-man-hinh-qhd-hd-fullhd-2k-4k-la-gi--592178#fullhd\" target=\"_blank\" title=\"Tìm hiểu độ phân giải màn hình Full HD+\" type=\"Tìm hiểu độ phân giải màn hình Full HD+\">Full HD+</a>, tấm nền <a href=\"https://www.thegioididong.com/tin-tuc/loai-man-hinh-tft-lcd-amoled-la-gi--592346#ltpslcd\" target=\"_blank\" title=\"Tìm hiểu tấm nền LTPS LCD\">LTPS LCD</a> và tỷ lệ hiển thị đạt khoảng 85%, mức rất cao trong phân khúc này.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/199801/oppo-f11-tgdd-2-1.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại OPPO F11 - Màn hình giọt nước\" src=\"https://cdn.tgdd.vn/Products/Images/42/199801/oppo-f11-tgdd-2-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/199801/oppo-f11-tgdd-2-1.jpg\" title=\"Điện thoại OPPO F11 - Màn hình giọt nước\"></a></p><h3>Hiệu năng mượt mà trong hầu hết điều kiện</h3><p>Cung cấp sức mạnh cho F11 là con chip <a href=\"https://www.thegioididong.com/tin-tuc/mediatek-helio-p70-ra-mat-hieu-suat-tot-hon-13--cai-tien-ai-1126674\" target=\"_blank\" title=\"Tìm hiểu về dòng chip Helio P70\" type=\"Tìm hiểu về dòng chip Helio P70\">MediaTek Helio P70</a> 8 nhân, bộ nhớ RAM lên tới 6 GB, ROM 64 GB.</p><p>Với cấu hình như vậy, OPPO F11 có thể chạy đa nhiệm cùng lúc nhiều ứng dụng, xử lý tốt các tác vụ nặng, kể cả những game đòi hỏi nhiều sức mạnh đồ họa như PUBG hay Liên Quân ở mức cấu hình trung.</p><h2><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/199801/oppo-f11-tgdd-6-1.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại OPPO F11 - Giao diện ColorOS\" src=\"https://cdn.tgdd.vn/Products/Images/42/199801/oppo-f11-tgdd-6-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/199801/oppo-f11-tgdd-6-1.jpg\" title=\"Điện thoại OPPO F11 - Giao diện ColorOS\"></a></h2><p>OPPO F11 đang chạy phiên bản ColorOS 6 dựa trên <a href=\"https://www.thegioididong.com/hoi-dap/top-5-tinh-nang-moi-duoc-yeu-thich-nhat-tren-and-1107119\" target=\"_blank\" title=\"Tìm hiểu Android 9.0 (Pie)\">Android 9.0 (Pie)</a> với rất nhiều bổ sung mới từ thanh thông báo được thiết kế lại, điều hướng cử chỉ, quản lý thời gian sử dụng...</p><h3>Dung lượng pin thoải mái sử dụng cả ngày</h3><p>OPPO trang bị một viên pin có dung lượng cao 4.020 mAh trên điện thoại để đảm bảo bạn có đủ năng lượng vượt qua những ngày bận rộn nhất.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/199801/oppo-f11-tgdd-9-1.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại OPPO F11 - Cổng sạc trên máy\" src=\"https://cdn.tgdd.vn/Products/Images/42/199801/oppo-f11-tgdd-9-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/199801/oppo-f11-tgdd-9-1.jpg\" title=\"Điện thoại OPPO F11 - Cổng sạc trên máy\"></a></p><p>Tương tự như nhiều sản phẩm khác của hãng, OPPO F11 cũng hỗ trợ <a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-ve-cong-nghe-sac-nhanh-vooc-cua-oppo-918283\" target=\"_blank\" title=\"Tìm hiểu công nghệ sạc nhanh VOOC\" type=\"Tìm hiểu công nghệ sạc nhanh VOOC\">công nghệ sạc nhanh VOOC</a>.</p></article>', 41, 20500000, 0, '', '', '2019-06-23 20:57:44', 1, '2019-06-23 20:57:44', 1, 1),
(33, 10, 'OPPO F9 6GB', 'oppo-f9-6gb', 'oppo-f9-6gb.jpg', '<h2>Là chiếc <a href=\"https://www.thegioididong.com/dtdd-oppo\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại OPPO tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại OPPO tại Thegioididong.com\">điện thoại OPPO</a> được nâng cấp cấu hình, cụ thể là RAM lên tới 6 GB, <a href=\"https://www.thegioididong.com/dtdd/oppo-f9-6gb\" target=\"_blank\" title=\"Chi tiết điện thoại OPPO F9 6GB tại Thegioididong.com\" type=\"Chi tiết điện thoại OPPO F9 6GB tại Thegioididong.com\">OPPO F9 6GB</a> còn trang bị nhiều tính năng đột phá như sở hữu công nghệ sạc VOOC mới nhất, mặt lưng chuyển màu độc đáo, màn hình tràn viền giọt nước và camera chụp chân dung tích hợp trí tuệ nhân tạo A.I hoàn hảo.</h2><h3>Cấu hình mạnh mẽ với RAM lên tới 6 GB và chip <a href=\"https://www.thegioididong.com/tin-tuc/mediatek-gioi-thieu-helio-p60-tai-mwc-2018-tap-trung-ai-va-hieu-suat-1069899\" target=\"_blank\" title=\"Tham khảo chip Helio P60 trên điện thoại\" type=\"Tham khảo chip Helio P60 trên điện thoại\">Helio P60</a></h3><p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Files/2018/08/21/1111299/unboxoppof9_011_800x450.jpg\" onclick=\"return false;\"><img alt=\"Đánh giá cấu hình điện thoại OPPO F9 6GB\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Files/2018/08/21/1111299/unboxoppof9_011_800x450.jpg\" title=\"Đánh giá cấu hình điện thoại OPPO F9 6GB\"></a></p><p>Cung cấp sức mạnh cho máy là RAM lên tới 6 GB cùng chip Helio P60. Với cấu hình này máy chiến tốt các thể loại game đang đình đám hiện nay như Liên Quân hay Pubg.</p><p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Files/2018/08/15/1109958/oppof9-30_800x450_800x450.jpg\" onclick=\"return false;\"><img alt=\"Chơi game trên điện thoại OPPO F9 6GB\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Files/2018/08/15/1109958/oppof9-30_800x450_800x450.jpg\" title=\"Chơi game trên điện thoại OPPO F9 6GB\"></a></p><p>Ngoài ra bộ nhớ trong rộng lớn lên tới 64 GB cũng giúp bạn thoải mái lưu trữ mà không hề lo lắng về vấn đề đầy bộ nhớ.</p><p><div class=video allow=\"autoplay; encrypted-media\" allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"https://www.youtube.com/embed/RV1Q0LmrMTk\" width=\"560\"></div></p><h3>Trang bị <a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-ve-cong-nghe-sac-nhanh-vooc-cua-oppo-918283\" target=\"_blank\" title=\"Tìm hiểu công nghệ sạc nhanh VOOC của OPPO\" type=\"Tìm hiểu công nghệ sạc nhanh VOOC của OPPO\">sạc nhanh VOOC</a> cao cấp</h3><p>Trong những giây phút gay cấn như chơi game điện thoại báo hết pin hay sáng dậy đi làm nhưng phát hiện quên sạc pin thì bộ sạc của OPPO F9 sẽ đem lại sự cứu trợ ngay lập tức.</p><p>Với sạc VOOC 5V/AA, tốc độ sạc trở nên nhanh chóng so với các bộ sạc thông thường.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/182153/dtdd-oppo-f9-3.jpg\" onclick=\"return false;\"><img alt=\"OPPO F9 6GB hỗ trợ sạc nhanh VOOC\" src=\"https://cdn.tgdd.vn/Products/Images/42/182153/dtdd-oppo-f9-3.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/182153/dtdd-oppo-f9-3.jpg\" title=\"OPPO F9 6GB hỗ trợ sạc nhanh VOOC\"></a></p><p>Cùng công nghệ mới sử dụng \"điện áp thấp và cường độ dòng điện cao\", tích hợp 5 cấp độ an toàn độc quyền của hãng, bộ sạc của OPPO F9 vừa giữ được tốc độ sạc nhanh, vừa đem lại sự an toàn tuyệt đối cho người sử dụng, kể cả khi vừa xài vừa chơi game.</p><h3>Màn hình tràn viền giọt nước đầu tiên của OPPO</h3><p>OPPO F9 đem lại cho người dùng một chiếc <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại đang kinh doanh tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại đang kinh doanh tại Thegioididong.com\">điện thoại</a> đầu tiên có thiết kế khung viền hình giọt nước, thay vì thiết kế tai thỏ đã dần trở nên phổ biến.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/182153/oppo-f9-red-1.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại OPPO F9 6GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/182153/oppo-f9-red-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/182153/oppo-f9-red-1.jpg\" title=\"Màn hình điện thoại OPPO F9 6GB\"></a></p><p>Diện tích trải nghiệm toàn màn hình rộng hơn, xem phim hay chơi game đều cảm thấy thoải mái và không còn cảm giác mỏi mắt.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/182153/oppo-f9-red-6.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại OPPO F9 6GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/182153/oppo-f9-red-6.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/182153/oppo-f9-red-6.jpg\" title=\"Màn hình điện thoại OPPO F9 6GB\"></a></p><h3>Thiết kế độc đáo với sự chuyển màu phá cách</h3><p>Những mặt lưng đơn sắc đã quá quen thuộc, thì thiết kế mới của OPPO F9 như một sự phá cách trong phân khúc giá với mức giá hấp dẫn.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/182153/oppo-f9-red-7.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại OPPO F9 6GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/182153/oppo-f9-red-7.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/182153/oppo-f9-red-7.jpg\" title=\"Thiết kế điện thoại OPPO F9 6GB\"></a></p><p>Mỗi thao tác sử dụng, mặt lưng lại chuyển những sắc màu đậm nhạt khác nhau. Đây là dòng F đầu tiên được OPPO ưu ái trang bị một thiết kế cao cấp và đem lại sự thu hút đặc biệt.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/182153/oppo-f9-red-3.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại OPPO F9 6GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/182153/oppo-f9-red-3.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/182153/oppo-f9-red-3.jpg\" title=\"Thiết kế điện thoại OPPO F9 6GB\"></a></p><h3>Đột phá chụp ảnh chân dung với A.I Beauty 2.1</h3><p>Xu hướng selfie và chia sẻ trên các phương tiện xã hội đã trở thành một thói quen không thể thiếu. Hiểu được điều đó tính năng camera của OPPO F9 luôn được cải thiện.</p><p>Camera trước của máy có độ phân giải 25 MP, tích hợp chế độ chụp ảnh chân dung cùng các hiệu ứng ánh sáng tuỳ chỉnh và công nghệ trí tuệ nhân tạo A.I Beauty 2.1.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/182153/oppo-f9-xanh-3-1.jpg\" onclick=\"return false;\"><img alt=\"Camera selfie điện thoại OPPO F9 6GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/182153/oppo-f9-xanh-3-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/182153/oppo-f9-xanh-3-1.jpg\" title=\"Camera selfie điện thoại OPPO F9 6GB\"></a></p><p>Nhờ vậy ảnh chụp selfie của bạn trở nên tinh tế, như được các chuyên gia trang điểm chăm chút với ánh sáng hài hoà nhưng vẫn rất nghệ thuật.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/182153/oppo-f9-red-5.jpg\" onclick=\"return false;\"><img alt=\"Camera điện thoại OPPO F9 6GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/182153/oppo-f9-red-5.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/182153/oppo-f9-red-5.jpg\" title=\"Camera điện thoại OPPO F9 6GB\"></a></p><p>Camera kép mặt sau gồm một ống kính chính 16 MP + ống kính phụ 2 MP có khả năng xoá phông tốt, làm nổi bật chủ thể cùng các đường nét trên khuôn mặt.</p><p><div class=video allow=\"autoplay; encrypted-media\" allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"https://www.youtube.com/embed/6xvC50wuDQ0\" width=\"560\"></div></p><h3>Nhiều tính năng hỗ trợ đắt giá</h3><p>Ngoài bảo mật vân tay giúp mở máy nhanh chóng thiết kế ở mặt lưng, OPPO F9 còn tích hợp khả năng nhận diện khuôn mặt mở máy hiện đại và cao cấp.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/182153/oppo-f9-red-4.jpg\" onclick=\"return false;\"><img alt=\"Cảm biến vân tay mặt lưng điện thoại OPPO F9 6GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/182153/oppo-f9-red-4.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/182153/oppo-f9-red-4.jpg\" title=\"Cảm biến vân tay mặt lưng điện thoại OPPO F9 6GB\"></a></p><p>Hay Google Lens nhận biết được văn bản, hình ảnh hiển thị, thương hiệu,... như một bách khoa toàn thư thông minh giúp bạn giải đáp được những thắc mắc.</p></article>', 57, 24400000, 25180000, '', '', '2019-06-23 20:57:45', 1, '2019-06-23 20:57:45', 1, 1);
INSERT INTO `uml_product` (`product_id`, `product_catid`, `product_name`, `product_slug`, `product_img`, `product_detail`, `product_number`, `product_price`, `product_pricesale`, `product_metakey`, `product_metadesc`, `product_createdat`, `product_createdby`, `product_updatedat`, `product_updatedby`, `product_status`) VALUES
(34, 10, 'OPPO F9', 'oppo-f9', 'oppo-f9.jpg', '<h2>Là chiếc <a href=\"https://www.thegioididong.com/dtdd-oppo\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại OPPO tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại OPPO tại Thegioididong.com\">điện thoại OPPO</a> mới nhất sở hữu công nghệ sạc VOOC đột phá, <a href=\"https://www.thegioididong.com/dtdd/oppo-f9\" target=\"_blank\" title=\"Chi tiết điện thoại OPPO F9 tại Thegioididong.com\" type=\"Chi tiết điện thoại OPPO F9 tại Thegioididong.com\">OPPO F9</a> còn được ưu ái nhiều tính năng nổi trội như thiết kế mặt lưng chuyển màu độc đáo, màn hình tràn viền giọt nước và camera chụp chân dung tích hợp trí tuệ nhân tạo A.I hoàn hảo.</h2><h3><a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-ve-cong-nghe-sac-nhanh-vooc-cua-oppo-918283\" target=\"_blank\" title=\"Tìm hiểu công nghệ sạc nhanh VOOC của OPPO\" type=\"Tìm hiểu công nghệ sạc nhanh VOOC của OPPO\">Sạc VOOC</a> nhanh đột phá</h3><p>Trong những giây phút gay cấn như chơi game điện thoại báo hết pin hay sáng dậy đi làm nhưng phát hiện quên sạc pin thì bộ sạc của OPPO F9 sẽ đem lại sự cứu trợ ngay lập tức.</p><p>Với sạc VOOC 5V/AA, tốc độ sạc trở nên nhanh chóng so với các bộ sạc thông thường.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/182153/dtdd-oppo-f9-3.jpg\" onclick=\"return false;\"><img alt=\"OPPO F9 hỗ trợ sạc nhanh VOOC\" src=\"https://cdn.tgdd.vn/Products/Images/42/182153/dtdd-oppo-f9-3.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/182153/dtdd-oppo-f9-3.jpg\" title=\"OPPO F9 hỗ trợ sạc nhanh VOOC\"></a></p><p>Cùng công nghệ mới sử dụng \"điện áp thấp và cường độ dòng điện cao\", tích hợp 5 cấp độ an toàn độc quyền của hãng, bộ sạc của OPPO F9 vừa giữ được tốc độ sạc nhanh, vừa đem lại sự an toàn tuyệt đối cho người sử dụng, kể cả khi vừa xài vừa chơi game.</p><h3>Thiết kế độc đáo với sự chuyển màu phá cách</h3><p>Những mặt lưng đơn sắc đã quá quen thuộc, thì thiết kế mới của OPPO F9 như một sự phá cách trong phân khúc giá với mức giá hấp dẫn.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/182153/oppo-f9-red-7.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại OPPO F9\" src=\"https://cdn.tgdd.vn/Products/Images/42/182153/oppo-f9-red-7.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/182153/oppo-f9-red-7.jpg\" title=\"Thiết kế điện thoại OPPO F9\"></a></p><p>Mỗi thao tác sử dụng, mặt lưng lại chuyển những sắc màu đậm nhạt khác nhau. Đây là dòng F đầu tiên được OPPO ưu ái trang bị một thiết kế cao cấp và đem lại sự thu hút đặc biệt.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/182153/oppo-f9-red-3.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại OPPO F9\" src=\"https://cdn.tgdd.vn/Products/Images/42/182153/oppo-f9-red-3.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/182153/oppo-f9-red-3.jpg\" title=\"Thiết kế điện thoại OPPO F9\"></a></p><h3>Màn hình tràn viền giọt nước</h3><p>OPPO F9 đem lại cho người dùng một chiếc <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại đang kinh doanh tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại đang kinh doanh tại Thegioididong.com\">điện thoại</a> đầu tiên có thiết kế khung viền hình giọt nước, thay vì thiết kế tai thỏ đã dần trở nên phổ biến.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/182153/oppo-f9-red-1.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại OPPO F9\" src=\"https://cdn.tgdd.vn/Products/Images/42/182153/oppo-f9-red-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/182153/oppo-f9-red-1.jpg\" title=\"Màn hình điện thoại OPPO F9\"></a></p><p>Diện tích trải nghiệm toàn màn hình rộng hơn, xem phim hay chơi game đều cảm thấy thoải mái và không còn cảm giác mỏi mắt.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/182153/oppo-f9-red-6.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại OPPO F9\" src=\"https://cdn.tgdd.vn/Products/Images/42/182153/oppo-f9-red-6.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/182153/oppo-f9-red-6.jpg\" title=\"Màn hình điện thoại OPPO F9\"></a></p><h3>Đột phá chụp ảnh chân dung với A.I Beauty 2.1</h3><p>Xu hướng selfie và chia sẻ trên các phương tiện xã hội đã trở thành một thói quen không thể thiếu. Hiểu được điều đó tính năng camera của OPPO F9 luôn được cải thiện.</p><p>Camera trước của máy có độ phân giải 25 MP, tích hợp chế độ chụp ảnh chân dung cùng các hiệu ứng ánh sáng tuỳ chỉnh và công nghệ trí tuệ nhân tạo A.I Beauty 2.1.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/182153/oppo-f9-xanh-3-1.jpg\" onclick=\"return false;\"><img alt=\"Camera selfie điện thoại OPPO F9\" src=\"https://cdn.tgdd.vn/Products/Images/42/182153/oppo-f9-xanh-3-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/182153/oppo-f9-xanh-3-1.jpg\" title=\"Camera selfie điện thoại OPPO F9\"></a></p><p>Nhờ vậy ảnh chụp selfie của bạn trở nên tinh tế, như được các chuyên gia trang điểm chăm chút với ánh sáng hài hoà nhưng vẫn rất nghệ thuật.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/182153/oppo-f9-red-5.jpg\" onclick=\"return false;\"><img alt=\"Camera điện thoại OPPO F9\" src=\"https://cdn.tgdd.vn/Products/Images/42/182153/oppo-f9-red-5.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/182153/oppo-f9-red-5.jpg\" title=\"Camera điện thoại OPPO F9\"></a></p><p>Camera kép mặt sau với ống kính chính 16 MP và kèm theo ống kính phụ 2 MP có khả năng xoá phông tốt, làm nổi bật chủ thể cùng các đường nét trên khuôn mặt.</p><p><div class=video allow=\"autoplay; encrypted-media\" allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"https://www.youtube.com/embed/6xvC50wuDQ0\" width=\"560\"></div></p><h3>Mượt mà và ổn định với chip Helio P60</h3><p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Files/2018/08/21/1111299/unboxoppof9_011_800x450.jpg\" onclick=\"return false;\"><img alt=\"Đánh giá cấu hình điện thoại OPPO F9\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Files/2018/08/21/1111299/unboxoppof9_011_800x450.jpg\" title=\"Đánh giá cấu hình điện thoại OPPO F9\"></a></p><p>Cung cấp sức mạnh cho máy là RAM 4 GB cùng chip Helio P60. Với cấu hình này máy chiến tốt các thể loại game đang đình đám hiện nay như Liên Quân hay Pubg.</p><p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Files/2018/08/15/1109958/oppof9-30_800x450_800x450.jpg\" onclick=\"return false;\"><img alt=\"Chơi game trên điện thoại OPPO F9\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Files/2018/08/15/1109958/oppof9-30_800x450_800x450.jpg\" title=\"Chơi game trên điện thoại OPPO F9\"></a></p><p>Ngoài ra bộ nhớ trong rộng lớn lên tới 64 GB cũng giúp bạn thoải mái lưu trữ mà không hề lo lắng về vấn đề đầy bộ nhớ.</p><p><div class=video allow=\"autoplay; encrypted-media\" allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"https://www.youtube.com/embed/RV1Q0LmrMTk\" width=\"560\"></div></p><h3>Nhiều tính năng hỗ trợ đắt giá</h3><p>Ngoài bảo mật vân tay giúp mở máy nhanh chóng thiết kế ở mặt lưng, OPPO F9 còn tích hợp khả năng nhận diện khuôn mặt mở máy hiện đại và cao cấp.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/182153/oppo-f9-red-4.jpg\" onclick=\"return false;\"><img alt=\"Cảm biến vân tay mặt lưng điện thoại OPPO F9\" src=\"https://cdn.tgdd.vn/Products/Images/42/182153/oppo-f9-red-4.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/182153/oppo-f9-red-4.jpg\" title=\"Cảm biến vân tay mặt lưng điện thoại OPPO F9\"></a></p><p>Hay Google Lens nhận biết được văn bản, hình ảnh hiển thị, thương hiệu,... như một bách khoa toàn thư thông minh giúp bạn giải đáp được những thắc mắc.</p></article>', 158, 8100000, 0, '', '', '2019-06-23 20:57:47', 1, '2019-06-23 20:57:47', 1, 1),
(35, 8, 'Xiaomi Mi 8', 'xiaomi-mi-8', 'xiaomi-mi-8.jpg', '<h2><a href=\"https://www.thegioididong.com/dtdd/xiaomi-mi-8\" target=\"_blank\" title=\"Chi tiết điện thoại Xiaomi Mi 8\" type=\"Chi tiết điện thoại Xiaomi Mi 8\">Xiaomi Mi 8</a> sẽ là cái tên được nhắc đến nhiều trong gia đình <a href=\"https://www.thegioididong.com/dtdd-xiaomi\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại Xiaomi chính hãng\" type=\"Tham khảo các dòng điện thoại Xiaomi chính hãng\">Xiaomi</a> khi mang trong mình đầy đủ những gì gọi là cao cấp đến từ vẻ đẹp bên ngoài cũng như phần cứng mạnh mẽ bên trong.</h2><h3>Thiết kế sang trọng</h3><p>Xiaomi luôn biết làm mới mình khi tạo ra <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại chính hãng tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại chính hãng tại Thegioididong.com\">smartphone</a> Mi 8 với thân hình long lanh và quyến rũ đến từng chi tiết nhờ sự kết hợp tinh tế giữa khung kim loại và 2 mặt kính cao cấp.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/167539/xiaomi-mi-8-3.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Mi 8\" src=\"https://cdn.tgdd.vn/Products/Images/42/167539/xiaomi-mi-8-3.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/167539/xiaomi-mi-8-3.jpg\" title=\"Xiaomi Mi 8\"></a></p><p><em>Mặt kính <a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-cong-25d-chuan-muc-moi-cho-smartphone-837574\" target=\"_blank\" title=\"cong 2.5D\" type=\"cong 2.5D\">cong 2.5D</a> đem lại sự mượt mà khi vuốt chạm</em></p><p>Mặt lưng của máy có khả năng thay đổi màu sắc ở các góc nghiêng khác nhau giúp máy trở nên nổi bật và thu hút mọi ánh nhìn.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/167539/xiaomi-mi-8-2.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Mi 8\" src=\"https://cdn.tgdd.vn/Products/Images/42/167539/xiaomi-mi-8-2.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/167539/xiaomi-mi-8-2.jpg\" title=\"Xiaomi Mi 8\"></a></p><p><em><a href=\"https://www.thegioididong.com/hoi-dap/mo-khoa-bang-dau-van-tay-645010\" target=\"_blank\" title=\"Cảm biến vân tay\" type=\"Cảm biến vân tay\">Cảm biến vân tay</a> được đặt ở vị trí thuận tiện, dễ dàng mở khóa</em></p><p>Sắc sảo không kém khi mặt trước của máy là một không gian hiển thị rộng lớn với điểm nhấn là chiếc tai thỏ \"dễ thương\" tạo cho bạn cảm giác vừa thân thuộc nhưng cũng đầy phá cách.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/167539/xiaomi-mi-8-11.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Mi 8\" src=\"https://cdn.tgdd.vn/Products/Images/42/167539/xiaomi-mi-8-11.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/167539/xiaomi-mi-8-11.jpg\" title=\"Xiaomi Mi 8\"></a></p><p><em>Máy thon gọn vừa vặn với lòng bàn tay</em></p><p>Các góc cạnh của máy được bo cong mềm mại đem lại khả năng cầm nắm chắc chắn và ít sợ bị trơn trượt mỗi khi tay bạn ra mồ hôi.</p><h3>Màn hình sắc nét</h3><p>Được tối ưu với tỷ lệ màn hình 18:9 đem đến cho Xiaomi Mi 8 một diện tích hiển thị lên đến 83.8% trên kích thước 6.21 inch đảm bảo cho bạn có một không gian trải nghiệm tương đối thoải mái.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/167539/xiaomi-mi-8-8.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Mi 8\" src=\"https://cdn.tgdd.vn/Products/Images/42/167539/xiaomi-mi-8-8.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/167539/xiaomi-mi-8-8.jpg\" title=\"Xiaomi Mi 8\"></a></p><p><em>Không gian trải nghiệm được tối ưu</em></p><p>Nhờ được trang bị tấm nền <a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-super-amoled-la-gi-905770\" target=\"_blank\" title=\"Super AMOLED\" type=\"Super AMOLED\">Super AMOLED</a> cùng độ phân giải Full HD+ giúp hình ảnh luôn sống động, rực rỡ và nịnh mắt để bạn có thể đắm chìm trong những thước phim chất lượng cao.</p><h3>Hiệu năng hàng đầu</h3><p>Bộ não trung tâm của máy chính là con chip <a href=\"https://www.thegioididong.com/tin-tuc/chip-snapdragon-845-manh-hon-25-tap-trung-ai-bao-mat-cao-1048166\" target=\"_blank\" title=\"Snapdragon 845\" type=\"Snapdragon 845\">Snapdragon 845</a> với một hiệu năng mạnh mẽ được tối ưu về khả năng xử lý hình ảnh, đồ họa trong game hay các tác vụ khác trở nên mượt mà và nhanh chóng.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/167539/xiaomi-mi-8-6.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Mi 8\" src=\"https://cdn.tgdd.vn/Products/Images/42/167539/xiaomi-mi-8-6.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/167539/xiaomi-mi-8-6.jpg\" title=\"Xiaomi Mi 8\"></a></p><p><em><a href=\"https://www.thegioididong.com/hoi-dap/4g-la-gi-731757\" target=\"_blank\" title=\"Hỗ trợ 4G\" type=\"Hỗ trợ 4G\">Hỗ trợ 4G</a> tốc độ cao</em></p><p>Bên cạnh đó, máy còn được trang bị RAM lên đến 6 GB khá dư giả để bạn có thể chạy đa nhiệm một cách ổn định hay thoải mái lưu trữ với bộ nhớ trong 64 GB.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/167539/xiaomi-mi-8-9.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Mi 8\" src=\"https://cdn.tgdd.vn/Products/Images/42/167539/xiaomi-mi-8-9.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/167539/xiaomi-mi-8-9.jpg\" title=\"Xiaomi Mi 8\"></a></p><p><em>Điểm số Antutu ấn tượng dễ dàng vượt qua thử thách khi chơi game</em></p><p>Không những thế, với việc được cài sẵn <a href=\"https://www.thegioididong.com/hoi-dap/co-gi-dac-biet-o-android-81-oreo-1073208\" target=\"_blank\" title=\"Android 8.1 (Oreo)\" type=\"Android 8.1 (Oreo)\">Android 8.1 (Oreo)</a> cùng giao diện MIUI 10 thông minh sẽ giúp các thao tác và trải nghiệm của bạn trở nên đơn giản và dễ dàng hơn rất nhiều.</p><h3>Camera xuất sắc</h3><p>Ngoài một hiệu năng ấn tượng, thì camera của máy cũng được Xiaomi quan tâm một cách đặc biệt để trở thành một trong những smartphone chụp ảnh đẹp nhất hiện nay với 105 điểm DxOMark.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/167539/xiaomi-mi-8-4.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Mi 8\" src=\"https://cdn.tgdd.vn/Products/Images/42/167539/xiaomi-mi-8-4.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/167539/xiaomi-mi-8-4.jpg\" title=\"Xiaomi Mi 8\"></a></p><p><em>Hỗ trợ <a href=\"https://www.thegioididong.com/tin-tuc/chuan-quay-phim-tren-dien-thoai-596472#4k\" target=\"_blank\" title=\"quay phim 4K 2160p@60fps\" type=\"quay phim 4K 2160p@60fps\">quay phim 4K 2160p@30fps</a></em></p><p>Một chút về thông số, máy sở hữu cụm camera kép cùng độ phân giải là 12 MP tích hợp nhiều công nghệ chụp ảnh tiên tiến nhất hiện nay.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/167539/xiaomi-mi-8-7.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Mi 8\" src=\"https://cdn.tgdd.vn/Products/Images/42/167539/xiaomi-mi-8-7.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/167539/xiaomi-mi-8-7.jpg\" title=\"Xiaomi Mi 8\"></a></p><p><em>Tích hợp đèn LED kép tăng cường độ sáng khi chụp ảnh ban đêm</em></p><p>Chất lượng ảnh chụp cho ra vô cùng ấn tượng với độ chi tiết cao, màu sắc hài hòa nhưng không kém phần rực rỡ, cùng với đó là khả năng xóa phông mềm mại.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/167539/xiaomi-mi-8-13.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Mi 8\" src=\"https://cdn.tgdd.vn/Products/Images/42/167539/xiaomi-mi-8-13.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/167539/xiaomi-mi-8-13.jpg\" title=\"Xiaomi Mi 8\"></a></p><p><em>Hình ảnh với chi tiết rõ nét, độ sáng cao</em></p><p>Độ phân giải 20 MP là thông số của cụm camera trước, hứa hẹn sẽ mang đến cho bạn một bức ảnh selfie đẹp lung linh mà không cần dùng đến các ứng dụng bên thứ 3.</p><h3>Tích hợp nhiều tính năng hiện đại</h3><p>Để trở thành một trong những kẻ dẫn đầu thì phải kể đến công nghệ <a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-ve-sac-pin-nhanh-quick-charge-30-917509\" target=\"_blank\" title=\"Tìm hiểu sạc nhanh Quick Charge 4.0\" type=\"Tìm hiểu sạc nhanh Quick Charge 4.0\">sạc nhanh Quick Charge 3.0</a> hay tính năng <a href=\"https://www.thegioididong.com/hoi-dap/mo-khoa-man-hinh-bang-khuon-mat-tren-android-646039\" target=\"_blank\" title=\"mở khóa bằng khuôn mặt\" type=\"mở khóa bằng khuôn mặt\">mở khóa bằng khuôn mặt</a> đang là xu hướng thì cũng đều có mặt trên Mi 8.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/167539/xiaomi-mi-8-10.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Mi 8\" src=\"https://cdn.tgdd.vn/Products/Images/42/167539/xiaomi-mi-8-10.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/167539/xiaomi-mi-8-10.jpg\" title=\"Xiaomi Mi 8\"></a></p><p><em>Thuận tiện với 2 SIM 2 sóng</em></p><p>Hơn nữa là các kết nối như <a href=\"https://www.thegioididong.com/tin-tuc/bluetooth5-moi-nhat-la-gi-no-co-y-nghia-the-nao-voi-chung-ta-925698\" target=\"_blank\" title=\"Bluetooth\" type=\"Bluetooth\">Bluetooth</a>, <a href=\"https://www.thegioididong.com/tin-tuc/wifi-la-gi-cai-dat-wifi-hotspot-nhu-the-nao--590309#80211ac\" target=\"_blank\" title=\"Wifi\" type=\"Wifi\">Wifi</a>, <a href=\"https://www.thegioididong.com/tin-tuc/nfc-la-gi--590273\" target=\"_blank\" title=\"NFC\" type=\"NFC\">NFC</a> thì máy đều được trang bị những chuẩn mới và hiện đại nhất.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/167539/xiaomi-mi-8-12.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Mi 8\" src=\"https://cdn.tgdd.vn/Products/Images/42/167539/xiaomi-mi-8-12.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/167539/xiaomi-mi-8-12.jpg\" title=\"Xiaomi Mi 8\"></a></p><p><em>Máy được trang bị cổng <a href=\"https://www.thegioididong.com/hoi-dap/usb-typec-chuan-muc-cong-ket-noi-moi-723760\" target=\"_blank\" title=\"USB Type C\" type=\"USB Type C\">USB Type C</a> kết nối nhanh chóng</em></p><p>Ngoài ra, với viên pin 3400 mAh tuy không lớn nhưng vẫn có thể đáp ứng cho bạn khoảng 1 ngày sử dụng với các tác vụ cơ bản hằng ngày.</p></article>', 182, 11800000, 0, '', '', '2019-06-23 20:58:18', 1, '2019-06-23 20:58:18', 1, 1),
(36, 8, 'Xiaomi Mi 9 SE', 'xiaomi-mi-9-se', 'xiaomi-mi-9-se.jpg', '<h2>Vẫn như thường lệ thì bên cạnh chiếc flagship <a href=\"https://www.thegioididong.com/dtdd/xiaomi-mi-9\" target=\"_blank\" title=\"Tham khảo điện thoại Xiaomi Mi 9 chính hãng\">Xiaomi Mi 9</a> cao cấp của mình thì Xiaomi cũng giới thiệu thêm phiên bản rút gọn là chiếc <a href=\"https://www.thegioididong.com/dtdd/xiaomi-mi-9-se\" target=\"_blank\" title=\"Tham khảo điện thoại Xiaomi Mi 9 SE chính hãng\">Xiaomi Mi 9 SE</a>. Máy vẫn sở hữu cho mình nhiều trang bị cao cấp tương tự đàn anh.</h2><h3>Thiết kế sang trọng tương tự Xiami Mi 9</h3><p>Về ngoại hình thì chiếc <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại chính hãng\">smartphone</a> Xiaomi Mi 9 SE khá tương đồng với người anh em cao cấp và bạn sẽ vẫn có một thiết kế mặt lưng với hiệu ứng đổi màu gradient đẹp mắt.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/198394/xiaomi-mi-9-se3.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế của điện thoại Xiaomi Mi 9 SE chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/198394/xiaomi-mi-9-se3.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/198394/xiaomi-mi-9-se3.jpg\" title=\"Thiết kế của điện thoại Xiaomi Mi 9 SE chính hãng\" /></a></p><p>Kích thước màn hình trên máy được rút gọn xuống chỉ còn 5.97 inch với viền màn hình được làm cong 2.5D rất hiện đại.</p><p>Máy vẫn giữ kiểu thiết kế màn hình giọt nước qua đó tăng tỉ lệ màn hình ở mặt trước lên tới 90.47%.</p><p>Các góc cạnh được bo cong mềm mại cho bạn cầm nắm thoải mái trong thời gian dài.</p><h3>Được trang bị con chip hoàn toàn mới</h3><p>Năm ngoái chiếc Xiaomi Mi 8 SE là chiếc máy đầu tiên được trang bị con chip Snapdragon 710 thì chiếc Mi 9 SE năm nay cũng là chiếc máy đầu tiên được trang bị con chip <a href=\"https://www.thegioididong.com/tin-tuc/snapdragon-712-ra-mat-hieu-nang-tang-10-so-snapdragon-710-1148674\" target=\"_blank\" title=\"Tìm hiểu chip Snapdragon 712 8 nhân 64-bit\">Snapdragon 712 8 nhân 64-bit</a>.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/198394/xiaomi-mi-9-se2.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình của điện thoại Xiaomi Mi 9 SE chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/198394/xiaomi-mi-9-se2.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/198394/xiaomi-mi-9-se2.jpg\" title=\"Cấu hình của điện thoại Xiaomi Mi 9 SE chính hãng\" /></a></p><p>Con chip mới này cho hiệu năng tốt hơn 10% so với thế hệ cũ và bạn có thể tự tin \"chiến\" mọi tựa game nặng nhất hiện nay trên Mi 9 SE.</p><p>Ngoài ra máy còn được trang bị RAM lên tới 6 GB và bộ nhớ trong 64 GB thoải mái cho bạn lưu trữ dữ liệu.</p><h3>Camera số một trong tầm giá</h3><p>Một điểm rất đáng giá trên chiếc <a href=\"https://www.thegioididong.com/dtdd-xiaomi\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại Xiaomi chính hãng\">smartphone Xiaomi</a> nàychính máy vẫn có tới 3 camera tương tự trên Mi 9.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/198394/xiaomi-mi-9-se5.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera của điện thoại Xiaomi Mi 9 SE chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/198394/xiaomi-mi-9-se5.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/198394/xiaomi-mi-9-se5.jpg\" title=\"Ảnh chụp từ camera của điện thoại Xiaomi Mi 9 SE chính hãng\" /></a></p><p>Cụm camera sau gồm cảm biến chính 48 MP cùng 2 cảm biến phụ 8 MP và 13 MP, hứa hẹn mang đến cho người dùng những bức ảnh vô cùng chất lượng.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/198394/xiaomi-mi-9-se4.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera của điện thoại Xiaomi Mi 9 SE chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/198394/xiaomi-mi-9-se4.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/198394/xiaomi-mi-9-se4.jpg\" title=\"Ảnh chụp từ camera của điện thoại Xiaomi Mi 9 SE chính hãng\" /></a></p><p>Mặt trước vẫn là camera 20 MP với chế độ làm đẹp được tích hợp sẵn thoải mái cho bạn selfie với bạn bè.</p><h3>Tối ưu tốt cho các game thủ</h3><p>Nếu bạn là một tín đồ của game mobile thì tin mừng là Xiaomi đã tích hợp cho máy công nghệ Game Turbo giúp mang lại trải nghiệm chơi game tốt hơn cho các game thủ.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/198394/xiaomi-mi-9-se1.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình của điện thoại Xiaomi Mi 9 SE chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/198394/xiaomi-mi-9-se1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/198394/xiaomi-mi-9-se1.jpg\" title=\"Cấu hình của điện thoại Xiaomi Mi 9 SE chính hãng\" /></a></p><p>Việc chơi game trên Xiaomi Mi 9 sẽ mượt mà và bạn sẽ không bị làm phiền bởi các thông báo khi đang trong \"trận chiến\".</p><h3>Cảm biến vân tay dưới màn hình</h3><p>Thật bất ngờ khi Xiaomi Mi 9 SE vẫn được Xiaomi ưu ái trang bị công nghệ bảo mật vân tay dưới màn hình tiện lợi.</p><p>Dung lượng pin của máy lên tới 3.070 mAh và đặc biệt máy còn hỗ trợ sạc nhanh công suất 18W cho bạn rút ngắn thời gian sạc pin đáng kể.</p></article>', 101, 15400000, 16180000, '', '', '2019-06-23 20:58:20', 1, '2019-06-23 20:58:20', 1, 1),
(37, 8, 'Xiaomi Mi 8 Lite 4GB/64GB', 'xiaomi-mi-8-lite-4gb-64gb', 'xiaomi-mi-8-lite-4gb-64gb.jpg', '<h2><a href=\"https://www.thegioididong.com/dtdd/xiaomi-mi-8-lite\" target=\"_blank\" title=\"Tham khảo điện thoại Xiaomi Mi 8 Lite tại Thegioididong.com\">Xiaomi Mi 8 Lite</a> có lẽ là chiếc <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại chính hãng tại Thegioididong.com\">smartphone</a> rẻ nhất của dòng Mi (dòng flagship của Xiaomi) mà vẫn giữ được cho mình thiết kế đẹp mắt và nhiều tính năng thú vị.</h2><h3>Thiết kế bắt mắt, thu hút mọi ánh nhìn</h3><p>Điểm nhấn trên chiếc <a href=\"https://www.thegioididong.com/dtdd-xiaomi\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại Xiaomi tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại Xiaomi tại Thegioididong.com\">điện thoại Xiaomi</a> này chính là phần mặt lưng với chất liệu kính với hiệu ứng gradient biến đổi màu sắc tùy theo mức độ ánh sáng chiếu vào. Nó thực sự đẹp mắt và ấn tượng mỗi khi nhìn vào.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/192317/xiaomi-mi-8-lite-9.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại Xiaomi Mi 8 Lite chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/192317/xiaomi-mi-8-lite-9.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/192317/xiaomi-mi-8-lite-9.jpg\" title=\"Thiết kế điện thoại Xiaomi Mi 8 Lite chính hãng\"></a></p><p>Ngoài ra Xiaomi cũng tối ưu cách sắp xếp phần cứng bên trong để đạt được độ mỏng chỉ 7.5mm cho điện thoại, nhằm mang lại trải nghiệm cầm nắm tốt hơn cho người dùng.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/192317/xiaomi-mi-8-lite-10.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại Xiaomi Mi 8 Lite chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/192317/xiaomi-mi-8-lite-10.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/192317/xiaomi-mi-8-lite-10.jpg\" title=\"Thiết kế điện thoại Xiaomi Mi 8 Lite chính hãng\"></a></p><h3>Camera chất lượng không kém các máy cao cấp</h3><p>Camera kép phía sau của Xiaomi Mi 8 Lite gồm ống kính chính độ phân giải 12 MP có khẩu độ f/1.9 + cảm biến chiều sâu 5 MP với khẩu độ f/2.0, có thể tùy chỉnh độ mờ phông nền và đi kèm với tính năng AI Scene Recognition.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/192317/xiaomi-mi-8-lite-15.jpg\" onclick=\"return false;\"><img alt=\"Camera sau điện thoại Xiaomi Mi 8 Lite chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/192317/xiaomi-mi-8-lite-15.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/192317/xiaomi-mi-8-lite-15.jpg\" title=\"Camera sau điện thoại Xiaomi Mi 8 Lite chính hãng\"></a></p><p>Xiaomi hứa hẹn khả năng chụp ảnh của điện thoại sẽ ấn tượng trong phân khúc với AI cải tiến, bộ xử lý ảnh chuyên dụng được tích hợp cùng với chip Snapdragon 660.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/192317/xiaomi-mi-8-lite-8.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ điện thoại Xiaomi Mi 8 Lite\" src=\"https://cdn.tgdd.vn/Products/Images/42/192317/xiaomi-mi-8-lite-8.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/192317/xiaomi-mi-8-lite-8.jpg\" title=\"Ảnh chụp từ điện thoại Xiaomi Mi 8 Lite\"></a></p><p>Mi 8 Lite có một camera selfie AI 24 MP với cảm biến Sony IMX576 và khẩu độ F/2.2, tích hợp công nghệ AI Smart Beauty thế hệ mới được Xiaomi gọi là AI Makeup Beauty, thậm chí hỗ trợ cả AI Beauty khi quay video.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/192317/xiaomi-mi-8-lite-7.jpg\" onclick=\"return false;\"><img alt=\"Ảnh selfie từ điện thoại Xiaomi Mi 8 Lite\" src=\"https://cdn.tgdd.vn/Products/Images/42/192317/xiaomi-mi-8-lite-7.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/192317/xiaomi-mi-8-lite-7.jpg\" title=\"Ảnh selfie từ điện thoại Xiaomi Mi 8 Lite\"></a></p><h3>Hiệu năng mạnh mẽ trong phân khúc</h3><p>Mi 8 Lite dùng chip Snapdragon 660 AIE (chuyên xử lý các tác vụ liên quan đến trí tuệ nhân tạo) được sản xuất trên quy trình FinFET 10 nm, kết hợp với nhân đồ họa Adreno 512.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/192317/xiaomi-mi-8-lite-12.jpg\" onclick=\"return false;\"><img alt=\"Android trên điện thoại Xiaomi Mi 8 Lite chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/192317/xiaomi-mi-8-lite-12.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/192317/xiaomi-mi-8-lite-12.jpg\" title=\"Android trên điện thoại Xiaomi Mi 8 Lite chính hãng\"></a></p><p>Máy vẫn sở hữu cho mình tới 4 GB RAM và 64 GB bộ nhớ trong mang lại trải nghiệm mượt mà với hầu hết các tác vụ hằng ngày.</p><p>Viên pin dung lượng 3.350 mAh với sạc nhanh Qualcomm Quick Charge 3.0 đủ đáp ứng cho bạn nhu cầu sử dụng trong cả ngày.</p><p><div class=video allow=\"accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"https://www.youtube.com/embed/JfJrqc9Ec4E\" width=\"560\"></div></p><h3>Màn hình tai thỏ đẹp hơn</h3><p>Giống như các người anh em của mình, Xiaomi Mi 8 Lite cũng có notch ở phía trên màn hình nhưng kích thước đã được tinh giản tới mức tối thiểu.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/192317/xiaomi-mi-8-lite-8-1.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại Xiaomi Mi 8 Lite chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/192317/xiaomi-mi-8-lite-8-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/192317/xiaomi-mi-8-lite-8-1.jpg\" title=\"Màn hình điện thoại Xiaomi Mi 8 Lite chính hãng\"></a></p><p>Mi 8 Lite có màn hình 6.26 inch Full HD+ (2.280 x 1.080 pixels), tỉ lệ khung hình 19:9.</p></article>', 163, 17900000, 0, '', '', '2019-06-23 20:58:21', 1, '2019-06-23 20:58:21', 1, 1),
(38, 8, 'Xiaomi Mi 8 Lite 6GB/128GB', 'xiaomi-mi-8-lite-6gb-128gb', 'xiaomi-mi-8-lite-6gb-128gb.jpg', '<h2>Được nhiều khách hàng đánh giá cao bởi hiệu năng mượt mà và ổn định nhờ RAM tới 6 GB, <a href=\"https://www.thegioididong.com/dtdd/xiaomi-mi-8-lite-6gb\" target=\"_blank\" title=\"Tham khảo điện thoại Xiaomi Mi 8 Lite tại Thegioididong.com\">Xiaomi Mi 8 Lite</a> còn sở hữu những ưu điểm nổi bật khác như thiết kế đẹp mắt và camera A.I hiện đại.</h2><h3>Mạnh mẽ hơn với chip Snapdragon 660 AIE và RAM 6 GB</h3><p>Mi 8 Lite dùng chip Snapdragon 660 AIE (chuyên xử lý các tác vụ liên quan đến trí tuệ nhân tạo) được sản xuất trên quy trình FinFET 10 nm, kết hợp với nhân đồ họa Adreno 512.</p><p><div class=video allow=\"accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"https://www.youtube.com/embed/JfJrqc9Ec4E\" width=\"560\"></div></p><p>Đây là phiên bản nâng cấp sở hữu tới 6 GB RAM và 128 GB bộ nhớ trong mang lại trải nghiệm mượt mà với hầu hết các tác vụ hằng ngày.</p><p>Viên pin dung lượng 3.350 mAh với sạc nhanh Qualcomm Quick Charge 3.0 đủ đáp ứng cho bạn nhu cầu sử dụng trong cả ngày.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/195576/xiaomi-mi-8-lite-6gb-4-1.jpg\" onclick=\"return false;\"><img alt=\"Giao diện Android điện thoại Xiaomi Mi 8 Lite 6GB chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/195576/xiaomi-mi-8-lite-6gb-4-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/195576/xiaomi-mi-8-lite-6gb-4-1.jpg\" title=\"Giao diện Android điện thoại Xiaomi Mi 8 Lite 6GB chính hãng\"></a></p><h3>Nổi bật với mặt lưng hiệu ứng chuyển màu gradient</h3><p>Điểm nhấn trên chiếc <a href=\"https://www.thegioididong.com/dtdd-xiaomi\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại Xiaomi tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại Xiaomi tại Thegioididong.com\">điện thoại Xiaomi</a> này chính là phần mặt lưng với chất liệu kính với hiệu ứng gradient biến đổi màu sắc tùy theo mức độ ánh sáng chiếu vào. Nó thực sự đẹp mắt và ấn tượng mỗi khi nhìn vào.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/195576/xiaomi-mi-8-lite-6gb-5-1.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại Xiaomi Mi 8 Lite 6GB chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/195576/xiaomi-mi-8-lite-6gb-5-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/195576/xiaomi-mi-8-lite-6gb-5-1.jpg\" title=\"Thiết kế điện thoại Xiaomi Mi 8 Lite 6GB chính hãng\"></a></p><p>Ngoài ra Xiaomi cũng tối ưu cách sắp xếp phần cứng bên trong để đạt được độ mỏng chỉ 7.5mm cho điện thoại, nhằm mang lại trải nghiệm cầm nắm tốt hơn cho người dùng.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/195576/xiaomi-mi-8-lite-6gb-6-1.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại Xiaomi Mi 8 Lite 6GB chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/195576/xiaomi-mi-8-lite-6gb-6-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/195576/xiaomi-mi-8-lite-6gb-6-1.jpg\" title=\"Thiết kế điện thoại Xiaomi Mi 8 Lite 6GB chính hãng\"></a></p><h3>Camera kép trang bị trí tuệ nhân tạo</h3><p>Camera kép phía sau của Xiaomi Mi 8 Lite gồm ống kính chính có độ phân giải 12 MP với khẩu độ f/1.9 + ống kính phụ 5 MP với khẩu độ f/2.0, có thể tùy chỉnh độ mờ phông nền và đi kèm với tính năng AI Scene Recognition.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/195576/xiaomi-mi-8-lite-6gb-11-1.jpg\" onclick=\"return false;\"><img alt=\"Camera sau điện thoại Xiaomi Mi 8 Lite 6GB chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/195576/xiaomi-mi-8-lite-6gb-11-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/195576/xiaomi-mi-8-lite-6gb-11-1.jpg\" title=\"Camera sau điện thoại Xiaomi Mi 8 Lite 6GB chính hãng\"></a></p><p>Xiaomi hứa hẹn khả năng chụp ảnh của điện thoại sẽ ấn tượng trong phân khúc với AI cải tiến, bộ xử lý ảnh chuyên dụng được tích hợp cùng với chip Snapdragon 660.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/195576/xiaomi-mi-8-lite-6gb-2-1.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera sau điện thoại Xiaomi Mi 8 Lite 6GB chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/195576/xiaomi-mi-8-lite-6gb-2-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/195576/xiaomi-mi-8-lite-6gb-2-1.jpg\" title=\"Ảnh chụp từ camera sau điện thoại Xiaomi Mi 8 Lite 6GB chính hãng\"></a></p><p>Mi 8 Lite có một camera selfie AI 24 MP với cảm biến Sony IMX576 và khẩu độ F/2.2, tích hợp công nghệ AI Smart Beauty thế hệ mới được Xiaomi gọi là AI Makeup Beauty, thậm chí hỗ trợ cả AI Beauty khi quay video.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/195576/xiaomi-mi-8-lite-6gb-1-1.jpg\" onclick=\"return false;\"><img alt=\"Ảnh selfie điện thoại Xiaomi Mi 8 Lite 6GB chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/195576/xiaomi-mi-8-lite-6gb-1-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/195576/xiaomi-mi-8-lite-6gb-1-1.jpg\" title=\"Ảnh selfie điện thoại Xiaomi Mi 8 Lite 6GB chính hãng\"></a></p><h3>Màn hình tai thỏ đẹp hơn</h3><p>Giống như các người anh em của mình, chiếc <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại chính hãng tại Thegioididong.com\">smartphone</a> này cũng có notch ở phía trên màn hình nhưng kích thước đã được tinh giản tới mức tối thiểu.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/195576/xiaomi-mi-8-lite-6gb-8-1.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại Xiaomi Mi 8 Lite 6GB chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/195576/xiaomi-mi-8-lite-6gb-8-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/195576/xiaomi-mi-8-lite-6gb-8-1.jpg\" title=\"Màn hình điện thoại Xiaomi Mi 8 Lite 6GB chính hãng\"></a></p><p>Mi 8 Lite có màn hình 6.26 inch Full HD+ (2.280 x 1.080 pixels), tỉ lệ khung hình 19:9.</p></article>', 193, 18400000, 0, '', '', '2019-06-23 20:58:22', 1, '2019-06-23 20:58:22', 1, 1),
(39, 8, 'Xiaomi Redmi Note 7 4GB/64GB', 'xiaomi-redmi-note-7-4gb-64gb', 'xiaomi-redmi-note-7-4gb-64gb.jpg', '<h2><a href=\"https://www.thegioididong.com/dtdd/xiaomi-redmi-note-7\" target=\"_blank\" title=\"Tham khảo điện thoại Xiaomi Redmi Note 7 chính hãng\">Xiaomi Redmi Note 7</a> là chiếc <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại chính hãng\">smartphone</a> giá rẻ mới được giới thiệu vào đầu năm 2019 với nhiều trang bị đáng giá như thiết kế notch giọt nước hay camera lên tới 48 MP.</h2><h3>Hiệu năng mạnh mẽ trải nghiệm game mượt mà</h3><p>Redmi Note 7 xứng đáng là một trong những chiếc smartphone có hiệu năng tốt, với điểm Antutu đo được khoảng 137586.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/167535/xiaomi-redmi-note-7-tgdd-antutu.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Redmi Note 7 4GB 64GB | Điểm Antutu\" src=\"https://cdn.tgdd.vn/Products/Images/42/167535/xiaomi-redmi-note-7-tgdd-antutu.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/167535/xiaomi-redmi-note-7-tgdd-antutu.jpg\" title=\"Xiaomi Redmi Note 7 4GB 64GB | Điểm Antutu\"></a></p><p>Nhờ được cung cấp sức mạnh bởi con chip Snapdragon 660 cùng RAM 4 GB, máy chiến mượt mà các dòng game hiện này với cấu hình tầm trung hoặc cao (tuỳ game) như Liên Quân, PUBG,...</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/167535/xiaomi-redmi-note-7-tgdd-giaodien-1.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Redmi Note 7 4GB 64GB | Giao diện Android\" src=\"https://cdn.tgdd.vn/Products/Images/42/167535/xiaomi-redmi-note-7-tgdd-giaodien-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/167535/xiaomi-redmi-note-7-tgdd-giaodien-1.jpg\" title=\"Xiaomi Redmi Note 7 4GB 64GB | Giao diện Android\"></a></p><p>Giao diện Android 9 mới được tuỳ biến đem lại cho Redmi Note 7 nhiều tính năng hỗ trợ: Chia đôi màn hình, Ứng dụng kém, Không gian thứ hai,...</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/167535/xiaomi-redmi-note-7-tgdd-giaodien-2.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Redmi Note 7 4GB 64GB | Giao diện tính năng\" src=\"https://cdn.tgdd.vn/Products/Images/42/167535/xiaomi-redmi-note-7-tgdd-giaodien-2.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/167535/xiaomi-redmi-note-7-tgdd-giaodien-2.jpg\" title=\"Xiaomi Redmi Note 7 4GB 64GB | Giao diện tính năng\"></a></p><p>Giao diện trực quan dễ sử dụng, có thể tuỳ chỉnh phím điều hướng cũng là một ưu điểm của máy.</p><h3>Thiết kế hiện đại, theo xu thế</h3><p>Chiếc <a href=\"https://www.thegioididong.com/dtdd-xiaomi\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại Xiaomi chính hãng\">điện thoại Xiaomi</a> mới là một sự lột xác hoàn toàn về thiết kế so với các dòng Redmi trước đây.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/167535/xiaomi-redmi-note-7-tgdd-4.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Redmi Note 7 4GB 64GB | Thiết kế\" src=\"https://cdn.tgdd.vn/Products/Images/42/167535/xiaomi-redmi-note-7-tgdd-4.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/167535/xiaomi-redmi-note-7-tgdd-4.jpg\" title=\"Xiaomi Redmi Note 7 4GB 64GB | Thiết kế\"></a></p><p>Máy sở hữu cho mình mặt lưng bằng kính phối màu gradient, mang lại cho máy cảm giác cao cấp và sang trọng hơn rất nhiều.</p><p>4 góc máy được tăng cường độ bền đáng kể giúp chống vỡ kính gấp 4 lần so với các smartphone khác trên thị trường hiện nay.</p><h3>Màn hình giọt nước thích mắt</h3><p>Xiaomi Redmi Note 7 sở hữu màn hình 6.3 inch của có tỷ lệ khung hình 19.5:9, <a href=\"https://www.thegioididong.com/tin-tuc/do-phan-giai-man-hinh-qhd-hd-fullhd-2k-4k-la-gi--592178#fullhd\" target=\"_blank\" title=\"Tìm hiểu độ phân giải Full HD+\" type=\"Tìm hiểu độ phân giải Full HD+\">độ phân giải Full HD+</a> (2.340 x 1.080 pixel).</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/167535/xiaomi-redmi-note-7-tgdd-1.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Redmi Note 7 4GB 64GB | Màn hình\" src=\"https://cdn.tgdd.vn/Products/Images/42/167535/xiaomi-redmi-note-7-tgdd-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/167535/xiaomi-redmi-note-7-tgdd-1.jpg\" title=\"Xiaomi Redmi Note 7 4GB 64GB | Màn hình\"></a></p><p>Thiết kế màn hình theo kiểu giọt nước giúp máy gây ấn tượng cho người dùng ngay từ cái nhìn đầu tiên.</p><p>Độ sáng của máy cũng khá cao lên tới 450 nits giúp bạn dễ dàng xem nội dung dưới ánh sáng mặt trời và bảo vệ mắt.</p><h3>Camera chất lượng tốt trong tầm giá</h3><p>Xiaomi Redmi Note 7 trở thành chiếc điện thoại có camera \"khủng\" giá rẻ nhất trên thị trường hiện nay với \"số chấm\" lên tới  48 MP.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/167535/xiaomi-redmi-note-7-camera-tgdd-4.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Redmi Note 7 4GB 64GB | Ảnh chụp từ camera\" src=\"https://cdn.tgdd.vn/Products/Images/42/167535/xiaomi-redmi-note-7-camera-tgdd-4.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/167535/xiaomi-redmi-note-7-camera-tgdd-4.jpg\" title=\"Xiaomi Redmi Note 7 4GB 64GB | Ảnh chụp từ camera\"></a></p><p>Đi kèm cảm biến phụ 5 MP dùng để lấy dữ liệu chiều sâu trong chế độ chụp ảnh chân dung.</p><p>Camera của máy còn được hỗ trợ bởi AI giúp bạn có được những bức ảnh đẹp mà không cần phải qua hậu kỳ.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/167535/xiaomi-redmi-note-7-camera-tgdd-2.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Redmi Note 7 4GB 64GB | Ảnh chụp chân dung\" src=\"https://cdn.tgdd.vn/Products/Images/42/167535/xiaomi-redmi-note-7-camera-tgdd-2.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/167535/xiaomi-redmi-note-7-camera-tgdd-2.jpg\" title=\"Xiaomi Redmi Note 7 4GB 64GB | Ảnh chụp chân dung\"></a></p><p>Camera trước cũng được đầu tư mạnh mẽ với độ phân giải 13 MP cũng được tích hợp sẵn chế độ làm đẹp hứa hẹn sẽ không làm các chị em phải thất vọng.</p><h3>Dung lượng pin khủng</h3><p>Xiaomi Redmi Note 7 được trang bị viên pin có dung lượng lên tới 4000 mAh cho bạn sử dụng thoải mái cả ngày dài.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/167535/xiaomi-redmi-note-7-10.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Redmi Note 7 4GB 64GB | Trải nghiệm và đánh giá\" src=\"https://cdn.tgdd.vn/Products/Images/42/167535/xiaomi-redmi-note-7-10.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/167535/xiaomi-redmi-note-7-10.jpg\" title=\"Xiaomi Redmi Note 7 4GB 64GB | Trải nghiệm và đánh giá\"></a></p><p>Một điểm cộng nữa chính là máy hỗ trợ sạc nhanh giúp bạn rút ngắn thời gian nạp đầy năng lượng cho máy đáng kể.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/167535/xiaomi-redmi-note-7-tgdd-2.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Redmi Note 7 4GB 64GB | Cổng sạc Type-C\" src=\"https://cdn.tgdd.vn/Products/Images/42/167535/xiaomi-redmi-note-7-tgdd-2.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/167535/xiaomi-redmi-note-7-tgdd-2.jpg\" title=\"Xiaomi Redmi Note 7 4GB 64GB | Cổng sạc Type-C\"></a></p><p>Và máy cũng có một thay đổi đáng kể là đã chuyển sang cổng kết nối USB Type-C thay vì Micro USB như trước.</p></article>', 102, 3500000, 0, '', '', '2019-06-23 20:58:22', 1, '2019-06-23 20:58:22', 1, 1);
INSERT INTO `uml_product` (`product_id`, `product_catid`, `product_name`, `product_slug`, `product_img`, `product_detail`, `product_number`, `product_price`, `product_pricesale`, `product_metakey`, `product_metadesc`, `product_createdat`, `product_createdby`, `product_updatedat`, `product_updatedby`, `product_status`) VALUES
(40, 8, 'Xiaomi Redmi Note 6 Pro 64GB', 'xiaomi-redmi-note-6-pro-64gb', 'xiaomi-redmi-note-6-pro-64gb.jpg', '<h2><a href=\"https://www.thegioididong.com/dtdd/xiaomi-redmi-note-6-pro\" target=\"_blank\" title=\"Tham khảo chi tiết điện thoại Xiaomi Redmi Note 6 Pro tại Thegioididong.com\" type=\"Tham khảo chi tiết điện thoại Xiaomi Redmi Note 6 Pro tại Thegioididong.com\">Xiaomi Redmi Note 6 Pro</a>, một thành viên mới của nhà <a href=\"https://www.thegioididong.com/dtdd-xiaomi\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại Xiaomi đang kinh doanh tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại Xiaomi đang kinh doanh tại Thegioididong.com\">Xiaomi</a> vừa được ra mắt với một chút thay đổi nhẹ trong thiết kế cũng như hiệu năng. Đặc biệt hơn, chiếc <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại đang kinh doanh tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại đang kinh doanh tại Thegioididong.com\">điện thoại</a> này được trang bị đến 4 camera, điều mà nhiều đối thủ của nó không có được.</h2><h3>Thiết kế theo xu hướng</h3><p>Xiaomi Redmi Note 6 Pro mang đến cho bạn một cái nhìn thân thuộc đến từ thiết kế của máy trông có phần giống như chiếc điện thoại <a href=\"https://www.thegioididong.com/dtdd/iphone-x-64gb\" target=\"_blank\" title=\"Chi tiết điện thoại iPhone X\" type=\"Chi tiết điện thoại iPhone X\">iPhone X</a>.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/188649/xiaomi-redmi-note-6-pro-7-1.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại Xiaomi Redmi Note 6 Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/188649/xiaomi-redmi-note-6-pro-7-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/188649/xiaomi-redmi-note-6-pro-7-1.jpg\" title=\"Thiết kế điện thoại Xiaomi Redmi Note 6 Pro\"></a></p><p>Các góc được bo cong mềm mại giúp bạn nắm máy trên tay được thoải mái và dễ chịu hơn rất nhiều.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/188649/xiaomi-redmi-note-6-pro-3-1.jpg\" onclick=\"return false;\"><img alt=\"Góc cạnh điện thoại Xiaomi Redmi Note 6 Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/188649/xiaomi-redmi-note-6-pro-3-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/188649/xiaomi-redmi-note-6-pro-3-1.jpg\" title=\"Góc cạnh điện thoại Xiaomi Redmi Note 6 Pro\"></a></p><p>Thiết kế nguyên khối giúp tổng thể máy Redmi Note 6 Pro tạo nên sự sang trọng và quyến rũ như một sản phẩm cao cấp.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/188649/xiaomi-redmi-note-6-pro-2-1.jpg\" onclick=\"return false;\"><img alt=\"Góc cạnh điện thoại Xiaomi Redmi Note 6 Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/188649/xiaomi-redmi-note-6-pro-2-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/188649/xiaomi-redmi-note-6-pro-2-1.jpg\" title=\"Góc cạnh điện thoại Xiaomi Redmi Note 6 Pro\"></a></p><p>Cảm biến vân tay của máy được đặt ở mặt lưng với vị trí thuận lợi giúp bạn mở khóa trong nháy mắt trong một lần chạm nhẹ.</p><h3>Màn hình rộng chuẩn <a href=\"https://www.thegioididong.com/tin-tuc/do-phan-giai-man-hinh-qhd-hd-fullhd-2k-4k-la-gi--592178#2k\" target=\"_blank\" title=\"Tìm hiểu độ phân giải màn hình Full HD+\" type=\"Tìm hiểu độ phân giải màn hình Full HD+\">Full HD+</a></h3><p>Redmi Note 6 Pro sở hữu màn hình kích thước lên đến 6.26 inch với <a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-ips-lcd-la-gi-905752\" target=\"_blank\" title=\"Tìm hiểu tấm nền IPS\" type=\"Tìm hiểu tấm nền IPS\">tấm nền IPS</a> đảm bảo cho bạn có những khung hình sắc nét, sống động và một góc nhìn rộng.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/188649/xiaomi-redmi-note-6-pro-6-2.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại Xiaomi Redmi Note 6 Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/188649/xiaomi-redmi-note-6-pro-6-2.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/188649/xiaomi-redmi-note-6-pro-6-2.jpg\" title=\"Màn hình điện thoại Xiaomi Redmi Note 6 Pro\"></a></p><p>Bên cạnh đó với tỷ lệ 19:9 thời thượng sẽ cho bạn một không gian trải nghiệm rộng rãi nhưng vẫn vừa vặn trong lòng bàn tay khi cầm nắm.</p><h3>Hiệu năng ổn định với <a href=\"https://www.thegioididong.com/tin-tuc/snapdragon-636hieu-nang-tang-40-ho-tro-man-hinh-ti-le-189-1033632\" target=\"_blank\" title=\"Tìm hiểu về dòng chip Snapdragon 636\" type=\"Tìm hiểu về dòng chip Snapdragon 636\">Snapdragon 636</a></h3><p>Sức mạnh cung cấp cho Redmi Note 6 Pro chính là con chip Snapdragon 636 mạnh mẽ đi kèm với 4 GB RAM.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/188649/xiaomi-redmi-note-6-pro-10.jpg\" onclick=\"return false;\"><img alt=\"Chơi game trên điện thoại Xiaomi Redmi Note 6 Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/188649/xiaomi-redmi-note-6-pro-10.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/188649/xiaomi-redmi-note-6-pro-10.jpg\" title=\"Chơi game trên điện thoại Xiaomi Redmi Note 6 Pro\"></a></p><p>Hơn nữa, con chip này được đánh giá cao về khả năng tiết kiệm năng lượng nên sẽ giúp bạn có thời gian trải nghiệm được lâu và bền bỉ hơn.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/188649/xiaomi-redmi-note-6-pro-8-1.jpg\" onclick=\"return false;\"><img alt=\"Trải nghiệm điện thoại Xiaomi Redmi Note 6 Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/188649/xiaomi-redmi-note-6-pro-8-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/188649/xiaomi-redmi-note-6-pro-8-1.jpg\" title=\"Trải nghiệm điện thoại Xiaomi Redmi Note 6 Pro\"></a></p><p>Hệ điều hành <a href=\"https://www.thegioididong.com/hoi-dap/co-gi-dac-biet-o-android-81-oreo-1073208\" target=\"_blank\" title=\"Tìm hiểu về các tính năng trên Android 8.1 (Oreo)\" type=\"Tìm hiểu về các tính năng trên Android 8.1 (Oreo)\">Android 8.1 (Oreo)</a> cũng được cài sẵn với nhiều tùy biến thông minh qua giao diện MIUI 9.6 giúp nâng cao trải nghiệm của bạn hơn đáng kể.</p><h3>Camera kép cả trước và sau</h3><p>Redmi Note 6 Pro được trang bị cụm camera kép phía sau với độ phân giải 12 MP (cảm biến chính) và 5 MP (cảm biến phụ) đáp ứng nhanh chóng cho nhu cầu <a href=\"https://www.thegioididong.com/tin-tuc/tim-hieu-xoa-phong-huong-dan-xoa-phong-tren-moi-smartphone-892585\" target=\"_blank\" title=\"Hướng dẫn cách chụp ảnh xóa phông trên điện thoại\" type=\"Hướng dẫn cách chụp ảnh xóa phông trên điện thoại\">chụp ảnh xóa phông</a> của bạn.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/188649/xiaomi-redmi-note-6-pro-1-1.jpg\" onclick=\"return false;\"><img alt=\"Cụm camera kép mặt sau điện thoại Xiaomi Redmi Note 6 Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/188649/xiaomi-redmi-note-6-pro-1-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/188649/xiaomi-redmi-note-6-pro-1-1.jpg\" title=\"Cụm camera kép mặt sau điện thoại Xiaomi Redmi Note 6 Pro\"></a></p><p>Không những thế, máy còn được tích hợp thêm công nghệ <a href=\"https://www.thegioididong.com/tin-tuc/ai-cac-tinh-nang-camera-nang-cao-se-duoc-chu-y-tai-mwc-2018-1068951\" target=\"_blank\" title=\"Tìm hiểu về tính năng mới AI camera\" type=\"Tìm hiểu về tính năng mới AI camera\">AI camera</a> tự động điều chỉnh các thông số cần thiết nhằm đem lại cho bạn một bức ảnh tuyệt vời nhất.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/188649/xiaomi-redmi-note-6-pro-9.jpg\" onclick=\"return false;\"><img alt=\"Camera trước điện thoại Xiaomi Redmi Note 6 Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/188649/xiaomi-redmi-note-6-pro-9.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/188649/xiaomi-redmi-note-6-pro-9.jpg\" title=\"Camera trước điện thoại Xiaomi Redmi Note 6 Pro\"></a></p><p>Camera selfie của máy cũng là cụm camera kép với ống kính chính có độ phân giải là 20 MP và ống kính phụ 2 MP đi kèm tính năng <a href=\"https://www.thegioididong.com/hoi-dap/cong-nghe-selfie-ai-beauty-la-gi-1049958\" target=\"_blank\" title=\"Tìm hiểu về công nghệ Selfie A.I Beauty\" type=\"Tìm hiểu về công nghệ Selfie A.I Beauty\">Selfie A.I Beauty</a>, hứa hẹn sẽ mang lại cho bạn những ảnh tự sướng đẹp tự nhiên mà không cần chỉnh sửa.</p><h3>Dung lượng pin khủng</h3><p>Với viên pin 4000 mAh hứa hẹn sẽ cho bạn thời gian sử dụng vô cùng thoải mái mỗi khi thưởng thức những bộ phim hay mà không cần phải quan tâm đến việc sạc pin cho máy.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/188649/xiaomi-redmi-note-6-pro-5-1.jpg\" onclick=\"return false;\"><img alt=\"Đánh giá điện thoại Xiaomi Redmi Note 6 Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/188649/xiaomi-redmi-note-6-pro-5-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/188649/xiaomi-redmi-note-6-pro-5-1.jpg\" title=\"Đánh giá điện thoại Xiaomi Redmi Note 6 Pro\"></a></p><p>Ngoài ra, bạn có thể bật tính năng <a href=\"https://www.thegioididong.com/tin-tuc/cac-che-do-tiet-kiem-pin-tren-smartphone-872268\" target=\"_blank\" title=\"Tìm hiểu về chế độ tiết kiệm pin trên điện thoại\" type=\"Tìm hiểu về chế độ tiết kiệm pin trên điện thoại\">tiết kiệm pin</a> được cài sẵn để có thêm thời gian trải nghiệm một cách trọn vẹn hơn nữa.</p></article>', 194, 11800000, 12580000, '', '', '2019-06-23 20:58:23', 1, '2019-06-23 20:58:23', 1, 1),
(41, 8, 'Xiaomi Redmi Note 6 Pro 32GB', 'xiaomi-redmi-note-6-pro-32gb', 'xiaomi-redmi-note-6-pro-32gb.jpg', '<h2><a href=\"https://www.thegioididong.com/dtdd/xiaomi-redmi-note-6-pro-32gb\" target=\"_blank\" title=\"Tham khảo chi tiết điện thoại Xiaomi Redmi Note 6 Pro 32GB tại Thegioididong.com\" type=\"Tham khảo chi tiết điện thoại Xiaomi Redmi Note 6 Pro 32GB tại Thegioididong.com\">Xiaomi Redmi Note 6 Pro 32GB</a> là chiếc <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại đang kinh doanh tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại đang kinh doanh tại Thegioididong.com\">điện thoại</a> sở hữu cấu hình thấp hơn một chút, với RAM 3GB nhưng vẫn được trang bị đến 4 camera, điều mà nhiều đối thủ của <a href=\"https://www.thegioididong.com/dtdd-xiaomi\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại Xiaomi đang kinh doanh tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại Xiaomi đang kinh doanh tại Thegioididong.com\">Xiaomi</a> không có được.</h2><h3>Camera kép mặt trước lẫn sau</h3><p>Redmi Note 6 Pro được trang bị cụm camera kép phía sau với độ phân giải 12 MP dành cho cảm biến chính và 5 MP dành cho cảm biến chiều sâu đáp ứng nhanh chóng cho nhu cầu <a href=\"https://www.thegioididong.com/tin-tuc/tim-hieu-xoa-phong-huong-dan-xoa-phong-tren-moi-smartphone-892585\" target=\"_blank\" title=\"Hướng dẫn cách chụp ảnh xóa phông trên điện thoại\" type=\"Hướng dẫn cách chụp ảnh xóa phông trên điện thoại\">chụp ảnh xóa phông</a> của bạn.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/191776/xiaomi-redmi-note-6-pro-32gb-7-1.jpg\" onclick=\"return false;\"><img alt=\"Cụm camera sau điện thoại Xiaomi Redmi Note 6 Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/191776/xiaomi-redmi-note-6-pro-32gb-7-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/191776/xiaomi-redmi-note-6-pro-32gb-7-1.jpg\" title=\"Cụm camera sau điện thoại Xiaomi Redmi Note 6 Pro\"></a></p><p>Không những thế, máy còn được tích hợp thêm công nghệ <a href=\"https://www.thegioididong.com/tin-tuc/ai-cac-tinh-nang-camera-nang-cao-se-duoc-chu-y-tai-mwc-2018-1068951\" target=\"_blank\" title=\"Tìm hiểu về tính năng mới AI camera\" type=\"Tìm hiểu về tính năng mới AI camera\">AI camera</a> tự động điều chỉnh các thông số cần thiết nhằm đem lại cho bạn một bức ảnh tuyệt vời nhất.</p><p>Camera selfie của máy cũng là cụm camera kép với chiếc ống kính chính 20 MP và ống kính phụ 2 MP đi kèm tính năng <a href=\"https://www.thegioididong.com/hoi-dap/cong-nghe-selfie-ai-beauty-la-gi-1049958\" target=\"_blank\" title=\"Tìm hiểu về công nghệ Selfie A.I Beauty\" type=\"Tìm hiểu về công nghệ Selfie A.I Beauty\">Selfie A.I Beauty</a>, hứa hẹn sẽ mang lại cho bạn những ảnh tự sướng đẹp tự nhiên mà không cần chỉnh sửa.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/191776/xiaomi-redmi-note-6-pro-32gb-9-1.jpg\" onclick=\"return false;\"><img alt=\"Trải nghiệm điện thoại Xiaomi Redmi Note 6 Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/191776/xiaomi-redmi-note-6-pro-32gb-9-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/191776/xiaomi-redmi-note-6-pro-32gb-9-1.jpg\" title=\"Cụm camera trước điện thoại Xiaomi Redmi Note 6 Pro\"></a></p><h3>Hiệu năng ổn định với <a href=\"https://www.thegioididong.com/tin-tuc/snapdragon-636hieu-nang-tang-40-ho-tro-man-hinh-ti-le-189-1033632\" target=\"_blank\" title=\"Tìm hiểu về dòng chip Snapdragon 636\" type=\"Tìm hiểu về dòng chip Snapdragon 636\">Snapdragon 636</a></h3><p>Sức mạnh cung cấp cho Redmi Note 6 Pro chính là con chip Snapdragon 636 mạnh mẽ đi kèm với 3 GB RAM.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/191776/xiaomi-redmi-note-6-pro-32gb-1-1.jpg\" onclick=\"return false;\"><img alt=\"Trải nghiệm điện thoại Xiaomi Redmi Note 6 Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/191776/xiaomi-redmi-note-6-pro-32gb-1-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/191776/xiaomi-redmi-note-6-pro-32gb-1-1.jpg\" title=\"Trải nghiệm điện thoại Xiaomi Redmi Note 6 Pro\"></a></p><p>Hơn nữa, con chip này được đánh giá cao về khả năng tiết kiệm năng lượng nên sẽ giúp bạn có thời gian trải nghiệm được lâu và bền bỉ hơn.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/191776/xiaomi-redmi-note-6-pro-32gb-10-1.jpg\" onclick=\"return false;\"><img alt=\"Chơi game trên điện thoại Xiaomi Redmi Note 6 Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/191776/xiaomi-redmi-note-6-pro-32gb-10-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/191776/xiaomi-redmi-note-6-pro-32gb-10-1.jpg\" title=\"Chơi game trên điện thoại Xiaomi Redmi Note 6 Pro\"></a></p><p>Hệ điều hành <a href=\"https://www.thegioididong.com/hoi-dap/co-gi-dac-biet-o-android-81-oreo-1073208\" target=\"_blank\" title=\"Tìm hiểu về các tính năng trên Android 8.1 (Oreo)\" type=\"Tìm hiểu về các tính năng trên Android 8.1 (Oreo)\">Android 8.1 (Oreo)</a> cũng được cài sẵn với nhiều tùy biến thông minh qua giao diện MIUI 9.6 giúp nâng cao trải nghiệm của bạn hơn đáng kể.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/191776/xiaomi-redmi-note-6-pro-32gb-8-1.jpg\" onclick=\"return false;\"><img alt=\"Trải nghiệm điện thoại Xiaomi Redmi Note 6 Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/191776/xiaomi-redmi-note-6-pro-32gb-8-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/191776/xiaomi-redmi-note-6-pro-32gb-8-1.jpg\" title=\"Trải nghiệm điện thoại Xiaomi Redmi Note 6 Pro\"></a></p><p><div class=video allow=\"autoplay; encrypted-media\" allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"https://www.youtube.com/embed/HKS5BBAluAk\" width=\"560\"></div></p><h3>Thiết kế tai thỏ thời thượng</h3><p>Xiaomi Redmi Note 6 Pro mang đến cho bạn một cái nhìn thân thuộc đến từ thiết kế của máy trông có phần giống như chiếc điện thoại <a href=\"https://www.thegioididong.com/dtdd/iphone-x-64gb\" target=\"_blank\" title=\"Chi tiết điện thoại iPhone X\" type=\"Chi tiết điện thoại iPhone X\">iPhone X</a>.</p><p>Các góc được bo cong mềm mại giúp bạn nắm máy trên tay được thoải mái và dễ chịu hơn rất nhiều.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/191776/xiaomi-redmi-note-6-pro-32gb-1-3.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại Xiaomi Redmi Note 6 Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/191776/xiaomi-redmi-note-6-pro-32gb-1-3.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/191776/xiaomi-redmi-note-6-pro-32gb-1-3.jpg\" title=\"Thiết kế điện thoại Xiaomi Redmi Note 6 Pro\"></a></p><p>Thiết kế nguyên đem giúp tổng thể máy Redmi Note 6 Pro tạo nên sự sang trọng và quyến rũ như một sản phẩm cao cấp.</p><p>Cảm biến vân tay của máy được đặt ở mặt lưng với vị trí thuận lợi giúp bạn mở khóa trong nháy mắt trong một lần chạm nhẹ.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/191776/xiaomi-redmi-note-6-pro-32gb-4-1.jpg\" onclick=\"return false;\"><img alt=\"Vân tay mặt lưng trên điện thoại Xiaomi Redmi Note 6 Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/191776/xiaomi-redmi-note-6-pro-32gb-4-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/191776/xiaomi-redmi-note-6-pro-32gb-4-1.jpg\" title=\"Vân tay mặt lưng trên điện thoại Xiaomi Redmi Note 6 Pro\"></a></p><h3>Màn hình rộng chuẩn <a href=\"https://www.thegioididong.com/tin-tuc/do-phan-giai-man-hinh-qhd-hd-fullhd-2k-4k-la-gi--592178#2k\" target=\"_blank\" title=\"Tìm hiểu độ phân giải màn hình Full HD+\" type=\"Tìm hiểu độ phân giải màn hình Full HD+\">Full HD+</a></h3><p>Redmi Note 6 Pro sở hữu màn hình kích thước lên đến 6.26 inch với <a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-ips-lcd-la-gi-905752\" target=\"_blank\" title=\"Tìm hiểu tấm nền IPS\" type=\"Tìm hiểu tấm nền IPS\">tấm nền IPS</a> đảm bảo cho bạn có những khung hình sắc nét, sống động và một góc nhìn rộng.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/191776/xiaomi-redmi-note-6-pro-32gb-6-1.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại Xiaomi Redmi Note 6 Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/191776/xiaomi-redmi-note-6-pro-32gb-6-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/191776/xiaomi-redmi-note-6-pro-32gb-6-1.jpg\" title=\"Màn hình điện thoại Xiaomi Redmi Note 6 Pro\"></a></p><p>Bên cạnh đó với tỷ lệ 19:9 thời thượng sẽ cho bạn một không gian trải nghiệm rộng rãi nhưng vẫn vừa vặn trong lòng bàn tay khi cầm nắm.</p><h3>Dung lượng pin khủng tới 4000 mAh</h3><p>Với viên pin 4000 mAh hứa hẹn sẽ cho bạn thời gian sử dụng vô cùng thoải mái mỗi khi thưởng thức những bộ phim hay mà không cần phải quan tâm đến việc sạc pin cho máy.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/191776/xiaomi-redmi-note-6-pro-32gb-3-2.jpg\" onclick=\"return false;\"><img alt=\"Đánh giá điện thoại Xiaomi Redmi Note 6 Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/191776/xiaomi-redmi-note-6-pro-32gb-3-2.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/191776/xiaomi-redmi-note-6-pro-32gb-3-2.jpg\" title=\"Đánh giá điện thoại Xiaomi Redmi Note 6 Pro\"></a></p><p>Ngoài ra, bạn có thể bật tính năng <a href=\"https://www.thegioididong.com/tin-tuc/cac-che-do-tiet-kiem-pin-tren-smartphone-872268\" target=\"_blank\" title=\"Tìm hiểu về chế độ tiết kiệm pin trên điện thoại\" type=\"Tìm hiểu về chế độ tiết kiệm pin trên điện thoại\">tiết kiệm pin</a> được cài sẵn để có thêm thời gian trải nghiệm một cách trọn vẹn hơn hay bảo mật vân tay mở máy nhanh chóng, tiện lợi.</p></article>', 182, 13900000, 0, '', '', '2019-06-23 20:58:24', 1, '2019-06-23 20:58:24', 1, 1),
(42, 8, 'Xiaomi Redmi 7', 'xiaomi-redmi-7', 'xiaomi-redmi-7.jpg', '<h2>Nếu bạn đang tìm kiếm một <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại chính hãng\">smartphone</a> có mức giá rẻ nhưng cũng phải đáp ứng tốt nhu cầu sử dụng hằng ngày và phải có một thiết kế thời trang thì không có sự lựa chọn nào hợp lý hơn <a href=\"https://www.thegioididong.com/dtdd/xiaomi-redmi-7\" target=\"_blank\" title=\"Tham khảo điện thoại Xiaomi Redmi 7 chính hãng\">Xiaomi Redmi 7</a>.</h2><h3>Điểm nổi trội về cấu hình khi nhắc đến máy</h3><p>Cấu hình là điểm đáng tiền nhất trên Xiaomi Redmi 7 khi máy sở hữu con chip Snapdragon 632 cho sức mạnh vượt trội trong tầm giá.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/198167/xiaomi-redmi-7-tgdd-antutu.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Redmi 7 32GB | Điểm Antutu\" src=\"https://cdn.tgdd.vn/Products/Images/42/198167/xiaomi-redmi-7-tgdd-antutu.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/198167/xiaomi-redmi-7-tgdd-antutu.jpg\" title=\"Xiaomi Redmi 7 32GB | Điểm Antutu\"></a></p><p>Máy dễ dàng đáp ứng tốt các nhu cầu cơ bản và đủ sức chơi các tựa game đang hot hiện tại như: Liên Quân Mobile, PUBG Mobile....</p><p>Thật khó để bạn có thể tìm ra một chiếc máy nào có hiệu năng tốt hơn chiếc <a href=\"https://www.thegioididong.com/dtdd-xiaomi\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại Xiaomi chính hãng\">điện thoại Xiaomi</a> này trong cùng tầm giá.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/198167/xiaomi-redmi-7-tgdd-giao-dien.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Redmi 7 32GB | Giao diện Android và tính năng\" src=\"https://cdn.tgdd.vn/Products/Images/42/198167/xiaomi-redmi-7-tgdd-giao-dien.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/198167/xiaomi-redmi-7-tgdd-giao-dien.jpg\" title=\"Xiaomi Redmi 7 32GB | Giao diện Android và tính năng\"></a></p><p>Bên cạnh đó máy còn được ưu ái sử dụng giao diện MIUI 10 dựa trên <a href=\"https://www.thegioididong.com/hoi-dap/top-5-tinh-nang-moi-duoc-yeu-thich-nhat-tren-and-1107119\" target=\"_blank\" title=\"Tìm hiểu Android 9.0 (Pie)\">Android 9.0 (Pie)</a> mang lại sự tối ưu mượt mà hơn.</p><h3>Camera kép xoá phông tốt</h3><p>Nếu như cấu hình vẫn chưa đủ làm bạn hài lòng thì Redmi 7 còn sở hữu cho mình cụm camera kép ở mặt lưng với khả năng chụp ảnh xóa phông khá ảo diệu.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/198167/xiaomi-redmi-7-tgdd-9.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Redmi 7 32GB | Giao diện chụp ảnh\" src=\"https://cdn.tgdd.vn/Products/Images/42/198167/xiaomi-redmi-7-tgdd-9.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/198167/xiaomi-redmi-7-tgdd-9.jpg\" title=\"Xiaomi Redmi 7 32GB | Giao diện chụp ảnh\"></a></p><p>Ngoài ra camera trước 8 MP hỗ trợ tính năng làm đẹp tự nhiên giúp bạn có những tấm ảnh sống ảo vô cùng ấn tượng.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/198167/xiaomi-redmi-7-tgdd-camera-2.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Redmi 7 32GB | Ảnh chụp xoá phông\" src=\"https://cdn.tgdd.vn/Products/Images/42/198167/xiaomi-redmi-7-tgdd-camera-2.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/198167/xiaomi-redmi-7-tgdd-camera-2.jpg\" title=\"Xiaomi Redmi 7 32GB | Ảnh chụp xoá phông\"></a></p><p>Chất lượng ảnh cho ra từ máy khá sắc nét, khả năng bắt nét cũng rất nhanh giúp bạn không bỏ lỡ bất cứ khoảnh khắc đẹp nào trong cuộc sống.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/198167/xiaomi-redmi-7-tgdd-camera-3.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Redmi 7 32GB | Ảnh chụp từ camera sau\" src=\"https://cdn.tgdd.vn/Products/Images/42/198167/xiaomi-redmi-7-tgdd-camera-3.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/198167/xiaomi-redmi-7-tgdd-camera-3.jpg\" title=\"Xiaomi Redmi 7 32GB | Ảnh chụp từ camera sau\"></a></p><h3>Ngoại hình bắt mắt và nổi bật</h3><p>Bên trong tốt là thế nhưng ngoại hình của máy vẫn rất được chú ý với kiểu hoàn thiện bóng bẩy và thời trang.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/198167/xiaomi-redmi-7-tgdd-4.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Redmi 7 32GB | Thiết kế\" src=\"https://cdn.tgdd.vn/Products/Images/42/198167/xiaomi-redmi-7-tgdd-4.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/198167/xiaomi-redmi-7-tgdd-4.jpg\" title=\"Xiaomi Redmi 7 32GB | Thiết kế\"></a></p><p>Màn hình thiết kế theo xu thế giọt nước mang lại trải nghiệm mới mẻ và thoải mái.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/198167/xiaomi-redmi-7-tgdd-3.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Redmi 7 32GB | Màn hình\" src=\"https://cdn.tgdd.vn/Products/Images/42/198167/xiaomi-redmi-7-tgdd-3.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/198167/xiaomi-redmi-7-tgdd-3.jpg\" title=\"Xiaomi Redmi 7 32GB | Màn hình\"></a></p><p>Kích thước màn hình lớn lên tới 6.26 inch <a href=\"https://www.thegioididong.com/tin-tuc/do-phan-giai-man-hinh-qhd-hd-fullhd-2k-4k-la-gi--592178#hd\" target=\"_blank\" title=\"Tìm hiểu độ phân giải HD+\" type=\"Tìm hiểu độ phân giải HD+\">độ phân giải HD+</a> cũng sẽ giúp bạn có những giây phút thư giãn thoải mái với những thước phim hấp dẫn.</p><h3>Viên pin khủng đến 4000 mAh</h3><p>Để người dùng có được trải nghiệm \"hoàn hảo\" nhất thì nhà sản xuất cũng đã rất cố gắng khi mang đến viên pin dung lượng lên tới 4000 mAh.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/198167/xiaomi-redmi-7-tgdd-8.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Redmi 7 32GB | Trên tay trải nghiệm\" src=\"https://cdn.tgdd.vn/Products/Images/42/198167/xiaomi-redmi-7-tgdd-8.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/198167/xiaomi-redmi-7-tgdd-8.jpg\" title=\"Xiaomi Redmi 7 32GB | Trên tay trải nghiệm\"></a></p><p>Nhờ đó bạn có thể sử dụng Redmi 7 cả ngày mà không sợ hết pin giữa chừng.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/198167/xiaomi-redmi-7-tgdd-7.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Redmi 7 32GB | Khe 2 sim và 1 thẻ nhớ\" src=\"https://cdn.tgdd.vn/Products/Images/42/198167/xiaomi-redmi-7-tgdd-7.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/198167/xiaomi-redmi-7-tgdd-7.jpg\" title=\"Xiaomi Redmi 7 32GB | Khe 2 sim và 1 thẻ nhớ\"></a></p><p>Tất nhiên bạn cũng sẽ có 2 SIM 2 sóng hay kết nối 4G tốc độ cao khi sở hữu cho mình một chiếc Xiaomi Redmi 7.</p></article>', 79, 18300000, 0, '', '', '2019-06-23 20:58:25', 1, '2019-06-23 20:58:25', 1, 1),
(43, 8, 'Xiaomi Mi A2 Lite', 'xiaomi-mi-a2-lite', 'xiaomi-mi-a2-lite.jpg', '<h2>Kế thừa sự thành công từ người đàn anh <a href=\"https://www.thegioididong.com/dtdd/xiaomi-mi-a1\" target=\"_blank\" title=\"Tham khảo điện thoại Xiaomi Mi A1 tại Thegioididong.com\">Xiaomi Mi A1</a> thì Xiaomi tiếp tục giới thiệu tới người dùng phiên bản kế nhiệm là <a href=\"https://www.thegioididong.com/dtdd/xiaomi-mi-a2-lite\" target=\"_blank\" title=\"Tham khảo điện thoại Xiaomi Mi A2 Lite tại Thegioididong.com\">Xiaomi Mi A2 Lite</a> và Xiaomi Mi A2 với nhiều cải tiến đáng giá.</h2><h3>Màn hình tai thỏ thời thượng</h3><p>Nếu như bạn là một người dùng thích một <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại chính hãng tại Thegioididong.com\">smartphone</a> có \"tai thỏ\", một màn hình mang hơi hướng của iPhone X thì chiếc <a href=\"https://www.thegioididong.com/dtdd-xiaomi\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại Xiaomi chính hãng\" type=\"Tham khảo các dòng điện thoại Xiaomi chính hãng\">điện thoại Xiaomi</a> này sẽ là sự lựa chọn hợp lý dành cho bạn.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/182617/xiaomi-mi-a2-lite7.jpg\" onclick=\"return false;\"><img alt=\"Màn hình của điện thoại Xiaomi Mi A2 Lite\" src=\"https://cdn.tgdd.vn/Products/Images/42/182617/xiaomi-mi-a2-lite7.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/182617/xiaomi-mi-a2-lite7.jpg\" title=\"Màn hình của điện thoại Xiaomi Mi A2 Lite\"></a></p><p>Kích thước màn hình 5.84 inch, tỉ lệ 19:9 giúp bạn dễ dàng để cầm nắm và sử dụng mà không gặp quá nhiều khó khăn.</p><h3>Thiết kế kim loại nguyên khối chắc chắn</h3><p>Một thiết kế kim loại nguyên khối quen thuộc và chắc chắn là điểm cộng khá lớn của Xiaomi Mi A2 Lite so với các đối thủ trong phân khúc.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/182617/xiaomi-mi-a2-lite9.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại Xiaomi Mi A2 Lite\" src=\"https://cdn.tgdd.vn/Products/Images/42/182617/xiaomi-mi-a2-lite9.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/182617/xiaomi-mi-a2-lite9.jpg\" title=\"Thiết kế điện thoại Xiaomi Mi A2 Lite\"></a></p><p>Cụm camera chính trên máy cũng được đặt dọc thay vì đặt ngang như trên Mi A1 và hơi lồi nhẹ so với phần mặt lưng.</p><h3>Hiệu năng là lợi thế</h3><p>Cung cấp sức mạnh cho máy là con chip <a href=\"https://www.thegioididong.com/hoi-dap/qualcomm-snapdragon-625-839970\" target=\"_blank\" title=\"Tìm hiểu chip Snapdragon 625 8 nhân 64-bit\">Snapdragon 625 8 nhân 64-bit</a> với hiệu năng khá ấn tượng tới từ Qualcomm đi kèm với 3 GB RAM đáp ứng tốt hầu hết nhu cầu sử dụng của người dùng.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/182617/xiaomi-mi-a2-lite4.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình của điện thoại Xiaomi Mi A2 Lite\" src=\"https://cdn.tgdd.vn/Products/Images/42/182617/xiaomi-mi-a2-lite4.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/182617/xiaomi-mi-a2-lite4.jpg\" title=\"Cấu hình của điện thoại Xiaomi Mi A2 Lite\"></a></p><p>Máy chạy trên nền Android One với sự hợp tác của Xiaomi và Google mang lại cho bạn sự hỗ trợ lâu dài và luôn nhận được các bản cập nhật sớm nhất có thể.</p><h3>Camera tích hợp trí tuệ nhân tạo AI</h3><p>Xiaomi Mi A2 Lite gây ấn tượng bởi hệ thống camera kép gồm 2 cảm biến với cảm biến chính 12 MP + 5 MP cảm biến chiều sâu được tích hợp sẵn AI.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/182617/xiaomi-mi-a2-lite6.jpg\" onclick=\"return false;\"><img alt=\"Camera sau của điện thoại Xiaomi Mi A2 Lite\" src=\"https://cdn.tgdd.vn/Products/Images/42/182617/xiaomi-mi-a2-lite6.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/182617/xiaomi-mi-a2-lite6.jpg\" title=\"Camera sau của điện thoại Xiaomi Mi A2 Lite\"></a></p><p>Khả năng chụp xóa phông của máy ở mức khá, ngoài ra AI cũng làm việc khá hiệu quả giúp những bức ảnh của bạn trông rực rỡ hơn và sẵn sàng đăng lên mạng xã hội ngay sau khi chụp.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/182617/xiaomi-mi-a2-lite8.jpg\" onclick=\"return false;\"><img alt=\"Camera sau của điện thoại Xiaomi Mi A2 Lite\" src=\"https://cdn.tgdd.vn/Products/Images/42/182617/xiaomi-mi-a2-lite8.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/182617/xiaomi-mi-a2-lite8.jpg\" title=\"Camera sau của điện thoại Xiaomi Mi A2 Lite\"></a></p><p>Camera trước với độ phân giải 5 MP cũng được tích hợp sẵn chế độ làm đẹp hứa hẹn sẽ không làm các bạn phải thất vọng.</p><h3>Dung lượng pin lớn</h3><p>Viên pin 4.000 mAh giúp bạn có thể sử dụng máy khá thoải mái trong khoảng hơn 1 ngày.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/182617/xiaomi-mi-a2-lite5.jpg\" onclick=\"return false;\"><img alt=\"Thời lượng pin Xiaomi Mi A2 Lite\" src=\"https://cdn.tgdd.vn/Products/Images/42/182617/xiaomi-mi-a2-lite5.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/182617/xiaomi-mi-a2-lite5.jpg\" title=\"Thời lượng pin Xiaomi Mi A2 Lite\"></a></p><p>Ngoài ra cảm biến vân tay ở mặt lưng cũng giúp bạn mở khóa thiết bị dễ dàng và bảo mật thông tin an toàn hơn.</p></article>', 195, 5000000, 5780000, '', '', '2019-06-23 20:58:26', 1, '2019-06-23 20:58:26', 1, 1),
(44, 8, 'Xiaomi Redmi 7 2GB/16GB', 'xiaomi-redmi-7-2gb-16gb', 'xiaomi-redmi-7-2gb-16gb.jpg', '<h2><a href=\"https://www.thegioididong.com/dtdd/xiaomi-redmi-7-16gb\" target=\"_blank\" title=\"Tham khảo điện thoại Xiaomi Redmi 7 16GB chính hãng\">Xiaomi Redmi 7 16GB</a> đã gây được chú ý cho người dùng khi ra mắt với một cấu hình khủng nhưng lại sở hữu một mức giá \"khó tin\".</h2><h3>Hiệu năng \"vô đối\" trong phân khúc</h3><p>Xiaomi luôn khiến người dùng phải bất ngờ với những trang bị về cấu hình cho những chiếc smartphone của mình và với con chip <a href=\"https://www.thegioididong.com/tin-tuc/snapdragon-632-439-429-chinh-thuc-duoc-qualcomm-trinh-lang-1097845#snapdragon632\" target=\"_blank\" title=\"Tìm hiểu Qualcomm Snapdragon 632 8 nhân 64-bit\">Qualcomm Snapdragon 632 8 nhân 64-bit</a> thì Redmi 7 có thể làm hài lòng cả những người dùng khó tính nhất.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/200599/xiaomi-redmi-7-16gb-antutu-benchmark.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Redmi 7 2GB 16GB | Điểm Antutu\" src=\"https://cdn.tgdd.vn/Products/Images/42/200599/xiaomi-redmi-7-16gb-antutu-benchmark.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/200599/xiaomi-redmi-7-16gb-antutu-benchmark.jpg\" title=\"Xiaomi Redmi 7 2GB 16GB | Điểm Antutu\"></a></p><p>Những con chip tới từ Qualcomm luôn được người dùng yêu thích bởi sự ổn định và khả năng tối ưu hóa khi chơi game hay sử dụng các tác vụ nặng.</p><p>Bạn có thể chơi các tựa game trung bình hay thậm chí các game nặng (tất nhiên mức đồ họa sẽ giảm xuống đôi chút) một cách khá mượt mà hiếm có chiếc smartphone nào sánh được.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/200599/xiaomi-redmi-7-16gb-tgdd-giao-dien.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Redmi 7 2GB 16GB | Giao diện EMUI\" src=\"https://cdn.tgdd.vn/Products/Images/42/200599/xiaomi-redmi-7-16gb-tgdd-giao-dien.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/200599/xiaomi-redmi-7-16gb-tgdd-giao-dien.jpg\" title=\"Xiaomi Redmi 7 2GB 16GB | Giao diện EMUI\"></a></p><p>Để có một hiệu năng tốt hơn thì Xiaomi đã cài sẵn cho máy phiên bản <a href=\"https://www.thegioididong.com/hoi-dap/top-5-tinh-nang-moi-duoc-yeu-thich-nhat-tren-and-1107119\" target=\"_blank\" title=\"Tìm hiểu Android 9.0 (Pie)\">Android 9.0 (Pie)</a> mới mẻ giúp tối ưu hóa hiệu quả hơn.</p><h3>Dung lượng pin thoải mái sử dụng</h3><p>Xiaomi Redmi 7 mang trong mình viên pin có dung lượng lên tới 4000 mAh cho bạn sử dụng lên tới ngày thứ 2 với các tác vụ cơ bản.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/200599/xiaomi-redmi-7-16gb-tgdd-8.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Redmi 7 2GB 16GB | Trên tay và trải nghiệm\" src=\"https://cdn.tgdd.vn/Products/Images/42/200599/xiaomi-redmi-7-16gb-tgdd-8.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/200599/xiaomi-redmi-7-16gb-tgdd-8.jpg\" title=\"Xiaomi Redmi 7 2GB 16GB | Trên tay và trải nghiệm\"></a></p><p>Theo nhà sản xuất thì pin trên máy được sản xuất theo quy trình mới cho độ ổn định cao hơn và sử dụng được lâu hơn.</p><p>Ngoài ra máy cũng sở hữu cho mình tính năng AI hỗ trợ tiết kiệm năng lượng một cách thông minh hơn so với những chiếc <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại chính hãng\">smartphone</a> khác.</p><h3>Camera chất lượng tốt trong tầm giá</h3><p>Sở hữu Redmi 7 thì bạn không chỉ có một cấu hình mạnh, pin tốt mà còn sở hữu một cụm camera khá chất lượng.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/200599/xiaomi-redmi-7-16gb-tgdd-9.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Redmi 7 2GB 16GB | Giao diện chụp ảnh\" src=\"https://cdn.tgdd.vn/Products/Images/42/200599/xiaomi-redmi-7-16gb-tgdd-9.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/200599/xiaomi-redmi-7-16gb-tgdd-9.jpg\" title=\"Xiaomi Redmi 7 2GB 16GB | Giao diện chụp ảnh\"></a></p><p>Bộ đôi camera chính 12 MP và phụ 2 MP có thể cho bạn những bức ảnh xóa phông ảo diệu để khoe với bạn bè trên Facebook.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/200599/xiaomi-redmi-7-16gb-camera-1.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Redmi 7 2GB 16GB | Ảnh chụp từ camera sau\" src=\"https://cdn.tgdd.vn/Products/Images/42/200599/xiaomi-redmi-7-16gb-camera-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/200599/xiaomi-redmi-7-16gb-camera-1.jpg\" title=\"Xiaomi Redmi 7 2GB 16GB | Ảnh chụp từ camera sau\"></a></p><p>Trong điều kiện đủ sáng thì camera này cũng thể hiện tốt với những bức ảnh sắc nét, nhiều chi tiết và màu sắc khá thích mắt.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/200599/xiaomi-redmi-7-16gb-camera-2.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Redmi 7 2GB 16GB | Ảnh selfie\" src=\"https://cdn.tgdd.vn/Products/Images/42/200599/xiaomi-redmi-7-16gb-camera-2.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/200599/xiaomi-redmi-7-16gb-camera-2.jpg\" title=\"Xiaomi Redmi 7 2GB 16GB | Ảnh selfie\"></a></p><p>Ngoài ra camera trước 8 MP hỗ trợ tính năng làm đẹp tự nhiên giúp bạn có những tấm ảnh sống ảo vô cùng ấn tượng.</p><h3>Thiết kế vẫn rất đẹp và thu hút</h3><p>Có quá nhiều điểm sáng trên Xiaomi Redmi 7 và tất nhiên chúng ta cũng không thể bỏ qua về thiết kế.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/200599/xiaomi-redmi-7-16gb-tgdd-10.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Redmi 7 2GB 16GB | Thiết kế\" src=\"https://cdn.tgdd.vn/Products/Images/42/200599/xiaomi-redmi-7-16gb-tgdd-10.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/200599/xiaomi-redmi-7-16gb-tgdd-10.jpg\" title=\"Xiaomi Redmi 7 2GB 16GB | Thiết kế\"></a></p><p>Xu hướng màn hình giọt nước tràn viền cùng mặt lưng bóng bẩy đã được Xiaomi mang lên chiếc <a href=\"https://www.thegioididong.com/dtdd-xiaomi\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại Xiaomi chính hãng\">điện thoại Xiaomi</a> của mình giúp bạn có một thiết bị không hề thua kém các smartphone đắt tiền.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/200599/xiaomi-redmi-7-16gb-tgdd-3.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Redmi 7 2GB 16GB | Màn hình giọt nước\" src=\"https://cdn.tgdd.vn/Products/Images/42/200599/xiaomi-redmi-7-16gb-tgdd-3.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/200599/xiaomi-redmi-7-16gb-tgdd-3.jpg\" title=\"Xiaomi Redmi 7 2GB 16GB | Màn hình giọt nước\"></a></p><p>Kích thước màn hình lớn lên tới 6.26 inch <a href=\"https://www.thegioididong.com/tin-tuc/do-phan-giai-man-hinh-qhd-hd-fullhd-2k-4k-la-gi--592178#hd\" target=\"_blank\" title=\"Tìm hiểu về độ phân giải HD+\" type=\"Tìm hiểu về độ phân giải HD+\">độ phân giải HD+</a> cũng sẽ giúp bạn có những giây phút thư giản thoải mái với những thước phim hấp dẫn.</p></article>', 192, 16900000, 17680000, '', '', '2019-06-23 20:58:26', 1, '2019-06-23 20:58:26', 1, 1),
(45, 9, 'Huawei P30 Pro', 'huawei-p30-pro', 'huawei-p30-pro.jpg', '<h2><a href=\"https://www.thegioididong.com/dtdd/huawei-p30-pro\" target=\"_blank\" title=\"Tham khảo điện thoại Huawei P30 Pro chính hãng\">Huawei P30 Pro</a> là một bước đột phá của Huawei cũng như camera trên <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại chính hãng\">smartphone</a> khi đem lại khả năng zoom như một \"kính viễn vọng\".</h2><h3>Định nghĩa lại camera phone</h3><p>Camera là điểm nhấn trên Huawei P30 Pro với camera chính SuperSpectrum độ phân giải 40 MP.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/197228/huawei-p30-pro9.jpg\" onclick=\"return false;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/197228/huawei-p30-pro9.jpg\" onclick=\"return false;\"><img alt=\"Camera sau của điện thoại Huawei P30 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/197228/huawei-p30-pro9.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/197228/huawei-p30-pro9.jpg\" title=\"Camera sau của điện thoại Huawei P30 Pro chính hãng\"></a></a></p><p>Cảm biến SuperSpectrum sử dụng cấu trúc RYYB (Red Yellow Yellow Blue), trái ngược với RGB (Red Green Blue), thay thế các pixel màu xanh lá cây bằng màu vàng, theo <a href=\"https://www.thegioididong.com/dtdd-huawei\" target=\"_blank\" title=\"Tham khảo điện thoại Huawei chính hãng\">Huawei</a> cho phép cảm biến cho ánh sáng nhiều hơn.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/197228/huawei-p30-pro5.jpg\" onclick=\"return false;\"><img alt=\"Camera sau của điện thoại Huawei P30 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/197228/huawei-p30-pro5.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/197228/huawei-p30-pro5.jpg\" title=\"Camera sau của điện thoại Huawei P30 Pro chính hãng\"></a></p><p>Cụm 3 camera còn lại bao gồm một ống kính góc siêu rộng 20 MP, tiêu cự 17mm, camera tele 8 MP tiêu cự 125mm (zoom 5X, khẩu độ f/3.4 và cuối cùng là camera ToF.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/197228/huawei-p30-pro2.jpg\" onclick=\"return false;\"><img alt=\"Camera sau của điện thoại Huawei P30 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/197228/huawei-p30-pro2.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/197228/huawei-p30-pro2.jpg\" title=\"Camera sau của điện thoại Huawei P30 Pro chính hãng\"></a></p><p>Cảm biến ToF sẽ thu thập thông tin chiều sâu bằng cách chiếu ánh sáng hồng ngoại vào đối tượng và phản xạ lại camera để đo khoảng cách. Công nghệ này sẽ cho phép chế độ chụp chân dung có hiệu ứng bo-keh chuyên nghiệp hơn giúp phông nền có độ mờ hợp lý.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/197228/huawei-p30-pro6.jpg\" onclick=\"return false;\"><img alt=\"Camera sau của điện thoại Huawei P30 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/197228/huawei-p30-pro6.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/197228/huawei-p30-pro6.jpg\" title=\"Camera sau của điện thoại Huawei P30 Pro chính hãng\"></a></p><p>Huawei đã tinh tế khi dùng thấu kính kết hợp gương nằm nghiêng thay vì xếp chồng như cách truyền thống. Thay đổi này cho phép P30 Pro zoom quang hybrid 10x mà không làm thay đổi chất lượng hình ảnh.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/197228/huawei-p30-pro12.jpg\" onclick=\"return false;\"><img alt=\"Khả năng zoom 5X của điện thoại Huawei P30 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/197228/huawei-p30-pro12.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/197228/huawei-p30-pro12.jpg\" title=\"Khả năng zoom 5X của điện thoại Huawei P30 Pro chính hãng\"></a></p><p>Chưa dừng lại ở đó, máy còn cho phép người dùng zoom tới 50X giúp bạn có thể nhìn rõ những hình ảnh ở xa mà mắt thường không thể nào quan sát được.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/197228/huawei-p30-pro13.jpg\" onclick=\"return false;\"><img alt=\"Khả năng zoom 10X của điện thoại Huawei P30 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/197228/huawei-p30-pro13.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/197228/huawei-p30-pro13.jpg\" title=\"Khả năng zoom 10X của điện thoại Huawei P30 Pro chính hãng\"></a></p><p>Ngoài ra Huawei P30 Pro còn là chiếc smartphone có độ nhạy sáng lớn nhất thế giới lên tới 409.600, kết hợp với khẩu độ rộng hơn sẽ cho phép camera có thể thu thập thêm nhiều thông tin ánh sáng để chất lượng hình ảnh được cải thiện.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/197228/huawei-p30-pro7.jpg\" onclick=\"return false;\"><img alt=\"Camera sau của điện thoại Huawei P30 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/197228/huawei-p30-pro7.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/197228/huawei-p30-pro7.jpg\" title=\"Camera sau của điện thoại Huawei P30 Pro chính hãng\"></a></p><p>Tính năng Dual-View cho phép người dùng cùng lúc quay phim bằng hai camera sau với hai tiêu cự khác nhau (góc cận và siêu rộng).</p><h3>Hiệu năng hàng đầu thế giới smartphone</h3><p>Cung cấp sức mạnh cho máy là bộ xử lý <a href=\"https://www.thegioididong.com/tin-tuc/hieu-nang-chip-kirin-980-co-manh-hon-apple-a12-nhu-quang-ba-1125230\" target=\"_blank\" title=\"Tìm hiểu Hisilicon Kirin 980 8 nhân 64-bit\">Hisilicon Kirin 980 8 nhân 64-bit</a> kết hợp với 8 GB RAM và 256 GB bộ nhớ trong đem lại cho bạn hiệu năng vượt trội.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/197228/huawei-p30-pro4.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình của điện thoại Huawei P30 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/197228/huawei-p30-pro4.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/197228/huawei-p30-pro4.jpg\" title=\"Cấu hình của điện thoại Huawei P30 Pro chính hãng\"></a></p><p>Hiện tại, đây là con chip mạnh mẽ nhất hiện nay của Huawei cho nên bạn có thể tự tin sử dụng tất cả các game và ứng dụng nặng nhất hiện nay mà máy vẫn hoạt động một cách mượt mà.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/197228/huawei-p30-pro1.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình của điện thoại Huawei P30 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/197228/huawei-p30-pro1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/197228/huawei-p30-pro1.jpg\" title=\"Cấu hình của điện thoại Huawei P30 Pro chính hãng\"></a></p><p>Huawei P30 Pro chạy trên phiên bản EMUI 9.1 mới nhất tới từ Huawei với những cải tiến mới như điều hướng cử chỉ hay các tính năng kỹ thuật số.</p><p>Máy còn hỗ trợ vài tính năng mới thú vị như OneHop, cho phép quay lại màn hình PC bằng điện thoại hay đồng bộ hóa tập tin.</p><h3>Những trang bị cao cấp chỉ có trên P30 Pro</h3><p>Trên Huawei P30 Pro thì bạn sẽ không còn thấy sự xuất hiện của loa thoại và Huawei đã sử dụng công nghệ truyền âm thanh qua màn hình rất độc đáo.</p><p>Bên cạnh đó vẫn là cảm biến được đặt ngay bên dưới màn hình nên bạn sẽ không phải hi sinh bất cứ bộ phận nào trên thiết kế để sử dụng tính năng này.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/197228/huawei-p30-pro9.jpg\" onclick=\"return false;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/197228/huawei-p30-pro9.jpg\" onclick=\"return false;\"><img alt=\"Camera sau của điện thoại Huawei P30 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/197228/huawei-p30-pro9.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/197228/huawei-p30-pro9.jpg\" title=\"Camera sau của điện thoại Huawei P30 Pro chính hãng\"></a></a></p><p>Tính năng nhận diện khuôn mặt vẫn xuất hiện và bạn có thể mở khóa máy chỉ trong tích tắc.</p><p>Ngoài ra máy cũng sẽ hỗ trợ chuẩn kháng nước và bụi bẩn IP68 cao cấp nhất hiện nay cho bạn thoải mái sử dụng trong những điều kiện khắc nghiệt của thời tiết.</p><h3>Dung lượng pin khủng, sạc nhanh siêu ấn tượng</h3><p>Huawei P30 Pro trang bị viên pin 4200 mAh cùng với khả năng sạc siêu nhanh SuperCharge 40W.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/197228/huawei-p30-pro11.jpg\" onclick=\"return false;\"><img alt=\"Thời lượng pin của điện thoại Huawei P30 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/197228/huawei-p30-pro11.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/197228/huawei-p30-pro11.jpg\" title=\"Thời lượng pin của điện thoại Huawei P30 Pro chính hãng\"></a></p><p>Huawei tuyên bố P30 Pro có thể sạc từ 0 lên 70% chỉ mất 30 phút và đây chính là một trong những chiếc smartphone sạc nhanh nhất trên thị trường hiện nay.</p><p>Công nghệ sạc ngược không dây cho tai nghe, bàn chải hoặc điện thoại khác của P30 Pro mang đến sự tiện dụng và linh hoạt cho người dùng.</p></article>', 154, 24600000, 25380000, '', '', '2019-06-23 21:00:48', 1, '2019-06-23 21:00:48', 1, 1);
INSERT INTO `uml_product` (`product_id`, `product_catid`, `product_name`, `product_slug`, `product_img`, `product_detail`, `product_number`, `product_price`, `product_pricesale`, `product_metakey`, `product_metadesc`, `product_createdat`, `product_createdby`, `product_updatedat`, `product_updatedby`, `product_status`) VALUES
(46, 9, 'Huawei P30', 'huawei-p30', 'huawei-p30.jpg', '<h2><a href=\"https://www.thegioididong.com/dtdd/huawei-p30\" target=\"_blank\" title=\"Tham khảo điện thoại Huawei P30 chính hãng\">Huawei P30</a> là chiếc <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại chính hãng\">smartphone</a> cao cấp vừa được Huawei giới thiệu với thiết kế tuyệt đẹp, hiệu năng mạnh mẽ và thiết lập camera ấn tượng.</h2><h3>Camera siêu ấn tượng</h3><p>Điện thoại dòng P của <a href=\"https://www.thegioididong.com/dtdd-huawei\" target=\"_blank\" title=\"Tham khảo điện thoại Huawei chính hãng\">Huawei</a> luôn cho ra mắt những công nghệ camera mới và P30 cũng không ngoại lệ.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/189785/huawei-p30-black9.jpg\" onclick=\"return false;\"><img alt=\"Camera sau của điện thoại Huawei P30 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/189785/huawei-p30-black9.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/189785/huawei-p30-black9.jpg\" title=\"Camera sau của điện thoại Huawei P30 chính hãng\"></a></p><p>Huawei P30 có 3 camera trên mặt lưng bao gồm ống kính chính SuperSpectrum 40 MP, khẩu độ f / 1.8 + ống kính góc cực rộng 16 MP + ống kính tele 8 MP với khả năng zoom quang 3X và khẩu độ f / 2.4.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/189785/huawei-p30-black6.jpg\" onclick=\"return false;\"><img alt=\"Camera sau của điện thoại Huawei P30 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/189785/huawei-p30-black6.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/189785/huawei-p30-black6.jpg\" title=\"Camera sau của điện thoại Huawei P30 chính hãng\"></a></p><p>SuperSpectrum là tên của Huawei cho bộ lọc màu RYYB, loại bỏ các yếu tố màu xanh lá cây khỏi bộ lọc RGGB và thay thế chúng bằng màu vàng.</p><p>Huawei nói rằng việc chuyển từ màu xanh lục sang màu vàng sẽ giúp cảm biến hấp thụ nhiều ánh sáng hơn.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/189785/huawei-p30-black4.jpg\" onclick=\"return false;\"><img alt=\"Camera sau của điện thoại Huawei P30 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/189785/huawei-p30-black4.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/189785/huawei-p30-black4.jpg\" title=\"Camera sau của điện thoại Huawei P30 chính hãng\"></a></p><p>Màu vàng cũng hấp thụ thêm ánh sáng xanh lá và đỏ, giúp cải thiện chất lượng hình ảnh trong bất cứ điều kiện nào, đặc biệt là trong điều kiện ánh sáng yếu.</p><p>Ngoài ra còn có một tính năng AI mới gọi là AI HDR, giúp điều chỉnh ánh sáng trên tấm ảnh dựa trên nguồn sáng từ đó đem lại cho người dùng những bức ảnh chất lượng hơn.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/189785/huawei-p30-black5.jpg\" onclick=\"return false;\"><img alt=\"Camera sau của điện thoại Huawei P30 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/189785/huawei-p30-black5.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/189785/huawei-p30-black5.jpg\" title=\"Camera sau của điện thoại Huawei P30 chính hãng\"></a></p><p>Theo Huawei cho biết họ đã cải thiện phần mềm để ứng dụng máy ảnh sẽ phản hồi nhanh hơn, đặc biệt là khi hoán đổi giữa các ống kính và mức thu phóng.</p><p>Với rất nhiều trang bị tập trung cho camera thì Huawei P30 sẽ thỏa mãn nhu cầu nhiếp ảnh cho cả những người dùng khó tính nhất.</p><h3>Hiệu năng mạnh mẽ, trang bị hàng đầu</h3><p>Cung cấp sức mạnh cho máy là bộ xử lý <a href=\"https://www.thegioididong.com/tin-tuc/hieu-nang-chip-kirin-980-co-manh-hon-apple-a12-nhu-quang-ba-1125230\" target=\"_blank\" title=\"Tìm hiểu Hisilicon Kirin 980 8 nhân 64-bit\">Hisilicon Kirin 980 8 nhân 64-bit</a> kết hợp với 8 GB RAM và 128 GB bộ nhớ trong đem lại cho bạn hiệu năng vượt trội.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/189785/huawei-p30-black3.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình của điện thoại Huawei P30 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/189785/huawei-p30-black3.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/189785/huawei-p30-black3.jpg\" title=\"Cấu hình của điện thoại Huawei P30 chính hãng\"></a></p><p>Có thể bạn không biết thì Kirin 980 chính là bộ vi xử lý dự trên tiến trình 7nm đầu tiên trên thế giới đem lại hiệu năng cao hơn nhưng lại tiết kiệm năng lượng hơn.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/189785/huawei-p30-black1.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình của điện thoại Huawei P30 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/189785/huawei-p30-black1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/189785/huawei-p30-black1.jpg\" title=\"Cấu hình của điện thoại Huawei P30 chính hãng\"></a></p><p>Máy chạy trên phiên bản EMUI 9.1 của Huawei được dựa trên Android 9.0 Pie cài đặt sẵn cho điện thoại, mang lại trải nghiệm người dùng tuyệt vời như Mate 20 Pro.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/189785/huawei-p30-black2.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình của điện thoại Huawei P30 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/189785/huawei-p30-black2.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/189785/huawei-p30-black2.jpg\" title=\"Cấu hình của điện thoại Huawei P30 chính hãng\"></a></p><p>Máy còn hỗ trợ tính năng OneHop cho phép người dùng có thể đồng bộ hóa các tập tin ngay lập tức với các PC tương thích chỉ với một lần nhấn.</p><h3>Nhiều trang bị dành riêng cho những chiếc máy cao cấp</h3><p>Một chiếc máy cao cấp như Huawei P30 tất nhiên sẽ không thể thiếu công nghệ cảm biến vân tay dưới màn hình tiên tiến nhất hiện nay.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/189785/huawei-p30-black11.jpg\" onclick=\"return false;\"><img alt=\"Cảm biến vân tay trên điện thoại Huawei P30 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/189785/huawei-p30-black11.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/189785/huawei-p30-black11.jpg\" title=\"Cảm biến vân tay trên điện thoại Huawei P30 chính hãng\"></a></p><p>Tốc độ nhận diện nhanh cùng việc cảm biến được đặt ngay bên dưới màn hình nên bạn sẽ không phải hi sinh bất cứ bộ phận nào trên thiết kế để sử dụng tính năng này.</p><p>Bạn cũng có thể sử dụng tính năng mở khóa khuôn mặt đề phòng trường hợp cảm biến vân tay không khả dụng.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/189785/huawei-p30-black10.jpg\" onclick=\"return false;\"><img alt=\"Thời lượng pin của điện thoại Huawei P30 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/189785/huawei-p30-black10.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/189785/huawei-p30-black10.jpg\" title=\"Thời lượng pin của điện thoại Huawei P30 chính hãng\"></a></p><p>Tiếp theo không thể không kể đến là công nghệ sạc nhanh với viên pin dung lượng ổn 3.650 mAh đáp ứng nhu cầu sử dụng cho bạn trong cả ngày.</p><p>Ngoài ra còn rất nhiều trang bị hấp dẫn khác có thể kết tới như hỗ trợ kháng nước IP53, Dolby Atmos, Bluetooth 5.0, GPS băng tần kép, cổng kết nối 3.5 mm và tính năng Bluetooth aptX HD.</p><h3>Thiết kế cao cấp, thu hút mọi ánh nhìn</h3><p>Huawei P30 được được gia công bằng chất liệu kính và kim loại cao cấp nên khi bạn cầm trên tay sẽ phần nào tôn lên sự sang trọng và quý phái cho chủ nhân của nó.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/189785/huawei-p30-black8.jpg\" onclick=\"return false;\"><img alt=\"Màn hình của điện thoại Huawei P30 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/189785/huawei-p30-black8.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/189785/huawei-p30-black8.jpg\" title=\"Màn hình của điện thoại Huawei P30 chính hãng\"></a></p><p>Thay vì sử dụng màn hình “tai thỏ” như năm ngoái, năm nay Huawei P30 được nâng cấp lên màn hình “giọt sương” và phần khuyết chỉ có một cái camera selfie vì vậy máy sẽ được nhiều diện tích hiển thị hơn.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/189785/huawei-p30-black7.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế của điện thoại Huawei P30 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/189785/huawei-p30-black7.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/189785/huawei-p30-black7.jpg\" title=\"Thiết kế của điện thoại Huawei P30 chính hãng\"></a></p><p>Huawei P30 khi bán ra sẽ có những màu sắc mang hiệu ứng đổi màu khá đẹp mắt khi nghiêng ở các góc nhìn khác nhau.</p></article>', 63, 16500000, 17280000, '', '', '2019-06-23 21:00:49', 1, '2019-06-23 21:00:49', 1, 1),
(47, 9, 'Huawei Mate 20 Pro', 'huawei-mate-20-pro', 'huawei-mate-20-pro.jpg', '<h2>Thế hệ siêu phẩm mới của <a href=\"https://www.thegioididong.com/dtdd-huawei\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại Huawei chính hãng, giá tốt đang kinh doanh tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại Huawei chính hãng, giá tốt đang kinh doanh tại Thegioididong.com\">Huawei</a> chính thức lộ diện với cái tên <a href=\"https://www.thegioididong.com/dtdd/huawei-mate-20-pro\" target=\"_blank\" title=\"Tham khảo chi tiết điện thoại Huawei Mate 20 Pro tại Thegioididong.com\" type=\"Tham khảo chi tiết điện thoại Huawei Mate 20 Pro tại Thegioididong.com\">Huawei Mate 20 Pro</a>, chiếc <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại chính hãng, giá tốt đang kinh doanh tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại chính hãng, giá tốt đang kinh doanh tại Thegioididong.com\">điện thoại</a> thu hút trong thiết kế, mạnh mẽ trong hiệu năng cùng một hệ thống camera ấn tượng.</h2><h3>Thiết kế mới mẻ, bắt mắt</h3><p>Sang trọng và đẳng cấp là những từ mà bạn có thể thốt lên khi chiêm ngưỡng thân hình của siêu phẩm Huawei Mate 20 Pro.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/188963/huawei-mate-20-pro-1-1.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại Huawei Mate 20 Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/188963/huawei-mate-20-pro-1-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/188963/huawei-mate-20-pro-1-1.jpg\" title=\"Thiết kế điện thoại Huawei Mate 20 Pro\"></a></p><p>Máy sở hữu lối thiết kế hiện đại, trang nhã trong sự kết hợp hài hòa giữa khung kim loại chắc chắn cùng 2 mặt kính cường lực cao cấp.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/188963/huawei-mate-20-pro-5-1.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại Huawei Mate 20 Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/188963/huawei-mate-20-pro-5-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/188963/huawei-mate-20-pro-5-1.jpg\" title=\"Thiết kế điện thoại Huawei Mate 20 Pro\"></a></p><p>Mềm mại và uyển chuyển từ các góc cạnh là những gì mà bạn sẽ cảm nhận được mỗi khi cầm nắm máy trên tay.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/188963/huawei-mate-20-pro-10.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại Huawei Mate 20 Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/188963/huawei-mate-20-pro-10.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/188963/huawei-mate-20-pro-10.jpg\" title=\"Thiết kế điện thoại Huawei Mate 20 Pro\"></a></p><p>Hơn nữa, Mate 20 Pro còn được tô điểm bởi những màu sắc vô cùng quyến rũ trông máy nổi bật và tinh tế.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/188963/huawei-mate-20-pro-8.jpg\" onclick=\"return false;\"><img alt=\"Cụm camera sau điện thoại Huawei Mate 20 Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/188963/huawei-mate-20-pro-8.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/188963/huawei-mate-20-pro-8.jpg\" title=\"Cụm camera sau điện thoại Huawei Mate 20 Pro\"></a></p><p>Ấn tượng nhất phải kể đến mặt lưng với cụm camera hình vuông mới lạ giúp bạn cảm thấy cực kì thích thú mỗi khi ngắm nhìn cũng như giúp máy tạo nên điểm nhấn riêng trong thiết kế.</p><h3>Màn hình siêu sắc nét, chuẩn 2K+</h3><p>Huawei Mate 20 Pro được trang bị một không gian hiển thị rộng rãi có kích thước lên đến 6.39 inch đi kèm độ phân giải Quad HD+ (2K+) siêu nét.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/188963/huawei-mate-20-pro-12.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại Huawei Mate 20 Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/188963/huawei-mate-20-pro-12.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/188963/huawei-mate-20-pro-12.jpg\" title=\"Màn hình điện thoại Huawei Mate 20 Pro\"></a></p><p>Chưa hết, đi kèm theo đó là một tấm nền <a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-oled-la-gi-905762\" target=\"_blank\" title=\"Tìm hiểu tấm nền OLED\" type=\"Tìm hiểu tấm nền OLED\">OLED</a> giúp cho các khung hình luôn được sống động, rực rỡ để bạn có thể hòa mình vào những bộ phim hay một cách trọn vẹn.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/188963/huawei-mate-20-pro-2-1.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại Huawei Mate 20 Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/188963/huawei-mate-20-pro-2-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/188963/huawei-mate-20-pro-2-1.jpg\" title=\"Màn hình điện thoại Huawei Mate 20 Pro\"></a></p><p>Đặc biệt, màn hình này còn có độ sáng cực cao nên việc sử dụng máy ngoài trời sẽ không còn là vấn đề khiến bạn phải băn khoăn nữa.</p><h3>Hiệu năng hàng đầu hiện nay</h3><p>Hisilicon Kirin 980, một con chip \"cây nhà lá vườn\" được Huawei trang bị cho máy, ẩn chứa một sức mạnh khủng khiếp giúp Mate 20 Pro dễ dàng vượt qua các thử thách về hiệu năng.</p><p>Bên cạnh đó, với 6 GB RAM và 128 GB bộ nhớ trong đi kèm giúp bạn thỏa sức chạy đa nhiệm hay lưu trữ game và ứng dụng mà không cần lo lắng.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/188963/huawei-mate-20-pro-11.jpg\" onclick=\"return false;\"><img alt=\"Giao diện Android trên điện thoại Huawei Mate 20 Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/188963/huawei-mate-20-pro-11.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/188963/huawei-mate-20-pro-11.jpg\" title=\"Giao diện Android trên điện thoại Huawei Mate 20 Pro\"></a></p><p>Từng đó là chưa đủ thì Mate 20 Pro còn có thêm <a href=\"https://www.thegioididong.com/hoi-dap/top-5-tinh-nang-moi-duoc-yeu-thich-nhat-tren-and-1107119\" target=\"_blank\" title=\"Tìm hiểu hệ điều hành Android 9\" type=\"Tìm hiểu hệ điều hành Android 9\">Android 9.0 (Pie)</a> mới nhất, sẽ đưa bạn đến những trải nghiệm cực kì thú vị với hàng loạt các tính năng mới được cập nhật và bổ sung.</p><h3>Điểm nhấn đặc biệt về camera</h3><p>Về khả năng nhiếp ảnh, Mate 20 Pro sở hữu cho mình cụm camera 3 thấu kính với thấu kính chính 40 MP và hai thấu kính phụ 20 MP + 8 MP.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/188963/huawei-mate-20-pro-9.jpg\" onclick=\"return false;\"><img alt=\"Camera trên điện thoại Huawei Mate 20 Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/188963/huawei-mate-20-pro-9.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/188963/huawei-mate-20-pro-9.jpg\" title=\"Camera trên điện thoại Huawei Mate 20 Pro\"></a></p><p>Cụ thể hơn, cảm biến 40 MP dùng để bắt chính đối tượng, 8 MP phục vụ cho khả năng phóng to hay thu nhỏ ảnh còn 20 MP là một ống kính góc rộng.</p><p>Với sự hỗ trợ đắc lực từ ống kính LEICA nổi tiếng đã giúp Mate 20 Pro trở thành một trong những smartphone có chất lượng ảnh chụp đẹp nhất hiện nay.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/188963/huawei-mate-20-pro-4-1.jpg\" onclick=\"return false;\"><img alt=\"Camera sau trên điện thoại Huawei Mate 20 Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/188963/huawei-mate-20-pro-4-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/188963/huawei-mate-20-pro-4-1.jpg\" title=\"Camera sau trên điện thoại Huawei Mate 20 Pro\"></a></p><p>Sâu hơn một chút thì máy được trang bị gần như là đầy đủ các tính năng chụp ảnh cao cấp, hiện đại điển hình như: <a href=\"https://www.thegioididong.com/tin-tuc/tim-hieu-xoa-phong-huong-dan-xoa-phong-tren-moi-smartphone-892585\" target=\"_blank\" title=\"Tìm hiểu tính năng chụp ảnh xóa phông\" type=\"Tìm hiểu tính năng chụp ảnh xóa phông\">chụp xóa phông</a>, quay phim <a href=\"https://www.thegioididong.com/hoi-dap/slow-motion-la-gi-1013728\" target=\"_blank\" title=\"Tìm hiểu tính năng quay phim Slow Motion\" type=\"Tìm hiểu tính năng quay phim Slow Motion\">Slow Motion</a>, AI camera,...</p><p>Ảnh chụp mà máy cho ra vô cùng ấn tượng với độ sáng cao, màu sắc hài hòa, sống động và độ chi tiết được tái tạo một cách tối ưu.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/188963/huawei-mate-20-pro-camera-1.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera điện thoại Huawei Mate 20 Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/188963/huawei-mate-20-pro-camera-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/188963/huawei-mate-20-pro-camera-1.jpg\" title=\"Ảnh chụp từ camera điện thoại Huawei Mate 20 Pro\"></a></p><p>Về khả năng chụp ảnh tự sướng thì Mate 20 Pro sẽ không khiến bạn thất vọng nhờ có cụm camera 24 MP hỗ trợ nhiều tính năng chuyên nghiệp.</p><h3>Tích hợp hàng loạt các trang bị hiện đại</h3><p>Là một flagship hàng đầu nên Huawei cũng không quên trang bị cho Mate 20 Pro khả năng <a href=\"https://www.thegioididong.com/hoi-dap/chong-nuoc-va-chong-bui-tren-smart-phone-771130\" target=\"_blank\" title=\"Tìm hiểu tính năng kháng nước và bụi\" type=\"Tìm hiểu tính năng kháng nước và bụi\">kháng nước và bụi</a> cao cấp để giúp máy được an toàn hơn.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/188963/huawei-mate-20-pro-7.jpg\" onclick=\"return false;\"><img alt=\"Đánh giá điện thoại Huawei Mate 20 Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/188963/huawei-mate-20-pro-7.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/188963/huawei-mate-20-pro-7.jpg\" title=\"Đánh giá điện thoại Huawei Mate 20 Pro\"></a></p><p>Song song với đó, máy còn đi kèm công nghệ <a href=\"https://www.thegioididong.com/tin-tuc/clear-id-la-gi-cam-bien-van-tay-duoi-man-hinh-co-gi-moi-1057643\" target=\"_blank\" title=\"Tìm hiểu công nghệ cảm biến vân tay dưới màn hình\" type=\"Tìm hiểu công nghệ cảm biến vân tay dưới màn hình\">cảm biến vân tay dưới màn hình</a> siêu chất mà nhiều sản phẩm chưa có được.</p><p>Tốc độ mở khóa máy khá nhanh và chuẩn xác, chỉ thua kém một chút so với cảm biến điện dung thông thường.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/188963/huawei-mate-20-pro-3-1.jpg\" onclick=\"return false;\"><img alt=\"Đánh giá điện thoại Huawei Mate 20 Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/188963/huawei-mate-20-pro-3-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/188963/huawei-mate-20-pro-3-1.jpg\" title=\"Đánh giá điện thoại Huawei Mate 20 Pro\"></a></p><h3>Dung lượng siêu khủng</h3><p>Không chỉ có một thiết kế đẹp, một hiệu năng mạnh mẽ mà Mate 20 Pro còn được trang bị viên pin khủng với dung lượng 4200 mAh đáp ứng thoải mái cho nhu cầu giải trí thường ngày.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/188963/huawei-mate-20-pro-6-1.jpg\" onclick=\"return false;\"><img alt=\"Đánh giá điện thoại Huawei Mate 20 Pro\" src=\"https://cdn.tgdd.vn/Products/Images/42/188963/huawei-mate-20-pro-6-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/188963/huawei-mate-20-pro-6-1.jpg\" title=\"Đánh giá điện thoại Huawei Mate 20 Pro\"></a></p><p><span 4200=\"\" cho=\"\" dejavu=\"\" i=\"\" liberation=\"\" ng=\"\" nhu=\"\" p=\"\" span=\"\" u=\"\">Ngoài ra, máy còn tích hợp <a href=\"https://www.thegioididong.com/hoi-dap/cong-nghe-sac-nhanh-tren-smartphone-755549\" target=\"_blank\" title=\"Tìm hiểu công nghệ sạc nhanh\" type=\"Tìm hiểu công nghệ sạc nhanh\">công nghệ sạc nhanh</a> giúp việc trải nghiệm, khám phá của bạn sẽ không bị gián đoạn quá lâu.</span></p><p><div class=video allow=\"accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"https://www.youtube.com/embed/d9cg9G3ZVn4\" width=\"560\"></div></p></article>', 168, 28000000, 0, '', '', '2019-06-23 21:00:49', 1, '2019-06-23 21:00:49', 1, 1),
(48, 9, 'Huawei P30 Lite', 'huawei-p30-lite', 'huawei-p30-lite.jpg', '<h2>Mới đây Huawei đã chính thức giới thiệu chiếc <a href=\"https://www.thegioididong.com/dtdd/huawei-p30-lite\" target=\"_blank\" title=\"Tham khảo điện thoại Huawei P30 Lite chính hãng\">Huawei P30 Lite</a> bên cạnh các sản phẩm khác trong dòng P30 series và chiếc <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại chính hãng\">smartphone</a> được định hướng tới phân khúc tầm trung.</h2><h3>Camera vẫn rất nổi bật trong phân khúc</h3><p>Điểm nhấn đáng chú ý nhất trên chiếc <a href=\"https://www.thegioididong.com/dtdd-huawei\" target=\"_blank\" title=\"Tham khảo điện thoại Huawei chính hãng\">điện thoại Huawei</a> là hệ thống 3 camera sau bao gồm cảm biến chính 24 MP (khẩu độ f/.8), cảm biến độ sâu 2 MP và cảm biến 8 MP hỗ trợ chụp góc rộng.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/198985/huawei-p30-lite6.jpg\" onclick=\"return false;\"><img alt=\"Camera sau của điện thoại Huawei P30 Lite chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/198985/huawei-p30-lite6.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/198985/huawei-p30-lite6.jpg\" title=\"Camera sau của điện thoại Huawei P30 Lite chính hãng\" /></a></p><p>Với việc hỗ trợ thêm một camera góc rộng thì bạn có thể có được cho mình những bức ảnh với phong cách rộng tuyệt đẹp, không bỏ sót bất cứ thứ gì.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/198985/huawei-p30-lite1.jpg\" onclick=\"return false;\"><img alt=\"Camera sau của điện thoại Huawei P30 Lite chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/198985/huawei-p30-lite1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/198985/huawei-p30-lite1.jpg\" title=\"Camera sau của điện thoại Huawei P30 Lite chính hãng\" /></a></p><p>Đồng thời, camera của máy còn có chế độ chụp ảnh đêm mới và tích hợp AI để tối ưu chụp ảnh, tạo nhãn dán AR hay tìm kiếm thông tin trên ảnh.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/198985/huawei-p30-lite9.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera của điện thoại Huawei P30 Lite chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/198985/huawei-p30-lite9.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/198985/huawei-p30-lite9.jpg\" title=\"Ảnh chụp từ camera của điện thoại Huawei P30 Lite chính hãng\" /></a></p><p>Đây cũng là chiếc điện thoại đầu tiên của Huawei được trang bị camera selfie lên đến 32MP, cao nhất thế giới hiện nay, hứa hẹn đem đến cho người dùng những bức ảnh tự sướng có chất lượng cao.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/198985/huawei-p30-lite8.jpg\" onclick=\"return false;\"><img alt=\"Camera trước selfie của điện thoại Huawei P30 Lite chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/198985/huawei-p30-lite8.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/198985/huawei-p30-lite8.jpg\" title=\"Camera trước selfie của điện thoại Huawei P30 Lite chính hãng\" /></a></p><p>Tất nhiên cũng không thể thiếu tính năng làm đẹp bằng AI cho camera trước, máy sẽ làm đẹp dựa trên cấu trúc và hình dạng khuôn mặt của mỗi người sẽ đảm bảo bạn luôn có được những bức hình rạng rỡ nhất.</p><h3>Thoải mái lưu trữ với 128 GB bộ nhớ trong</h3><p>Huawei P30 Lite cải thiện RAM và dung lượng bộ nhớ tạo ra nhiều không gian lưu giữ hơn cho những thứ bạn yêu thích.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/198985/huawei-p30-lite5.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình của điện thoại Huawei P30 Lite chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/198985/huawei-p30-lite5.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/198985/huawei-p30-lite5.jpg\" title=\"Cấu hình của điện thoại Huawei P30 Lite chính hãng\" /></a></p><p>Bạn có thể thoải mái lưu trữ bài hát, hình ảnh hay video trong thời gian dài mà không phải xóa đi bất cứ thứ gì.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/198985/huawei-p30-lite3.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình của điện thoại Huawei P30 Lite chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/198985/huawei-p30-lite3.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/198985/huawei-p30-lite3.jpg\" title=\"Cấu hình của điện thoại Huawei P30 Lite chính hãng\" /></a></p><p>Bên cạnh đó GPU Turbo đã được nâng cấp thêm để hỗ trợ nhiều game hơn, cho phép HUAWEI P30 Lite mang đến người dùng trải nghiệm tốt hơn với mọi tựa game hàng đầu hiện nay.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/198985/huawei-p30-lite4.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình của điện thoại Huawei P30 Lite chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/198985/huawei-p30-lite4.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/198985/huawei-p30-lite4.jpg\" title=\"Cấu hình của điện thoại Huawei P30 Lite chính hãng\" /></a></p><p>Với P30 Lite, người dùng còn có thể tải clip trên các website hoặc nền tảng mạng xã hội để sử dụng tính năng hỗ trợ cài đặt nhạc chuông bằng video.</p><h3>Màn hình rực rỡ, pin xài cả ngày</h3><p>Huawei P30 Lite sở hữu màn hình \"giọt nước\" 6.13 inch với độ phân giải Full HD+ với tấm nền IPS cho màu sắc đẹp mắt, hình ảnh sắc nét.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/198985/huawei-p30-lite2.jpg\" onclick=\"return false;\"><img alt=\"Màn hình của điện thoại Huawei P30 Lite chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/198985/huawei-p30-lite2.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/198985/huawei-p30-lite2.jpg\" title=\"Màn hình của điện thoại Huawei P30 Lite chính hãng\" /></a></p><p>Người dùng cũng sẽ có tính năng ẩn đi tai thỏ với cạnh viền bo cong thay vì đường thẳng, điều này tạo nên nét hài hòa cho máy khi nhìn vào tổng thể.</p><p>Dung lượng pin 3340 mAh của máy giúp bạn thoải mái sử dụng cả ngày mà không lo máy hết pin giữa chừng.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/198985/huawei-p30-lite7.jpg\" onclick=\"return false;\"><img alt=\"Thời lượng pin của điện thoại Huawei P30 Lite chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/198985/huawei-p30-lite7.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/198985/huawei-p30-lite7.jpg\" title=\"Thời lượng pin của điện thoại Huawei P30 Lite chính hãng\" /></a></p><p>Công nghệ sạc nhanh giúp tối ưu thời gian sạc đồng thời giúp bạn vẫn có được thời gian sử dụng thoải mái khi không có nhiều thời gian sạc pin.</p></article>', 148, 24400000, 25180000, '', '', '2019-06-23 21:00:50', 1, '2019-06-23 21:00:50', 1, 1),
(49, 9, 'Huawei Y9 Prime (2019)', 'huawei-y9-prime-2019', 'huawei-y9-prime-2019.jpg', '<h2><a href=\"https://www.thegioididong.com/dtdd/huawei-y9-prime-2019\" target=\"_blank\" title=\"Tham khảo điện thoại Huawei Y9 Prime (2019) chính hãng\">Huawei Y9 Prime (2019)</a> là phiên bản kế nhiệm của chiếc Y9 Prime (2018) đã ra mắt năm ngoái và cũng là chiếc điện thoại đầu tiên của <a href=\"https://www.thegioididong.com/dtdd-huawei\" target=\"_blank\" title=\"Tham khảo điện thoại Huawei chính hãng\">Huawei</a> được trang bị công nghệ camera trượt và màn hình tràn viền ra bốn cạnh.</h2><h3>Thiết kế tràn viền đúng nghĩa</h3><p>Gần đây thì xu thế làm <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại chính hãng\">smartphone</a> \"tai thỏ\" hay \"giọt nước\" đã không còn được yêu thích như trước mà thay vào đó là màn hình <a href=\"https://www.thegioididong.com/dtdd?f=man-hinh-tran-vien\" target=\"_blank\" title=\"Tham khảo giá màn hình tràn viền\" type=\"Tham khảo giá màn hình tràn viền\">tràn viền</a> đúng nghĩa.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/202268/huawei-y9-prime-2019-tgdd5.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế của điện thoại Huawei Y9 Prime (2019) chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/202268/huawei-y9-prime-2019-tgdd5.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/202268/huawei-y9-prime-2019-tgdd5.jpg\" title=\"Thiết kế của điện thoại Huawei Y9 Prime (2019) chính hãng\" /></a></p><p>Ấn tượng đầu tiên về thiết kế của Huawei Y9 Prime (2019) đó là việc thiết bị này được trang bị màn hình không bị khuyết bất cứ một phần nhỏ nào.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/202268/huawei-y9-prime-2019-tgdd1.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế của điện thoại Huawei Y9 Prime (2019) chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/202268/huawei-y9-prime-2019-tgdd1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/202268/huawei-y9-prime-2019-tgdd1.jpg\" title=\"Thiết kế của điện thoại Huawei Y9 Prime (2019) chính hãng\" /></a></p><p>Bạn sẽ có một trải nghiệm hoàn toàn mới, không gian giờ đây như mở rộng ra về bốn cạnh.</p><p>Màn hình của Huawei Y9 Prime (2019) có kích thước 6.59 inch, độ phân giải Full HD+, tấm nền LCD IPS.</p><p>Hình ảnh hiển thị trên tấm nền ở mức khá, màu sắc hiển thị ở tone màu trung tính, khá chính xác với màu sắc thực tế.</p><p>Nhìn chung với màn hình lớn việc giải trí trên Y9 Prime (2019) rất tốt, đặc biệt là những tác vụ giải trí và chơi game.</p><h3>Camera \"thoắt ẩn thoắt hiện\"</h3><p>Điểm nhấn trên Huawei Y9 Prime (2019) chính là cụm camera trước đó là khả năng “thò thụt” giúp chiếc máy trông cực ngầu và hiện đại.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/202268/huawei-y9-prime-2019-tgdd6.jpg\" onclick=\"return false;\"><img alt=\"Camera trước selfie của điện thoại Huawei Y9 Prime (2019) chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/202268/huawei-y9-prime-2019-tgdd6.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/202268/huawei-y9-prime-2019-tgdd6.jpg\" title=\"Camera trước selfie của điện thoại Huawei Y9 Prime (2019) chính hãng\" /></a></p><p>Ống kính được trang bị trên cụm camera trước là 16 MP cho phép người dùng ghi lại những khoảnh khắc rất chân thật.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/202268/huawei-y9-prime-2019-tgdd3.jpg\" onclick=\"return false;\"><img alt=\"Camera trước selfie của điện thoại Huawei Y9 Prime (2019) chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/202268/huawei-y9-prime-2019-tgdd3.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/202268/huawei-y9-prime-2019-tgdd3.jpg\" title=\"Camera trước selfie của điện thoại Huawei Y9 Prime (2019) chính hãng\" /></a></p><p>Camera chính của máy cũng gây ấn tượng với 3 camera có độ phân giải lần lượt là: 16 MP, 8 MP và 2 MP cho khả năng xoá phông tốt, thuật toán AI nhận diện bối cảnh và chủ thể nhanh, cho độ chính xác cao.</p><p>Camera góc rộng là điểm cộng trên chiếc Huawei Y9 Prime (2019), khi thực hiện chụp ảnh góc rộng, ảnh cho ra những hiệu ứng bo cong trông rất ấn tượng.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/202268/huawei-y9-prime-2019-tgdd2.jpg\" onclick=\"return false;\"><img alt=\"Camera sau của điện thoại Huawei Y9 Prime (2019) chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/202268/huawei-y9-prime-2019-tgdd2.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/202268/huawei-y9-prime-2019-tgdd2.jpg\" title=\"Camera sau của điện thoại Huawei Y9 Prime (2019) chính hãng\" /></a></p><p>Nhìn chung Huawei Y9 Prime (2019) là một chiếc máy có camera tốt trong tầm giá và bạn có thể tự tin dùng chiếc smartphone này để ghi lại những khoảnh khắc đẹp hằng ngày.</p><h3>Dung lượng pin thoải mái cả ngày</h3><p>Huawei Y9 Prime (2019) được nhà sản xuất ưu ái trang bị viên <a href=\"https://www.thegioididong.com/dtdd?f=pin-khung-3000-mah\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại có dung lượng pin \">pin dung lượng cao</a> với 4000 mAh giúp bạn thoải mái dùng cả ngày mà không cần sạc pin.</p><p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/202268/huawei-y9-prime-2019-tgdd4.jpg\" onclick=\"return false;\"><img alt=\"Thời lượng pin của điện thoại Huawei Y9 Prime (2019) chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/202268/huawei-y9-prime-2019-tgdd4.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/202268/huawei-y9-prime-2019-tgdd4.jpg\" title=\"Thời lượng pin của điện thoại Huawei Y9 Prime (2019) chính hãng\" /></a></p><p>Bên cạnh đó máy cũng sở hữu tính năng tiết kiệm pin giúp máy có thể hoạt động lâu hơn trong những trường hợp khẩn cấp.</p><p>2 SIM 2 sóng cùng kết nối 4G tốc độ cao tất nhiên sẽ là những tính năng không thể thiếu giúp bạn thoải mái Facebook mọi lúc mọi nơi.</p></article>', 193, 12600000, 13380000, '', '', '2019-06-23 21:00:51', 1, '2019-06-23 21:00:51', 1, 1),
(50, 9, 'Huawei Nova 3i', 'huawei-nova-3i', 'huawei-nova-3i.jpg', '<h2>Với những smartphone như <a href=\"https://www.thegioididong.com/dtdd/huawei-nova-2i\" target=\"_blank\" title=\"Tham khảo điện thoại Huawei Nova 2i tại Thegioididong.com\">Nova 2i</a> hay <a href=\"https://www.thegioididong.com/dtdd/huawei-nova-3e\" target=\"_blank\" title=\"Tham khảo điện thoại Huawei Nova 3e tại Thegioididong.com\">Nova 3e</a> thì Huawei đã và đang tạo nên những cơn sốt rất lớn trong phân khúc tầm trung và cái tên mới <a href=\"https://www.thegioididong.com/dtdd/huawei-nova-3i\" target=\"_blank\" title=\"Tham khảo điện thoại Huawei Nova 3i tại Thegioididong.com\">Huawei Nova 3i</a> được cải tiến và nâng cấp nhiều điểm mới, hứa hẹn sẽ mang lại làn gió mới cho thị trường.</h2><h3>Thiết kế bắt mắt với mặt lưng chuyển màu</h3><p>Nova 3i sở hữu mặt lưng kính chuyển màu gradient kiểu như <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại chính hãng tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại chính hãng tại Thegioididong.com\">điện thoại</a> Huawei P20 Pro, nhờ thiết kế mặt lưng kính ấn tượng đã đem lại cho máy một thiết kế hiện đại và tinh tế.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/179673/huawei-nova-3i8.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại Huawei Nova 3i\" src=\"https://cdn.tgdd.vn/Products/Images/42/179673/huawei-nova-3i8.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/179673/huawei-nova-3i8.jpg\" title=\"Thiết kế điện thoại Huawei Nova 3i\"></a></p><p>Máy sở hữu khung viền kim loại sẽ mang lại độ bền cao hơn, đảm bảo an toàn khi xảy ra các va chạm với môi trường.</p><h3>Cụm camera được nâng cấp</h3><p>Chiếc <a href=\"https://www.thegioididong.com/dtdd-huawei\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại Huawei chính hãng tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại Huawei chính hãng tại Thegioididong.com\">điện thoại Huawei</a> này sở hữu cụm camera kép ở mặt lưng với cảm biến chính 16 MP và cảm biến phụ 2 MP có khả năng lấy nét rất nhanh.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/179673/huawei-nova-3i10.jpg\" onclick=\"return false;\"><img alt=\"Camera sau của điện thoại Huawei Nova 3i\" src=\"https://cdn.tgdd.vn/Products/Images/42/179673/huawei-nova-3i10.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/179673/huawei-nova-3i10.jpg\" title=\"Camera sau của điện thoại Huawei Nova 3i\"></a></p><p>Khả năng xóa phông của máy cũng được cải thiện đáng kể, màu sắc bắt mắt và mang lại những bức ảnh sống ảo đẹp mắt cho người dùng.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/179673/huawei-nova-3i-cameraai.gif\" onclick=\"return false;\"><img alt=\"Camera sau điện thoại Huawei Nova 3\" src=\"https://cdn.tgdd.vn/Products/Images/42/179673/huawei-nova-3i-cameraai.gif\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/179673/huawei-nova-3i-cameraai.gif\" title=\"Camera sau điện thoại Huawei Nova 3\"></a></p><p>Máy cũng có cụm camera selfie kép gồm thấu kính chính 24 MP và thấu kính phụ 2 MP có hỗ trợ chế độ <a href=\"https://www.thegioididong.com/hoi-dap/ar-stickers-la-gi-vi-sao-hang-nao-cung-ap-dung-ar-1096228\" target=\"_blank\" title=\"Tìm hiểu tính năng AR Stickers trên camera điện thoại\" type=\"Tìm hiểu tính năng AR Stickers trên camera điện thoại\">AR Stickers</a> kết hợp khả năng làm đẹp, cho bạn thoải mái tự sướng với bạn bè.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/179673/huawei-nova-3i-ar-stickers.gif\" onclick=\"return false;\"><img alt=\"\" src=\"https://cdn.tgdd.vn/Products/Images/42/179673/huawei-nova-3i-ar-stickers.gif\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/179673/huawei-nova-3i-ar-stickers.gif\" title=\"Tính năng AR Stickers trên điện thoại Huawei Nova 3i\"></a></p><h3>Màn hình rất đẹp với \"tai thỏ\" tỉ lệ mới</h3><p>Nova 3i sở hữu một màn hình có tai thỏ tỉ lệ 19.5:9, với kích thước 6.3 inch, độ phân giải Full HD+ với màu sắc tươi tắn, độ sáng khá cao.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/179673/huawei-nova-3i6.jpg\" onclick=\"return false;\"><img alt=\"Màn hình của điện thoại Huawei Nova 3i\" src=\"https://cdn.tgdd.vn/Products/Images/42/179673/huawei-nova-3i6.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/179673/huawei-nova-3i6.jpg\" title=\"Màn hình của điện thoại Huawei Nova 3i\"></a></p><p>Kích thước màn hình lớn đem lại cho người dùng một không gian rộng rãi, thích hợp cho các tác vụ giải trí như xem phim, chơi game,...</p><h3>Hiệu năng ấn tượng với chip mới Kirin 710</h3><p>Cung cấp sức mạnh cho máy là con chip HiSillicon Kirin 710, với 4 GB RAM và 128 GB bộ nhớ trong.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/179673/huawei-nova-3i4.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình của điện thoại Huawei Nova 3i\" src=\"https://cdn.tgdd.vn/Products/Images/42/179673/huawei-nova-3i4.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/179673/huawei-nova-3i4.jpg\" title=\"Cấu hình của điện thoại Huawei Nova 3i\"></a></p><p>Đặc biệt, Nova 3i cũng hỗ trợ công nghệ GPU Turbo, hứa hẹn mang lại trải nghiệm chơi game mượt mà.</p><h3>Mở khóa khuôn mặt ấn tượng</h3><p>Mở khóa khuôn mặt đang là trào lưu khá hot hiện nay trên các dòng điện thoại thông minh và với Huawei Nova 3i thì tất nhiên cũng không thể thiếu công nghệ này.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/179673/huawei-nova-3i2.jpg\" onclick=\"return false;\"><img alt=\"Tính năng mở khoá bằng khuôn mặt của điện thoại Huawei Nova 3i\" src=\"https://cdn.tgdd.vn/Products/Images/42/179673/huawei-nova-3i2.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/179673/huawei-nova-3i2.jpg\" title=\"Tính năng mở khoá bằng khuôn mặt của điện thoại Huawei Nova 3i\"></a></p><p>Ngoài ra cảm biến vân tay vẫn được giữ lại ở mặt lưng cho bạn có thể mở khóa trong những trường hợp không thể sử dụng khuôn mặt.</p><h3>Thời lượng pin tốt</h3><p>Với viên pin dung lượng 3.340 mAh kết hợp với một con chip HiSillicon tiết kiệm điện cho bạn sử dụng khá thoải mái trong một ngày.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/179673/huawei-nova-3i1.jpg\" onclick=\"return false;\"><img alt=\"Thời lượng pin Huawei Nova 3i\" src=\"https://cdn.tgdd.vn/Products/Images/42/179673/huawei-nova-3i1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/179673/huawei-nova-3i1.jpg\" title=\"Thời lượng pin Huawei Nova 3i\"></a></p></article>', 154, 10600000, 0, '', '', '2019-06-23 21:00:51', 1, '2019-06-23 21:00:51', 1, 1),
(51, 9, 'Huawei Y9 2019', 'huawei-y9-2019', 'huawei-y9-2019.jpg', '<h2><a href=\"https://www.thegioididong.com/dtdd/huawei-y9-2019\" target=\"_blank\" title=\"Tham khảo điện thoại Huawei Y9 (2019) tại Thegioididong.com\">Huawei Y9 (2019)</a> là chiếc <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại chính hãng tại Thegioididong.com\">smartphone</a> được thiết kế dành riêng cho giới trẻ, với các màu sắc thời trang đi kèm nhiều xu hướng công nghệ mới nhất.</h2><h3>Thiết kế thu hút từ cái nhìn đầu tiên</h3><p>Rất nhanh chóng thì <a href=\"https://www.thegioididong.com/dtdd-huawei\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại Huawei  chính hãng tại Thegioididong.com\">Huawei</a> đã mang lên chiếc Y9 (2019) thiết kế độc đáo với mặt lưng có thể thay đổi màu sắc trong môi trường và điều kiện khác nhau.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/192313/huawei-y9-2019-1-1.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại Huawei Y9 2019\" src=\"https://cdn.tgdd.vn/Products/Images/42/192313/huawei-y9-2019-1-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/192313/huawei-y9-2019-1-1.jpg\" title=\"Thiết kế điện thoại Huawei Y9 2019\"></a></p><p>Bạn sẽ bị \"hút hồn\" ngay từ cái nhìn đầu tiên với vẻ ngoài của Huawei Y9 (2019).</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/192313/huawei-y9-2019-4.jpg\" onclick=\"return false;\"><img alt=\"Các góc cạnh của điện thoại Huawei Y9 2019\" src=\"https://cdn.tgdd.vn/Products/Images/42/192313/huawei-y9-2019-4.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/192313/huawei-y9-2019-4.jpg\" title=\"Các góc cạnh của điện thoại Huawei Y9 2019\"></a></p><h3>Màn hình \"tai thỏ\" FullView kích thước lớn</h3><p>Huawei Y9 (2019) sở hữu màn hình FullView kích thước 6.5 inch độ phân giải Full HD+ cho góc nhìn tốt, màu sắc thể hiện trung thực.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/192313/huawei-y9-2019-7.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại Huawei Y9 2019\" src=\"https://cdn.tgdd.vn/Products/Images/42/192313/huawei-y9-2019-7.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/192313/huawei-y9-2019-7.jpg\" title=\"Màn hình điện thoại Huawei Y9 2019\"></a></p><p>Màn hình này cũng đi theo xu hướng \"tai thỏ\" cùng phần \"cằm dưới\" khá mỏng tối ưu hóa không gian hiển thị với kích thước máy không quá lớn.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/192313/huawei-y9-2019-10.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại Huawei Y9 2019\" src=\"https://cdn.tgdd.vn/Products/Images/42/192313/huawei-y9-2019-10.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/192313/huawei-y9-2019-10.jpg\" title=\"Màn hình điện thoại Huawei Y9 2019\"></a></p><h3>Camera chụp ảnh chất lượng tốt</h3><p>Huawei Y9 2019 mang trong mình tới 4 camera bao gồm camera kép phía trước độ phân giải 16 MP (chính) + 2 MP (phụ) và camera kép phía sau là 13 MP (chính) + 2 MP (phụ), cả hai cụm camera đều tích hợp với công nghệ chụp ảnh AI (trí tuệ nhân tạo).</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/192313/huawei-y9-2019-16.jpg\" onclick=\"return false;\"><img alt=\"Camera sau điện thoại Huawei Y9 2019\" src=\"https://cdn.tgdd.vn/Products/Images/42/192313/huawei-y9-2019-16.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/192313/huawei-y9-2019-16.jpg\" title=\"Camera sau điện thoại Huawei Y9 2019\"></a></p><p>Những tích hợp nâng cao này giúp việc chụp ảnh nói chung và chụp ảnh phong cảnh nói riêng đạt được cấp độ chuyên nghiệp.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/192313/huawei-y9-2019-15.jpg\" onclick=\"return false;\"><img alt=\"Camera trước điện thoại Huawei Y9 2019\" src=\"https://cdn.tgdd.vn/Products/Images/42/192313/huawei-y9-2019-15.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/192313/huawei-y9-2019-15.jpg\" title=\"Camera trước điện thoại Huawei Y9 2019\"></a></p><p>Đồng thời máy cũng cho ra những bức selfie lung linh để phục vụ cho sở thích và lối sống năng động của giới trẻ.</p><h3>Công nghệ nhận diện vân tay 4.0</h3><p>Với công nghệ nhận diện vân tay 4.0, cho phép người dùng mở khóa Huawei Y9 (2019) chỉ trong 0.3 giây.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/192313/huawei-y9-2019-11.jpg\" onclick=\"return false;\"><img alt=\"Cảm biến vân tay ở mặt lưng điện thoại Huawei Y9 2019\" src=\"https://cdn.tgdd.vn/Products/Images/42/192313/huawei-y9-2019-11.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/192313/huawei-y9-2019-11.jpg\" title=\"Cảm biến vân tay ở mặt lưng điện thoại Huawei Y9 2019\"></a></p><p>Bạn cũng có thể mở khóa bằng khuôn mặt được trang bị sẵn với tốc độ khá ấn tượng.</p><h3>Hiệu năng tốt, thoải mái chiến game</h3><p>Cung cấp sức mạnh cho Huawei Y9 là vi xử lý Kirin 710, RAM tùy chọn 4 GB, bộ nhớ trong 64 GB cho bạn thoải mái lưu trữ dữ liệu.</p><p>Theo Huawei thì với AI Power 7.0 Huawei Y9 (2019) sẽ mang đến cho người dùng hiệu suất mạnh mẽ và thời gian sử dụng lâu hơn.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/192313/huawei-y9-2019-8.jpg\" onclick=\"return false;\"><img alt=\"Trải nghiệm điện thoại Huawei Y9 2019\" src=\"https://cdn.tgdd.vn/Products/Images/42/192313/huawei-y9-2019-8.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/192313/huawei-y9-2019-8.jpg\" title=\"Trải nghiệm điện thoại Huawei Y9 2019\"></a></p><p>Bên cạnh đó, máy còn được tích hợp công nghệ GPU Turbo, mang lại hiệu năng cao, tiết kiệm điện năng hơn, chất lượng hình ảnh cao và khả năng chơi game trơn tru.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/192313/huawei-y9-2019-14.jpg\" onclick=\"return false;\"><img alt=\"Đánh giá điện thoại Huawei Y9 2019\" src=\"https://cdn.tgdd.vn/Products/Images/42/192313/huawei-y9-2019-14.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/192313/huawei-y9-2019-14.jpg\" title=\"Đánh giá điện thoại Huawei Y9 2019\"></a></p><p><div class=video allow=\"autoplay; encrypted-media\" allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"https://www.youtube.com/embed/YqjIFpN4zH8\" width=\"560\"></div></p></article>', 24, 22300000, 0, '', '', '2019-06-23 21:00:52', 1, '2019-06-23 21:00:52', 1, 1);
INSERT INTO `uml_product` (`product_id`, `product_catid`, `product_name`, `product_slug`, `product_img`, `product_detail`, `product_number`, `product_price`, `product_pricesale`, `product_metakey`, `product_metadesc`, `product_createdat`, `product_createdby`, `product_updatedat`, `product_updatedby`, `product_status`) VALUES
(52, 9, 'Huawei Y7 Pro 2019', 'huawei-y7-pro-2019', 'huawei-y7-pro-2019.jpg', '<h2>Hoàn toàn lột xác so với phiên bản tiền nhiệm, <a href=\"https://www.thegioididong.com/dtdd/huawei-y7-pro-2019\" target=\"_blank\" title=\"Chi tiết điện thoại Huawei Y7 Pro 2019\">Y7 Pro (2019)</a> đã giúp <a href=\"https://www.thegioididong.com/dtdd-huawei\" target=\"_blank\" title=\"Tham khảo các dòng smartphone Huawei chính hãng\" type=\"Tham khảo các dòng smartphone Huawei chính hãng\">Huawei</a> có thêm điểm cộng trong mắt người dùng nhờ việc đem thiết kế mặt lưng gradient, màn hình giọt nước và pin khủng lên chiếc <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng smartphone chính hãng\" type=\"Tham khảo các dòng smartphone chính hãng\">smartphone</a> giá rẻ của mình.</h2><h3>Màn hình hiển thị giọt nước cực lớn</h3><p>Y7 Pro (2019) được trang bị màn hình rộng lên đến 6.26 inch, với độ phân giải HD+.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/197205/huawei-y7-pro-2019-1-1.jpg\" onclick=\"return false;\"><img alt=\"Màn hình Huawei Y7 Pro 2019 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/197205/huawei-y7-pro-2019-1-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/197205/huawei-y7-pro-2019-1-1.jpg\" title=\"Màn hình Huawei Y7 Pro 2019 chính hãng\"></a></p><p>Cùng với <a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-ips-lcd-la-gi-905752\" target=\"_blank\" title=\"Tìm hiểu về công nghệ IPS LCD\" type=\"Tìm hiểu về công nghệ IPS LCD\">tấm nền IPS</a>, hình ảnh hiển thị trên máy rõ nét và đầy đủ, màu sắc tự nhiên trên mọi góc nhìn. Người dùng hoàn toàn có thể trải nghiệm thế giới đầy màu sắc trong tầm tay.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/197205/huawei-y7-pro-2019-10.jpg\" onclick=\"return false;\"><img alt=\"Màn hình Huawei Y7 Pro 2019 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/197205/huawei-y7-pro-2019-10.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/197205/huawei-y7-pro-2019-10.jpg\" title=\"Màn hình Huawei Y7 Pro 2019 chính hãng\"></a></p><h3>Mặt lưng chuyển màu gradient cao cấp</h3><p>Tưởng rằng mặt lưng gradient chỉ áp dụng cho các dòng sản phẩm tầm trung hoặc cao cấp, thì nay với Y7 Pro (2019) bạn đã có thể sở hữu được một thiết bị có thiết kế đầy nổi bật với cá tính.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/197205/huawei-y7-pro-2019-6.jpg\" onclick=\"return false;\"><img alt=\"Mặt lưng Huawei Y7 Pro 2019 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/197205/huawei-y7-pro-2019-6.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/197205/huawei-y7-pro-2019-6.jpg\" title=\"Mặt lưng Huawei Y7 Pro 2019 chính hãng\"></a></p><p>Phần khung viền của máy được bo cong mềm mại, giúp khả năng cầm nắm trơn tru, gọn trong lòng bàn tay, mọi thao tác đều dễ dàng và nhanh chóng.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/197205/huawei-y7-pro-2019-2.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế Huawei Y7 Pro 2019 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/197205/huawei-y7-pro-2019-2.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/197205/huawei-y7-pro-2019-2.jpg\" title=\"Thiết kế Huawei Y7 Pro 2019 chính hãng\"></a></p><h3>Khả năng nhiếp ảnh thông minh</h3><p>Với <a href=\"https://www.thegioididong.com/tin-tuc/cong-nghe-camera-kep-2-camera-la-gi-giong-va-khac-nhau-nhu-the-nao--962027\" target=\"_blank\" title=\"Tìm hiểu về camera kép trên smartphone\" type=\"Tìm hiểu về camera kép trên smartphone\">camera kép</a> mặt sau tích hợp <a href=\"https://www.thegioididong.com/tin-tuc/ai-la-gi-sao-tri-tue-nhan-tao-lam-smartphone-thong-minh-hon-955420\" target=\"_blank\" title=\"Tìm hiểu về trí tuệ nhân tạo AI\" type=\"Tìm hiểu về trí tuệ nhân tạo AI\">trí tuệ nhân tạo AI</a> bao gồm thấu kính chính 13 MP + thấu kính phụ 2 MP, Y7 Pro (2019) có thể dễ dàng nhận ra các vật thể, lấy nét tốt với ánh sáng hợp lý hơn nhờ khẩu độ f/1.8.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/197205/huawei-y7-pro-2019-4.jpg\" onclick=\"return false;\"><img alt=\"Cụm camera kép Huawei Y7 Pro 2019 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/197205/huawei-y7-pro-2019-4.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/197205/huawei-y7-pro-2019-4.jpg\" title=\"Cụm camera kép Huawei Y7 Pro 2019 chính hãng\"></a></p><p>Camera AI trên máy nhận biết được hơn 500 bối cảnh thuộc 22 danh mục ngay lập tức, do đó mọi ảnh chụp của bạn đều trở nên chân thực và đẹp nhất.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/197205/huawei-y7-pro-2019-5.jpg\" onclick=\"return false;\"><img alt=\"Camera trước Huawei Y7 Pro 2019 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/197205/huawei-y7-pro-2019-5.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/197205/huawei-y7-pro-2019-5.jpg\" title=\"Camera trước Huawei Y7 Pro 2019 chính hãng\"></a></p><p>Ngoài ra, camera selfie 16 MP có khả năng tự động tinh chỉnh, bạn không cần tốn công chỉnh sửa hình ảnh và tự tin up ngay tấm hình vừa chụp lên mạng xã hội chia sẻ với bạn bè.</p><h3>Dung lượng pin lớn 4000 mAh</h3><p>So với Y7 Pro (2018) có viên pin chỉ 3000 mAh thì phiên bản 2019 này đã có sự cải thiện tới 4000 mAh.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/197205/huawei-y7-pro-2019-9.jpg\" onclick=\"return false;\"><img alt=\"Đánh giá Huawei Y7 Pro 2019 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/197205/huawei-y7-pro-2019-9.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/197205/huawei-y7-pro-2019-9.jpg\" title=\"Đánh giá Huawei Y7 Pro 2019 chính hãng\"></a></p><p>Cùng với công nghệ tiết kiệm năng lượng bằng AI, Huawei Y7 Pro (2019) giúp bạn thoải mái sử dụng, nhất là trong các chuyến đi dài, tha hồ trải nghiệm cuộc sống mà không lo sạc pin.</p><h3>Hiệu năng mượt mà và thông minh hơn</h3><p>Huawei Y7 Pro (2019) mang trong mình con chip 8 nhân <a href=\"https://www.thegioididong.com/tin-tuc/qualcomm-snapdragon-450-ra-mat-sieu-tiet-kiem-pin-ho-tro-camera-kep-quick-charge-3-0-997437\" target=\"_blank\" title=\"Tìm hiểu về con chip Snapdragon 450\" type=\"Tìm hiểu về con chip Snapdragon 450\">Snapdragon 450</a> với RAM 3 GB. </p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/197205/huawei-y7-pro-2019-13.jpg\" onclick=\"return false;\"><img alt=\"Chơi game trên Huawei Y7 Pro 2019 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/197205/huawei-y7-pro-2019-13.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/197205/huawei-y7-pro-2019-13.jpg\" title=\"Chơi game trên Huawei Y7 Pro 2019 chính hãng\"></a></p><p>Cấu hình này đủ để đáp ứng các giải trí cơ bản thường ngày, cũng như xử lý ổn định các dòng game phổ biến hiện nay như Liên Quân ở mức cấu hình trung hay DDTank, Plants And Zombies,...</p><p><div class=video allow=\"accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"https://www.youtube.com/embed/eZfKW02quQU\" width=\"560\"></div></p><p>Y7 Pro (2019) không có <a href=\"https://www.thegioididong.com/hoi-dap/mo-khoa-bang-dau-van-tay-645010\" target=\"_blank\" title=\"Tìm hiểu về cảm biến vân tay\" type=\"Tìm hiểu về cảm biến vân tay\">cảm biến vân tay</a> ở mặt lưng, nhưng bạn hoàn toàn yên tâm bởi <a href=\"https://www.thegioididong.com/hoi-dap/mo-khoa-man-hinh-bang-khuon-mat-tren-android-646039\" target=\"_blank\" title=\"Tìm hiểu mở khoá khuôn mặt trên smartphone\" type=\"Tìm hiểu mở khoá khuôn mặt trên smartphone\">công nghệ nhận diện khuôn mặt</a> sẽ giúp bạn mở khoá máy nhanh chóng với độ bảo mật cao.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/197205/huawei-y7-pro-2019-11.jpg\" onclick=\"return false;\"><img alt=\"Mở khoá khuôn mặt Huawei Y7 Pro 2019 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/197205/huawei-y7-pro-2019-11.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/197205/huawei-y7-pro-2019-11.jpg\" title=\"Mở khoá khuôn mặt Huawei Y7 Pro 2019 chính hãng\"></a></p><p><div class=video allow=\"accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"https://www.youtube.com/embed/SGoi9Dswneo\" width=\"560\"></div></p></article>', 88, 28200000, 28980000, '', '', '2019-06-23 21:00:53', 1, '2019-06-23 21:00:53', 1, 1),
(53, 9, 'Huawei Y6 Prime', 'huawei-y6-prime', 'huawei-y6-prime.jpg', '<h2><a href=\"https://www.thegioididong.com/dtdd/huawei-y6-prime\" target=\"_blank\" title=\"Chi tiết điện thoại Huawei Y6 Prime\" type=\"Chi tiết điện thoại Huawei Y6 Prime\">Huawei Y6 Prime</a> là chiếc <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại di động chính hãng\" type=\"Tham khảo các dòng điện thoại di động chính hãng\">smartphone</a> giá rẻ với màn hình tràn viền kích thước lớn.</h2><h3>Thiết kế cứng cáp</h3><p><a href=\"https://www.thegioididong.com/dtdd-huawei\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại Huawei chính hãng\" type=\"Tham khảo các dòng điện thoại Huawei chính hãng\">Huawei</a> tiếp tục mang lên một chiếc smartphone giá rẻ của mình màn hình tràn viền 18:9 với kích thước 5.7 inch.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/158937/huawei-y6-prime1.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại Huawei Y6 Prime 2018\" src=\"https://cdn.tgdd.vn/Products/Images/42/158937/huawei-y6-prime1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/158937/huawei-y6-prime1.jpg\" title=\"Thiết kế điện thoại Huawei Y6 Prime 2018\"></a></p><p><em>Màu sắc sang trọng, quý phái</em></p><p>Các góc cạnh được bo cong mềm mại mang lại cảm giác cầm nắm khá thoải mái.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/158937/huawei-y6-prime2.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại Huawei Y6 Prime 2018\" src=\"https://cdn.tgdd.vn/Products/Images/42/158937/huawei-y6-prime2.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/158937/huawei-y6-prime2.jpg\" title=\"Thiết kế điện thoại Huawei Y6 Prime 2018\"></a></p><p><em>Mặt lưng bo cong mềm mại</em></p><p>Các phím bấm được gia công tỉ mỉ có độ đàn hồi cao giúp bạn dễ dàng sử dụng.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/158937/huawei-y6-prime3.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại Huawei Y6 Prime 2018\" src=\"https://cdn.tgdd.vn/Products/Images/42/158937/huawei-y6-prime3.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/158937/huawei-y6-prime3.jpg\" title=\"Thiết kế điện thoại Huawei Y6 Prime 2018\"></a></p><p><em>Các chi tiết gia công tỉ mỉ</em></p><p>Điểm yếu có lẽ vẫn là độ phân giải chỉ dừng lại ở mức HD+ khiến chất lượng hiển thị sẽ không được sắc nét như những màn hình Full HD.</p><h3>Hiệu năng khá</h3><p>Huawei Y6 Prime sở hữu chip Snapdragon 425 cùng RAM 2 GB. Tuy cấu hình không cao nhưng vẫn đảm bảo giúp bạn có những trải nghiệm tốt nhất, đáp ứng được nhu cầu sử dụng đơn giản cơ bản hằng ngày.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/158937/huawei-y6-prime4.jpg\" onclick=\"return false;\"><img alt=\"Đánh giá điện thoại Huawei Y6 Prime 2018\" src=\"https://cdn.tgdd.vn/Products/Images/42/158937/huawei-y6-prime4.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/158937/huawei-y6-prime4.jpg\" title=\"Đánh giá điện thoại Huawei Y6 Prime 2018\"></a></p><p><em>Khá đáng tiếc khi máy vẫn dùng cổng micro USB</em></p><p>Chiếc smartphone này đang chạy trên giao diện EMUI được tùy biến trên hệ điều hành <a href=\"https://www.thegioididong.com/tin-tuc/android-8-la-gi-vi-sao-co-ten-goi-khac-la-android-o--966604\" target=\"_blank\" title=\"Android 8.0 (Oreo)\">Android 8.0 (Oreo)</a>.</p><h3>Camera chất lượng</h3><p>Sở hữu camera chính độ phân giải 13 MP với khả năng lấy nét nhanh cho phép bạn bắt trọn mọi khoảnh khắc đáng nhớ. Ngoài ra máy còn hỗ trợ chế độ Time-Lapse giúp bạn ghi lại những khoảnh khắc thú vị.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/158937/huawei-y6-prime6.jpg\" onclick=\"return false;\"><img alt=\"Camera sau điện thoại Huawei Y6 Prime 2018\" src=\"https://cdn.tgdd.vn/Products/Images/42/158937/huawei-y6-prime6.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/158937/huawei-y6-prime6.jpg\" title=\"Camera sau điện thoại Huawei Y6 Prime 2018\"></a></p><p><em>Camera được nâng cấp mạnh mẽ</em></p><p>Camera trước độ phân giải 8 MP với tính năng làm đẹp được trang bị sẵn giúp bạn thoải mái selfie với bạn bè.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/158937/huawei-y6-prime7.jpg\" onclick=\"return false;\"><img alt=\"Camera selfie điện thoại Huawei Y6 Prime 2018\" src=\"https://cdn.tgdd.vn/Products/Images/42/158937/huawei-y6-prime7.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/158937/huawei-y6-prime7.jpg\" title=\"Camera selfie điện thoại Huawei Y6 Prime 2018\"></a></p><p><em>Ảnh selfie sắc nét, làm đẹp tự nhiên</em></p><h3>Tính năng khác</h3><p>Pin của máy có dung lượng là 3.000 mAh đủ đáp ứng nhu cầu sử dụng trong khoảng 1 ngày.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/158937/huawei-y6-prime5.jpg\" onclick=\"return false;\"><img alt=\"Sử dụng điện thoại Huawei Y6 Prime 2018\" src=\"https://cdn.tgdd.vn/Products/Images/42/158937/huawei-y6-prime5.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/42/158937/huawei-y6-prime5.jpg\" title=\"Sử dụng điện thoại Huawei Y6 Prime 2018\"></a></p><p><em>Pin khá, thoải mái sử dụng</em></p><p>Máy có 2 khe cắm sim cùng một số kết nối phổ biến như Wi-Fi b/g/n, micro USB và jack cắm tai nghe 3.5mm.</p><p>Đặc biệt, máy vừa hỗ trợ mở khóa vân tay ở mặt lưng đồng thời nhận diện khuôn mặt giúp bạn mở máy an toàn, nhanh chóng và chính xác.</p></article>', 53, 29300000, 30080000, '', '', '2019-06-23 21:00:54', 1, '2019-06-23 21:00:54', 1, 1),
(54, 11, 'MacBook Air 2018 128GB MREE2SA Trả góp 0%, tặng PMH 500.000đ', 'macbook-air-2018-128gb-mree2sa-tra-gop-0-tang-pmh-500-000d', 'macbook-air-2018-128gb-mree2sa-tra-gop-0-tang-pmh-500-000d.jpg', '<h2><a href=\"https://www.thegioididong.com/laptop/apple-macbook-air-mree2sa-a-i5-8gb-128gb-133-gold\" target=\"_blank\" title=\"Macbook air chính hãng đang kinh doanh tại Thegioididong.com\">Macbook Air 2018</a> sở hữu vẻ ngoài sang trọng và mỏng nhẹ. Cấu hình đáp ứng đầy đủ nhu cầu sử dụng văn phòng, giải trí cùng thời lượng pin đủ để bạn sử dụng suốt cả ngày dài.</h2><p><div class=video allow=\"accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"https://www.youtube.com/embed/G7iI7YmIr30\" width=\"560\"></div></p><h3>Đáp ứng tuyệt vời nhu cầu văn phòng</h3><p>Dung lượng RAM 8 GB của Macbook Air 2018 cho phép bạn sử dụng cùng lúc nhiều ứng dụng, mở nhiều tab trình duyệt mà không bị đứng, giật.</p><p>Chiếc <a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Laptop chính hãng đang kinh doanh tại Thegioididong.com\">laptop</a> <strong>Apple </strong>sở hữu ổ cứng SSD cho tốc độ khởi chạy các ứng dụng trong chớp mắt, dung lượng 128 GB là đủ dùng với nhu cầu lưu trữ tài liệu, hình ảnh, bài thuyết trình...</p><p>Mẫu <a href=\"https://www.thegioididong.com/laptop-apple-macbook\" target=\"_blank\" title=\"Macbook chính hãng đang được kinh doanh tại Thegioididong.com\">Macbook</a> 2018 còn cho phép bạn có thể sử dụng mượt mà các ứng dụng thiết kế chuyên nghiệp như Photoshop, AI,... </p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/44/197305/apple-macbook-air-mree2sa-a-i5-8gb-128gb-133-gold-content-cauhinhtgdd.jpg\" onclick=\"return false;\"><img alt=\"Hiệu năng mạnh mẽ trên laptop nhỏ gọn Macbook Air MREE2SA/A (2018)\" src=\"https://cdn.tgdd.vn/Products/Images/44/197305/apple-macbook-air-mree2sa-a-i5-8gb-128gb-133-gold-content-cauhinhtgdd.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/44/197305/apple-macbook-air-mree2sa-a-i5-8gb-128gb-133-gold-content-cauhinhtgdd.jpg\" title=\"Hiệu năng mạnh mẽ trên laptop nhỏ gọn Macbook Air MREE2SA/A (2018)\"></a></p><h3><a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-retina-la-gi-905780\" target=\"_blank\" title=\"Màn hình Retina là gì? | Thegioididiong.com\">Màn hình Retina</a> độ phân giải cao và sắc nét </h3><p>Màn hình của Macbook Air 2018 là điểm cải tiến nổi bật nhất so với thế hệ trước. Viền màn hình mỏng hơn 50% cùng độ phân giải cao 2K Retina mang lại trải nghiệm hình ảnh sắc nét, chân thực hơn, cho bạn yêu hơn những gì bạn thấy. </p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/44/197305/apple-macbook-air-mree2sa-a-i5-8gb-128gb-133-gold-content-manhinhtgdd.jpg\" onclick=\"return false;\"><img alt=\"Màn hình retina sắc nét trên laptop nhỏ gọn Macbook Air MREE2SA/A (2018)\" src=\"https://cdn.tgdd.vn/Products/Images/44/197305/apple-macbook-air-mree2sa-a-i5-8gb-128gb-133-gold-content-manhinhtgdd.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/44/197305/apple-macbook-air-mree2sa-a-i5-8gb-128gb-133-gold-content-manhinhtgdd.jpg\" title=\"Màn hình retina sắc nét trên laptop nhỏ gọn Macbook Air MREE2SA/A (2018)\"></a></p><h3>Thiết kế sang trọng, độ mỏng ấn tượng</h3><p>Macbook Air 2018 được làm từ 100% là nhôm tái chế. Đặc biệt, với cân nặng chỉ 1,25kg và mỏng 15,6mm giúp bạn dễ dàng mang theo mọi lúc, mọi nơi.</p><h3>Pin dùng được cả ngày</h3><p>Thời lượng pin trên Macbook Air 2018 lên đến 12 giờ trong điều kiện sử dụng bình thường. 7 - 8 giờ khi sử dụng hỗn hợp các tác vụ Chrome, Photoshop, ứng dụng văn phòng… Thoải mái cho một ngày làm việc.</p><h2><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/44/197305/apple-macbook-air-mree2sa-a-i5-8gb-128gb-133-gold-content-thietke-1.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế hiện đại, siêu nhẹ trên laptop nhỏ gọn macbook air 2018\" src=\"https://cdn.tgdd.vn/Products/Images/44/197305/apple-macbook-air-mree2sa-a-i5-8gb-128gb-133-gold-content-thietke-1.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/44/197305/apple-macbook-air-mree2sa-a-i5-8gb-128gb-133-gold-content-thietke-1.jpg\" title=\"Thiết kế hiện đại, siêu nhẹ trên laptop nhỏ gọn macbook air 2018\"></a></h2><h3>Cảm biến vân tay, bảo mật cao </h3><p>Dòng Macbook Air 2018 được cải biến với cảm biến vân tay Touch ID tích hợp thẳng vào nút nguồn. Chip bảo mật riêng Apple T2 nhận dạng chính xác và giúp mở khóa nhanh chóng.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/44/197305/apple-macbook-air-mree2sa-a-i5-8gb-128gb-133-gold-content-vantaytgdd.jpg\" onclick=\"return false;\"><img alt=\"Cảm biến vân tay trên nút nguồn trên laptop nhỏ gọn Macbook Air MREE2SA/A (2018)\" src=\"https://cdn.tgdd.vn/Products/Images/44/197305/apple-macbook-air-mree2sa-a-i5-8gb-128gb-133-gold-content-vantaytgdd.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/44/197305/apple-macbook-air-mree2sa-a-i5-8gb-128gb-133-gold-content-vantaytgdd.jpg\" title=\"Cảm biến vân tay trên nút nguồn trên laptop nhỏ gọn Macbook Air MREE2SA/A (2018)\"></a></p><h3>Tích hợp nhiều tính năng cao cấp từ Macbook Pro</h3><p>Loa âm thanh nổi cung cấp âm trầm gấp đôi và âm lượng lớn hơn 25 phần trăm so với thế hệ trước.  Âm thanh nổi rộng sống động cho bạn đắm mình vào các bài hát, bộ phim,...</p><p>Bàn di chuột <a href=\"https://www.thegioididong.com/hoi-dap/force-touch-trackpad-tren-macbook-la-gi-956432\" target=\"_blank\" title=\"Force Touch TrackPad trên Macbook là gì?\" type=\"Force Touch TrackPad trên Macbook là gì?\"><strong>cảm ứng lực Force Touch</strong></a> cho phép bạn tương tác với máy Mac của mình theo nhiều cách khác nhau. Bàn phím Butterfly giúp ít phát ra âm thanh khi gõ, đèn nền LED với cảm biến ánh sáng cho bạn có thể sử dụng Macbook trong điều kiện thiếu sáng thoải mái hơn.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/44/197305/apple-macbook-air-mree2sa-a-i5-8gb-128gb-133-gold-content-banphimtgdd.jpg\" onclick=\"return false;\"><img alt=\"Màn phím cánh bướm thế hệ 3 trên laptop nhỏ gọn Macbook Air MREE2SA/A (2018)\" src=\"https://cdn.tgdd.vn/Products/Images/44/197305/apple-macbook-air-mree2sa-a-i5-8gb-128gb-133-gold-content-banphimtgdd.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/44/197305/apple-macbook-air-mree2sa-a-i5-8gb-128gb-133-gold-content-banphimtgdd.jpg\" title=\"Màn phím cánh bướm thế hệ 3 trên laptop nhỏ gọn Macbook Air MREE2SA/A (2018)\"></a></p><p>Cổng kết nối <a href=\"https://www.thegioididong.com/hoi-dap/thunderbolt-3-usbc-la-gi-959274\" target=\"_blank\" title=\"Thunderbolt 3 USB - C là gì? | Thegioididong.com\">Type C Thunderbolt 3</a> đa năng: truyền dữ liệu, sạc và xuất video trong một đầu nối duy nhất.</p><p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/44/197305/apple-macbook-air-mree2sa-a-i5-8gb-128gb-133-gold-content-congketnoi.jpg\" onclick=\"return false;\"><img alt=\"Cổng type C chuẩn Thunderbolt 3 đầy đủ chức năng trên laptop nhỏ gọn Macbook Air MREE2SA/A (2018)\" src=\"https://cdn.tgdd.vn/Products/Images/44/197305/apple-macbook-air-mree2sa-a-i5-8gb-128gb-133-gold-content-congketnoi.jpg\" class=\'lazy\' src=\"https://cdn.tgdd.vn/Products/Images/44/197305/apple-macbook-air-mree2sa-a-i5-8gb-128gb-133-gold-content-congketnoi.jpg\" title=\"Cổng type C chuẩn Thunderbolt 3 đầy đủ chức năng trên laptop nhỏ gọn Macbook Air MREE2SA/A (2018)\"></a></p></article>', 156, 8700000, 0, '', '', '2019-06-23 21:04:33', 1, '2019-06-23 21:04:33', 1, 1),
